GGRC.Templates['components/search-toolbar/search-toolbar']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="input-filters flex-box">\n    <div class="flex-size-1">\n        <h6>Object type</h6>\n        <mapping-type-selector types="mapper.types" selected-type="mapper.type"\n                               disabled="isLoadingOrSaving"></mapping-type-selector>\n    </div>\n    <div class="flex-size-1">\n        <div class="modal-search">\n            <h6>Matching terms</h6>\n            <div class="modal-search objective-selector">\n                <input id="search" class="input-block-level" placeholder="Enter text to search..."\n                       can-value="mapper.term" type="text" autofocus>\n            </div>\n        </div>\n    </div>\n    <div class="flex-size-1">\n        <h6>Filter by owner or contact</h6>\n        <div class="modal-search objective-selector">\n            <autocomplete\n                    search-items-type="Person"\n                    can-item-selected="mapper.setContact"\n                    disable="mapper.is_saving"\n                    placeholder="Find owner or contact"\n                    value="mapper.contactEmail"\n                    class-name="search-by-owner input-block-level"\n            ></autocomplete>\n        </div>\n    </div>\n</div>\n<div class="mapping-filters">\n    <relevant-filter search_only="mapper.search_only"\n                     type="mapper.type"\n                     relevant-to="mapper.relevantTo"\n                     relevant="mapper.relevant"\n    ></relevant-filter>\n</div>\n';
GGRC.Templates['components/mapping-controls/mapping-type-selector']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<select {{#disabled}}disabled{{/disabled}}\n        class="input-block-level"\n        can-value="selectedType"\n        name="type-select">\n  {{#each types}}\n    {{#if items}}\n      {{#if items.length}}\n          <optgroup label="{{name}}">\n            {{#each items}}\n              {{#if name}}\n                  <option data-singular="{{singular}}"\n                          data-plural="{{plural}}"\n                          value="{{value}}"\n                          label="{{name}}"></option>\n              {{/if}}\n            {{/each}}\n          </optgroup>\n      {{/if}}\n    {{else}}\n      {{#if name}}\n          <option data-singular="{{singular}}" data-plural="{{plural}}" value="{{value}}" label="{{name}}"></option>\n      {{/if}}\n    {{/if}}\n  {{/each}}\n</select>\n';
GGRC.Templates['components/effective-dates/effective-dates']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div data-id="effective_date_hidden" class="span4 hidable">\n  <a data-id="hide_effective_date_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n  <datepicker\n      tabindex="20"\n      label="configStartDate.label"\n      date="instance.start_date"\n      set-max-date="instance.end_date"\n      helptext="configStartDate.helpText"\n      test-id="startTestId"\n      {{#configStartDate.required}}required="required"{{/configStartDate.required}}\n  />\n</div>\n\n<div data-id="stop_date_hidden" class="span4 hidable">\n  <a data-id="hide_stop_date_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n  <datepicker\n      tabindex="21"\n      label="configEndDate.label"\n      date="instance.end_date"\n      set-min-date="instance.start_date"\n      helptext="configEndDate.helpText"\n      test-id="endTestId"\n      {{#configEndDate.required}}required="required"{{/configEndDate.required}}\n  />\n</div>\n';
GGRC.Templates['components/read-more/read-more']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{#if text}}\n      {{#if overflowing}}\n        <div class="read-more__body">\n          {{#if expanded}}\n            {{{text}}}\n          {{else}}\n            {{resultedText}}\n          {{/if}}\n        </div>\n          <div class="read-more__controls">\n              <a can-click="toggle" class="read-more__btn">{{btnText}}</a>\n          </div>\n      {{else}}\n        {{{text}}}\n      {{/if}}\n{{else}}\n    <span class="empty-message">None</span>\n{{/if}}\n';
