GGRC.Templates['components/assessment/controls-toolbar/controls-toolbar']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<button class="btn btn-small btn-draft btn-extra-gray" can-click="showRelatedResponses" title="Show Prior Audit Responses">Prior Audit Responses</button>\n {{> \'/static/mustache/mixins/stateful.mustache\' }}\n<simple-modal instance="instance" modal-title="Prior Audit Responses" state="modal" extra-css-class="related-assessments">\n    {{> \'/static/mustache/assessments/related-assessments.mustache\' }}\n</simple-modal>\n';
GGRC.Templates['components/assessment/mapped-objects/mapped-controls']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<collapsible-panel-header title-text="titleText" expanded="expanded">\n  {{#is_allowed \'update\' instance context=\'for\'}}\n      <i class="fa fa-plus-circle"\n         rel="tooltip"\n         data-type="{{mappingType}}"\n         data-placement="right"\n         data-toggle="unified-mapper"\n         data-join-mapping="related_objects"\n         data-join-option-type="{{model.shortName}}"\n         data-join-object-id="{{instance.id}}"\n         data-join-object-type="{{instance.class.shortName}}"\n         data-original-title="Map {{firstnonempty mappingType \'Object\'}} to this {{firstnonempty instance.class.title_singular \'Object\'}}">\n      </i>\n  {{/is_allowed}}\n</collapsible-panel-header>\n<collapsible-panel-body expanded="expanded" class="collapsible-panel-body">\n    <mapped-objects mapping="{{mapping}}" selected-item="selectedItem" parent-instance="instance" filter="filter">\n       {{> \'/static/mustache/components/assessment/mapped-objects/mapped-controls-item.mustache\'}}\n    </mapped-objects>\n    <object-popover class="object-popover" item="selectedItem">\n        <assessment-mapped-controls-popover class="assessment-mapped-controls-popover" item="item" expanded="expanded"></assessment-mapped-controls-popover>\n    </object-popover>\n</collapsible-panel-body>\n';
GGRC.Templates['components/assessment/mapped-objects/mapped-controls-item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="mapped-objects__item">\n    <div class="title title-bold">\n        <i class="fa fa-{{instance.class.table_singular}}"></i>\n      {{{firstnonempty instance.title instance.description_inline instance.name instance.email \'\'}}}\n    </div>\n    <div class="description">\n      <read-more text="instance.description" max-text-length="90"></read-more>\n    </div>\n</div>\n';
GGRC.Templates['components/assessment/mapped-objects/mapped-controls-popover']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<!-- Objectives and Regulations Grids -->\n<collapsible-panel title-text="Show Related Objectives and Regulations" collapsed="true" expanded="expanded"\n                   extra-css-class="no-padding">\n    <div class="grid-data-simple">\n        <!-- Header -->\n        <div class="grid-data-header flex-box flex-box-single flex-row">\n            <div class="flex-size-1">\n                <i class="fa fa-objective color"></i>\n                Objectives ({{objectives.length}})\n            </div>\n            <div class="flex-size-1">\n                <i class="fa fa-regulation color"></i>\n                Regulations ({{regulations.length}})\n            </div>\n        </div>\n        <!-- Sub Header -->\n        <div class="grid-data-sub-header flex-box flex-box-single flex-row">\n            <div class="flex-box flex-row flex-box-single flex-size-1">\n                <div class="flex-size-5">\n                    Objective Description\n                </div>\n                <div class="flex-size-3">\n                    Notes\n                </div>\n            </div>\n            <div class="flex-box flex-row flex-box-single flex-size-1">\n                <div class="flex-size-5">\n                    Regulation Description\n                </div>\n                <div class="flex-size-3">\n                    Notes\n                </div>\n            </div>\n        </div>\n      <!-- Body -->\n        <div class="grid-data-body flex-box flex-box-single flex-row {{#isLoading}}loading{{/isLoading}}">\n            <spinner toggle="isLoading" extra-css-class="grid-spinner"\n                     class="spinner-wrapper {{#isLoading}}active{{/isLoading}}"></spinner>\n            <object-list items="objectives" class="flex-size-1">\n                <div class="grid-data-row flex-box flex-row flex-box-single">\n                    <div class="flex-box flex-row flex-box-single flex-size-1">\n                        <div class="flex-size-5 flex-size-limit">\n                            <div class="title-text">\n                              {{title}}\n                            </div>\n                            <read-more text="description"></read-more>\n                        </div>\n                        <read-more text="notes" class="flex-size-3 flex-size-limit"></read-more>\n                    </div>\n                </div>\n            </object-list>\n            <object-list items="regulations" class="flex-size-1">\n                <div class="grid-data-row flex-box flex-row flex-box-single">\n                    <div class="flex-box flex-row flex-box-single flex-size-1">\n                        <div class="flex-size-5 flex-size-limit">\n                            <div class="title-text">\n                              {{title}}\n                            </div>\n                            <read-more text="description"></read-more>\n                        </div>\n                        <read-more text="notes" class="flex-size-3 flex-size-limit"></read-more>\n                    </div>\n                </div>\n            </object-list>\n        </div>\n    </div>\n</collapsible-panel>\n';
GGRC.Templates['components/assessment/mapped-objects/mapped-related-information']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<collapsible-panel-header title-text="titleText" expanded="expanded">\n  {{#is_allowed \'update\' instance context=\'for\'}}\n      <i class="fa fa-plus-circle"\n         rel="tooltip"\n         data-type="{{mappingType}}"\n         data-placement="right"\n         data-toggle="unified-mapper"\n         data-join-mapping="related_objects"\n         data-join-option-type="{{model.shortName}}"\n         data-join-object-id="{{instance.id}}"\n         data-join-object-type="{{instance.class.shortName}}"\n         data-original-title="Map {{firstnonempty mappingType \'Object\'}} to this {{firstnonempty instance.class.title_singular \'Object\'}}">\n      </i>\n  {{/is_allowed}}\n</collapsible-panel-header>\n<collapsible-panel-body expanded="expanded" class="collapsible-panel-body">\n    <mapped-objects mapping="{{mapping}}" selected-item="selectedItem" parent-instance="instance" filter="filter">\n        {{> \'/static/mustache/components/assessment/mapped-objects/mapped-related-information-item.mustache\'}}\n    </mapped-objects>\n    <object-popover class="object-popover" item="selectedItem"></object-popover>\n</collapsible-panel-body>\n';
GGRC.Templates['components/assessment/mapped-objects/mapped-related-information-item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="mapped-objects__item">\n    <div class="mapped-objects__item-body" >\n        <div class="title">\n            <i class="fa fa-{{instance.class.table_singular}}"></i>\n          {{{firstnonempty instance.title instance.description_inline instance.name instance.email \'\'}}}\n        </div>\n    </div>\n</div>\n';
GGRC.Templates['components/assessment/urls-list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<h6>URL</h6>\n{{#is_allowed \'update\' instance context=\'for\'}}\n  {{#toggle show_new_object_form}}\n    <ggrc-quick-add\n              parent_instance="instance"\n              join_model="Relationship"\n              quick_create="create_url"\n        {{#if_in instance.status \'Completed,Verified\'}}\n              verify_event="true"\n              modal_description=\'You are about to move request from "{{instance.status}}" to "In Progress" - are you sure about that?\'\n              modal_title=\'Confirm moving Request to "In Progress"\'\n              modal_button=\'Confirm\'\n        {{/if_in}}\n      >\n        {{#prune_context}}\n            <div class="objective-selector field-wrap">\n                <input tabindex="3" type="text" name="instance" placeholder="Add URL">\n                <a href="javascript://" {{toggle_button}}><i class="fa fa-trash"></i></a>\n                <a href="javascript://" class="btn btn-small btn-success no-float" data-toggle="submit" {{toggle_button \'modal:success\'}}>Add</a>\n            </div>\n            <input type="hidden" name="role_name" value="Auditor" />\n        {{/prune_context}}\n      </ggrc-quick-add>\n  {{else}}\n      <a class="assessment-url-list__add-btn" {{toggle_button}}><i class="fa fa-plus-circle"></i></a>\n  {{/toggle}}\n{{/is_allowed}}\n<mapping-tree-view\n  parent-instance="instance"\n  mapping="instance.class.info_pane_options.urls.mapping"\n  item-template="instance.class.info_pane_options.urls.show_view"\n>\n</mapping-tree-view>\n{{#with_mapping instance.class.info_pane_options.urls.mapping instance}}\n  {{#info_related_objects}}\n    {{{render parent_instance.class.info_pane_options.urls.show_view instance=instance}}}\n  {{/info_related_objects}}\n{{/with_mapping}}\n';
GGRC.Templates['components/assessment/attachments-list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Evidence</h6>\n  {{#is_allowed \'update\' instance context=\'for\'}}\n    {{{render_hooks "Request.gdrive_evidence_storage"}}}\n    <div class="attach-file-info">(you can upload up to 10 files in a single batch)</div>\n  {{/is_allowed}}\n{{^if_null instance._mandatory_attachment_msg}}\n  <div class="alert alert-info alert-dismissible" role="alert">\n    {{instance._mandatory_attachment_msg}}\n    <button type="button" class="close" data-dismiss="alert" aria-label="Close">&times;</button>\n  </div>\n{{/if_null}}\n<mapping-tree-view\n  parent-instance="instance"\n  mapping="instance.class.info_pane_options.evidence.mapping"\n  item-template="instance.class.info_pane_options.evidence.show_view"\n>\n</mapping-tree-view>\n';
GGRC.Templates['components/assessment/custom-attributes']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{#items}}\n    <ca-object-validation class="flex-box flex-row" def="def" value-id="{{id}}" errors="preconditions_failed" value="attribute_value" modal="modal" is-modified="isModified">\n        <ca-object class="flex-box flex-col" instance="instance" value-id="{{id}}" def="def" value="attribute_value" modal="modal" type="attributeType" is-modified="isModified">\n            <ca-object-value-mapper value="attribute_value" type="type" value-obj="attribute_object" def="def">\n                <assessment-inline-edit\n                        value="input.value"\n                        options="input.options"\n                        type="input.type"\n                        title-text="input.title"\n                        placeholder="input.placeholder"\n                        is-saving="isSaving"\n                  {{^is_allowed \'update\' instance context=\'for\'}}\n                        readonly\n                  {{/is_allowed}}\n                  can-before-edit="instance.confirmBeginEdit"\n                ></assessment-inline-edit>\n            </ca-object-value-mapper>\n        </ca-object>\n    </ca-object-validation>\n{{/items}}\n<ca-object-modal modal="modal" instance="instance"></ca-object-modal>\n';
GGRC.Templates['components/assessment/inline/inline']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="inline-edit\n  {{#if isEdit}}inline-edit--active{{/if}}\n  {{#if readonly}}inline-edit--readonly{{/if}}">\n    <spinner toggle="isSaving"></spinner>\n  {{^if isSaving}}\n      <div class="inline-edit__content {{#if_equals type \'text\'}}inline-edit__content--wide{{/if_equals}}">\n        {{#renderInnerTemplateByType type}}{{/renderInnerTemplateByType}}\n          <ul class="inline-edit__controls {{#if isEdit}}inline-edit__controls--edit-mode{{/if}}">\n            {{#if isEdit}}\n                <li><a can-click="onSave"><i class="fa fa-check"></i></a></li>\n                <li><a can-click="onCancel"><i class="fa fa-times"></i></a></li>\n            {{else}}\n              {{^if readonly}}\n                  <li><a can-click="enableEdit"><i class="fa fa-pencil"></i></a></li>\n              {{/if}}\n            {{/if}}\n          </ul>\n      </div>\n  {{/if}}\n</div>\n';
GGRC.Templates['components/assessment/inline/text']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if isEdit}}\n  <rich-text text="context.value"></rich-text>\n{{else}}\n  <div class="inline-edit__text">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </div>\n{{/if}}\n';
GGRC.Templates['components/assessment/inline/input']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if isEdit}}\n  <input type="text" can-value="context.value" autofocus>\n{{else}}\n  <div class="inline-edit__text">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </div>\n{{/if}}\n';
GGRC.Templates['components/assessment/inline/dropdown']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if isEdit}}\n  <dropdown\n    options-list="options"\n    name="context.value"\n    no-value="true"\n    class-name="input-medium"\n    ></dropdown>\n{{else}}\n  <div class="inline-edit__value">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </div>\n{{/if}}\n';
GGRC.Templates['components/assessment/inline/date']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if isEdit}}\n  <datepicker persistent="true" date="context.value"></datepicker>\n{{else}}\n  <div class="inline-edit__text">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </div>\n{{/if}}\n';
GGRC.Templates['components/assessment/inline/person']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if isEdit}}\n  {{#if context.value}}\n    <a href="#" can-click="unsetPerson" class="inline-block">\n      <i class="fa fa-times" aria-hidden="true"></i>\n    </a>\n    <person-info class="inline-block" person-obj="context.value"></person-info>\n  {{/if}}\n  <autocomplete\n    search-items-type="Person"\n    can-item-selected="setPerson"\n    placeholder="Select person"\n    class-name=""\n  ></autocomplete>\n{{else}}\n  {{#if context.value}}\n    <person-info person-obj="context.value"></person-info>\n  {{else}}\n    <span class="empty-message">No person</span>\n  {{/if}}\n{{/if}}\n';
GGRC.Templates['components/assessment/inline/checkbox']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<label class="inline-edit__checkbox">\n  {{#if isEdit}}\n      <input can-value="context.value" type="checkbox" autofocus> {{titleText}}\n  {{else}}\n      <input disabled type="checkbox" can-value="context.value"> {{titleText}}\n  {{/if}}\n</label>\n';
