GGRC.Templates['components/assessment/comment/input']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="">\n    <rich-text class="input--textarea" {{#isSaving}}disabled="disabled"{{/isSaving}} placeholder="{{commentPlaceHolder}}" text="comment.value" can-keyup="removeEmptyMark"></rich-text>\n</div>\n';
GGRC.Templates['components/assessment/comment/controls']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="comment-control flex-box">\n  {{#if isSaving}}\n    <spinner toggle="isSaving"></spinner>\n  {{else}}\n      <button {{#if isEmpty}}disabled{{/if}} class="btn btn-success btn-small pull-right" can-click="save">Add</button>\n      <label>\n          <input type="checkbox" name="send_notification" can-value="comment.checked">\n          Send Notifications\n      </label>\n  {{/if}}\n</div>\n';
