GGRC.Templates['components/object-popover/object-popover']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="object-popover-wrapper {{#isActive}}active{{/isActive}}" style="{{openStyle}}">\n    <div class="object-popover-body">\n        {{#itemData}}\n          <div class="object-popover-title">\n              <a href="{{itemData.viewLink}}" target="_blank">{{itemData.title}}</a>\n          </div>\n          <div class="object-popover-content flex-box flex-col flex-box-single" style="max-height: {{maxInnerHeight}}px;">\n              <div class="flex-size-1">\n                  <h6>Description</h6>\n                  <read-more text="itemData.description"></read-more>\n              </div>\n              <div class="flex-size-1">\n                  <h6>Notes</h6>\n                  <read-more text="itemData.notes"></read-more>\n              </div>\n              <div class="object-popover-attributes flex-box flex-row flex-box-multi">\n                  <div class="flex-size-1">\n                      <h6>Code</h6>\n                      <span>{{itemData.slug}}</span>\n                  </div>\n                  <div class="flex-size-1">\n                      <h6>Owner</h6>\n                    {{#if itemData.owners.length}}\n                        <ul class="inner-count-list">\n                          {{#each itemData.owners}}\n                              <li>\n                                  <person-info person-id="{{id}}"></person-info>\n                              </li>\n                          {{/each}}\n                        </ul>\n                    {{else}}\n                        <span class="empty-message">None</span>\n                    {{/if}}\n                  </div>\n                  <div class="flex-size-1">\n                      <h6>Primary Contact</h6>\n                    {{#if itemData.contact}}\n                        <person-info person-obj="itemData.contact"></person-info>\n                    {{else}}\n                        <span class="empty-message">None</span>\n                    {{/if}}\n                  </div>\n                  <div class="flex-size-1">\n                      <h6>Secondary Contact</h6>\n                    {{#if itemData.secondary_contact}}\n                        <person-info person-obj="itemData.secondary_contact"></person-info>\n                    {{else}}\n                        <span class="empty-message">None</span>\n                    {{/if}}\n                  </div>\n              </div>\n              <content></content>\n          </div>\n        {{/itemData}}\n    </div>\n</div>\n';
GGRC.Templates['components/mapped-objects/mapped-objects']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<object-list items="mappedItems" selected-item="selectedItem" is-loading="isLoading">\n  <content></content>\n</object-list>\n';
GGRC.Templates['components/reusable-objects/reusable-objects-item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<content></content>\n<reusable-objets-item-control>\n  {{#is_allowed \'update\' baseInstance context=\'for\'}}\n    {{#is_allowed \'update\' instance context=\'for\'}}\n      {{#if disabled}}\n          <input type="checkbox" checked="checked" disabled/>\n      {{else}}\n          <input type="checkbox" {{#isSaving}}disabled{{/isSaving}}/>\n      {{/if}}\n    {{/is_allowed}}\n  {{/is_allowed}}\n</reusable-objets-item-control>\n';
