GGRC.Templates['components/ca-object/ca-object-title']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<h6 class="inline-edit__title">\n    <span class="inline-edit__title-body">{{titleText}}\n      {{#def.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{def.helptext}}"></i>{{/def.helptext}}\n      </span>\n      {{#is_allowed \'update\' instance context=\'for\'}}\n        <span class="inline-edit__extra-controls">\n          {{#value}}<i can-click="addComment" class="fa fa-edit" rel="tooltip" title="Add comment"></i>{{/value}}\n        </span>\n      {{/is_allowed}}\n</h6>\n';
GGRC.Templates['components/ca-object/ca-object-validation']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<ca-object-validation-icon validation="validation"></ca-object-validation-icon>\n<content></content>\n';
GGRC.Templates['components/ca-object/ca-object-validation-icon']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<i class="fa validation-icon {{iconCls}}"></i>\n';
GGRC.Templates['components/ca-object/ca-object-modal']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="simple-modal {{modalCls}}">\n    <div class="modal-header">\n        <h4>{{modifiedField.modalTitleText}}</h4>\n        <a class="modal-dismiss" can-click="hide">\n            <i class="fa fa-times black"></i>\n        </a>\n    </div>\n    <ca-object-modal-content content="modal.content" ca-ids="modal.caIds"></ca-object-modal-content>\n    <div class="modal-footer">\n        <div class="row-fluid">\n            <div class="confirm-buttons">\n                <a class="attachment-modal__btn-close" can-click="hide">Close</a>\n              {{#comment}}\n                  <button class="btn btn-small btn-success" {{#isEmpty}}disabled{{/isEmpty}}\n                          can-click="saveAttachments">Save\n                  </button>\n              {{/comment}}\n            </div>\n        </div>\n    </div>\n</div>\n<div class="simple-modal__overlay {{modalOverlayCls}}"></div>\n';
GGRC.Templates['components/ca-object/ca-object-modal-content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<form class="modal-body attachment-modal__body">\n    <div class="attachment-modal__field">\n        <h6 class="attachment-modal__field-title">{{content.title}}</h6>\n        <div class="attachment-modal__field-value">\n          {{#if isPerson}}\n              <person-info person-obj="content.value"></person-info>\n          {{else}}\n              <read-more text="content.value"></read-more>\n          {{/if}}\n        </div>\n    </div>\n    <div class="attachment-modal__controls">\n      {{#comment}}\n          <assessment-add-comment instance="instance" is-empty="isEmpty" state="state" ca-ids="caIds" comment-place-holder="Enter comment"></assessment-add-comment>\n      {{/comment}}\n      {{#evidence}}\n          <assessment-attachments-list class="attachment-list" source_mapping="instance"\n                                       parent_instance="instance"></assessment-attachments-list>\n      {{/evidence}}\n    </div>\n</form>\n';
GGRC.Templates['components/view-object-buttons/view-object-buttons']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if instance.viewLink}}\n  {{#is_allowed "view_object_page" instance}}\n    {{^openIsHidden}}\n    <a href="{{instance.viewLink}}" target="_blank" class="btn btn-mini btn-outline">\n      Open\n    </a>\n    {{/openIsHidden}}\n    <a href="#" class="btn btn-mini btn-outline" can-click="maximizeObject">\n      View\n    </a>\n  {{/is_allowed}}\n{{/if}}\n';
GGRC.Templates['components/add-object-button/add-object-button']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'create\' singular context=instance.context}}\n    <a href="javascript://" class="{{linkclass}}" \n        {{#if title}}\n        rel="tooltip" \n        data-placement="left" \n        data-original-title="{{title}}" \n        {{/if}}\n        data-toggle="modal-ajax-form" \n        data-modal-reset="reset" \n        data-modal-class="modal-wide" \n        data-object-singular="{{singular}}" \n        data-object-plural="{{plural}}" \n        data-object-params=\'{ "{{instance.class.table_singular}}": {"id" : {{instance.id}}, "type" : "{{instance.class.model_singular}}" }, "context": { "id" : {{firstnonempty instance.context.id "null"}}, "href" : "{{instance.context.href}}", "type" : "{{instance.context.type}}" } }\'>\n            {{content}}\n    </a>\n{{/is_allowed}}';
GGRC.Templates['components/info-pin-buttons/info-pin-buttons']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<ul class="pin-action">\n  {{#if maximized}}\n  <li>\n    <a href="#" class="normal" can-click="toggleSize">\n      <i class="fa fa-fw fa-compress" rel="tooltip" data-placement="left" title="Minimize height"></i>\n    </a>\n  </li>\n  {{else}}\n  <li>\n    <a href="#" class="max" can-click="toggleSize">\n      <i class="fa fa-fw fa-expand" rel="tooltip" data-placement="left" title="Maximize height"></i>\n    </a>\n  </li>\n  {{/if}}\n  <li>\n    <a href="#" class="close-pane" can-click="close">\n      <i class="fa fa-fw fa-times" rel="tooltip" data-placement="left" title="Close info pane"></i>\n    </a>\n  </li>\n</ul>\n';
GGRC.Templates['add_widget_menu']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#categories}}\n    <li class="title">{{title}} Widgets</li>\n    {{#objects}}\n    <li class="{{widget_id}}">\n      <a href="javascript://">\n        {{widget_name}}\n      </a>\n    </li>\n    {{/objects}}\n    <li class="divider"></li>\n{{/categories}}\n';
GGRC.Templates['base_objects/autocomplete_result']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each items}}\n<li class="ui-menu-item" {{data \'ui-autocomplete-item\'}}>\n  <a href="javascript://" class=\'show-extended\' {{data \'model\'}}>\n    {{title}}\n  </a>\n</li>\n{{/each}}\n{{^items}}\n  <li class="ui-menu-item" data-ui-autocomplete-item="">\n    No results\n  </li>\n{{/items}}\n{{#if items_loading}}\n  <li class="spinner ui-menu-item" data-ui-autocomplete-item="">\n    <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n  </li>\n{{/if}}\n{{#if model}}\n{{#is_allowed "create" model.shortName context=null}}\n    {{^if disableCreate}}\n      {{^if_equals model.shortName "Workflow"}}\n        <li class="ui-menu-item add-new oneline" data-ui-autocomplete-item="">\n          <a data-object-plural="{{model.table_plural}}"\n             data-modal-class="modal-wide"\n             href="javascript://"\n             data-object-singular="{{model.model_singular}}"\n             data-toggle="modal-ajax-form"\n             data-modal-reset="reset"\n             data-mapping="{{mapping}}">\n            + Create New\n          </a>\n        </li>\n      {{/if_equals}}\n    {{/if}}\n{{/is_allowed}}\n{{/if}}\n';
GGRC.Templates['base_objects/confirm_delete']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="centered">\n<p>\n{{#instance}}\nAre you sure you want to delete\n  <span class="user-string">\n    {{#if display_name}}\n      {{display_name}}?\n    {{else}}\n      this {{type_to_readable instance.type}}?\n    {{/if}}\n  </span>\n  {{#if_instance_of instance \'CustomAttributeDefinition\'}}\n  <div>\n    <h2>Warning!</h2>\n    <p>This will also remove <strong>ALL</strong> values for this attribute.</p>\n    <p>The action is not reversible.</p>\n  </div>\n  {{/if_instance_of}}\n  {{#if delete_counts.loading}}\n	<br/><span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block;\' }}></span>\n  {{else}}\n	<br/>{{delete_counts.counts}}\n  {{/if}}\n{{/instance}}\n</p>\n</div>\n';
GGRC.Templates['base_objects/confirm_unmap']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="centered">\n<p>\n{{#instance}}\nAre you sure you want to unmap {{display_name}}?\n{{/instance}}\n</p>\n</div>\n';
GGRC.Templates['base_objects/contacts']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n<div class="row-fluid wrap-row">\n  <div data-test-id="title_manager_7a906d2e" class="span6">\n    {{#if is_program}}\n    <h6>Manager</h6>\n    <p class="oneline">\n      {{#with_mapping \'authorizations\' instance}}\n        {{#if authorizations.length}}\n          {{#each authorizations}}\n            <span>\n              {{#using role=instance.role}}\n                {{#if_equals role.name \'ProgramOwner\'}}\n                  {{#using contact=instance.person}}\n                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n                  {{/using}}\n                {{/if_equals}}\n              {{/using}}\n            </span>\n          {{/each}}\n        {{else}}\n          <span class="empty-message">None</span>\n        {{/if}}\n      {{/with_mapping}}\n    {{else}}\n    <h6>Owner</h6>\n    <p class="oneline">\n      {{#if owners.length}}\n        {{#using contacts=owners}}\n        <ul class="inner-count-list">\n          {{#each contacts}}\n            <li>\n              {{#using person=this}}\n                {{>\'/static/mustache/people/popover.mustache\'}}\n              {{/using}}\n            </li>\n          {{/each}}\n        </ul>\n        {{/using}}\n      {{else}}\n        <span class="empty-message">None</span>\n      {{/if}}\n    {{/if}}\n    </p>\n  </div>\n    <div data-test-id="title_contacts_696de7244b84" class="span6">\n      {{#using person=contact}}\n      <div data-test-id="title_primary_contact_696de7244b84"\n           class="bottom-space">\n        <h6>Primary Contact</h6>\n        <p data-test-id="text_primary_contact_696de7244b84" class="oneline">\n          {{#if person}}\n            {{>\'/static/mustache/people/popover.mustache\'}}\n          {{else}}\n            <span data-test-id="text_primary_contact_696de7244b84" class="empty-message">None</span>\n          {{/if}}\n        </p>\n      </div>\n      {{/using}}\n      {{#using person=secondary_contact}}\n      <h6>Secondary Contact</h6>\n      <p data-test-id="text_secondary_contact_696de7244b84" class="oneline">\n        {{#if person}}\n          {{>\'/static/mustache/people/popover.mustache\'}}\n        {{else}}\n          <span data-test-id="text_secondary_contact_696de7244b84" class="empty-message">None</span>\n        {{/if}}\n      </p>\n      {{/using}}\n    </div>\n</div>\n{{/instance}}\n';
GGRC.Templates['base_objects/create_objective_dropdown_option']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<li>\n  <a\n    href="javascript://"\n    data-toggle="modal-ajax-form"\n    data-modal-reset="reset"\n    data-modal-class="modal-wide"\n    data-object-singular="Objective"\n    data-object-plural="objectives"\n    data-object-params=\'{ "section": { "id": {{instance.id}}, "title": "{{title}}" }, "title": "{{title}}", "description": "{{json_escape description}}" }\'>Create Objective from Section\n  </a>\n</li>\n';
GGRC.Templates['base_objects/general_info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{! transcluded via renderLive in, for example, assets/mustache/sections/tier2_content.mustache }}\n{{#instance}}\n  <div class="pane-header {{#if snapshot}}snapshot{{/if}}" >\n    <div class="row-fluid wrap-row">\n      <div data-test-id="title_0ad9fbaf" class="span9">\n        <h6>Title</h6>\n        <h3>{{title}}</h3>\n        {{#if snapshot}}\n          <span class="state-value snapshot">{{instance.class.title_singular}} version as at {{date instance.updated_at}}</span>\n        {{/if}}\n        {{#if status}}\n          <span class="state-value">{{un_camel_case status}}</span>\n        {{/if}}\n        {{#if type}}\n        <p>\n          {{type.title}}\n        </p>\n        {{/if}}\n      </div>\n      {{#if snapshot}}\n        {{^isLatestRevision}}\n          <div class="span12 snapshot">\n            <hr class="snapshot">\n            <p>\n              <snapshot-individual-update instance="instance">\n                  <a href="javascript://" can-click="updateIt">Click here</a>\n                  if you want to check for latest version and update.\n              </snapshot-individual-update>\n            </p>\n          </div>\n        {{/isLatestRevision}}\n      {{/if}}\n    </div>\n  </div>\n  <div data-test-id="title_review_0ad9fbaf" class="row-fluid wrap-row">\n    <div class="span12">\n      <h6>Object Review</h6>\n      {{#if class}}\n        {{{render_hooks instance.class.shortName \'info_widget_actions\'}}}\n      {{/if}}\n    </div>\n  </div>\n{{/instance}}\n';
GGRC.Templates['base_objects/description']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n\n  <div class="row-fluid wrap-row">\n    <div data-test-id="title_description_7a906d2e" class="span12">\n      <h6>Description</h6>\n      <div data-test-id="title_description_content_7a906d2e" class="rtf-block">\n        {{{firstnonempty description \'<span class="empty-message">None</span>\'}}}\n      </div>\n    </div>\n  </div>\n\n{{/instance}}\n';
GGRC.Templates['base_objects/test_plan']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  {{#if test_plan}}\n  <div class="row-fluid wrap-row">\n    <div class="span12">\n      <h6>Test Plan</h6>\n      <div class="rtf-block">\n        {{{firstnonempty test_plan \'<span class="empty-message">None</span>\'}}}\n      </div>\n    </div>\n  </div>\n  {{/if}}\n{{/instance}}\n';
GGRC.Templates['base_objects/extended_info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{#instance}}\n\n  <div class="row-fluid">\n    <div class="span12">\n      {{#if_equals instance.class.table_singular \'person\'}}\n        <a class="main-title {{class.category}} oneline" href="{{viewLink}}">\n          {{firstnonempty name email}}\n        </a>\n      {{else}}\n        <a class="main-title {{class.category}} oneline" href="{{viewLink}}">\n          {{title}}\n        </a>\n      {{/if_equals}}\n    </div>\n  </div>\n\n  {{^if_equals instance.class.table_singular \'person\'}}\n    <div class="row-fluid">\n      <div class="span12">\n        <h6>Description</h6>\n        <div class="rtf">\n          {{{firstnonempty description \'<span class="empty-message">None</span>\'}}}\n        </div>\n      </div>\n    </div>\n  {{/if_equals}}\n\n  {{#if_equals instance.class.table_singular \'person\'}}\n    <div class="row-fluid">\n      <div class="span12">\n        <h6>Email</h6>\n        <p class="oneline">\n          {{#using person=this}}\n            {{>\'/static/mustache/people/popover.mustache\'}}\n          {{/using}}\n        </p>\n      </div>\n    </div>\n\n    {{#if company.length}}\n      <div class="row-fluid">\n        <div class="span12">\n          <h6>Company</h6>\n          <p class="oneline">\n            {{company}}\n          </p>\n        </div>\n      </div>\n    {{/if}}\n\n  {{/if_equals}}\n\n  {{^if_equals instance.class.table_singular \'person\'}}\n    {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n  {{/if_equals}}\n\n  <div class="row-fluid">\n    <div class="span5">\n      {{#visibility_delay 500}}\n      <div class="info">\n        <i class="fa fa-control color"></i>\n        {{#with_mapping_count instance \'controls\' \'related_controls\' \'controls\'}}\n          {{count}}\n        {{else}}\n          <span {{attach_spinner \'{ "radius": 2.5, "length": 2.5, "width": 2 }\' \'display:inline-block; top: -5px; left: 2px;\' }}></span>\n        {{/with_mapping_count}}\n        &nbsp;&nbsp;\n        <i class="fa fa-process color"></i>\n        {{#with_mapping_count instance \'related_processes\' \'related_processes\' \'processes\'}}\n          {{count}}\n        {{else}}\n          <span {{attach_spinner \'{ "radius": 2.5, "length": 2.5, "width": 2 }\' \'display:inline-block; top: -5px; left: 2px;\' }}></span>\n        {{/with_mapping_count}}\n        &nbsp;&nbsp;\n        <i class="fa fa-system color"></i>\n        {{#with_mapping_count instance \'related_systems\' \'related_systems\' \'systems\'}}\n          {{count}}\n        {{else}}\n          <span {{attach_spinner \'{ "radius": 2.5, "length": 2.5, "width": 2 }\' \'display:inline-block; top: -5px; left: 2px;\' }}></span>\n        {{/with_mapping_count}}\n      </div>\n      {{/visibility_delay}}\n    </div>\n  </div>\n  {{^if_equals instance.class.table_singular \'person\'}}\n    <div class="row-fluid">\n      <div class="span12">\n        <div class="code">\n          Code: {{slug}}\n        </div>\n      </div>\n    </div>\n  {{/if_equals}}\n  <div class="links">\n    <div class="row-fluid">\n      <div class="span6">\n        <a class="secondary" href="{{viewLink}}">\n          View {{class.title_singular}}\n        </a>\n      </div>\n      <div class="span6">\n        <div class="oneline">\n          {{#with_page_object_as \'page_instance\'}}\n            {{^if_equals instance page_instance}}\n              {{#with_direct_mappings_as \'mappings\' page_instance instance}}\n                {{#if mappings.length}}\n                  <span class="primary"\n                        data-test-id="extended_info_object_already_mapped">\n                    <i class="fa fa-check-square-o"></i>\n                    Mapped to this {{#page_instance.class.title_singular}}\n                      {{toLowerCase}}{{/page_instance.class.title_singular}}\n                  </span>\n                {{else}}\n                  {{#is_allowed_to_map page_instance instance join=true}}\n                  <a class="primary map-to-page-object"\n                     href="javascript://"\n                     data-follow="true"\n                     data-test-id="extended_info_button_map"\n                      {{#instance}}{{data \'instance\'}}\n                  {{/instance}}>\n                    <i class="fa fa-random black"></i>\n                    Map to this {{#page_instance.class.title_singular}}{{toLowerCase}}{{/page_instance.class.title_singular}}\n                  </a>\n                  {{/is_allowed_to_map}}\n                {{/if}}\n              {{/with_direct_mappings_as}}\n            {{/if_equals}}\n          {{/with_page_object_as}}\n        </div>\n      </div>\n    </div>\n  </div>\n\n{{/instance}}\n';
GGRC.Templates['base_objects/urls']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <div class="row-fluid wrap-row">\n    <div data-test-id="title_program_url_aa7d1a65" class="span6">\n      <h6>\n        {{firstnonempty constructor.title_singular}}\n        URL\n      </h6>\n      <p>\n        {{#if url}}\n          <a data-test-id="text_program_url_aa7d1a65" class="url" href="{{schemed_url url}}" target="_blank">\n            {{url}}\n          </a>\n        {{else}}\n          <span class="empty-message">None</span>\n        {{/if}}\n      </p>\n    </div>\n    <div data-test-id="title_reference_url_aa7d1a65" class="span6">\n      <h6>Reference URL</h6>\n      <p>\n        {{#if reference_url}}\n          <a data-test-id="text_reference_url_aa7d1a65" class="url" href="{{schemed_url reference_url}}" target="_blank">\n            {{reference_url}}\n          </a>\n        {{else}}\n          <span class="empty-message">None</span>\n        {{/if}}\n      </p>\n    </div>\n  </div>\n\n{{/instance}}\n';
