GGRC.Templates['base_objects/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/notes.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div><!-- row-fluid end -->\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span4">\n              <h6>Code</h6>\n              <p>\n                {{slug}}\n              </p>\n            </div>\n            <div class="span4">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span4">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div><!-- row-fluid end -->\n        </div><!-- hidden-fields-area end -->\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['base_objects/dropdown_menu']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if_helpers \'\\n   #if\' is_info_pin \'\\n   and #is_allowed_to_map\' page_instance instance \'\\n   or #if\' is_info_pin \'\\n   and #if\' instance.viewLink \'\\n   or #is_allowed\' \'update\' instance \'\\n   \' _4_context=\'for\'}}\n<div class="details-wrap">\n  <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n    <span class="bubble"></span>\n    <span class="bubble"></span>\n    <span class="bubble"></span>\n  </a>\n  <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n    {{#is_allowed \'update\' instance context=\'for\'}}    \n      {{#if instance.is_snapshotable}}\n        <li>\n          <snapshot-scope-update instance="instance">\n            <a href="javascript://" can-click="upsertIt">\n              <i class="fa fa-refresh"></i>\n              Update objects to latest version</a>\n          </snapshot-scope-update>\n        </li>\n      {{/if}}\n      {{> /static/mustache/base_objects/edit_object_link.mustache}}\n    {{/is_allowed}}\n\n    {{! Audit-specific link for defining its AssessmentTemplate}}\n    {{#if_equals instance.type \'Audit\'}}\n    {{#is_allowed \'update\' instance context=\'for\'}}\n    <li>\n      <a href="javascript://"\n        data-toggle="modal-ajax-form"\n        data-modal-reset="reset"\n        data-modal-class="modal-wide"\n        data-object-singular="{{model_info \'AssessmentTemplate\' \'model_singular\'}}"\n        data-object-plural="{{model_info \'AssessmentTemplate\' \'root_collection\'}}"\n        data-object-params=\'{\n          "audit": {\n            "id": {{instance.id}},\n            "type": "{{json_escape instance.type}}"\n          },\n          "context": {\n            "id": {{instance.context.id}},\n            "type": "{{json_escape instance.context.type}}"\n          },\n          "audit_title": "{{json_escape instance.title}}"\n        }\'\n      >\n        <i class="fa fa-sliders"></i>\n        Define Assessment template\n      </a>\n    </li>\n    {{/is_allowed}}\n    {{/if_equals}}\n\n    {{#if instance.class.is_clonable}}\n    {{#is_allowed \'update\' instance context=\'for\'}}\n    <li>\n      <object-cloner\n        instance="instance"\n        modal-title="Clone {{ instance.type }}"\n        modal-description="Select all that you would like to clone as well?"\n\n      >\n        <a href="javascript://" can-click="cloneObject">\n          <i class="fa fa-clone"></i>\n          Clone {{ instance.type }}</a>\n      </object-cloner>\n    </li>\n    {{/is_allowed}}\n    {{/if}}\n\n    <li>\n      {{#is_allowed \'create\' \'CycleTaskGroupObjectTask\' context=null}}\n        {{#is_allowed \'update\' instance context=\'for\'}}\n          <a\n            rel="tooltip"\n            data-placement="left"\n            data-original-title="Create Cycle Task for object"\n            data-object-plural="cycle_task_group_object_tasks"\n            data-modal-class="modal-wide" href="javascript://"\n            data-object-singular="CycleTaskGroupObjectTask"\n            data-toggle="modal-ajax-form"\n            data-modal-reset="reset"\n            data-object-params=\'{\n              "pre_mapped_objects": [{\n                "type": "{{ instance.type }}",\n                "id": {{ instance.id }}\n              }],\n              "modal_title": "Create New Task"}\'>\n            <i class="fa fa-calendar-check-o"></i> Create task\n          </a>\n        {{/is_allowed}}\n      {{/is_allowed}}\n    </li>\n\n    <li>\n      <clipboard-link\n              data-test-id="dropdown_settings_get_permalink_75e3bf91"\n              title="Get permalink"\n              notify="true"\n              text="{{get_permalink_for_object instance}}" />\n    </li>\n\n    {{#is_info_pin}}\n      {{#is_allowed_to_map page_instance instance}}\n        {{^options.is_in_selector}}\n          {{> /static/mustache/base_objects/unmap.mustache}}\n\n        {{/options}}\n      {{/is_allowed_to_map}}\n      {{#if instance.viewLink}}\n        {{#is_allowed "view_object_page" instance}}\n          <li>\n            <a href="{{instance.viewLink}}">\n              <i class="fa fa-long-arrow-right"></i>\n              Open {{instance.class.title_singular}}\n            </a>\n          </li>\n        {{/is_allowed}}\n      {{/if}}\n    {{/is_info_pin}}\n\n    {{#is_allowed \'delete\' instance}}\n      <li>\n        <a data-test-id="dropdown_settings_delete_6a62eaaf"\n           data-toggle="modal-ajax-deleteform"\n           data-object-plural="{{model.table_plural}}"\n           data-object-singular="{{model.model_singular}}"\n           data-modal-reset="reset" data-modal-class="modal"\n           data-object-id="{{instance.id}}" href="javascript://">\n          <i class="fa fa-trash"></i>\n          Delete\n        </a>\n      </li>\n    {{/is_allowed}}\n\n  </ul>\n</div>\n{{/if_helpers}}\n';
