GGRC.Templates['base_objects/map_related_modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!<div class="modal-body">}}\n\n<div class="row-fluid">\n\n  {{#page_object}}\n  <div class="span4 detail_column {{class.model_singular.toLowerCase}}">\n    <input type="hidden" name="source.type" value="{{class.model_singular}}"/>\n    <input type="hidden" name="source.id" value="{{id}}"/>\n    <div class="selector-info centered">\n        <h4 class="{{class.category}}">\n          <i class="fa fa-{{class.model_singular.toLowerCase}} color"></i>\n          {{class.title_singular}}\n        </h4>\n        <br>\n        {{ title }}\n        <br>\n        {{ link }}\n        <br>\n    </div>\n  </div>\n  {{/page_object}}\n\n  <div class="span4 option_column">\n    {{#relationships}}\n    <input type="radio" name="relationship_type_id" value="{{id}}">\n    <span>{{name}}</span>\n    <br>\n    {{/relationships}}\n  </div>\n\n  {{#related_object}}\n  <div class="span4 detail_column {{class.model_singular.toLowerCase}}">\n    <input type="hidden" name="destination.type" value="{{class.model_singular}}"/>\n    <input type="hidden" name="destination.id" value="{{id}}"/>\n    <div class="selector-info centered">\n        <h4 class="{{class.category}}">\n          <i class="fa fa-{{class.model_singular.toLowerCase}} color"></i>\n          {{class.title_singular}}\n        </h4>\n        <br>\n        {{ title }}\n        <br>\n        {{ link }}\n        <br>\n    </div>\n  </div>\n  {{/related_object}}\n\n  <input type="hidden" name="context.id" value="{{determine_context page_object related_object}}" />\n</div>\n{{!</div>}}\n';
GGRC.Templates['base_objects/mappings_detail']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if_result_has_extended_mappings result.bindings_compute parent_instance}}\n<div class="row-fluid wrap-row">\n  This {{instance.class.title_singular}} is mapped to\n  {{#each_with_extras_as "binding" result.bindings_compute}}\n    {{#unless isFirst}}{{#if isLast}}and{{/if}}{{/unless}}\n    {{#if_equals binding.instance parent_instance}}\n    this {{parent_instance.class.title_singular}}{{#if isLast}}.{{else}},{{/if}}\n    {{else}}\n    {{#if_instance_of binding.instance "Section"}}\n    <i class="fa fa-{{binding.instance.class.table_singular}} color"></i><b href="{{link_to_tree binding.instance.directive binding.instance}}">{{binding.instance.title}}</b>\n    {{#if binding.instance.directive}}\n      of\n      {{#using directive=binding.instance.directive}}\n        <i class="fa fa-{{directive.class.table_singular}} color"></i><b href="{{link_to_tree directive}}">{{directive.title}}</b>{{#if isLast}}.{{else}},{{/if}}\n      {{/using}}\n    {{/if}}\n    {{else}}\n    <i class="fa fa-{{binding.instance.class.table_singular}} color"></i><b href="{{link_to_tree binding.instance}}">{{binding.instance.title}}</b>{{#if isLast}}.{{else}},{{/if}}\n    {{/if_instance_of}}\n    {{/if_equals}}\n  {{/each_with_extras_as}}\n</div>\n{{/if_result_has_extended_mappings}}\n';
