GGRC.Templates['base_objects/page_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h1 class="entities">\n  {{#if_equals model.table_singular \'person\'}}\n    {{#is_dashboard_or_all}}\n      {{#is_dashboard}}\n        My Work\n      {{else}}\n        All Objects\n      {{/is_dashboard}}\n    {{else}}\n      <span class="title-content">\n        <i class="fa fa-{{model.table_singular}}"></i>\n        {{firstnonempty instance.name instance.email}}\n      </span>\n    {{/is_dashboard_or_all}}\n  {{/if_equals}}\n\n  {{^if_equals model.table_singular \'person\'}}\n    <span class="title-content">\n      <i class="fa fa-{{model.table_singular}}"></i>\n      {{instance.title}}\n    </span>\n  {{/if_equals}}\n</h1>\n<ul class="menu">\n  <li>\n    <a href="javascript://" data-toggle="unified-search" data-join-object-type="MultitypeSearch" data-original-title="search items">\n      <i class="fa fa-search"></i>\n      <span>Search</span>\n    </a>\n  </li>\n  <li>\n    <a href="/dashboard#task_widget" id="page-help">\n      <i class="fa fa-calendar-check-o"></i>\n      <span>\n        My Tasks\n        <object-counter\n          counter="user_task_count"\n          {{#if GGRC.counters.user_overdue_task_count}}class="critical"{{/if}}\n          model-name="CycleTaskGroupObjectTask"\n          search-keys="contact_id;cycle.is_current;status__in"\n          search-values="current_user;true;Assigned,InProgress,Finished,Declined"\n        ></object-counter>\n      </span>\n    </a>\n  </li>\n  <li>\n    {{#is_allobjectview}}\n      <a id="myWorkView" href="/dashboard">\n        <i class="fa fa-home"></i>\n        <span>\n          My Work\n        </span>\n      </a>\n    {{else}}\n      <a id="allObjectView" href="/objectBrowser">\n        <i class="fa fa-files-o"></i>\n        <span>\n          All Objects\n        </span>\n      </a>\n    {{/is_allobjectview}}\n  </li>\n  <li class="user user-dropdown dropdown">\n    <a href="#" class="dropdown-toggle" data-toggle="dropdown" role="button">\n      <i class="fa fa-person"></i>\n      <span>{{current_user.email}}</span>\n      <i class="fa fa-caret-down"></i>\n    </a>\n    <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n      <li class="full-opacity">\n        <a href="/dashboard">\n          <i class="fa fa-home"></i>\n          My Work ({{current_user.system_wide_role}})\n        </a>\n      </li>\n      <li class="full-opacity">\n        <a href="/assessments_view">\n          <i class="fa fa-assessment"></i>\n          My Assessments\n        </a>\n      </li>\n      {{#current_user_is_admin}}\n      <li>\n        <a href="/admin#people_list_widget">\n          <i class="fa fa-tachometer"></i>\n          Admin Dashboard\n        </a>\n      </li>\n      {{/current_user_is_admin}}\n      <li class="notify-wrap">\n        <span>\n          <i class="fa fa-clock-o"></i>\n          Notifications\n        </span>\n        <div class="inner-list">\n          <label>\n            <input type="checkbox" name="notifications" value="Email_Digest">\n            Daily email digest\n          </label>\n        </div>\n      </li>\n      <li>\n        <a href="/import">\n          Data import\n        </a>\n      </li>\n      <li>\n        <a href="/export">\n          Data export\n        </a>\n      </li>\n      <li>\n        <a class="nav-logout" href="/logout" tabindex="-1">\n          <i class="fa fa-power-off"></i>\n          Logout\n        </a>\n      </li>\n    </ul>\n  </li>\n  <li>\n    <a id="#page-help" href="{{GGRC.config.external_help_url}}" target="_blank">\n      <i class="fa fa-question-circle"></i>\n      <span>Help</span>\n    </a>\n  </li>\n</ul>\n';
GGRC.Templates['base_objects/paginate']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<content></content>\n\n{{#if totalPages}}\n<ul class="pagination">\n  <li {{#if_equals currentPage 1}}class="disabled"{{/if_equals}}>\n    <a can-click="setPrevious" href="#">Previous</a>\n  </li>\n  <li>\n    <span class="status">\n      <span>{{currentPage}} of {{totalPages}}</span>\n    </span>\n  </li>\n  <li {{#if_equals currentPage totalPages}}class="disabled"{{/if_equals}}>\n    <a can-click="setNext" href="#">Next</a>\n  </li>\n</ul>\n{{/if}}\n';
GGRC.Templates['base_objects/search_actions']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'create\' model.shortName context=\'any\'}}\n<li class="add-new oneline" data-ui-autocomplete-item="">\n  <a data-object-plural="{{model.table_plural}}" data-test-id="button_lhn_create_new_program_522c563f" data-form-target="redirect" data-modal-class="modal-wide" href="javascript://" data-object-singular="{{model.model_singular}}" data-toggle="modal-ajax-form" data-modal-reset="reset">\n    + Create New\n  </a>\n</li>\n{{/is_allowed}}\n';
