GGRC.Templates['base_objects/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> \'/static/mustache/base_objects/tree_view_filter.mustache\'}}\n<header class="header sticky sticky-header tree-header {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n  <div class="row-fluid">\n    <div class="span{{display_options.title_width}}">\n      <div class="title-heading oneline">\n        <span class="widget-col-title" data-field="title">\n          Title\n          <i class="fa fa-caret-up"></i>\n        </span>\n      </div>\n    </div>\n\n    {{> \'/static/mustache/base_objects/tree_column_names.mustache\'}}\n\n    <div class="span{{display_options.action_width}}">\n      <ul class="tree-action-list">\n        {{#if add_item_view}}\n        <li>\n          {{{renderLive add_item_view}}}\n        </li>\n        {{/if}}\n        <li>\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu tree-action-list-items" role="menu">\n              {{> \'/static/mustache/import_export/import_export_buttons.mustache\'}}\n              <assessment-generator-button audit="parent_instance"></assessment-generator-button>\n              {{> \'/static/mustache/base_objects/filter_trigger.mustache\'}}\n              {{> \'/static/mustache/base_objects/tree_column_selector.mustache\'}}\n              {{> \'/static/mustache/base_objects/sub_tree_type_selector.mustache\'}}\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n';
GGRC.Templates['base_objects/tree_view_filter']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <div class="sticky sticky-header tree-filter {{#if original_list.length}}tree-filter_paging{{/if}}"\n       {{#filter_is_hidden}}style="height: 0; margin-bottom: 0;overflow: hidden"{{/filter_is_hidden}}\n       data-height="42"\n       data-margin-bottom="25">\n    <div class="sticky-filter inner-filter-sticky">\n      <div class="tree-filter__inline-filtering clearfix" {{ (el) -> el.ggrc_controllers_tree_filter() }} >\n\n        <div class="tree-filter__fluid-wrap">\n          <div class="tree-filter__title-wrap">\n            <div class="tree-filter__title">\n              <h6>\n                Filter\n                {{#instance}}\n                  {{far_model.title_plural}}\n                {{/instance}}\n              </h6>\n            </div>\n          </div>\n          <div class="tree-filter__input-wrap">\n            <div class="tree-filter__expression-holder">\n              <input name="filter_query" type="text" class="tree-filter__input tree-filter__input--wide">\n              <span class="tree-filter__expression-holder__is-expression">\n                <i class="fa fa-check-circle-o"></i>\n              </span>\n            </div>\n          </div>\n          <div class="tree-filter__button-wrap">\n            <div class="tree-filter__button">\n              <a id="page-help" class="popover-template" data-get-template="help/filters_helper_content" href="javascript://">\n                <i class="fa fa-question-circle"></i>\n              </a>\n              <input type="reset" class="btn btn-small btn-draft" name="" value="Reset">\n              <input type="submit" class="btn btn-small btn-info" name="" value="Filter">\n            </div>\n          </div>\n          <div class="tree-filter__paging-wrap">\n            <div class="tree-filter__paging">\n                <tree-pagination paging="paging"></tree-pagination>\n            </div>\n          </div>\n        </div>\n      </div>\n    </div>\n  </div>\n';
GGRC.Templates['base_objects/tree_column_selector']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<li>\n<tree-header-selector>\n  <a href="#" class="dropdown-toggle tview-dropdown-toggle" data-toggle="nested-dropdown">\n    <i class="fa fa-fw fa-cog"></i>\n    Set Visible Fields for {{model.title_singular}}\n  </a>\n  <div class="dropdown-menu dropdown-menu-form">\n    <a class="pull-right close-dropdown"><i class="fa fa-times black"></i></a>\n    <h5>Set visible fields for {{model.title_singular}}</h5>\n\n    <ul class="attr-list">\n      {{#select_attr_list}}\n      <li class="{{#if mandatory}}disabled{{/if_equals}}">\n        <label class="checkbox-inline" title="{{attr_title}}">\n          <input type="checkbox"\n            {{#if display_status}}checked="checked"{{/if}}\n            {{#if mandatory}}disabled{{/if_equals}}\n            value="{{attr_name}}"\n            class="notify-wrap attr-checkbox {{#if mandatory}}mandatory{{/if_equals}}"\n          >\n          {{attr_title}}\n        </label>\n      </li>\n      {{/select_attr_list}}\n    </ul>\n\n    <a class="btn btn-small btn-info set-tree-attrs" href="javascript://">Set fields</a>\n  </div>\n</tree-header-selector>\n</li>\n';
GGRC.Templates['base_objects/tree_column_names']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="span{{display_options.selectable_width}}">\n  <div class="row-fluid">\n    {{#display_attr_list}}\n      <div class="span{{display_options.selectable_attr_width}}">\n        <div class="oneline">\n          <span class="widget-col-title" data-field="{{attr_sort_field}}">\n            {{attr_title}}\n            <i class="fa fa-caret-up"></i>\n          </span>\n        </div>\n      </div>\n    {{/display_attr_list}}\n  </div>\n</div>\n';
GGRC.Templates['base_objects/filter_trigger']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^if_page_type "admin"}}\n<li class="filter-trigger">\n{{^filter_is_hidden}}\n  <a href="javascript://" class="active">\n    <i class="fa fa-fw fa-search"></i>\n    <span>Hide Filter</span>\n  </a>\n{{/filter_is_hidden}}\n\n{{#filter_is_hidden}}\n  <a href="javascript://">\n    <i class="fa fa-fw fa-search"></i>\n    <span>Show Filter</span>\n  </a>\n{{/filter_is_hidden}}\n</li>\n{{/if_page_type}}\n';
GGRC.Templates['base_objects/generate_assessments_button']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if button}}\n  {{#is_allowed \'update\' parent_instance context="for"}}\n    <a\n      href="javascript://"\n      class="generate-control-assessments btn btn-small btn-primary"\n      rel="tooltip"\n      data-placement="left"\n      title="Generate Assessments"\n      data-original-title="Generate Assessments">\n      <i\n         class="fa fa-magic"\n         rel="tooltip"\n         data-placement="left"\n         title="Generate Assessments"\n         data-original-title="Generate Assessments">\n      </i>\n      Autogenerate\n    </a>\n  {{/is_allowed}}\n{{else}}\n  {{#if_instance_of parent_instance \'Audit\'}}\n    {{#if_in model.shortName \'Assessment,Control\'}}\n        {{#is_allowed \'update\' parent_instance context="for"}}\n          <li class="generate-control-assessments">\n            <a href="javascript://">\n              <i\n                class="fa fa-fw fa-magic"\n               ></i>\n              Generate Assessments\n            </a>\n          </li>\n        {{/is_allowed}}\n    {{/if_in}}\n  {{/if_instance_of}}\n{{/if}}\n';
GGRC.Templates['base_objects/states']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance.workflow_state}}\n  <div class="person-holder">\n    <a href="{{instance.viewLink}}#task_widget" class="info-action person-tooltip-trigger" data-placement="left">\n      {{#switch instance.workflow_state}}\n        {{#case \'Verified\'}}\n          <span class="green"><em>Verified</em></span>\n        {{/case}}\n        {{#case \'Assigned\'}}\n          <span class="gray"><em>Assigned</em></span>\n        {{/case}}\n        {{#case \'Finished\'}}\n          <span class="gray"><em>Finished</em></span>\n        {{/case}}\n        {{#case \'InProgress\'}}\n          <span class="yellow"><em>In progress</em></span>\n        {{/case}}\n        {{#case \'Overdue\'}}\n            <span class="error"><em>Overdue</em></span>\n        {{/case}}\n      {{/switch}}\n      {{#with_mapping_count instance \'object_tasks\'}}\n        <i class="fa fa-cycle_task_group_object_task"></i>\n        {{count}}\n      {{/with_mapping_count}}\n    </a>\n    {{#with_mapping \'object_tasks\' instance}}\n      <div class="custom-popover-content">\n        <div class="preview-container person-preview">\n          <div class="profile-info">\n            <h3 class="task-list-title">Current Tasks</h3>\n            <ul class="popup-task-list">\n              {{#object_tasks}}\n                <li>\n                  <div class="clearfix">\n                    <div class="pull-left">\n                      <i class="fa fa-cycle_task_group_object_task"></i>\n                    </div>\n                    <div class="task-title pull-left">\n                      {{instance.title}}\n                    </div>\n                    <div class="task-status pull-left">\n                      {{#switch instance.status}}\n                        {{#case \'Verified\'}}\n                          <span class="green"><em>Verified</em></span>\n                        {{/case}}\n                        {{#case \'Assigned\'}}\n                          <span class="gray"><em>Assigned</em></span>\n                        {{/case}}\n                        {{#case \'Finished\'}}\n                          <span class="blue"><em>Finished</em></span>\n                        {{/case}}\n                        {{#case \'InProgress\'}}\n                          <span class="yellow"><em>In progress</em></span>\n                        {{/case}}\n                      {{/switch}}\n                      {{^if_equals instance.status \'Verified\'}}{{#is_overdue instance.end_date}}\n                        <span class="error"><em>Overdue</em></span>\n                      {{/is_overdue}}\n                      {{/if_equals}}\n                    </div>\n                  </div>\n                </li>\n              {{/object_tasks}}\n            </ul>\n            <a class="info-action" href="{{instance.viewLink}}#task_widget"><i class="fa fa-long-arrow-right"></i> Open tasks</a>\n          </div>\n        </div>\n      </div>\n    {{/with_mapping}}\n  </div>\n{{/instance.workflow_state}}\n';
GGRC.Templates['base_objects/form_restore']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<!-- Reset button --> \n<div class="row-fluid">\n  <div class="span12">\n    <div class="pull-right reset-form">\n      <p>\n        <small><em>Fields are only temporarily hidden</em></small>\n        <a id="formHide" tabindex="36" class="btn btn-small btn-draft" href="javascript://">Hide all optional fields</a>\n        <a id="formRestore" tabindex="36" class="btn btn-small btn-draft" href="javascript://" style="display:none;">Show all optional fields</a>\n      </p>\n    </div>\n  </div>\n</div>\n';
