GGRC.Templates['base_objects/filters/filter_by_person']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Filter by person</h6>\n<div class="modal-search objective-selector">\n  <input\n    id="search-by-owner"\n    class="search-by-owner input-block-level"\n    placeholder="Find owner or contact"\n    type="text"\n    name="owner"\n    value="{{firstnonempty owner.name owner.email owner.title}}"\n    data-lookup="Person"\n   \n    data-template="/people/autocomplete_result.mustache"\n    null-if-empty="null-if-empty"\n    {{ autocomplete_select }}\n  >\n</div>\n';
GGRC.Templates['base_objects/filters/filter_by_relevance']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="relevance-filters">\n  <div class="row-fluid">\n    <div class="span12">\n      <h6>Filter by mapping</h6>\n      {{#each filter_list}}\n      <div class="single-line-filter">\n        <button type="submit" data-index="{{@index}}" class="remove_filter">\n          <i class="fa fa-trash"></i>\n        </button>\n        <label>\n          {{#if @index}}AND {{/if}}Mapped to:\n        </label>\n        <select class="input-small filter-type-selector select-filter{{@index}}" can-value="model_name">\n          {{#option_type_menu_2}}\n          <option\n            value="{{model_singular}}"\n            label="{{title_singular}}">\n          </option>\n          {{/option_type_menu_2}}\n        </select>\n        {{#model_name}}\n        <div class="modal-search objective-selector">\n          <input\n            id="search-filter{{@index}}"\n            class="input-large search-icon search-filter{{@index}}"\n            placeholder="Enter text to search for {{model_name}}"\n            type="text"\n            name="filter_list.{{@index}}.search_filter"\n            value="{{firstnonempty search_filter.name search_filter.email search_filter.title}}"\n            data-lookup="{{this}}"\n            data-template="/{{#if_equals model_name \'Person\'}}people{{else}}base_objects{{/if_equals}}/autocomplete_result.mustache"\n            {{ autocomplete_select }}\n            >\n        </div>\n        {{/model_name}}\n      </div>\n      {{/each}}\n      <div class="add-rule">\n        <a href="javascript://" id="addFilterRule" class="addFilterRule">+ Add New Rule</a>\n      </div>\n    </div>\n  </div>\n</div>\n';
