GGRC.Templates['base_objects/modal_contact_fields']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid inner-hide">\n  <div class="span12 hidable">\n    {{#using contact=instance.contact}}\n    <label>\n      Primary Contact\n      <i class="fa fa-question-circle" rel="tooltip" title="Point of contact for this object"></i>\n      <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n    </label>\n    <input data-test-id="new_program_field_primary_contact_86160053"\n           data-id="contact_txtbx"\n           tabindex="5"\n           class="input-block-level" name="contact.email" data-lookup="Person" placeholder="Enter email address" type="text" value="{{firstexist contact.email \'\'}}" />\n    {{/using}}\n  </div>\n</div>\n<div class="row-fluid inner-hide">\n  <div class="span12 hidable">\n    {{#using contact=instance.secondary_contact}}\n    <label>\n      Secondary Contact\n      <i class="fa fa-question-circle" rel="tooltip" title="Point of contact for this object"></i>\n      <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n    </label>\n    <input data-test-id="new_program_field_secondary_contact_86160053"\n           data-id="secondary_contact_txtbx" tabindex="5"\n           class="input-block-level" name="secondary_contact.email" data-lookup="Person" placeholder="Enter email address" type="text" value="{{firstexist contact.email \'\'}}" />\n    {{/using}}\n  </div>\n</div>\n';
