GGRC.Templates['base_objects/sub_tree_type_selector']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n <li>\n<tree-type-selector>\n    <a href="#" class="dropdown-toggle tview-type-toggle" data-toggle="nested-dropdown">\n      <i class="fa fa-fw fa-share-alt"></i>\n      Select Child Tree Object Type\n    </a>\n    <div class="dropdown-menu dropdown-menu2 dropdown-menu-form">\n      <a class="pull-right close-dropdown"><i class="fa fa-times black"></i></a>\n      <h5>\n        Select child tree object type for:\n        <select class="input-medium object-type-selector">\n          {{#select_model_list}}\n          <option\n            value="{{model_name}}"\n            label="{{display_name}}"\n            {{#if_equals model_name selected_model_name}}selected="selected"\n            {{/if_equals}}>\n          </option>\n          {{/select_model_list}}\n        </select>\n      </h5>\n\n      <ul class="select-clear-all">\n        <li>\n          <a href="javascript://" class="select-all">Select all </a>\n        </li>\n        <li>\n          <a href="javascript://" class="select-none">Select none </a>\n        </li>\n      </ul>\n\n      <ul class="model-list">\n        {{#selected_child_tree_model_list}}\n        <li>\n          <label class="checkbox-inline" title="{{display_name}}">\n            <input type="checkbox"\n              {{#if display_status}}checked="checked"{{/if}}\n              value="{{model_name}}"\n              class="notify-wrap model-checkbox"\n            >\n            <!--i class="fa fa-{{model_lowercase}} color"></i-->\n            {{display_name}}\n          </label>\n        </li>\n        {{/selected_child_tree_model_list}}\n      </ul>\n\n      <a class="btn btn-small btn-info set-display-object-list" href="javascript://">Set Visibility</a>\n    </div>\n</tree-type-selector>\n</li>\n';
