GGRC.Templates['base_templates/add_comment']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="add-comment">\n  {{^if_null parent_instance._mandatory_comment_msg}}\n    <div class="row-fluid">\n      <div class="span8">\n        <div class="alert alert-info alert-dismissible" role="alert">\n          {{parent_instance._mandatory_comment_msg}}\n          <button type="button" class="close" data-dismiss="alert" aria-label="Close">&times;</button>\n        </div>\n      </div>\n    </div>\n  {{/if_null}}\n  <div class="row-fluid">\n    <div class="span8">\n      <div class="controls top">\n        {{#is_allowed \'update\' parent_instance context=\'for\'}}\n          {{{render_hooks "Request.gdrive_comment_attachment"}}}\n\n          {{#toggle show_new_object_form}}\n            <ggrc-quick-add parent_instance="instance" join_model="Relationship" deferred="true" quick_create="create_url">\n              {{#prune_context}}\n                <div class="objective-selector field-wrap top-spacing">\n                  <input tabindex="3" type="text" name="instance" placeholder="Add URL">\n                  <a href="javascript://" {{toggle_button}}><i class="fa fa-trash"></i></a>\n                  <a href="javascript://" class="btn btn-small btn-success no-float" data-toggle="submit" {{toggle_button "modal:success"}}>Add URL</a>\n                </div>\n                <input type="hidden" name="role_name" value="Auditor" />\n              {{/prune_context}}\n            </ggrc-quick-add>\n          {{else}}\n            <a href="javascript://" class="btn btn-draft btn-small {{toggle_button}}" data-type="url">\n              <i class="fa fa-link"></i> Attach URL\n            </a>\n        {{/toggle}}\n        {{/is_allowed}}\n      </div>\n      <div class="wysiwyg-area">\n        <textarea {{#isSaving}}disabled="disabled"{{/isSaving}}\n          rows="4"\n          class="span12 triple wysihtml5"\n          name="comment-description"\n          id="comment-description"\n          placeholder="Enter comment (optional)"\n          can-value="description"\n        ></textarea>\n      </div>\n      <div class="attachments-preview">\n        <ul>\n        {{#instance._pending_joins}}\n          <li class="label label-light">\n            <span>{{what.title}}</span>\n            <a\n              href="#"\n              class="js-trigger-removeattachment"\n              can-click="removePending"\n            >&times;</a>\n          </li>\n        {{/instance._pending_joins}}\n        </ul>\n      </div>\n      <div class="controls clearfix">\n        <spinner toggle="isSaving"></spinner>\n        {{^if isSaving}}\n        <a\n          href="javascript://"\n          class="btn btn-success btn-small pull-left"\n          {{#if isSaving}}disabled="disabled"{{/if}}\n          >Add</a>\n\n        <label class="input--inline pull-left">\n          <input type="checkbox" can-value="sendNotification">\n          Send Notifications\n        </label>\n        {{/if}}\n      </div>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['base_templates/objects_tooltip']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<span class="person-holder">\n  <a href=\'{{viewLink}}\' target="_blank">\n    <span class="person-tooltip-trigger" data-placement="top">\n      <i class="fa fa-{{class.table_singular}} color"></i>\n      {{firstnonempty title description name email}}\n    </span>\n  </a>\n\n  <div class="custom-popover-content">\n    <div class="preview-container person-preview">\n      <div class="row-fluid">\n        <div class="span12">\n          <div class="profile-info">\n            <div class="row-fluid wrap-row">\n              <div class="span8">\n                <h6>Title</h6>\n                <h5 class="oneline">\n                  <i class="fa fa-{{class.table_singular}} color"></i>\n                  {{title}}\n                </h5>\n              </div>\n              <div class="span4">\n                <h6>Status</h6>\n                {{state}}\n              </div>\n            </div>\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Description</h6>\n                {{description}}\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n    </div>\n  </div>\n</span>\n';
GGRC.Templates['base_templates/attachment_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{^if_null instance._mandatory_attachment_msg}}\n  <div class="alert alert-info alert-dismissible" role="alert">\n    {{instance._mandatory_attachment_msg}}\n    <button type="button" class="close" data-dismiss="alert" aria-label="Close">&times;</button>\n  </div>\n{{/if_null}}\n\n<mapping-tree-view\n  parent-instance="instance"\n  mapping="instance.class.info_pane_options.evidence.mapping"\n  item-template="instance.class.info_pane_options.evidence.show_view"\n>\n</mapping-tree-view>\n\n{{#is_allowed \'update\' instance context=\'for\'}}\n  {{{render_hooks "Request.gdrive_evidence_storage"}}}\n{{/is_allowed}}\n';
GGRC.Templates['base_templates/url_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<mapping-tree-view\n  parent-instance="instance"\n  mapping="instance.class.info_pane_options.urls.mapping"\n  item-template="instance.class.info_pane_options.urls.show_view"\n  sort-field="instance.created_at"\n>\n</mapping-tree-view>\n{{#with_mapping instance.class.info_pane_options.urls.mapping instance}}\n  {{#info_related_objects}}\n    {{{render parent_instance.class.info_pane_options.urls.show_view instance=instance}}}\n  {{/info_related_objects}}\n{{/with_mapping}}\n</ul>\n{{#is_allowed \'update\' instance context=\'for\'}}\n{{#toggle show_new_object_form}}\n  <ggrc-quick-add\n    parent_instance="instance"\n    join_model="Relationship"\n    quick_create="create_url"\n    {{#if_in instance.status "Completed,Verified"}}\n      verify_event="true"\n      modal_description=\'You are about to move request from "{{instance.status}}" to "In Progress" - are you sure about that?\'\n      modal_title=\'Confirm moving Request to "In Progress"\'\n      modal_button=\'Confirm\'\n    {{/if_in}}\n  >\n    {{#prune_context}}\n    <div class="objective-selector field-wrap">\n      <input tabindex="3" type="text" name="instance" placeholder="Add URL">\n      <a href="javascript://" {{toggle_button}}><i class="fa fa-trash"></i></a>\n      <a href="javascript://" class="btn btn-small btn-success no-float" data-toggle="submit" {{toggle_button "modal:success"}}>Add</a>\n    </div>\n    <input type="hidden" name="role_name" value="Auditor" />\n    {{/prune_context}}\n  </ggrc-quick-add>\n{{else}}\n  <a href="javascript://" class="btn btn-small btn-draft" {{toggle_button}}>+ Add URL</a>\n{{/toggle}}\n{{/is_allowed}}\n</ul>\n';
GGRC.Templates['base_templates/attachment']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<reusable-object reusable="reusable" method="reuseMethod" list="reusedObjects" parent-instance="parentInstance" base-instance="baseInstance" mapping="mapping" isLoading="isLoading">\n  <li data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    {{^if reusable}}\n      {{#is_allowed \'update\' parentInstance context=\'for\'}}\n      {{#instance}}\n        <span class="file-controls">\n          <a href="javascript://" class="info-action unmap" data-toggle="unmap">\n            <span class="result" {{data \'result\'}}></span>\n            <i class="fa fa-trash"></i>\n          </a>\n        </span>\n      {{/instance}}\n      {{/is_allowed}}\n    {{/if}}\n\n    {{#instance.selfLink}}\n      <i class="fa fa-file-{{file_type instance}}-o"></i>\n    {{/instance.selfLink}}\n\n    {{^if reusable}}\n    <span class="date">{{date instance.created_at true}}</span>\n    {{/if}}\n\n    <a href="{{schemed_url instance.link}}" target="_blank">\n      <span>{{firstnonempty instance.title instance.link}}</span>\n    </a>\n\n    {{#if reusable}}\n      {{#is_allowed \'update\' baseInstance context=\'for\'}}\n        {{#is_allowed \'update\' instance context=\'for\'}}\n          {{#isDisabled instance}}\n            <input type="checkbox" checked disabled />\n          {{/isDisabled}}\n          {{^isDisabled instance}}\n            <input type="checkbox" {{#isLoading}}disabled{{/isLoading}} can-value="list.{{method}}-{{instance.type}}-{{instance.id}}" />\n          {{/isDisabled}}\n        {{/is_allowed}}\n      {{/is_allowed}}\n    {{/if}}\n  </li>\n</reusable-object>\n';
GGRC.Templates['base_templates/base']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<section class="info sticky-info-panel">\n\n  {{> /static/mustache/mockup_base_templates/info_panel/header.mustache}}\n\n  <div class="tier-content">\n  </div><!-- tier-content end -->\n\n</section>\n';
GGRC.Templates['base_templates/comment_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance.class.info_pane_options.comments}}\n{{#add_to_current_scope parent_instance=instance}}\n  <mapping-tree-view\n    parent-instance="instance"\n    is-loading="isLoading"\n    mapping="comments"\n    tree-view-class="comment-list"\n    item-template="/static/mustache/base_templates/comment_subtree.mustache"\n    >\n  </mapping-tree-view>\n{{/add_to_current_scope}}\n{{/instance}}\n';
GGRC.Templates['base_templates/comment_subtree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n<li>\n  <div class="w-status">\n    <span class="entry-author">\n      {{#using person=modified_by}}\n        {{{render \'/static/mustache/people/popover.mustache\' person=person}}}\n      {{/using}}\n      {{assignee_types assignee_type}} added a response - {{date created_at}}\n    </span>\n    {{#instance.custom_attribute_revision}}\n        <h6 class="comment-list__ca-description">{{instance.custom_attribute_revision.custom_attribute.title}}: {{instance.custom_attribute_revision.custom_attribute_stored_value}}</h6>\n    {{/instance.custom_attribute_revision}}\n    <div class="rtf-block">\n      <inline-edit\n        type="text"\n        instance="instance"\n        property="description"\n        value="description"\n        {{^canEditComment instance parentInstance}}readonly{{/canEditComment}}\n        can-on-save="instance.updateDescription"\n        empty-text="No comment was entered"\n      ></inline-edit>\n    </div>\n    <mapping-tree-view\n      parent-instance="instance"\n      mapping="instance.class.info_pane_options.documents.mapping"\n      item-template="instance.class.info_pane_options.documents.show_view"\n    >\n    </mapping-tree-view>\n  </div>\n</li>\n{{/instance}}\n';
GGRC.Templates['base_templates/dates_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Dates</h6>\n<div class="side-content">\n  <div {{#instance}}{{data "model"}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({ instance : el.data("model")}); }}>\n    <ul class="label-list dates">\n      {{#if created_at}}\n        <li class="gray">\n          <label>\n            Created on\n          </label>\n          <span name="created_at">{{localize_date created_at}}</span>\n        </li>\n      {{/if}}\n      {{#if due_on}}\n        <li>\n          {{#toggle change_due_date}}\n            <label>Due on</label>\n            <div class="input-group">\n              <span class="input-group-addon"><i class="fa fa-calendar"></i></span>\n              <input tabindex="5" class="span8 input-block-level date" name="due_on" data-toggle="datepicker" data-after="created_at" placeholder="MM/DD/YYYY" type="text" value="{{localize_date due_on}}">\n            </div>\n          {{else}}\n            {{#is_allowed \'update\' instance context=\'for\'}}\n              {{#if_in instance.status "In Progress"}}\n                <span class="file-controls">\n                  <a href="javascript://" {{toggle_button}}><i class="fa fa-pencil-square-o"></i></a>\n                </span>\n              {{/if_in}}\n            {{/is_allowed}}\n            <label>\n              Due on\n            </label>\n            {{#if_null instance.due_on}}\n              defined\n            {{/if}}\n            {{localize_date due_on}}\n          {{/toggle}}\n        </li>\n      {{/if}}\n      {{#if_helpers \'#if\' instance.finished_date \'or #if_null\' instance.finished_date}}\n        <li>\n          <label>Finished</label>\n          {{#if instance.finished_date}}\n            <span>{{localize_date instance.finished_date}}</span>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </li>\n      {{/if_helpers}}\n      {{#if_helpers \'#if\' instance.verified_date \'or #if_null\' instance.verified_date}}\n        <li>\n          <label>Verified</label>\n          {{#if instance.verified_date}}\n            <span>{{localize_date instance.verified_date}}</span>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </li>\n      {{/if_helpers}}\n    </ul>\n  </div>\n</div>\n';
GGRC.Templates['base_templates/description']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Description</h6>\n<div class="rtf-block">\n  {{description}}\n</div>\n';
GGRC.Templates['base_templates/header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="pane-header">\n  <div class="row-fluid wrap-row">\n    <div class="span9">\n      <h6>Title</h6>\n      <h3>{{title}}</h3>\n      {{#if_helpers \'\\n        #if_equals\' status "Verified" \'\\n        and ^if\' instance.verified}}\n          {{! don\'t show status, because it will be replaced soon }}\n      {{else}}\n        <span class="state-value">{{status}}</span>\n      {{/if_helpers}}\n      <span class="gray">\n        {{#if request_type}}\n          ({{capitalize request_type}})\n        {{/if}}\n      </span>\n      {{#if verified}}\n      <i class="fa fa-check-circle green"\n         rel="tooltip"\n         title="Verified on {{localize_date verified_date}}"/>\n      {{/if}}\n    </div>\n    <div class="span3">\n      <div class="info-pane-utility">\n        <div class="details-wrap">\n          <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n          </a>\n          <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n            {{#is_allowed \'update\' instance context=\'for\'}}\n              {{> /static/mustache/base_objects/edit_object_link.mustache}}\n            {{/is_allowed}}\n\n            <li>\n              {{#is_allowed \'create\' \'CycleTaskGroupObjectTask\' context=null}}\n                {{#is_allowed \'update\' instance context=\'for\'}}\n                  <a\n                    rel="tooltip"\n                    data-placement="left"\n                    data-original-title="Create Cycle Task for object"\n                    data-object-plural="cycle_task_group_object_tasks"\n                    data-modal-class="modal-wide" href="javascript://"\n                    data-object-singular="CycleTaskGroupObjectTask"\n                    data-toggle="modal-ajax-form"\n                    data-modal-reset="reset"\n                    data-object-params=\'{\n                      "pre_mapped_objects": [{\n                        "type": "{{ instance.type }}",\n                        "id": {{ instance.id }}\n                      }],\n                      "modal_title": "Create New Task"}\'>\n                    <i class="fa fa-calendar-check-o"></i> Create task\n                  </a>\n                {{/is_allowed}}\n              {{/is_allowed}}\n            </li>\n\n            <li>\n              <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n            </li>\n\n            {{#is_info_pin}}\n              {{#is_allowed_to_map page_instance instance}}\n                {{^options.is_in_selector}}\n                  {{> /static/mustache/base_objects/unmap.mustache}}\n\n                {{/options}}\n              {{/is_allowed_to_map}}\n              {{#if instance.viewLink}}\n                {{#is_allowed "view_object_page" instance}}\n                  <li>\n                    <a href="{{instance.viewLink}}">\n                      <i class="fa fa-long-arrow-right"></i>\n                      Open {{instance.class.title_singular}}\n                    </a>\n                  </li>\n                {{/is_allowed}}\n              {{/if}}\n            {{/is_info_pin}}\n\n            {{#is_allowed \'delete\' instance}}\n              <li>\n                <a data-toggle="modal-ajax-deleteform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n                  <i class="fa fa-trash"></i>\n                  Delete\n                </a>\n              </li>\n            {{/is_allowed}}\n\n          </ul>\n        </div>\n      </div>\n      {{> /static/mustache/mixins/stateful.mustache }}\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['base_templates/mapped_objects']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Mapped objects</h6>\n\n<mapping-tree-view\n  parent-instance="instance"\n  mapping="instance.class.info_pane_options.mapped_objects.mapping"\n  item-template="instance.class.info_pane_options.mapped_objects.show_view"\n  tree-view-class="tree-structure new-tree mapped-objects-tree">\n</mapping-tree-view>\n\n{{#if_equals instance.type \'CycleTaskGroupObjectTask\'}}\n  {{^if_in instance.status "Finished,Verified"}}\n    {{#is_allowed \'update\' instance context=\'for\'}}\n      <a\n        class="btn btn-draft btn-small"\n        href="javascript://"\n        rel="tooltip"\n        data-type="Program"\n        data-placement="right"\n        data-toggle="unified-mapper"\n        data-join-mapping="related_objects"\n        data-join-option-type="{{model.shortName}}"\n        data-join-object-id="{{instance.id}}"\n        data-join-object-type="{{instance.class.shortName}}"\n        data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty instance.class.title_singular \'Object\'}}">\n        Map Objects\n      </a>\n    {{/is_allowed}}\n  {{/if_in}}\n{{else}}\n  {{#is_allowed \'update\' instance context=\'for\'}}\n  <a\n    class="btn btn-draft btn-small"\n    href="javascript://"\n    rel="tooltip"\n    data-type="Program"\n    data-placement="right"\n    data-toggle="unified-mapper"\n    data-join-mapping="related_objects"\n    data-join-option-type="{{model.shortName}}"\n    data-join-object-id="{{instance.id}}"\n    data-join-object-type="{{instance.class.shortName}}"\n    data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty instance.class.title_singular \'Object\'}}">\n    Map Objects\n  </a>\n  {{/is_allowed}}\n{{/if_equals}}\n';
GGRC.Templates['base_templates/notes']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Notes</h6>\n<div class="rtf-block">\n  {{notes}}\n</div>\n';
GGRC.Templates['base_templates/people_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^disableTitle}}\n  <h6 class="people-list__label">People</h6>\n{{/disableTitle}}\n<div class="side-content">\n  {{#instance.roleConflicts}}\n    <div class="centered">\n      <div class="alert alert-error">\n        <p>\n            Assignee and Verifier cannot be the same person!\n        </p>\n      </div>\n    </div>\n  {{/instance.roleConflicts}}\n\n  <ul class="label-list">\n    {{#instance.class.assignable_list}}\n        <li>\n          <people-group\n                  deferred="deferred"\n                  editable="editable"\n                  instance="instance"\n                  mapping="mapping"\n                  type="type"\n                  heading="title"\n                  bold-title="disableTitle"\n                  tabindex="7"\n                  {{#if required}}\n                  required="true"\n                  validate="validate"\n                  {{/if}}>\n          </people-group>\n        </li>\n    {{/instance.class.assignable_list}}\n  </ul>\n</div>\n';
GGRC.Templates['base_templates/people_group']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<label class="people-group">\n    <span {{#boldTitle}}class="people-group_title"{{/boldTitle}}>\n      {{heading}}\n    </span>\n    {{#if editable}}\n      {{#if required}}\n        <span class="required">*</span>\n      {{/if}}\n    {{/if}}\n    {{#editable}}\n      {{#has_permissions}}\n        {{^toggle toggle_add}}\n          <span class="person-add">\n            <a href="javascript://" {{toggle_button}}>\n              <i class="fa fa-plus-circle"></i>\n            </a>\n          </span>\n        {{/toggle}}\n      {{/has_permissions}}\n    {{/editable}}\n</label>\n<ul class="inner-count-list">\n  {{^if results.length}}\n    <li><i> No {{type}}s</i></li>\n  {{/if}}\n  {{#results}}\n    <li>\n      {{#has_permissions}}\n        {{#can_unmap}}\n        <span class="file-controls">\n          <a href="javascript://" class="info-action unmap" can-click="remove_role" data-person={{id}}>\n            <span class="result" {{data \'result\'}}></span>\n            <i class="fa fa-trash"></i>\n          </a>\n        </span>\n        {{/can_unmap}}\n      {{/has_permissions}}\n      <person-info person-obj="." />\n    </li>\n  {{/results}}\n  <li class="person-add">\n    {{#show_add}}\n      {{#has_permissions}}\n        {{#toggle toggle_add}}\n          <i class="fa fa-user green pull-left icon-field"></i>\n          <div class="person-selector">\n            <input tabindex="7" type="text" name="" class="search-icon" data-lookup="Person" placeholder="Search for {{type}}" null-if-empty="false" value="" {{autocomplete_select}} />\n            <a href="javascript://" {{toggle_button}}>\n              <i class="fa fa-times"></i>\n            </a>\n          </div>\n        {{/toggle}}\n      {{/has_permissions}}\n    {{/show_add}}\n  </li>\n</ul>\n';
GGRC.Templates['base_templates/request_type']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Type</h6>\n<div class="side-content">\n  <ul class="label-list dates">\n    {{#if_equals request_type "documentation"}}\n      <li>\n      <span class="file-controls">\n        <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n      </span>\n        <label>\n          Documentation\n        </label>\n      </li>\n    {{/if_equals}}\n    {{#if_equals request_type "interview"}}\n      <li>\n      <span class="file-controls">\n        <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n      </span>\n        <label>\n          Interview\n        </label>\n      </li>\n    {{/if_equals}}\n  </ul>\n</div>\n';
GGRC.Templates['base_templates/test']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Test</h6>\n<div class="rtf-block">\n  {{test}}\n</div>\n';
GGRC.Templates['base_templates/urls']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<reusable-object reusable="reusable" method="reuseMethod" list="reusedObjects" parent-instance="parentInstance" base-instance="baseInstance" mapping="mapping" isLoading="isLoading">\n  <li data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    {{^if reusable}}\n    {{#is_allowed \'update\' parentInstance context=\'for\'}}\n    {{#instance}}\n      <span class="file-controls">\n        <a href="javascript://" class="info-action unmap" data-toggle="unmap">\n          <span class="result" {{data \'result\'}}></span>\n          <i class="fa fa-trash"></i>\n        </a>\n      </span>\n    {{/instance}}\n    {{/is_allowed}}\n    {{/if}}\n    <span class="date">{{date instance.created_at true}}</span>\n    <a href="{{schemed_url instance.link}}" target="_blank">\n      <span>{{firstnonempty instance.title instance.link}}</span>\n    </a>\n\n    {{#if reusable}}\n      {{#is_allowed \'update\' baseInstance context=\'for\'}}\n        {{#is_allowed \'update\' instance context=\'for\'}}\n          {{#isDisabled instance}}\n            <input type="checkbox" checked disabled />\n          {{/isDisabled}}\n          {{^isDisabled instance}}\n            <input type="checkbox" {{#isLoading}}disabled{{/isLoading}} can-value="list.{{method}}-{{instance.type}}-{{instance.id}}" />\n          {{/isDisabled}}\n        {{/is_allowed}}\n      {{/is_allowed}}\n    {{/if}}\n  </li>\n</reusable-object>\n';
GGRC.Templates['base_templates/mapping_tree_view']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="{{#if treeViewClass}}{{treeViewClass}}{{else}}attachment-list{{/if}}">\n  {{#mappedObjects}}\n    {{#using instance=instance}}\n      {{{render itemTemplate instance=instance baseInstance=baseInstance parentInstance=parentInstance reusable=reusable reusedObjects=reusedObjects reuseMethod=reuseMethod mapping=mapping isLoading=isLoading isExpandable=isExpandable}}}\n    {{/using}}\n  {{/mappedObjects}}\n</ul>\n';
GGRC.Templates['base_templates/assignable_extended_info']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <div class="row-fluid">\n    <div class="span12">\n      {{#using program=instance.program}}\n        <a class="main-title {{instance.class.category}} oneline" href="{{#is_allowed \'view_object_page\' \'Program\' context=program.context}}{{viewLink}}{{else}}/dashboard#audit_widget/audit/{{id}}{{/is_allowed}}">\n          {{instance.title}}\n        </a>\n      {{/using}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span9">\n      <h6>Description</h6>\n      <div class="rtf">\n        {{{firstnonempty description \'<span class="empty-message">None</span>\'}}}\n      </div>\n    </div>\n    <div class="span3">\n      <h6>Status</h6>\n      <p>\n        {{status}}\n      </p>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span9">\n      <people-list instance="instance"></people-list>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span3">\n      <h6>Start date</h6>\n      <p>\n        {{#if instance.start_date}}\n          {{localize_date instance.start_date}}\n        {{else}}\n          Not set\n        {{/if}}\n      </p>\n    </div>\n    <div class="span3">\n      <h6>End date</h6>\n      <p>\n        {{#if instance.end_date}}\n          {{localize_date instance.end_date}}\n        {{else}}\n          Not set\n        {{/if}}\n      </p>\n    </div>\n  </div>\n\n  <div class="links">\n    <div class="row-fluid">\n      <div class="span12">\n        {{#using program=instance.program}}\n          <a class="secondary oneline {{instance.class.category}}" href="{{#is_allowed \'view_object_page\' \'Program\' context=program.context}}{{program.viewLink}}{{else}}/dashboard{{/is_allowed}}#audit_widget/audit/{{id}}">\n            View {{instance.title}}\n          </a>\n        {{/using}}\n      </div>\n    </div>\n  </div>\n{{/instance}}\n';
GGRC.Templates['mixins/assignable']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{#instance}}\n  <ul class="label-list">\n    {{#each instance.assignable}}\n      <li>\n        <label>\n          <span class="person-label {{group}}"></span>\n          {{group}}\n        </label>\n        <ul>\n          {{#each group.people}}\n            <li>\n              <span class="file-controls">\n                <a href="javascript://"><i class="fa fa-trash"></i></a>\n                <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n              </span>\n              <a href="javascript://">{{name}}</a>\n            </li>\n          {{/each}}\n        </ul>\n      </li>\n    {{/each}}\n  </ul>\n{{/instance}}\n';
GGRC.Templates['mixins/stateful']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'update\' instance context=\'for\'}}\n  <div {{#instance}}{{data \'model\'}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({instance : el.data("model") }) }}>\n\n  {{#if_in instance.status "Not Started,In Progress"}}\n    {{#if_null instance._mandatory_msg}}\n      {{#unless instance._disabled}}\n        {{#if_verifiers_defined instance}}\n          <button class="btn btn-small btn-primary pull-right btn-info-pin-header {{instance._disabled}}"\n                  data-name="status"\n                  data-value="Ready for Review">\n            Complete\n          </button>\n        {{else}}\n          <button class="btn btn-small btn-primary pull-right btn-info-pin-header {{instance._disabled}}"\n                  data-name="status"\n                  data-value="Completed">Complete</button>\n        {{/if_verifiers_defined}}\n      {{/unless}}\n    {{else}}\n      <button class="btn btn-small btn-primary pull-right btn-info-pin-header disabled"\n              title="{{instance._mandatory_msg}}">\n        Complete\n      </button>\n    {{/if_null}}\n  {{/if_in}}\n\n  {{#if_equals instance.status "Ready for Review"}}\n    {{#with_mapping \'related_verifiers\' instance}}\n      {{#results}}\n        {{#if_current_user instance}}\n          {{#if_null instance._mandatory_msg}}\n            <button class="btn btn-small btn-success pull-right btn-info-pin-header {{instance._disabled}}"\n                    data-name="status"\n                    data-value="Verified">Verify</button>\n            <button class="btn btn-small btn-danger pull-right btn-info-pin-header {{instance._disabled}}"\n                    data-name="status"\n                    data-value="In Progress">Reject</button>\n          {{else}}\n            <button class="btn btn-small btn-success pull-right btn-info-pin-header disabled"\n                    data-name="status"\n                    data-value="Verified"\n                    title="{{instance._mandatory_msg}}">Verify</button>\n            <button class="btn btn-small btn-danger pull-right btn-info-pin-header disabled"\n                    data-name="status"\n                    data-value="In Progress"\n                    title="{{instance._mandatory_msg}}">Reject</button>\n          {{/if_null}}\n        {{/if_current_user}}\n      {{/results}}\n    {{/with_mapping}}\n  {{/if_equals}}\n\n    {{#instance._undo.0}}\n        <a href="javascript://" data-name="status" data-value="{{instance._undo.0}}" data-undo="true" class="undo btn-info-pin-header pull-right {{instance._disabled}}">Undo</a>\n    {{/instance._undo.0}}\n  </div>\n{{/is_allowed }}\n';
GGRC.Templates['clauses/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Enter Title"></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" id="section-title" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n    </div>\n    <div class="span6">\n      &nbsp;\n      <input id="directive.id" name="directive" type="hidden" model="Directive" value="{{instance.directive.id}}">\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div data-id="text_of_clause_hidden" class="span6 hidable">\n      <label>\n        Text of Clause\n        <i class="fa fa-question-circle" rel="tooltip" title="Enter text of clause"></i>\n        <a data-id="hide_text_of_clause_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" data-id="text_of_clause_txtbx" tabindex="2" id="section-description" class="span12 triple wysihtml5" name="description" placeholder="Enter text of clause">{{{description}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="6" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div class="row-fluid inner-hide">\n          <div data-id="url_hidden" class="span12 hidable">\n            <label>\n              Clause URL\n              <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n              <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <input data-id="url_txtbx" tabindex="7" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n          </div>\n        </div>\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="8" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div>\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="7" id="section-code" class="input-block-level" name="slug" placeholder="CLAUSE-XXX" type="text" value="{{slug}}">\n      </div>\n      <effective-dates instance="instance"/>\n    <div class="row-fluid">\n      <div data-id="state_hidden" class="span4 hidable">\n        <label>\n          State\n          <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n          <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n          <dropdown options-list="model.statuses"\n                    name="instance.status"\n                    tabindex="23">\n          </dropdown>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['contracts/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            Contract URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="6" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="7" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div>\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="CONTRACT-XXX" type="text" value="{{slug}}">\n      </div>\n      <effective-dates instance="instance"/>\n    </div>\n    <div class="row-fluid">\n      <div data-id="state_hidden" class="span4 hidable">\n        <label>\n          State\n          <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n          <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n          <dropdown options-list="model.statuses"\n                    name="instance.status"\n                    tabindex="23">\n          </dropdown>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
