GGRC.Templates['controls/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/test_plan.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/notes.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div>\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span3">\n              <h6>Code</h6>\n              <p>\n                {{slug}}\n              </p>\n            </div>\n            <div class="span3">\n              <h6>Principal Assignee</h6>\n              {{#using assessor=instance.principal_assessor}}\n              {{#if instance.principal_assessor}}\n                <p>\n                  {{{render \'/static/mustache/people/popover.mustache\' person=assessor}}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n              {{/using}}\n            </div>\n            <div class="span3">\n              <h6>Secondary Assignee</h6>\n              {{#using assessor=instance.secondary_assessor}}\n              {{#if instance.secondary_assessor}}\n                <p>\n                  {{{render \'/static/mustache/people/popover.mustache\' person=assessor}}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n              {{/using}}\n            </div>\n          </div>\n          <div class="row-fluid wrap-row">\n            <div class="span3">\n              <h6>Kind/Nature</h6>\n              {{#if kind}}\n                <p>\n                  {{#using kind=kind}}\n                    {{kind.title}}\n                  {{/using}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Fraud Related</h6>\n              <p>\n                {{#if_equals fraud_related "0"}}\n                  No\n                {{/if}}\n                {{#if_equals fraud_related "1"}}\n                  Yes\n                {{/if}}\n                {{^if_equals fraud_related "0" }}\n                  {{^if_equals fraud_related "1" }}\n                    <span class="empty-message">None</span>\n                  {{/if}}\n                {{/if}}\n              </p>\n            </div>\n            <div class="span3">\n              <h6>Significance</h6>\n              <p>\n                {{#if_equals key_control "1"}}\n                  Key\n                {{/if}}\n                {{#if_equals key_control "0"}}\n                  Non-Key\n                {{/if}}\n                {{^if_equals key_control "0" }}\n                  {{^if_equals key_control "1" }}\n                    <span class="empty-message">None</span>\n                  {{/if}}\n                {{/if}}\n              </p>\n            </div>\n            <div class="span3">\n              <h6>Type/Means</h6>\n              {{#if means}}\n                <p>\n                  {{#using means=means}}\n                    {{means.title}}\n                  {{/using}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div>\n          <div class="row-fluid wrap-row">\n            <div class="span3">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Frequency</h6>\n              {{#if verify_frequency}}\n                <p>\n                  {{#using verify_frequency=verify_frequency}}\n                    {{verify_frequency.title}}\n                  {{/using}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div>\n          <div class="row-fluid wrap-row">\n            <div class="span6">\n              <h6>Assertions</h6>\n              {{#using items=assertions}}\n              {{#if assertions}}\n                <ul>\n                  {{#items}}\n                    <li>{{name}}</li>\n                  {{/items}}\n                </ul>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n              {{/using}}\n            </div>\n            <div class="span6">\n              <h6>Categories</h6>\n              {{#using items=categories}}\n              {{#if categories}}\n                <ul>\n                  {{#items}}\n                    <li>{{name}}</li>\n                  {{/items}}\n                </ul>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n              {{/using}}\n            </div>\n          </div>\n        </div>\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
