GGRC.Templates['assessment_templates/attribute_field']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^field._pending_delete}}\n  <div class="span-custom2-and-half">{{field.title}}</div>\n  <div class="span-custom1-and-half">{{field.attribute_name}}</div>\n  <div class="span5">\n    {{#attrs}}\n        <div class="row">\n            <div class="span5">\n                <span class="block single-line" title="{{value}}">{{value}}</span>\n            </div>\n            <div class="span5 centered checkbox-wrap">\n                <input can-value="attachment" type="checkbox">\n            </div>\n            <div class="span2 centered checkbox-wrap">\n                <input can-value="comment" type="checkbox">\n            </div>\n        </div>\n    {{/attrs}}\n  </div>\n\n  <div class="span1 centered checkbox-wrap">\n    <input type="checkbox" can-value="field.mandatory">\n  </div>\n  <div class="span2">\n    <div class="pull-right">\n      {{#field}}\n        <a can-click="removeField" href="#"><i class="fa fa-trash"></i></a>\n      {{/field}}\n    </div>\n  </div>\n{{/field._pending_delete}}\n';
GGRC.Templates['assessment_templates/attribute_add_field']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="span-custom2-and-half{{#selected.invalidTitle}} field-failure{{/selected.invalidTitle}}">\n  <input class="input-block-level"\n    type="text"\n    can-value="selected.title"\n    placeholder="Enter title">\n    {{#selected.dublicateTitle}}\n      <label class="warning">A custom attribute with this title already exists</label>\n    {{/selected.dublicateTitle}}\n    {{#selected.emptyTitle}}\n      <label class="warning">A custom attribute title can not be blank</label>\n    {{/selected.emptyTitle}}\n</div>\n<div class="span-custom1-and-half">\n  <select class="input-block-level" can-value="selected.type">\n    {{#types}}\n      <option value="{{type}}">{{name}}</option>\n    {{/types}}\n  </select>\n</div>\n<div class="span2{{#selected.invalidValues}}\n            field-failure{{/selected.invalidValues}}\n            ca-values">\n  {{#in_array selected.type valueAttrs}}\n    <input class="input-block-level"\n      can-value="selected.values"\n      type="text"\n      placeholder="{{placeholder}}">\n      <i class="fa fa-question-circle" rel="tooltip" title="{{placeholder}}"></i>\n    {{#selected.invalidValues}}\n        <label class="warning">Cannot be blank</label>\n    {{/selected.invalidValues}}\n  {{else}}\n    N/A\n  {{/in_array}}\n</div>\n<div class="span2 centered checkbox-wrap">\n</div>\n<div class="span1 centered checkbox-wrap">\n</div>\n<div class="span1 centered checkbox-wrap">\n</div>\n<div class="span2">\n  <div class="pull-right">\n    <a can-click="addField" class="btn btn-small btn-info" href="#">Add field</a>\n  </div>\n</div>\n';
GGRC.Templates['assessment_templates/modal_content']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="hideable-holder">\n  <wrapper-assessment-template instance=\'instance\'>\n  <form action="javascript://">\n    {{> /static/mustache/base_objects/form_restore.mustache}}\n\n    <div class="row-fluid">\n      <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n        <label>\n          Title\n          <span class="required">*</span>\n          <i\n            class="fa fa-question-circle"\n            rel="tooltip"\n            title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."\n            ></i>\n        </label>\n\n        <input\n          data-id="title_txtbx" tabindex="1" class="input-block-level"\n          placeholder="Enter Title"\n          name="title"\n          type="text"\n          can-value="instance.title"\n          autofocus>\n\n        {{#instance.computed_errors.title}}\n          <label class="help-inline warning">{{this}}</label>\n        {{/instance.computed_errors.title}}\n      </div>\n    </div>\n\n    <div class="row-fluid">\n      <div class="span6">\n        <label>Audit</label>\n        <strong>{{object_params.audit_title}}</strong>\n      </div>\n    </div>\n\n    <br />\n\n    <div class="row-fluid choose-object">\n      <div class="span12">\n        <label>\n          Objects under assessment\n          <span class="required">*</span>\n        </label>\n\n        {{#add_to_current_scope objectTypes=instance._objectTypes}}\n        <select\n          class="input-medium pull-left"\n          can-value="instance.template_object_type">\n          {{#each objectTypes}}\n            {{#if items}}\n              {{#if items.length}}\n              <optgroup label="{{name}}">\n                {{#each items}}\n                  {{#if name}}\n                    <option value="{{value}}" label="{{name}}"></option>\n                  {{/if}}\n                {{/each}}\n              </optgroup>\n              {{/if}}\n            {{/if}}\n          {{/each}}\n        </select>\n        {{/add_to_current_scope}}\n\n        {{#if_equals instance.template_object_type \'Control\'}}\n        <label class="inline-check pull-left">\n          <input type="checkbox"\n            can-value="instance.test_plan_procedure"\n            class="js-toggle-controlplans">\n          Use control test plans as assessment test plan\n        </label>\n        {{/if_equals}}\n      </div>\n    </div>\n\n    <div class="row-fluid">\n      <div class="span6 hidable">\n        <label>\n          Default test plan\n          <i class="grcicon-help-black" rel="tooltip"\n            title="Provide more details on the purpose of this Assessment and\n                   provide context for how and when this CA might be used."></i>\n          <a data-id="hide_default_test_plan_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n\n        {{#if instance.test_plan_procedure}}\n          <div class="wysiwyg-area">\n            <textarea\n              class="span12 triple"\n              placeholder="Enter Description"\n              tabindex="2"\n              disabled\n              ></textarea>\n          </div>\n        {{else}}\n          <div class="wysiwyg-area">\n            <textarea\n              id="system_description"\n              can-value="instance.procedure_description"\n              class="span12 triple wysihtml5"\n              placeholder="Enter Description"\n              tabindex="2"\n              ></textarea>\n          </div>\n        {{/if}}\n      </div>\n\n      <div class="span6">\n        <div class="row-fluid choose-from-select">\n          <div class="span6 bottom-spacing">\n            <label>\n              Default Assignees\n              <span class="required">*</span>\n            </label>\n            <dropdown\n              class-name="input-block-level js-toggle-field"\n              options-list="instance.people_values"\n              name="instance.default_people.assessors"\n              on-change="instance.defaultAssesorsChanged"\n            ></dropdown>\n          </div>\n\n          <div class="span6{{#instance.computed_errors.assessorsList}} field-failure{{/instance.computed_errors.assessorsList}}">\n            <label>&nbsp;</label>\n            {{#if_equals instance.default_people.assessors \'other\'}}\n              <autocomplete\n                search-items-type="Person"\n                can-item-selected="instance.assessorAdded"\n                disable="instance.assessorsListDisable"\n                placeholder="Enter text to search for Assignees"\n              ></autocomplete>\n\n              {{#instance.computed_errors.assessorsList}}\n                <label class="help-inline warning">{{this}}</label>\n              {{/instance.computed_errors.assessorsList}}\n\n              {{#each instance.assessorsList}}\n                <person-info\n                  editable="true"\n                  person-id="{{@key}}"\n                  can-person-remove="instance.assessorRemoved"></person-info>\n              {{/each}}\n            {{/if}}\n          </div>\n        </div>\n\n        <div class="row-fluid choose-from-select">\n          <div class="span6 bottom-spacing">\n            <label>\n              Default Verifier\n              <span class="required">*</span>\n            </label>\n\n            <dropdown\n              class-name="input-block-level js-toggle-field"\n              options-list="instance.people_values"\n              name="instance.default_people.verifiers"\n              on-change="instance.defaultVerifiersChanged"\n            ></dropdown>\n          </div>\n\n          <div class="span6{{#instance.computed_errors.verifiersList}} field-failure{{/instance.computed_errors.verifiersList}}">\n            <label>&nbsp;</label>\n\n            {{#if_equals instance.default_people.verifiers \'other\'}}\n              <autocomplete\n                search-items-type="Person"\n                can-item-selected="instance.verifierAdded"\n                disable="instance.verifiersListDisable"\n                placeholder="Enter text to search for Verifiers"\n              ></autocomplete>\n\n              {{#instance.computed_errors.verifiersList}}\n                <label class="help-inline warning">{{this}}</label>\n              {{/instance.computed_errors.verifiersList}}\n\n              {{#each instance.verifiersList}}\n                <person-info\n                  editable="true"\n                  person-id="{{@key}}"\n                  can-person-remove="instance.verifierRemoved"></person-info>\n              {{/each}}\n            {{/if}}\n          </div>\n        </div>\n      </div>\n    </div>\n\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="3" class="input-block-level" name="slug" placeholder="AUDIT-XXX" type="text" value="{{instance.slug}}">\n      </div>\n    </div>\n\n    <div class="spacing-top row-fluid hidable">\n      <label>Custom Attributes</label>\n      <div class="custom-attr-wrap define-attr">\n        <div class="hidden-fields-area">\n          <div class="row-fluid">\n            <div class="custom-attr-list span12">\n              <assessment-template-attributes fields="instance.custom_attribute_definitions">\n                <div class="attr-titles">\n                  <ul>\n                    <li class="row-fluid">\n                      <div class="span-custom2-and-half">\n                        <h6>Attribute Title</h6>\n                      </div>\n                      <div class="span-custom1-and-half">\n                        <h6>Attribute type</h6>\n                      </div>\n                      <div class="span2">\n                        <h6>Attribute values</h6>\n                      </div>\n                      <div class="span2 centered">\n                        <h6>Attachment required</h6>\n                      </div>\n                      <div class="span1 centered">\n                        <h6>Comment required</h6>\n                      </div>\n                      <div class="span1 centered">\n                        <h6>Mandatory</h6>\n                      </div>\n                      <div class="span2">\n                        &nbsp;\n                      </div>\n                    </li>\n                  </ul>\n                </div>\n                <div class="sortable-list new-attr-list">\n                  <ul>\n                    <li class="row-fluid add-field">\n                      <add-template-field fields="fields">\n                      </add-template-field>\n                    </li>\n                    {{#fields}}\n                      {{^if _pending_delete}}\n                          <li class="row-fluid sortable-item" {{data \'field\'}}>\n                              <template-field\n                                field="."\n                                fields="fields"\n                                can-on-remove="fieldRemoved"></template-field>\n                          </li>\n                      {{/if _pending_delete}}\n                    {{/fields}}\n                  </ul>\n                </div>\n              </assessment-template-attributes>\n            </div>\n          </div>\n        </div>\n      </div><!-- custom-attr-wrap end -->\n    </div>\n </form>\n </wrapper-assessment-template>\n</div>\n';
GGRC.Templates['assessment_templates/info']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n\n      <div class="pane-header">\n        <div class="row-fluid wrap-row">\n          <div data-test-id="title_0ad9fbaf" class="span9">\n            <h6>Title</h6>\n            <h3>{{title}}</h3>\n\n            {{#if type}}\n              <p>{{type.title}}</p>\n            {{/if}}\n          </div>\n        </div>\n      </div>\n\n      <div class="row-fluid wrap-row">\n        <div class="span4">\n          <h6>Objects under assessment</h6>\n          {{#if template_object_type}}\n            {{template_object_type}}\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n\n        <div class="span4">\n          <h6>Use control test plans as procedures</h6>\n          {{#if test_plan_procedure}}YES{{else}}NO{{/if}}\n        </div>\n\n        <div class="span4">\n          <h6>Default procedure description</h6>\n          {{#if procedure_description}}\n            {{{procedure_description}}}\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n      </div>\n\n      <div class="row-fluid wrap-row">\n        <div class="span4">\n          <h6>Default Assignees</h6>\n          {{#if default_people.assessors}}\n            {{#if_string default_people.assessors}}\n              {{get_item DEFAULT_PEOPLE_LABELS default_people.assessors}}\n            {{else}}\n              {{#if default_people.assessors.length}}\n                <ul>\n                  {{#each default_people.assessors}}\n                    <li>\n                      <person-info editable="true" person-id="{{.}}"></person-info>\n                    </li>\n                  {{/each}}\n                </ul>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            {{/if_string}}\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n\n        <div class="span4">\n          <h6>Default Verifier</h6>\n          {{#if default_people.verifiers}}\n            {{#if_string default_people.verifiers}}\n              {{get_item DEFAULT_PEOPLE_LABELS default_people.verifiers}}\n            {{else}}\n              {{#if default_people.verifiers.length}}\n                <ul>\n                  {{#each default_people.verifiers}}\n                    <li>\n                      <person-info editable="true" person-id="{{.}}"></person-info>\n                    </li>\n                  {{/each}}\n                </ul>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            {{/if_string}}\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n\n        <div class="span4">\n          <h6>Code</h6>\n          <span class="empty-message">{{slug}}</span>\n        </div>\n      </div>\n\n    </div><!-- tier-content end -->\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['assessment_templates/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> /static/mustache/base_objects/dropdown_menu_tier2.mustache}}\n\n<div class="tier-content">\n  {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance }}}\n  {{{render \'/static/mustache/base_objects/mappings_detail.mustache\' result=result parent_instance=parent_instance}}}\n  {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n  {{{render \'/static/mustache/base_objects/notes_and_code.mustache\' instance=instance}}}\n</div>\n';
GGRC.Templates['assessment_templates/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'update\' parent_instance context=\'for\'}}\n  <a href="javascript://"\n     class="action-button create-button"\n     rel="tooltip"\n     data-placement="left"\n     data-toggle="modal-ajax-form"\n     data-original-title="Define Assessment template"\n     data-modal-reset="reset"\n     data-modal-class="modal-wide"\n     data-object-singular="{{model_info \'AssessmentTemplate\' \'model_singular\'}}"\n     data-object-plural="{{model_info \'AssessmentTemplate\' \'root_collection\'}}"\n     data-object-params=\'{\n      "audit": {\n        "id": {{parent_instance.id}},\n        "type": "{{json_escape parent_instance.type}}"\n      },\n      "context": {\n        "id": {{parent_instance.context.id}},\n        "type": "{{json_escape parent_instance.context.type}}"\n      },\n      "audit_title": "{{json_escape parent_instance.title}}"\n    }\'\n  >\n    Create New\n  </a>\n{{/is_allowed}}\n\n';
GGRC.Templates['assessments/related_issues']= '{{!\nCopyright (C) 2016 Google Inc.\nLicensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n`}}\n\n<related-objects base-instance="instance" related-items-type="Issue">\n    <div class="grid-data-toolbar flex-box">\n        <tree-pagination paging="paging" class="flex-size-1"></tree-pagination>\n      {{#is_allowed \'update\' baseInstance context=\'for\'}}\n          <div class="grid-data-toolbar-control">\n            {{#with_mapping \'related_audits\' baseInstance}}\n              {{#with_mapping \'related_controls\' baseInstance}}\n                {{#using control=control}}\n                  {{#with_create_issue_json baseInstance}}\n                      <a class="btn btn-small btn-danger"\n                         href="javascript://"\n                         data-toggle="modal-ajax-form"\n                         data-modal-class="modal-wide"\n                         data-object-singular="Issue"\n                         data-object-plural="issues"\n                         data-object-params=\'{{create_issue_json}}\'>\n                          Raise an Issue\n                      </a>\n                  {{/with_create_issue_json}}\n                {{/using}}\n              {{/with_mapping}}\n            {{/with_mapping}}\n          </div>\n      {{/is_allowed}}\n    </div>\n    <div class="grid-data-header flex-row flex-box">\n        <div class="flex-size-2">\n            Title\n        </div>\n        <div class="flex-size-3">\n            Description\n        </div>\n    </div>\n    <div class="grid-data-body {{#isLoading}}loading{{/isLoading}}">\n        <object-list items="relatedObjects" is-loading="isLoading" spinner-css="grid-spinner">\n            <div class="grid-data-row flex-row flex-box">\n                <div class="flex-size-2">\n                    <a href="{{viewLink}}" target="_blank">\n                      {{title}}\n                    </a>\n                </div>\n                <div class="flex-size-3">\n                    <read-more text="description"></read-more>\n                </div>\n            </div>\n        </object-list>\n    </div>\n</related-objects>\n';
GGRC.Templates['assessments/related_requests']= '{{!\nCopyright (C) 2016 Google Inc.\nLicensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<related-objects base-instance="instance" related-items-type="Request">\n    <div class="grid-data-toolbar flex-box">\n        <tree-pagination paging="paging" class="flex-size-1"></tree-pagination>\n      {{#is_allowed \'update\' baseInstance context=\'for\'}}\n          <div class="grid-data-toolbar-control">\n            {{#with_mapping \'related_audits\' baseInstance}}\n              {{#with_mapping \'related_controls\' baseInstance}}\n                {{#using control=control}}\n                  {{#with_create_issue_json baseInstance}}\n                      <a class="btn btn-small btn-primary"\n                         href="javascript://"\n                         data-toggle="modal-ajax-form"\n                         data-modal-class="modal-wide"\n                         data-object-singular="Request"\n                         data-object-plural="requests"\n                         data-object-params=\'{{create_issue_json}}\'>\n                          Create Request\n                      </a>\n                  {{/with_create_issue_json}}\n                {{/using}}\n              {{/with_mapping}}\n            {{/with_mapping}}\n          </div>\n      {{/is_allowed}}\n    </div>\n    <div class="grid-data-header flex-row flex-box">\n        <div class="flex-size-2">\n            Title\n        </div>\n        <div class="flex-size-3">\n            Description\n        </div>\n    </div>\n    <div class="grid-data-body {{#isLoading}}loading{{/isLoading}}">\n        <object-list items="relatedObjects" is-loading="isLoading" spinner-css="grid-spinner">\n            <div class="grid-data-row flex-row flex-box">\n                <div class="flex-size-2">\n                    <a href="{{viewLink}}" target="_blank">\n                      {{title}}\n                    </a>\n                </div>\n                <div class="flex-size-3">\n                    <read-more text="description"></read-more>\n                </div>\n            </div>\n        </object-list>\n    </div>\n</related-objects>\n';
GGRC.Templates['issues/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    {{#control}}\n    <div class="span3">\n        <label>\n          Control\n          <i class="fa fa-question-circle" rel="tooltip" title="Control for this Issue"></i>\n        </label>\n        <input tabindex="999" readonly="readonly" class="input-block-level" type="text" value="{{firstexist title \'\'}}" />\n    </div>\n    {{/control}}\n    {{#audit}}\n    <div class="span3">\n        <label>\n          Audit\n          <i class="fa fa-question-circle" rel="tooltip" title="Audit for this Issue"></i>\n        </label>\n        <input tabindex="999" readonly="readonly" class="input-block-level" placeholder="Choose Audit" type="text" value="{{firstexist title \'\'}}" />\n    </div>\n    {{/audit}}\n    {{#program}}\n    <div class="span3">\n        <label>\n          Program\n          <i class="fa fa-question-circle" rel="tooltip" title="Program for this Issue"></i>\n        </label>\n        <input tabindex="999" readonly="readonly" class="input-block-level" type="text" value="{{firstexist title \'\'}}" />\n    </div>\n    {{/program}}\n    {{#assessment}}\n    <div class="span3">\n        <label>\n          Assessment\n          <i class="fa fa-question-circle" rel="tooltip" title="Assessment for this Issue"></i>\n        </label>\n        <input tabindex="999" readonly="readonly" class="input-block-level" data-lookup="Assessment" type="text" value="{{firstexist title \'\'}}" />\n    </div>\n    {{/assessment}}\n  </div>\n\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="control_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n    </div>\n    </div>\n\n\n    <div class="row-fluid">\n    <div data-id="test_plan_hidden" class="span6 hidable">\n      <label>\n        Remediation Plan\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide a remediation plan for this {{model.model_singular}}."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="control_test_plan" class="span12 triple wysihtml5" name="test_plan" placeholder="Enter Remediation Plan">{{{test_plan}}}</textarea>\n      </div>\n    </div>\n\n\n    <div class="span6 hide-wrap hidable">\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n    </div>\n\n    <div class="row-fluid">\n      <div data-id="note_hidden" class="span6 hidable">\n        <label>\n          Notes\n          <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n          <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <div class="wysiwyg-area">\n          <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n        </div>\n      </div>\n      <div class="span6 hide-wrap hidable">\n        <div class="row-fluid inner-hide">\n          <div data-id="url_hidden" class="span12 hidable">\n            <label>\n              Issue URL\n              <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n              <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <input data-id="url_txtbx" tabindex="7" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n          </div>\n        </div>\n        <div class="row-fluid inner-hide">\n          <div data-id="reference_url_hidden" class="span12 hidable">\n            <label>\n              Reference URL\n              <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n              <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <input data-id="reference_url_txtbx" tabindex="8" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n          </div>\n        </div>\n      </div>\n    </div>\n\n  <div>\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="ISSUE-XXX" type="text" value="{{slug}}">\n      </div>\n      <effective-dates instance="instance"/>\n    </div>\n    <div class="row-fluid">\n      <div data-id="state_hidden" class="span4 hidable">\n        <label>\n          State\n          <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n          <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n          <dropdown options-list="model.statuses"\n                    no-value="true"\n                    no-value-label="---"\n                    name="instance.status"\n                    tabindex="23">\n          </dropdown>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['issues/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/issues/remediation_plan.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/notes.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div><!-- row-fluid end -->\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span4">\n              <h6>Code</h6>\n              <p>\n                {{slug}}\n              </p>\n            </div>\n            <div class="span4">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span4">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div><!-- row-fluid end -->\n        </div><!-- hidden-fields-area end -->\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['issues/remediation_plan']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n\n    <div class="row-fluid wrap-row">\n        <div class="span12">\n            <h6>Remediation Plan</h6>\n            <div class="rtf-block">\n                {{{firstnonempty test_plan \'<span class="empty-message">None</span>\'}}}\n            </div>\n        </div>\n    </div>\n\n{{/instance}}\n';
GGRC.Templates['components/inline_edit/inline']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="inline-edit inline-edit--{{type}}\n            {{#if context.isEdit}}inline-edit--active{{/if}}\n            {{#if readonly}}inline-edit--readonly{{/if}}">\n  <h6 class="inline-edit__title">\n    {{firstnonempty title label}}\n    {{#mandatory}}<span class="required">*</span>{{/mandatory}}\n    {{#helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{helptext}}"></i>{{/helptext}}\n  </h6>\n\n  <spinner toggle="isSaving"></spinner>\n  {{^if isSaving}}\n  <div class="inline-edit__content {{#if_equals type \'text\'}}inline-edit__content--wide{{/if_equals}}">\n    {{#switch type}}\n      {{#case \'checkbox\'}}\n        <inline-checkbox context="context"></inline-checkbox>\n      {{/case}}\n      {{#case \'input\'}}\n        <inline-input context="context"></inline-input>\n      {{/case}}\n      {{#case \'text\'}}\n        <inline-text context="context"></inline-text>\n      {{/case}}\n      {{#case \'dropdown\'}}\n        <inline-dropdown context="context"></inline-dropdown>\n      {{/case}}\n      {{#case \'person\'}}\n        <inline-person context="context"></inline-person>\n      {{/case}}\n      {{#case \'date\'}}\n        <inline-datepicker context="context"></inline-datepicker>\n      {{/case}}\n      {{#default}}\n        <content></content>\n      {{/default}}\n    {{/switch}}\n    <ul class="inline-edit__controls {{#if context.isEdit}}inline-edit__controls--edit-mode{{/if}}">\n      {{#if context.isEdit}}\n        <li><a can-click="onSave" href="#"><i class="fa fa-check"></i></a></li>\n        <li><a can-click="onCancel" href="#"><i class="fa fa-times"></i></a></li>\n      {{else}}\n        {{^if readonly}}\n          <li><a can-click="enableEdit" href="#"><i class="fa fa-pencil"></i></a></li>\n        {{/if}}\n      {{/if}}\n    </ul>\n  </div>\n  {{/if}}\n</div>\n';
GGRC.Templates['components/inline_edit/text']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  <rich-text text="context.value"></rich-text>\n{{else}}\n  <div class="inline-edit__text">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </div>\n{{/if}}\n';
GGRC.Templates['components/inline_edit/input']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  <input type="text" can-value="context.value" autofocus>\n{{else}}\n  <p class="inline-edit__text">\n    {{#if context.value}}\n      {{context.value}}\n    {{else}}\n      <span class="empty-message">{{emptyText}}</span>\n    {{/if}}\n  </p>\n{{/if}}\n';
GGRC.Templates['components/inline_edit/checkbox']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  <label class="inline-edit__checkbox">\n    <input can-value="context.value" type="checkbox" autofocus> {{label}}\n  </label>\n{{else}}\n  <label class="inline-edit__checkbox">\n    <input disabled type="checkbox" can-value="context.value"> {{label}}\n  </label>\n{{/if}}\n';
GGRC.Templates['components/inline_edit/dropdown']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  <dropdown\n    options-list="context.values"\n    name="context.value"\n    no-value="true"\n    class-name="input-medium"\n    ></dropdown>\n{{else}}\n  <p class="inline-edit__value">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </p>\n{{/if}}\n';
GGRC.Templates['components/inline_edit/person']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  {{#if context.value}}\n    <a href="#" can-click="unsetPerson" class="inline-block">\n      <i class="fa fa-times" aria-hidden="true"></i>\n    </a>\n    <person-info class="inline-block" person-obj="context.value"></person-info>\n  {{/if}}\n  <autocomplete\n    search-items-type="Person"\n    can-item-selected="setPerson"\n    placeholder="Select person"\n    class-name="span12"\n  ></autocomplete>\n{{else}}\n  {{#if context.value}}\n    <person-info person-obj="context.value"></person-info>\n  {{else}}\n    <span class="empty-message">No person</span>\n  {{/if}}\n{{/if}}\n';
GGRC.Templates['components/inline_edit/datepicker']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if context.isEdit}}\n  <datepicker persistent="true" date="context.value"></datepicker>\n{{else}}\n  <p class="inline-edit__text">\n    {{{firstnonempty context.value \'<span class="empty-message">None</span>\'}}}\n  </p>\n{{/if}}\n';
GGRC.Templates['components/autocomplete/autocomplete']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="{{className}} objective-selector autocomplete--wrapper">\n  <input type="text"\n    data-lookup="{{searchItemsType}}"\n    null-if-empty="true"\n    {{#if disable}}disabled{{/if}}\n    can-value="value"\n    data-no-automap="{{automappingOff}}"\n    class="{{^if context.isEdit}}input-block-level{{/if}} autocomplete--input"\n    placeholder="{{placeholder}}">\n</div>\n';
GGRC.Templates['components/unified_mapper/mapper_results']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="filter-actions wrap-modal-spacing">\n  <div class="row-fluid">\n    <div class="span12">\n      <a class="btn btn-small btn-info pull-right modalSearchButton {{#inProgress}}disabled{{/inProgress}}" href="javascript://">\n        Search\n      </a>\n    </div>\n  </div>\n</div>\n<div class="results modal-custom-spacing">\n  <div class="results-num">\n    <div class="row-fluid">\n      <div class="span9">\n        <strong>\n          {{ entries.length }} Objects found\n        </strong>\n      </div>\n      <div class="span3">\n        {{^if mapper.search_only}}\n        <div class="pull-right check-all">\n          <spinner toggle="is_loading"></spinner>\n          Select:\n            <a\n              href="#"\n              class="object-uncheck-all {{#inProgress}}disabled{{/inProgress}}"\n              can-click="unselectAll"\n              >None</a>,\n            <a\n              href="#"\n              can-click="selectAll"\n              class="object-check-all {{#allSelected}}disabled{{/allSelected}}"\n              rel="tooltip"\n              data-placement="right"\n              data-original-title="Processing more than 100 items may take long time. Please consider refining your search criteria."\n              >All</a>\n          </label>\n        </div>\n        {{/if}}\n      </div>\n    </div>\n  </div>\n  <div class="results-wrap">\n    <ul class="result-tree tree-structure new-tree">\n      {{#if mapper.search_only}}\n        {{> /static/mustache/search/advanced_search_option_items.mustache}}\n      {{else}}\n        {{> /static/mustache/selectors/object_selector_option_items.mustache}}\n      {{/if}}\n    </ul>\n    <div class="row-fluid centered clearfix">\n      <spinner size="large" toggle="page_loading"></spinner>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['components/assessment_templates/assessment_templates']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid">\n  <div class="span4">\n    <h6>Select assessment template</h6>\n    <dropdown options-list="templates" name="assessmentTemplate"></dropdown>\n  </div>\n</div>\n';
GGRC.Templates['components/dropdown/dropdown']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<select\n  class="{{firstnonempty className \'input-block-level\'}}"\n  can-value="{{name}}"\n  null-if-empty="null-if-empty"\n  can-change="onChange"\n  {{#isDisabled}}disabled="disabled"{{/isDisabled}}\n  >\n{{#options}}\n  {{#if title}}\n    <option value="{{value}}" label="{{title}}"></option>\n  {{/if}}\n  {{#if group}}\n    <optgroup label="{{group}}">\n    {{#subitems}}\n      <option value="{{value}}" label="{{title}}"></option>\n    {{/subitems}}\n    </optgroup>\n  {{/if}}\n{{/options}}\n</select>\n';
GGRC.Templates['components/object_history/object_history']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#revisions_diff instance}}\n  <paginate list="changeHistory" per-page="10">\n    <ul class="entry-list">\n      {{#entries}}\n        <li>\n          <span class="person-label {{lowercase role }}"></span>\n\n          <div class="w-status">\n            <span class="entry-author">\n              {{{render \'/static/mustache/people/popover.mustache\' person=madeBy}}\n              made changes &mdash; {{date updatedAt}}\n            </span>\n            <div class="clearfix">\n              <div class="third-col">\n                <p class="instruction">Field</p>\n              </div>\n              <div class="third-col">\n                <p class="instruction">Original value</p>\n              </div>\n              <div class="third-col">\n                <p class="instruction">New value</p>\n              </div>\n            </div>\n\n            {{#changes}}\n              <div class="clearfix">\n                <div class="third-col">\n                  {{fieldName}}\n                </div>\n                <div class="third-col">\n                  {{{origVal}}}\n                </div>\n                <div class="third-col">\n                  {{{newVal}}}\n                </div>\n              </div>\n            {{/changes}}\n\n          </div>\n        </li>\n      {{/entries}}\n\n      {{^list}}\n        {{#isLoading}}\n          <li class="spinner">\n            <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n          </li>\n        {{else}}\n          <li>The history of changes is currently empty.</li>\n        {{/isLoading}}\n\n      {{/list}}\n    </ul>\n  </paginate>\n{{/revisions_diff}}\n';
GGRC.Templates['components/person/person']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="person">\n  {{#if personObj}}\n    {{{renderLive \'/static/mustache/people/popover.mustache\' person=personObj}}}\n\n    {{#if editable}}\n    <a href="javascript://" class="unmap" title="Remove">\n      <i class="fa fa-trash"></i>\n    </a>\n    {{/if}}\n  {{else}}\n    <span class="empty-message">{{emptyText}}</span>\n  {{/if}}\n</div>\n';
GGRC.Templates['components/spinner/spinner']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#toggle}}\n<div class="spinner {{extraCssClass}}">\n  <i class="spinner-icon {{#size}}spinner-size__{{size}}{{/size}} fa fa-spinner fa-pulse" aria-hidden="true"></i>\n</div>\n{{/toggle}}\n';
GGRC.Templates['components/rich_text/rich_text']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="rich-text__wrapper">\n  <div class="rich-text__content"></div>\n  <div class="rich-text__toolbar">\n    <span class="ql-format-group">\n      <span title="Bold" class="ql-format-button ql-bold"></span>\n      <span class="ql-format-separator"></span>\n      <span title="Italic" class="ql-format-button ql-italic"></span>\n      <span class="ql-format-separator"></span>\n      <span title="Underline" class="ql-format-button ql-underline"></span>\n      <span class="ql-format-separator"></span>\n      <span title="Strikethrough" class="ql-format-button ql-strike"></span>\n      <span class="ql-format-separator"></span>\n      <span title="Link" class="ql-format-button ql-link"></span>\n    </span>\n    <span class="ql-format-group">\n      <span title="List" class="ql-format-button ql-list"></span>\n      <span class="ql-format-separator"></span>\n      <span title="Bullet" class="ql-format-button ql-bullet"></span>\n    </span>\n  </div>\n</div>\n';
GGRC.Templates['components/datepicker/datepicker']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="datepicker__wrapper">\n  {{#if label}}\n    <label>\n      {{label}}\n      {{#if helptext}}\n        <i class="fa fa-question-circle" rel="tooltip" title="{{helptext}}"></i>\n      {{/if}}\n      {{#if required}}\n        <span class="required">*</span>\n      {{/if}}\n    </label>\n  {{/if}}\n  <div class="datepicker__input-wrapper">\n    <i class="fa fa-calendar"></i>\n    <input\n      placeholder="{{pattern}}"\n      type="text"\n      class="datepicker__input date"\n      can-value="_date"\n      can-focus="onFocus"\n    >\n    <div\n      class="datepicker__calendar {{#showTop}}datepicker__calendar--top{{/showTop}}\n      {{#isHidden}}hide{{/isHidden}}"\n    ></div>\n  </div>\n</div>\n';
GGRC.Templates['components/object_counter/object_counter']= '{{!\n    Copyright (C) 2016 Google Inc., authors, and contributors\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<small class="count {{^count}}count-zero{{/count}}">\n  {{count}}\n</small>\n';
GGRC.Templates['components/tree_pagination/tree_pagination']= '<div class="tree-pagination">\n  <div class="tree-pagination__count">\n    <div class="dropdown">\n      <h6 class="dropdown-toggle tree-view-pagination__count__title"\n          data-toggle="dropdown">{{paginationInfo paging.current paging.pageSize paging.total}}</h6>\n      <div class="dropdown-menu">\n        <span>Items per page:</span>\n        <div class="btn-group">\n          {{#paging.pageSizeSelect}}\n            <button can-click="changePageSize" class="btn tree-view-pagination__count__button{{#if_equals paging.pageSize .}} active{{/if_equals}}"\n                      data-size="{{.}}">{{.}}</button>\n          {{/paging.pageSizeSelect}}\n        </div>\n      </div>\n    </div>\n  </div>\n  <ul class="pagination pull-right">\n    <li class="{{#if_equals paging.current 1}} disabled{{/if_equals}}">\n      <a href="javascript://" can-click="setFirstPage">\n        <i class="fa fa-angle-double-left" aria-hidden="true"></i>\n      </a>\n    </li>\n    <li class="{{#if_equals paging.current 1}} disabled{{/if_equals}}">\n      <a href="javascript://" can-click="setPrevPage">\n        <i class="fa fa-angle-left" aria-hidden="true"></i>\n      </a>\n    </li>\n    <li>\n      <input type="text" can-change="setCurrentPage" class="input"\n               placeholder="{{paginationPlaceholder paging.current paging.count}}" >\n    </li>\n    <li class="{{#if_equals paging.current paging.count}} disabled{{/if_equals}}">\n      <a href="javascript://" can-click="setNextPage">\n        <i class="fa fa-angle-right" aria-hidden="true"></i>\n      </a>\n    </li>\n    <li class="{{#if_equals paging.current paging.count}} disabled{{/if_equals}}">\n      <a href="javascript://" can-click="setLastPage">\n        <i class="fa fa-angle-double-right" aria-hidden="true"></i>\n      </a>\n    </li>\n  </ul>\n  <div class="clearfix"></div>\n</div>\n';
GGRC.Templates['components/simple-modal/simple-modal']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<div class="simple-modal {{extraCssClass}} {{modalCls}}">\n    <div class="modal-header">\n        <h4>{{modalTitle}}</h4>\n        <a class="modal-dismiss" can-click="hide">\n            <i class="fa fa-times black"></i>\n        </a>\n    </div>\n    <div class="modal-body">\n      {{#state.open}}\n          <content></content>\n      {{/state.open}}\n    </div>\n</div>\n<div class="simple-modal__overlay {{modalOverlayCls}}"></div>\n';
GGRC.Templates['custom_attributes/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#instance.class.is_custom_attributable}}\n  {{^if_instance_of instance \'Assessment\'}}\n  <custom-attributes instance="instance">\n    <div class="hideable-holder bare">\n      <div class="row-fluid">\n        {{#getValues}}\n          <div class="{{^cad.mandatory}}hidable{{/cad.mandatory}} span6\n            {{#ca_validation_error instance.computed_errors id}}field-failure{{/ca_validation_error}}">\n\n          {{#switch cad.attribute_type}}\n          {{#case \'Text\'}}\n            <label>\n              {{cad.title}}\n              {{#cad.mandatory}}<span class="required">*</span>{{/cad.mandatory}}\n              {{#cad.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{cad.helptext}}"></i>{{/cad.helptext}}\n              <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <input tabindex="{{add_index 20 @index}}" data-ram="{{@index}}" class="input-block-level" value="{{cav.attribute_value}}" placeholder="{{cad.placeholder}}" name="custom_attributes.{{cad.id}}" type="text">\n          {{/case}}\n          {{#case \'Rich Text\'}}\n            <label>\n              {{cad.title}}\n              {{#cad.mandatory}}<span class="required">*</span>{{/cad.mandatory}}\n              {{#cad.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{cad.helptext}}"></i>{{/cad.helptext}}\n              <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <div class="wysiwyg-area">\n              <textarea tabindex="{{add_index 20 @index}}" id="{{cad.title}}" class="span12 double wysihtml5" name="custom_attributes.{{cad.id}}" placeholder="{{cad.placeholder}}">{{{cav.attribute_value}}}</textarea>\n            </div>\n          {{/case}}\n          {{#case \'Dropdown\'}}\n            <label>\n              {{cad.title}}\n              {{#cad.mandatory}}<span class="required">*</span>{{/cad.mandatory}}\n              {{#cad.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{cad.helptext}}"></i>{{/cad.helptext}}\n              <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n            </label>\n            <select class="input-block-level" name="custom_attributes.{{cad.id}}" null-if-empty="null-if-empty" tabindex="20">\n              <option value="" {{#if_equals "" cav.attribute_value}}selected="true"{{/if_equals}}>---</option>\n              {{#iterate_string cad.multi_choice_options \',\'}}\n                <option {{#if_equals iterator cav.attribute_value}}selected="true"{{/if_equals}}>{{iterator}}</option>\n              {{/iterate}}\n            </select>\n          {{/case}}\n          {{#case \'Checkbox\'}}\n            <label>&nbsp;</label>\n            <div class="checkbox-area">\n              <label>\n                <input tabindex="{{add_index 20 @index}}" name="custom_attributes.{{cad.id}}" type="checkbox"  {{#if_equals cav.attribute_value "1"}}checked="checked"{{/if_equals}}>\n                {{cad.title}}\n                {{#cad.mandatory}}<span class="required">*</span>{{/cad.mandatory}}\n                {{#cad.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{cad.helptext}}"></i>{{/cad.helptext}}\n                <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n              </label>\n            </div>\n          {{/case}}\n          {{#case \'Date\'}}\n          <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          <datepicker\n            label="cad.title"\n            date="instance.custom_attributes.{{cad.id}}"\n            required="cad.mandatory"\n            helptext="cad.helptext"\n            />\n          {{/case}}\n          {{#case \'Map:Person\'}}\n          <label>\n            {{cad.title}}\n            {{#cad.mandatory}}<span class="required">*</span>{{/cad.mandatory}}\n            {{#cad.helptext}}<i class="fa fa-question-circle" rel="tooltip" title="{{cad.helptext}}"></i>{{/cad.helptext}}\n            <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n            {{#using person=cav.attribute_object}}\n            <input class="input-block-level"\n                   name="_custom_attribute_mappings.{{cad.id}}.email"\n                   data-lookup="Person" placeholder="Enter email address"\n                   data-lookup-cb="_custom_attribute_map {{cad.id}}"\n                   null-if-empty="null-if-empty"\n                   type="text"\n                   value="{{person.email}}" />\n            {{/using}}\n          {{/case}}\n          {{/switch}}\n\n          {{#ca_validation_error instance.computed_errors id}}\n            <label class="help-inline warning">{{errors.0}}</label>\n          {{/ca_validation_error}}\n        </div>\n        {{/getValues}}\n      </div>\n    </div>\n  </custom-attributes>\n  {{/if}}\n{{/instance.class.is_custom_attributable}}\n';
GGRC.Templates['custom_attributes/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<custom-attributes instance="instance">\n    {{#if instance.custom_attribute_definitions.length}}\n      {{#iterate_by_two items}}\n      <div class="row-fluid wrap-row">\n      {{#list}}\n        <div class="span6">\n          <div data-custom-attribute="{{id}}" data-type="{{attribute_type}}">\n            <inline-edit\n              instance="instance"\n              ca-id="cad.id"\n              {{#if cav.attribute_object}}\n              value="cav.attribute_object"\n              {{else}}\n              value="cav.attribute_value"\n              {{/if}}\n              values="cad.multi_choice_options"\n              title="cad.title"\n              label="cad.label"\n              type="{{type}}"\n              mandatory="cad.mandatory"\n              placeholder="cad.placeholder"\n              helptext="cad.helptext"\n              {{#if instance.snapshot}}\n                readonly\n              {{else}}\n                {{^is_allowed \'update\' instance context=\'for\'}}\n                  readonly\n                {{/is_allowed}}\n              {{/if}}\n              {{#if_instance_of instance \'Request\'}}\n                can-before-edit="instance.confirmBeginEdit"\n              {{/if_instance_of}}\n            ></inline-edit>\n          </div>\n        </div>\n      {{/list}}\n      </div>\n      {{/iterate_by_two}}\n    {{/if}}\n</custom-attributes>\n';
GGRC.Templates['custom_attribute_definitions/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n{{#instance}}\n<form action="javascript://">\n  <input type="hidden" name="definition_type" value="{{definition_type}}">\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>Attribute title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name."></i>\n      </label>\n      <input tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n    </div>\n    <div class="span4">\n      <label>\n        Attribute type\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Choose the type of attribute to create."></i>\n      </label>\n        <dropdown options-list="model.attributeTypes"\n                  name="instance.attribute_type"\n                  data-purpose="ca-type">\n        </dropdown>\n    </div>\n    <div class="span2">\n      <label>\n        Mandatory\n      </label>\n      <input tabindex="3" type="checkbox" class="input-block-level" placeholder="Enter Placeholder" name="mandatory" type="text"  {{#if mandatory}}checked="checked"{{/if}}>\n    </div>\n  </div>\n  {{#switch attribute_type}}\n  {{#case "Text"}}\n    <div class=\'row-fluid\'>\n      <div class="span6">\n        <label>\n          Inline help\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide text to help users understand what this attribute is for."></i>\n        </label>\n        <input tabindex="4" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n      </div>\n      <div class="span6">\n        <label>\n          Placeholder\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide text to show in the text field when the value is empty or not set. This should help users to understand what type of value to provide."></i>\n        </label>\n        <input tabindex="5" class="input-block-level" placeholder="Enter Placeholder" name="placeholder" type="text" value="{{placeholder}}">\n      </div>\n    </div>\n  {{/case}}\n  {{#case "Rich Text"}}\n    <div class=\'row-fluid\'>\n      <div class="span6">\n        <label>\n          Inline help\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide text to help users understand what this attribute is for."></i>\n        </label>\n        <input tabindex="4" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n      </div>\n      <div class="span6">\n        <label>\n          Placeholder\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide text to show in the text field when the value is empty or not set. This should help users to understand what type of value to provide."></i>\n        </label>\n        <input tabindex="5" class="input-block-level" placeholder="Enter Placeholder" name="placeholder" type="text" value="{{placeholder}}">\n      </div>\n    </div>\n  {{/case}}\n\n  {{#case "Dropdown"}}\n    <div class=\'row-fluid\'>\n      <div\n        class="span12 {{#if instance.computed_errors.multi_choice_options}}field-failure{{/if}}">\n        <label>\n          Possible values (separated by comma ",")\n          <span class="required">*</span>\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide a list of values from which users will be able to choose."></i>\n        </label>\n\n        {{#instance.computed_errors.multi_choice_options}}\n           <label class="help-inline warning">\n               {{this}}\n           </label>\n         {{/instance.computed_errors.multi_choice_options}}\n\n        <input\n          tabindex="4"\n          class="input-block-level"\n          placeholder="Enter possible values"\n          name="multi_choice_options"\n          type="text"\n          value="{{multi_choice_options}}">\n      </div>\n    </div>\n    <div class=\'row-fluid\'>\n      <div class="span6">\n        <label>\n          Inline help\n          <i class="fa fa-question-circle" rel="tooltip"\n            title="Provide text to help users understand what this attribute is for."></i>\n        </label>\n        <input tabindex="5" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n      </div>\n    </div>\n  {{/case}}\n\n  {{#case "Checkbox"}}\n  <div class=\'row-fluid\'>\n    <div class="span6">\n      <label>\n        Inline help\n        <i class="fa fa-question-circle" rel="tooltip"\n        title="Provide text to help users understand what this attribute is for."></i>\n      </label>\n      <input tabindex="4" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n    </div>\n  </div>\n  {{/case}}\n  {{#case "Date"}}\n  <div class=\'row-fluid\'>\n    <div class="span6">\n      <label>\n        Inline help\n        <i class="fa fa-question-circle" rel="tooltip"\n        title="Provide text to help users understand what this attribute is for."></i>\n      </label>\n      <input tabindex="5" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n    </div>\n  </div>\n  {{/case}}\n  {{#case "Map:Person"}}\n  <div class=\'row-fluid\'>\n    <div class="span6">\n      <label>\n        Inline help\n        <i class="fa fa-question-circle" rel="tooltip"\n        title="Provide text to help users understand what this attribute is for."></i>\n      </label>\n      <input tabindex="5" class="input-block-level" placeholder="Enter Help Text" name="helptext" type="text" value="{{helptext}}">\n    </div>\n  </div>\n  {{/case}}\n  {{#case "File upload"}}\n    <div class=\'row-fluid\'>\n      File upload\n    </div>\n  {{/case}}\n  {{/switch}}\n</form>\n{{/instance}}\n';
GGRC.Templates['custom_attribute_definitions/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<li class="tree-item" data-model="true">\n  <div class="item-main" {{data \'model\'}}>\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select disabled">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span12">\n              <div class="item-data">\n                <div>\n                  <i class="fa fa-{{instance.root_object}} color"></i>\n                  {{instance.title_plural}}\n                </div>\n              </div>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      <div class="table-wrap">\n        <table class="table">\n          <colgroup>\n            <col style="width: 50%"/>\n            <col style="width: 25%"/>\n            <col style="width: 15%"/>\n            <col style="width: 10%"/>\n          </colgroup>\n          <thead>\n            <tr>\n              <th>Attribute name</th>\n              <th>Attribute type</th>\n              <th>Mandatory</th>\n              <th>Edit</th>\n            </tr>\n          </thead>\n          {{#child_options.0}}\n          <tbody class="tree-structure new-tree" {{data \'options\'}}\n            {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}>\n          </tbody>\n          {{/child_options.0}}\n          <!-- Custom attribute definitions here -->\n          <tfoot class="add-item">\n            <tr>\n              <td colspan="4" class="tree-item tree-item-add">\n                <div class="row-fluid">\n                  <div class="span12">\n                    <a\n                      href="javascript://"\n                      data-toggle="modal-ajax-form"\n                      class="btn btn-small btn-draft"\n                      rel="tooltip"\n                      data-placement="right"\n                      data-object-title="{{instance.title_singular}}"\n                      data-object-singular="CustomAttributeDefinition"\n                      data-object-plural="custom_attribute_definitions"\n                      data-object-params=\'{ "definition_type": "{{instance.table_singular}}", "modal_title": "Add Attribute to type {{instance.title_singular}}" }\'\n                      data-original-title="Add Attribute">\n                      <i class="fa fa-plus-circle"\n                         data-test-id="button_add-86eaf948">\n                      </i>\n                    </a>\n                  </div>\n                </div>\n              </td>\n            </tr>\n          </tfoot>\n        </table>\n      </div>\n    </div>\n  </div>\n</li>\n';
GGRC.Templates['custom_attribute_definitions/subtree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<tr\n  data-object-id="{{instance.id}}"\n  data-object-type="{{instance.constructor.table_singular}}"\n>\n  {{#instance}}\n  <td>{{title}}</td>\n  <td>{{attribute_type}}</td>\n  <td>{{#if mandatory}}Yes{{else}}No{{/mandatory}}</td>\n  <td>\n    <ul class="tree-action-list">\n      <li>\n        <a href="javascript://" data-toggle="modal-ajax-form" data-modal-reset="reset" data-object-singular="{{class.model_singular}}" data-object-plural="{{class.table_plural}}" data-object-id="{{id}}">\n          <i class="fa fa-pencil-square-o"></i>\n          Edit\n        </a>\n      </li>\n    </ul>\n  </td>\n  {{/instance}}\n</tr>\n';
GGRC.Templates['custom_attribute_definitions/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<header class="header tree-header no-filter">\n  <div class="row-fluid">\n    <div class="span{{display_options.title_width}}">\n      <div class="title-heading oneline">\n          Object Type\n      </div>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n';
GGRC.Templates['dashboard/internav_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#widget_list}}\n  <li>\n    {{#if_helpers \'\\n      #if\' has_count \'\\n      and #if\' count \'\\n      or ^if\' has_count \'\\n      or #if\' force_show \'\\n      or #if\' instance.constructor.obj_nav_options.show_all_tabs \'\\n      or #in_array\' internav_display instance.constructor.obj_nav_options.force_show_list}}\n\n    <a href="{{urlPath}}{{ selector }}" rel="tooltip" data-placement="bottom" title="{{ internav_display }}">\n      <div class="oneline">\n        <i class="fa fa-{{ internav_icon }} color"></i>\n        {{#if show_title}}\n          {{internav_display}}\n          {{#has_count}}({{count}}{{#spinner}}...{{/spinner}}){{/has_count}}\n        {{else}}\n          {{#has_count}}{{count}}{{#spinner}}...{{/spinner}}{{/has_count}}\n        {{/if}}\n        {{^count}}{{#has_count}}\n          {{^instance.constructor.obj_nav_options.show_all_tabs}}\n            {{^in_array internav_display instance.constructor.obj_nav_options.force_show_list}}\n              <span class="closed"><i class="fa fa-times"></i></span>\n            {{/in_array}}\n          {{/instance.constructor.obj_nav_options.show_all_tabs}}\n        {{/has_count}}{{/count}}\n      </div>\n    </a>\n    {{/if_helpers}}\n  </li>\n{{/widget_list}}\n\n{{#is_allowed \'update\' instance}}\n{{#if widget_list.length}}\n<li data-test-id="button_widget_add_2c925d94" class="hidden-widgets-list">\n  <a href="{{urlPath}}#dropdown-widgets" class="dropdown-toggle" data-toggle="dropdown" rel="tooltip" data-placement="bottom" title="Add Tab"><i class="fa fa-plus-circle"></i> Add Tab</a>\n  <div class="dropdown-menu" role="menu">\n    {{#widget_list}}\n      <div class="inner-nav-item">\n        {{^if_helpers \'\\n          #if\' has_count \'\\n          and #if\' count \'\\n          or ^if\' has_count \'\\n          or #if\' force_show \'\\n          or #if\' instance.constructor.obj_nav_options.show_all_tabs \'\\n          or #in_array\' internav_display instance.constructor.obj_nav_options.force_show_list}}\n\n          {{#if_helpers \'\\n            #if_page_type\' \'programs\' \'\\n            and #if_equals\' model.shortName \'Audit\'}}\n            <a\n              href="{{urlPath}}{{selector}}"\n              rel="tooltip"\n              data-placement="left"\n              data-original-title="Create {{model.title_singular}}"\n              data-toggle="modal-ajax-form"\n              data-modal-reset="reset"\n              data-no-href-load="true"\n              data-modal-class="modal-wide"\n              data-object-singular="{{model.title_singular}}"\n              data-object-plural="{{model.table_plural}}"\n              data-object-params=\'{\n                  "{{instance.class.table_singular}}": {\n                      "id": {{instance.id}},\n                      "title": "{{instance.title}}"\n                  },\n                  "context": {\n                      "id": {{firstnonempty instance.context.id "null"}},\n                      "href": "{{instance.context.href}}",\n                      "type": "{{instance.context.type}}"\n                  }\n              }\'\n            >\n              <div class="oneline">\n                <i class="fa fa-{{ internav_icon }} color"></i>\n                {{internav_display}}\n              </div>\n            </a>\n          {{else}}\n            <a\n              href="{{urlPath}}{{ selector }}"\n              {{^is_dashboard_or_all}}\n              {{#is_mappable_type instance.type model.shortName}}\n              {{#is_allowed_to_map instance model.shortName}}\n                data-clickable="true"\n                data-toggle="unified-mapper"\n                data-join-option-type="{{model.shortName}}"\n                data-join-object-id="{{instance.id}}"\n                data-join-object-type="{{instance.class.shortName}}"\n              {{/is_allowed_to_map}}\n              {{/is_mappable_type}}\n              {{/is_dashboard_or_all}}\n            >\n              <div class="oneline">\n                <i class="fa fa-{{ internav_icon }} color"></i>\n                {{internav_display}}\n              </div>\n            </a>\n          {{/if_helpers}}\n\n        {{/if_helpers}}\n      </div>\n    {{/widget_list}}\n  </div>\n</li>\n{{/if}}\n{{/is_allowed}}\n\n{{{render_hooks \'ObjectNav.Actions\'}}}\n';
