GGRC.Templates['documents/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n\n{{#instance}}\n<form action="javascript://">\n  <div class="row-fluid">\n    <div class="span12">\n      <label>\n        URL\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Enter Title"></i>\n      </label>\n      <input tabindex="1" id="document_link" class="input-block-level" placeholder="https://www.example.com/" name="link" type="text" value="{{link}}" autofocus>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span9">\n      <label>\n        Title\n        <i class="fa fa-question-circle" rel="tooltip" title="Enter Title"></i>\n      </label>\n      <input tabindex="2" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}">\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span9">\n      <label>\n        Preview\n      </label>\n      {{{renderLive \'/static/mustache/pbc/iframe_tooltip.mustache\' url=instance.link}}}\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span12">\n      <div class="expand-link">\n        <a class="show-hidden-fields" href="javascript://">\n          <span class="out">\n            <i class="fa fa-caret-right"></i>\n            Show\n          </span>\n          <span class="in">\n            <i class="fa fa-caret-down"></i>\n            Hide\n          </span>\n          Advanced\n          <span class="hidden-fields out">\n            Description\n          </span>\n        </a>\n      </div>\n    </div>\n  </div>\n  <div class="hidden-fields-area" style="display:none">\n    <div class="row-fluid">\n      <div class="span12">\n        <label>\n          Description\n          <i class="fa fa-question-circle" rel="tooltip" title="Enter Description"></i>\n        </label>\n        <div class="wysiwyg-area">\n          <textarea tabindex="3" id="link-description" class="span12 double wysihtml5" name="description" placeholder="Enter description">{{{description}}}</textarea>\n        </div>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n{{!/div}}\n\n\n{{!<div class="span2">\n  <label for="document_year_id">Year</label>\n  <select class="span12" id="document_year_id" name="year_id">\n    <option value=""></option>\n  </select>\n  <span class="help-inline"></span>\n</div>\n<div class="span2">\n  <label for="document_language_id">Language</label>\n  <select class="span12" id="document_language_id" name="language_id">\n    <option value=""></option>\n  </select>\n  <span class="help-inline"></span>\n</div>\n<div class="span2">\n  <label for="document_kind_id">Reference Kind</label>\n  <select class="span12 " id="document_kind_id" name="kind_id">\n    <option value=""></option>\n  </select>\n  <span class="help-inline"></span>\n</div>\n<div class="span2">\n  <label for="document_type_id">DocType</label>\n  <select class="span12 " id="document_type_id" name="type_id">\n    <option value=""></option>\n  </select>\n  <span class="help-inline"></span>\n</div>}}\n';
GGRC.Templates['documents/option_column']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="search-title">\n  <div class="row-fluid">\n    <div class="span6">\n      <h4>\n        Search Results ({{ options.length }})\n      </h4>\n    </div>\n    <div class="span6">\n      <div class="guide">\n        &nbsp;\n      </div>\n    </div>\n  </div>\n</div>\n<div class="selector-list">\n  <ul class="new-tree">\n    {{#options}}\n      <li class="tree-item business" {{ data \'option\' }} data-id="{{ id }}">\n        <div class="item-main">\n          <div class="simple-link">\n            <div class="row-fluid">\n              <div class="span11">\n                <div class="item-data">\n                  <div class="tree-title-area">\n                    {{ firstnonempty title link }}\n                    {{#if title}}\n                    <span class="url-link">\n                      {{link}}\n                    </span>\n                    {{/if}}\n                  </div>\n                </div>\n              </div>\n              <div class="span1">\n                <input type="checkbox" rel="tooltip" title="map">\n              </div>\n            </div>\n          </div>\n        </div>\n      </li>\n    {{/options}}\n  </ul>\n</div>\n';
GGRC.Templates['documents/option_detail']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="selector-info">\n  <h4>\n    <i class="fa fa-link"></i>\n    {{firstnonempty title link }}\n  </h4>\n  {{#if description}}\n    <div class="rtf">\n      {{{description}}}\n    </div>\n  {{/if}}\n  <h4 class="simple-url">\n    <a href="{{ link }}" target="_blank">\n      {{ link }}\n    </a>\n  </h4>\n</div>\n';
GGRC.Templates['documents/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n  <li class="tree-item {{#if instance.snapshot}}snapshot{{/if}}" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-wrap">\n        {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span4">\n                <div class="title tree-title-area" title="{{instance.title}}">\n                  {{#is_subtree}}\n                    <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                  {{/is_subtree}}\n                  {{firstnonempty instance.title instance.link}}\n                </div>\n              </div>\n              {{#instance}}\n                <div class="span2">\n                  <div class="owner tree-title-area">\n                    {{#if owners.length}}\n                      {{#using contacts=owners}}\n                      {{#contacts}}\n                        {{{renderLive \'/static/mustache/people/popover.mustache\' person=this}}}\n                      {{/contacts}}\n                      {{/using}}\n                    {{/if}}\n                  </div>\n                </div>\n\n                <div class="span1">\n                  <div class="code tree-title-area">\n                    {{slug}}\n                  </div>\n                </div>\n\n                <div class="span1">\n                  <div class="state-value tree-title-area">\n                    {{status}}\n                  </div>\n                </div>\n              {{/instance}}\n              <div class="span4">\n                <div class="show-details">\n                  {{#child_options}}\n                    {{#if add_item_view}}\n                      {{{renderLive add_item_view}}}\n                    {{/if}}\n                  {{/child_options}}\n                  <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                </div>\n                <ul class="tree-action-list">\n                  {{#infer_roles instance}}\n                    <li>\n                      <i class="fa fa-person" rel="tooltip" data-placement="left" data-original-title="{{.}} "></i>\n                    </li>\n                  {{/infer_roles}}\n                </ul>\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n    {{#if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#if draw_children}}\n          {{#if child_options.length}}\n            {{#prune_context}} {{! this line is just chopping the context stack down to one element}}\n              <div class="inner-tree">\n                {{#child_options}}\n                  <ul class="tree-structure new-tree colored-list" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n                {{/child_options}}\n              </div>\n            {{/prune_context}}\n          {{/if}}\n        {{/if}}\n      </div>\n    </div>\n    {{/if}}\n  </li>\n';
GGRC.Templates['dashboard/modal_preload']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="modal-header">\n  <a class="modal-dismiss pull-right" href="javascript://" data-dismiss="modal">\n    <i class="fa fa-times black"></i>\n  </a>\n  <h2>Loading...</h2>\n</div>\n<div class="modal-body" style="padding-top:150px;"></div>\n<div class="modal-footer">\n</div>\n';
GGRC.Templates['dashboard/object_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{! dashboard object listing}}\n<ul class="tree-structure new-tree">\n{{#list}}\n  <li class="tree-item {{class.table_singular}}" data-model="true">\n    <div class="item-main" {{data \'model\'}}>\n      <div class="openclose">\n        <a class="openclose-placeholder" href="javascript://"> </a>\n        <div class="row-fluid">\n          <div class="span10">\n            <div class="item-data">\n              <div class="tree-title-area">\n                {{title}}\n              </div>\n            </div>\n          </div>\n          <div class="span1">\n            <div class="counter">\n              {{^contact}}\n                <i class="fa fa-person red"></i>\n              {{/contact}}\n            </div>\n          </div>\n        </div>\n      </div>\n    </div>\n\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n\n        <div class="row-fluid">\n          <div class="span6">\n            <a href="{{viewLink}}" class="info-action">\n              <i class="fa fa-long-arrow-right"></i>\n              Open {{class.title_singular}}\n            </a>\n          </div>\n        </div>\n\n        {{#description}}\n        <div class="show-description">\n          <div class="row-fluid">\n            <div class="span12">\n              <div class="tree-description short">\n                {{{description}}}\n              </div>\n              {{{show_long}}}\n            </div>\n          </div>\n        </div>\n        {{/description}}\n\n        {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=this}}}\n        {{#url}}\n        <div class="row-fluid">\n          <div class="span6">\n            <div class="tree-info">\n              <strong>URL:</strong>\n                <a class="url" href="{{schemed_url url}}" target="_blank">\n                  {{url}}\n                </a>\n            </div>\n          </div>\n        </div>\n        {{/url}}\n      </div>\n    </div>\n  </li>\n{{/list}}\n</ul>\n{{!#is_allowed_to_map parent_instance.class.shortName model.title_singular}}\n{{#is_allowed_to_map parent_instance.stub.type model.title_singular}}\n  {{#is_related}}\n    <ul class="new-tree">\n      <li class="tree-item tree-item-add">\n      <a\n        href="javascript://"\n        rel="tooltip"\n        data-placement="left"\n        data-toggle="unified-mapper"\n        data-modal-class="modal-wide"\n        data-modal-reset="reset"\n        data-original-title="Map {{model.title_singular}} to this {{parent_instance.class.shortName}}"\n        data-related-model="{{model.model_singular}}"\n        data-related-title-singular="{{model.title_singular}}"\n        data-related-title-plural="{{model.title_plural}}"\n        data-related-side="source" {{! FIXME This should depend on relationship type }}\n        data-related-table-plural="{{model.table_plural}}"\n        data-related-model-singular="{{model.model_singular}}"\n        data-relationship-type="" {{! FIXME This should be a real relationship type }}\n        data-tab-target="#related-objects-{{find_params.relationship_type_id}}">\n        + {{model.title_singular}} {{find_params.relationship_type_id}}\n      </a>\n      </li>\n    </ul>\n  {{/is_related}}\n{{/is_allowed_to_map}}\n{{#is_allowed \'create\' model.shortName context=null}}\n  {{^is_related}}\n    <ul class="new-tree">\n      <li class="tree-item tree-item-add">\n      <a\n        href="javascript://"\n        rel="tooltip"\n        data-placement="left"\n        data-toggle="modal-ajax-form"\n        data-object-plural="{{model.model_plural.toLowerCase}}"\n        data-object-singular="{{model.model_singular}}"\n        data-modal-class="modal-wide"\n        data-modal-reset="reset"\n        data-original-title="Create New {{model.title_singular.toLowerCase}}">\n        + {{model.title_singular}}\n      </a>\n      </li>\n    </ul>\n  {{/is_related}}\n{{/is_allowed}}\n';
GGRC.Templates['dashboard/object_widget']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!section class="widget... }}\n  {{#if_equals widget_id "roles_list"}}\n    <header class="header tree-header no-left-space {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n      <div class="row-fluid">\n        <div class="span6">\n          <div class="title-heading oneline">\n            Role\n          </div>\n        </div>\n        <div class="span6">\n          <div class="title-heading oneline right">\n            Role Scope\n          </div>\n        </div>\n      </div>\n    </header>\n  {{/if_equals}}\n\n  {{#if_equals widget_id "custom_attribute"}}\n  {{^is_dashboard}}\n    <header class="header">\n      <h2>\n        <i class="fa fa-workflow color"></i>\n        {{{widget_name}}}\n      </h2>\n    </header>\n  {{/is_dashboard}}\n  {{/if_equals}}\n\n  {{#if_equals widget_id "events_list"}}\n  {{^is_dashboard}}\n    <header class="header">\n      <h2>\n        <i class="fa-history"></i>\n        {{{widget_name}}}\n      </h2>\n    </header>\n  {{/is_dashboard}}\n  {{/if_equals}}\n\n  {{#if_equals widget_id "info"}}\n  {{^is_dashboard}}\n    <header class="header">\n      <h2>\n        <i class="{{widget_icon}}"></i>\n        {{{widget_name}}}\n      </h2>\n    </header>\n  {{/is_dashboard}}\n  {{/if_equals}}\n\n  <section class="content">\n    {{{widget_initial_content}}}\n\n    {{#add_to_current_scope showSpinner=true}}\n      <spinner\n        size="large"\n        extra-css-class="initial-spinner"\n        toggle="showSpinner"\n        ></spinner>\n    {{/add_to_current_scope}}\n  </section>\n{{!/section}}\n';
GGRC.Templates['dashboard/recently_viewed_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#list}}\n<li class="{{model.category.toLowerCase}}" data-model="true" {{data \'model\'}}>\n  <a href="{{viewLink}}" class="show-extended">\n    <div class="row-fluid">\n      <div class="span11">\n        <div class="oneline">\n          <i class="fa fa-{{model.table_singular}} color"></i>\n          {{firstnonempty title name email}}\n        </div>\n      </div>\n    </div>\n  </a>\n\n</li>\n{{/list}}\n';
GGRC.Templates['directives/autocomplete_result']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each items}}\n<li {{data \'ui-autocomplete-item\'}} class="ui-menu-item">\n  <a\n    href="javascript://" class=\'show-extended\' {{data \'model\'}}\n    {{#if_match type \'Assessment|Request\'}}\n      data-tooltip-view="/base_templates/assignable_extended_info.mustache"\n    {{/if}}\n  >\n    <i class="fa fa-{{class.table_singular}} color"></i>\n    {{firstnonempty title name email}}\n  </a>\n</li>\n{{/each}}\n{{^items}}\n  <li class="ui-menu-item" data-ui-autocomplete-item="">\n    No results\n  </li>\n{{/items}}\n{{#if items_loading}}\n  <li class="spinner ui-menu-item" data-ui-autocomplete-item="">\n    <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n  </li>\n{{/if}}\n';
GGRC.Templates['directives/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <li class="tree-item {{#if instance.snapshot}}snapshot{{/if}}" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}" {{addclass "t-" instance.workflow_state}}>\n    <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-wrap">\n        {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span{{display_options.title_width}}">\n                <div class="title tree-title-area" title="{{instance.title}}">\n                  {{#is_subtree}}\n                    <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                  {{/is_subtree}}\n                  {{instance.title}}\n                </div>\n              </div>\n\n              <div class="span{{display_options.selectable_width}}">\n                <div class="row-fluid">\n                  {{#display_attr_list}}\n                    <div class="span{{display_options.selectable_attr_width}}">\n                      {{#if_equals attr_type \'custom\'}}\n                        <div class="custom tree-title-area">\n                          {{#get_custom_attr_value this instance}}\n                            {{! because the object can currently only be a\n                                person there is no need to switch }}\n                            {{#using person=object}}\n                              {{>\'/static/mustache/people/popover.mustache\'}}\n                            {{/using}}\n                          {{/get_custom_attr_value}}\n                        </div>\n                      {{else}}\n                        <div class="owner tree-title-area">\n                          {{#instance}}\n                            {{#switch attr_name}}\n                              {{#case \'owner\'}}\n                                {{#if owners.length}}\n                                  {{#using contacts=owners}}\n                                  {{#contacts}}\n                                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=this}}}\n                                  {{/contacts}}\n                                  {{/using}}\n                                {{/if}}\n                              {{/case}}\n                              {{#case \'contact\'}}\n                                <span>\n                                  {{#using contact=instance.contact}}\n                                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n                                  {{/using}}\n                                </span>\n                              {{/case}}\n                              {{#case \'secondary_contact\'}}\n                                <span>\n                                  {{#using contact=instance.secondary_contact}}\n                                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n                                  {{/using}}\n                                </span>\n                              {{/case}}\n\n                              {{#default}}\n                                {{#if_helpers \'\\n                                  #if_equals\' attr_name \'url\' \'\\n                                  or #if_equals\' attr_name \'reference_url\'}}\n                                    <a class="url" href="{{get_url_value attr_name instance}}" target="_blank">\n                                      {{get_default_attr_value attr_name instance}}\n                                    </a>\n                                {{else}}\n                                  {{get_default_attr_value attr_name instance}}\n                                {{/if_helpers}}\n                              {{/default}}\n                            {{/switch}}\n                          {{/instance}}\n                        </div>\n                      {{/if_equals}}\n                    </div>\n                  {{/display_attr_list}}\n                </div>\n              </div>\n\n              <div class="span{{display_options.action_width}}">\n\n                <div class="show-details">\n                  {{#child_options}}\n                    {{#if add_item_view}}\n                      {{{renderLive add_item_view}}}\n                    {{/if}}\n                  {{/child_options}}\n                  <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                </div>\n                <ul class="tree-action-list">\n                  {{#infer_roles instance}}\n                    <li>\n                      <i class="fa fa-person" rel="tooltip" data-placement="left" data-original-title="{{.}} "></i>\n                    </li>\n                  {{/infer_roles}}\n                  <li>\n                    {{> \'/static/mustache/base_objects/states.mustache\'}}\n                  </li>\n                  {{#if_instance_of instance \'Contract\'}}\n                    <li>\n                      <div class="counter">\n                        <i class="fa fa-paragraph"></i>\n                        {{#with_mapping_count instance \'clauses\'}}\n                          {{count}}\n                        {{/with_mapping_count}}\n                      </div>\n                    </li>\n                  {{else}}\n                    <li>\n                      <div class="counter">\n                        <i class="fa fa-paragraph"></i>\n                        {{#with_mapping_count instance \'sections\'}}\n                          {{count}}\n                        {{/with_mapping_count}}\n                      </div>\n                    </li>\n                  {{/if_instance_of}}\n                </ul>\n\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n    {{#if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#if draw_children}}\n          {{#prune_context}} {{! this line is just chopping the context stack down to one element}}\n            {{#child_options}}\n              <div class="inner-tree">\n                  <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n              </div>\n            {{/child_options}}\n          {{/prune_context}}\n        {{/if}}\n      </div>\n    </div>\n    {{/if}}\n  </li>\n';
GGRC.Templates['events/object_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="tree-structure new-tree event-tree">\n  {{#each list}}\n    <li class="tree-item {{resource_type.toLowerCase}}" data-model="true" {{data \'model\'}}>\n      <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n        <div class="item-wrap">\n          <div class="row-fluid">\n            <div class="span12">\n              <div class="item-data">\n                <div class="tree-title-area">\n                  <ul>\n                    {{#revisions}}\n                    {{^if_match resource_type \'Context|RoleImplication\'}}\n                    <li>\n                      <strong>\n                        {{firstnonempty description display_name}}\n                      </strong>\n                    </li>\n                    {{/if_equals}}\n                    {{/revisions}}\n                    <li>\n                      <span class="event-owner">\n                        by\n                        {{#using modified_by=modified_by}}\n                          {{{render \'/static/mustache/people/popover.mustache\' person=modified_by}}}\n                        {{/using}}\n                      </span>\n                    </li>\n                    <li>\n                      <span class="event-time">\n                        on {{date created_at}}\n                      </span>\n                    </li>\n                  </ul>\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n    </li>\n  {{/each}}\n  {{#if list.length}}\n  <li class="tree-item">\n    {{#if has_prev_page}}\n    <a href="javascript://" class="view-more-paging">\n      Previous Page\n    </a>\n    {{/if}}\n\n    {{#if has_next_page}}\n    <a href="javascript://" class="pull-right view-more-paging" data-next="true">\n      Next Page\n    </a>\n    {{/if}}\n  </li>\n  {{/if}}\n</ul>\n';
GGRC.Templates['facilities/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            Facility URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="6" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="7" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div>\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="FACILITY-XXX" type="text" value="{{slug}}">\n      </div>\n      <effective-dates instance="instance"/>\n    </div>\n    <div class="row-fluid">\n      <div data-id="state_hidden" class="span4 hidable">\n        <label>\n          State\n          <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n          <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n          <dropdown options-list="model.statuses"\n                    name="instance.status"\n                    tabindex="23">\n          </dropdown>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['help/help_modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!<div class="modal-body">}}\n  <div class="help">\n  <div class="help-content rtf">\n      {{{firstnonempty instance.content \'<p>This content is coming soon.  We are working on it.</p>\'}}}\n  </div>\n\n\n  {{#allow_help_edit}}\n    <div id="helpedit" class="collapse {{#edit_btn_active}}in{{/edit_btn_active}}">\n      {{#instance}}\n      <div class="row-fluid">\n        <div class="well">     \n          <label for="help_help_title">Help title</label>\n          <input tabindex="1" class="span12" id="help_title" name="title" size="30" type="text" value="{{title}}" autofocus>\n          <label for="help_help_content">Help content</label>\n          <div class="wysiwyg-area">\n            <textarea tabindex="2" class="span12 double wysihtml5 rtftextarea" id="help_content" name="content" rows="8">{{{content}}}</textarea>\n          </div>\n          <div class="row-fluid">\n            <div class="span12">\n              <input tabindex="3" class="btn btn-warning pull-right" name="commit" type="submit" value="Save">\n            </div>\n          </div>\n          <input id="help_slug" name="slug" type="hidden" value="{{slug}}">\n\n        </div>\n      </div>\n      {{/instance}}\n    </div>\n  {{/allow_help_edit}}\n  </div> \n{{!</div>}}\n';
GGRC.Templates['help/help_modal_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-header"}}\n  {{#allow_help_edit}}\n    <a class="btn btn-mini pull-right help-edit" data-toggle=\'collapse\' href=\'#helpedit\' title="Edit Help" rel="tooltip" data-placement="left">\n      <i class="fa fa-pencil-square-o"></i>\n    </a>\n  {{/allow_help_edit}}\n  {{#instance.content}}\n    <span>{{instance.title}}</span>\n    {{^instance.title}}\n      <span>\n      {{slug}}\n      Help\n      </span>\n    {{/instance.title}}\n  {{/instance.content}}\n  {{^instance.content}}\n    {{#allow_help_edit}}\n      <a class="btn btn-mini pull-right help-edit" data-toggle=\'collapse\' href=\'#helpedit\' title="Edit Help" rel="tooltip" data-placement="left">\n        <i class="fa fa-pencil-square-o"></i>\n      </a>\n    {{/allow_help_edit}}\n    GRC Help\n  {{/instance.content}}\n{{!/div}}\n';
GGRC.Templates['help/filters_helper_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<header class="popup__title">\n  Filter Help\n</header>\n<div class="popup__content">\n  <div class="content-wrap">\n    <h3>Basic filter</h3>\n    <p>Type any word or phrase in the text search and you’ll get results based on conducted search on all visible object fields. For example:</p>\n    <p>\n      <span class="code">\n        login access<br>\n        john doe\n      </span>\n    </p>\n    <p>\n      you will get all objects that contain this phrase in all visible fields (and <em>code, description, notes,</em> ...) Indicator on the right of search box will remain grey if you are in <em>basic search</em> mode. If it turns green, that means you’ve created a valid advanced filter expression.\n    </p>\n    <h3>Advanced filter functions</h3>\n    <p>You can construct more thorough filter expressions by using some special keywords and tree column titles</p>\n    <table class="table table-bordered">\n      <col style="width: 12%;">\n      <col style="width: 16%;">\n      <col style="width: 36%;">\n      <col style="width: 36%;">\n      <thead>\n        <tr>\n          <th>Keyword</th>\n          <th>Meaning</th>\n          <th>Examples</th>\n          <th>Filter results</th>\n        </tr>\n      </thead>\n      <tbody>\n        <tr>\n          <td><span class="code">~</span></td>\n          <td><strong>contains</strong></td>\n          <td><span class="code">~ “login access”</span></td>\n          <td>All objects containing phrase <em>login access</em> anywhere in object attributes</td>\n        </tr>\n        <tr>\n          <td><span class="code"></span></td>\n          <td><strong></strong></td>\n          <td><span class="code">~ something</span></td>\n          <td>All objects containing word something</td>\n        </tr>\n        <tr>\n          <td><span class="code"></span></td>\n          <td><strong></strong></td>\n          <td><span class="code">owner ~ john</span></td>\n          <td>All objects that have <em>john</em> as part of the owner name</td>\n        </tr>\n        <tr>\n          <td><span class="code">=</span></td>\n          <td><strong>equals</strong></td>\n          <td><span class="code">owner = “john doe”</span></td>\n          <td>All objects that have <em>john doe</em> as an owner</td>\n        </tr>\n        <tr>\n          <td><span class="code">!~</span></td>\n          <td><strong>does not contain</strong></td>\n          <td><span class="code">title !~ PCI</span></td>\n          <td>All objects that do not contain <em>PCI</em> in their title</td>\n        </tr>\n        <tr>\n          <td><span class="code">!=</span></td>\n          <td><strong>is not equal</strong></td>\n          <td><span class="code">state !~ draft</span></td>\n          <td>All objects that are in different state than <em>draft</em></td>\n        </tr>\n        <tr>\n          <td><span class="code">&lt;, &gt;</span></td>\n          <td><strong>lesser than greater than</strong></td>\n          <td><span class="code">“modified date” &lt; 03/03/2015</span></td>\n          <td>All objects last time modified before March 3rd, 2015</td>\n        </tr>\n        <tr>\n          <td><span class="code">AND</span></td>\n          <td><strong>logical AND</strong></td>\n          <td><span class="code">title ~ PCI AND owner = “john doe”</span></td>\n          <td>All objects that contain word <em>PCI</em> in title and are owned by <em>john doe</em></td>\n        </tr>\n        <tr>\n          <td><span class="code">OR</span></td>\n          <td><strong>logical OR</strong></td>\n          <td><span class="code">code ~ 111 OR “modified date &gt; 03/03/2015</span></td>\n          <td>All objects that have <em>111</em> in code or are modified after March 3rd, 2015</td>\n        </tr>\n        <tr>\n          <td><span class="code">ORDER BY ASC/DESC</span></td>\n          <td><strong>sort by</strong></td>\n          <td><span class="code">title ~ PCI order by owner asc</span></td>\n          <td>All objects that contain <em>PCI</em> in the title, in ascending order by owner</td>\n        </tr>\n        <tr>\n          <td><span class="code"></span></td>\n          <td><strong></strong></td>\n          <td><span class="code">title ~ PCI order by title desc</span></td>\n          <td>All objects that contain <em>PCI</em> in the title in descending order</td>\n        </tr>\n      </tbody>\n    </table>\n    <h3>Gotcha’s</h3>\n    <ul>\n      <li>You can also use parentheses to create more complex searches like here: <span class="code">(title ~ PCI OR title ~ ISO) AND description ~ “needs to be”</span></li>\n      <li>Filters are case insensitive (example: <em>title</em> is the same as <em>Title</em> or <em>TITLE</em>)</li>\n      <li><span class="code">AND</span> has precedence over <span class="code">OR</span></li>\n      <li>Every time space (“ “) character is part of search phrase or filter expression you need to use quotes (<span class="code">“modified date” &gt; 03/03/2015 AND “control title” ~ PCI</span>)</li>\n      <li>Compatible date formats are <span class="code">MM/DD/YYYY</span></li>\n    </ul>\n  </div>\n  <div class="footer-wrap">\n    <a href="#" class="btn btn-small btn-success">OK, got it!</a>\n  </div>\n</div>\n';
