GGRC.Templates['people/object_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{! dashboard object listing}}\n<ul class="tree-structure new-tree">\n  {{#each list}}\n    <li class="tree-item role" data-model="true" {{data \'model\'}}>\n      <div class="item-main">\n        <div class="item-wrap">\n          {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n          <div class="select">\n            <div class="item-data">\n              <div class="row-fluid">\n                <div class="span4">\n                  <div class="title tree-title-area">\n                    {{name}}\n                  </div>\n                </div>\n                <div class="span4">\n                  <div class="email tree-title-area">\n                    <span class="email">{{email}}</span>\n                  </div>\n                </div>\n                <div class="span2">\n                  {{#person_roles this "System|Admin"}}\n                    {{#if roles.length}}\n                      <li>\n                        <div class="item-data">\n                          <div class="tree-title-area">\n                            <span class="role" title="{{#roles}}{{permission_summary}} {{/roles}}">\n                              {{roles.0.permission_summary}}\n                            </span>\n                          </div>\n                        </div>\n                      </li>\n                    {{else}}\n                      <li>\n                        <div class="item-data">\n                          <div class="tree-title-area">\n                            <span class="no-role">\n                              No Role\n                            </span>\n                          </div>\n                        </div>\n                      </li>\n                    {{/if}}\n                  {{/person_roles}}\n                </div>\n                <div class="span2">\n                  <div class="show-details">\n                    <a href="/people/{{id}}" class="btn btn-mini btn-outline">\n                      Open\n                    </a>\n                  </div>\n                </div>\n\n              </div>\n            </div><!-- item-data end -->\n          </div><!-- select end -->\n        </div><!-- item-wrap end -->\n      </div><!-- item-main end -->\n\n      <div class="tier-2-info item-content info">\n        <div class="tier-2-info-content">\n\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n              <li>\n                <a href="/people/{{id}}">\n                  <i class="fa fa-long-arrow-right"></i>\n                  Open Profile Page\n                </a>\n              </li>\n              {{#is_allowed \'create\' \'delete\' \'UserRole\' context=parent_instance.context.id}}\n                <li class="border">\n                  <a href="javascript://" data-modal-class="modal-wide" data-placement="left" data-toggle="user-roles-modal-selector" data-modal-selector-options="user_roles" data-person_id="{{ id }}">\n                    <i class="fa fa-key"></i>\n                    <span>Edit Authorizations</span>\n                  </a>\n                </li>\n              {{/is_allowed}}\n              {{#is_allowed \'update\' instance}}\n                <li>\n                  <a href="javascript://" data-object-plural="people" data-object-singular="Person" data-object-id="{{id}}" data-modal-class="modal-wide" data-placement="left" data-toggle="modal-ajax-form">\n                    <i class="fa fa-pencil-square-o"></i>\n                    <span>Edit Person</span>\n                  </a>\n                </li>\n              {{/is_allowed}}\n            </ul>\n          </div>\n\n          <div class="tier-content">\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Name</h6>\n                {{#if_match name \'\\\\S\'}}\n                  {{#name}}\n                    <h3>{{name}}</h3>\n                  {{/name}}\n                {{else}}\n                  <span class="empty-message">None</span>\n                {{/if_match}}\n              </div>\n            </div>\n\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Email</h6>\n                {{#if_match email \'\\\\S\'}}\n                  {{#email}}\n                    <p>\n                      <a href=\'mailto:{{email}}\' target="_blank">\n                        {{email}}\n                      </a>\n                    </p>\n                  {{/email}}\n                {{else}}\n                  <span class="empty-message">None</span>\n                {{/if_match}}\n              </div>\n            </div>\n\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Company</h6>\n                {{#if_match company \'\\\\S\'}}\n                  {{#company}}\n                    {{company}}\n                  {{/company}}\n                {{else}}\n                  <span class="empty-message">None</span>\n                {{/if_match}}\n              </div>\n            </div>\n\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Authorizations</h6>\n                <p>\n                  {{#person_roles this "System|Admin"}}\n                    {{#if roles.length}}\n                      {{#roles}}\n                        <span class="role">\n                          {{permission_summary}}\n                        </span>\n                      {{/roles}}\n                    {{else}}\n                      <li>\n                        <div class="item-data">\n                          <div class="tree-title-area">\n                            <span class="no-role">\n                              No Role\n                            </span>\n                          </div>\n                        </div>\n                      </li>\n                    {{/if}}\n                  {{/person_roles}}\n                </p>\n              </div>\n            </div>\n          </div>\n          {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=this}}}\n        </div>\n      </div><!-- tier 2 end -->\n    </li>\n  {{/each}}\n\n  <li class="tree-item">\n    {{#if has_prev_page}}\n    <a href="javascript://" class="view-more-paging">\n      Previous Page\n    </a>\n    {{/if}}\n\n    {{#if has_next_page}}\n    <a href="javascript://" class="pull-right view-more-paging" data-next="true">\n      Next Page\n    </a>\n    {{/if}}\n  </li>\n</ul>\n';
GGRC.Templates['people/option_column']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="search-title">\n  <div class="row-fluid">\n    <div class="span6">\n      <h4>\n        Search Results ({{ options.length }})\n      </h4>\n    </div>\n    <div class="span6">\n      <div class="guide">\n        &nbsp;\n      </div>\n    </div>\n  </div>\n</div>\n\n<div class="selector-list">\n  <ul class="new-tree">\n    {{#options}}\n      <li class="tree-item business" {{ data \'option\' }} data-id="{{ id }}">\n        <div class="item-main">\n          <div class="simple-link">\n            <div class="row-fluid">\n              <div class="span11">\n                <div class="item-data">\n                  <div class="tree-title-area">\n                    {{{render \'/static/mustache/people/popover.mustache\' person=this}}}\n                    <span class="url-link">\n                      {{email}}\n                    </span>\n                  </div>\n                </div>\n              </div>\n              <div class="span1">\n                <input type="checkbox" rel="tooltip" title="map">\n              </div>\n            </div>\n          </div>\n        </div>\n      </li>\n    {{/options}}\n  </ul>\n</div>\n';
GGRC.Templates['people/option_detail']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="selector-info" style="margin-right:-10%;">\n  {{!<div class="pull-right">\n    <a class="btn btn-mini" rel="tooltip" title="Edit">\n      <i class="fa fa-pencil-square-o"></i>\n    </a>\n    <a class="btn btn-mini" rel="tooltip" title="Remove">\n      <i class="fa fa-ban"></i>\n    </a>\n  </div>\n  <p>\n    <img src="/static/images/sample/profile100.jpg" alt="">\n  </p>}}\n  <h4>\n    {{{render \'/static/mustache/people/popover.mustache\' person=this}}}\n    <br>\n    {{ email }}\n    <br>\n    {{!<small>\n      Google, inc\n    </small>\n    <small></small>\n    }}\n    <br>\n    <a href="mailto:{{ email }}" target="_blank">\n      {{ email }}\n    </a>\n  </h4>\n</div>\n';
GGRC.Templates['people/owners_modal_connector']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ggrc-modal-connector\n  parent_instance="instance"\n  instance="instance"\n  source_mapping="owners"\n  mapping="owners"\n  deferred="true"\n  >\n{{#if list.length}}\n  <label>\n    {{#if_equals list.length 1}}Owner{{else}}Owners{{/if_equals}}\n    <i class="fa fa-question-circle" rel="tooltip"\n       title="This is the person in charge of this {{firstnonempty model_singular model.model_singular \'object\'}}."></i>\n    <a data-id="hide_owner_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n  </label>\n  {{#each list}}\n  <div class="span12">\n    {{{renderLive \'/static/mustache/people/popover.mustache\' person=this}}}\n    <a href="javascript://" class="unmap" data-toggle="unmap">\n      <span class="result" {{data \'result\'}}></span>\n      {{^if_equals list.length 1}}<i class="fa fa-trash" tabindex="11"></i>{{/if_equals}}\n    </a>\n  </div>\n  {{/each}}\n{{else}}\n  <label>\n    Owners\n    <i class="fa fa-question-circle" rel="tooltip"\n       title="This is the person in charge of this {{firstnonempty model_singular model.model_singular \'object\'}}."></i>\n    <a data-id="hide_owner_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n  </label>\n  <p>No owners assigned</p>\n{{/if}}\n{{#toggle show_new_object_form}}\n  {{#prune_context}}\n    <div class="span12 objective-selector field-wrap">\n      <input tabindex="11" data-id="owner_txtbx" type="text" data-lookup="Person" null-if-empty="true" class="search-icon" placeholder="Enter text to search for Owner" {{autocomplete_select}} value="{{instance.email}}">\n      <a class="remove-field" href="javascript://" {{toggle_button}}><i class="fa fa-trash"></i></a>\n\n    </div>\n    <!--input type="hidden" name="role_name" value="Owner" /-->\n  {{/prune_context}}\n{{else}}\n<a href="javascript://" class="btn btn-small btn-draft" tabindex="11" {{toggle_button}}>Add Owner</a>\n\n{{/toggle}}\n</ggrc-modal-connector>\n';
GGRC.Templates['people/popover']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#person}}\n<span class="person-holder">\n  {{#if show_email}}\n    <a data-test-id="text_manager_7a906d2e" href=\'mailto:{{email}}\'\n       target="_blank"><span class="person-tooltip-trigger" data-placement="top">\n      {{email}}\n    </span></a>\n  {{else}}\n    <a data-test-id="text_manager_7a906d2e" href="/people/{{id}}"><span\n            class="person-tooltip-trigger" data-placement="top">\n      {{firstnonempty name email}}\n    </span></a>\n  {{/if}}\n  {{#if_equals system_wide_role \'No Access\'}}\n    <span class="user-disabled">\n      (Inactive user)\n    </span>\n  {{/if_equals}}\n  <div class="custom-popover-content">\n    <div class="preview-container person-preview">\n      <div class="row-fluid">\n        <div class="span8">\n          <div class="profile-info">\n            <ul>\n              <li><h4>{{firstnonempty name email}}\n                {{#if_equals system_wide_role \'No Access\'}}\n                  <span class="user-disabled">\n                    (Inactive user)\n                  </span>\n                {{/if_equals}}\n              </h4></li>\n              <li><strong>Email:</strong> <a href="mailto:{{email}}" target="_blank">{{email}}</a></li>\n              {{#company}}<li><strong>Company:</strong> {{company}}</li>{{/company}}\n            </ul>\n\n            {{{render_hooks \'Person.popover_actions\' person=person}}}\n\n            <a class="info-action" href="/people/{{id}}">View profile <i class="fa fa-long-arrow-right"></i></a>\n          </div>\n        </div>\n\n        {{{render_hooks \'Person.popover_info\' person=person}}}\n\n      </div>\n    </div>\n  </div>\n</span>\n{{/person}}\n';
GGRC.Templates['people/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> /static/mustache/base_objects/dropdown_menu_tier2.mustache}}\n\n<div class="tier-content">\n  {{#if instance.email}}\n    <div class="row-fluid wrap-row">\n      <h6>Email</h6>\n      <p>\n        {{{render \'/static/mustache/people/popover.mustache\' person=instance show_email=true}}}\n      </p>\n    </div>\n  {{/if}}\n  {{{render \'/static/mustache/base_objects/mappings_detail.mustache\' result=result parent_instance=parent_instance}}}\n</div>\n';
GGRC.Templates['people/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <li class="tree-item" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-wrap">\n        {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span4">\n                <div class="title tree-title-area" title="{{instance.title}}">\n                  {{#is_subtree}}\n                    <i class="fa fa-person"></i>\n                  {{/is_subtree}}\n                  {{{firstnonempty instance.title instance.description_inline instance.name instance.email \'\'}}}\n                </div>\n              </div>\n\n              <div class="span2">\n                <div class="email tree-title-area">\n                  <span class="email">{{instance.email}}</span>\n                  {{#if_equals system_wide_role \'No Access\'}}\n                    <span class="user-disabled">\n                      (Inactive user)\n                    </span>\n                  {{/if_equals}}\n                </div>\n              </div>\n\n              <div class="span2">\n                <div class="role tree-title-area">\n                  {{#with_program_roles_as "roles" result}}\n                  {{#if roles.length}}\n                    <li>\n                      <div class="item-data">\n                        <div class="tree-title-area">\n                          <span class="role" title="{{#roles}}{{role.permission_summary}} {{/roles}}">\n                            {{#if_helpers \'\\n                              #if_equals\' roles.0.role.permission_summary \'Mapped\' \'\\n                              and ^if_equals\' roles.length 1}}\n                              {{roles.1.role.permission_summary}}\n                            {{else}}\n                              {{roles.0.role.permission_summary}}\n                            {{/if_helpers}}\n                            {{#roles.1}}\n                              {{#if_in_map roles \'role.permission_summary\' \'Mapped\'}}\n                                {{^if_equals roles.length 2}}\n                                  + {{sum roles.length \'-2\'}}\n                                {{/if_equals}}\n                              {{else}}\n                                + {{sum roles.length \'-1\'}}\n                              {{/if_in_map}}\n                            {{/roles.1}}\n                          </span>\n                        </div>\n                      </div>\n                    </li>\n                  {{/if}}\n                  {{/with_program_roles_as}}\n                </div>\n              </div>\n\n              <div class="span4">\n\n                <div class="show-details">\n                  {{#child_options}}\n                    {{{renderLive add_item_view}}}\n                  {{/child_options}}\n                  <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                </div>\n\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n    {{#if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#if draw_children}}\n          {{#if child_options.length}}\n            {{#prune_context}} {{! this line is just chopping the context stack down to one element}}\n              {{#child_options}}\n                <div class="inner-tree">\n                  <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n                </div>\n              {{/child_options}}\n            {{/prune_context}}\n          {{/if}}\n        {{/if}}\n      </div>\n    </div>\n    {{/if}}\n  </li>\n';
GGRC.Templates['people/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#if allow_mapping_or_creating}}\n{{#is_allowed_to_map parent_instance model.shortName}}\n  <a\n    class="btn btn-mini action-button map-button"\n    href="javascript://"\n    rel="tooltip"\n    data-placement="left"\n    data-toggle="unified-mapper"\n    data-join-mapping="{{mapping}}"\n    data-join-option-type="{{model.shortName}}"\n    data-join-object-id="{{parent_instance.id}}"\n    data-join-object-type="{{parent_instance.class.shortName}}"\n    data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}">\n    Map\n  </a>\n{{/is_allowed_to_map}}\n{{/if}}\n';
GGRC.Templates['people/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> \'/static/mustache/base_objects/tree_view_filter.mustache\'}}\n\n<header class="header sticky sticky-header tree-header {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n  <div class="row-fluid">\n    <div class="span4">\n      <div class="title-heading oneline">\n        <span class="widget-col-title" data-field="title">\n          Name\n          <i class="fa fa-caret-up"></i>\n        </span>\n      </div>\n    </div>\n    <div class="span2">\n      <div class="oneline">\n        <span class="widget-col-title" data-field="email">\n          Email\n          <i class="fa fa-caret-up"></i>\n        </span>\n      </div>\n    </div>\n    <div class="span2">\n      <div class="oneline">\n        Authorizations\n      </div>\n    </div>\n    <div class="span4">\n      <ul class="tree-action-list">\n        {{#if add_item_view}}\n        <li>\n          {{{renderLive add_item_view}}}\n        </li>\n        {{/if}}\n        <li>\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu tree-action-list-items" role="menu">\n              {{> \'/static/mustache/base_objects/filter_trigger.mustache\'}}\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n';
GGRC.Templates['people/search_result']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#list}}\n<li class="{{class.category}}" data-model="true" {{data \'model\'}}>\n  <a href="{{viewLink}}" class="show-extended">\n    <div>\n      <div class="lhs-main-title">\n        <span class="lhs-item lhs-item-long">\n          {{firstnonempty title name email}}\n        </span>\n      </div>\n    </div>\n  </a>\n</li>\n{{/list}}\n\n{{#list.is_loading}}\n  <li class="spinny"  {{attach_spinner \'{ "radius": 4, "length": 7, "width": 2 }\' }}>\n  </li>\n{{/list.is_loading}}\n';
GGRC.Templates['policies/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/notes.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div><!-- row-fluid end -->\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span3">\n              <h6>Code</h6>\n              <p>\n                {{slug}}\n              </p>\n            </div>\n            <div class="span3">\n              <h6>Kind/Type</h6>\n              {{#if kind}}\n                <p>\n                  {{kind}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div>\n        </div>\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['policies/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <!-- Title Row -->\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n  <!-- Description & owner/contact block  -->\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n  <!-- Notes & Process/Reference url block -->\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk"  href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            Policy URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="6" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="7" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <!-- Code & network block -->\n  <div class="row-fluid">\n    <div data-id="code_hidden" class="span6 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n        <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="POLICY-XXX" type="text" value="{{slug}}">\n    </div>\n    <div data-id="kind_type_hidden" class="span6 hidable">\n      <label>\n        Kind/Type\n        <i class="fa fa-question-circle" rel="tooltip" title="Choose from the options in the drop down list, or leave this set to None."></i>\n        <a data-id="hide_kind_type_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <select data-id="kind_type_dd" class="input-block-level" name="kind" tabindex="9">\n        <option {{^kind}}selected="selected"{{/kind}} {{^if kind}}selected="selected"{{/if}} value="">---</option>\n        <option {{#if_equals kind \'Company Policy\'}}selected="selected"{{/if_equals}} value="Company Policy">Company Policy</option>\n        <option {{#if_equals kind \'Org Group Policy\'}}selected="selected"{{/if_equals}} value="Org Group Policy">Org Group Policy</option>\n        <option {{#if_equals kind \'Data Asset Policy\'}}selected="selected"{{/if_equals}} value="Data Asset Policy">Data Asset Policy</option>\n        <option {{#if_equals kind \'Product Policy\'}}selected="selected"{{/if_equals}} value="Product Policy">Product Policy</option>\n        <option {{#if_equals kind \'Contract-Related Policy\'}}selected="selected"{{/if_equals}} value="Contract-Related Policy">Contract-Related Policy</option>\n        <option {{#if_equals kind \'Company Controls Policy\'}}selected="selected"{{/if_equals}} value="Company Controls Policy">Company Controls Policy</option>\n      </select>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <effective-dates instance="instance"/>\n    <div data-id="state_hidden" class="span4 hidable">\n      <label>\n        State\n        <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n        <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n        <dropdown options-list="model.statuses"\n                  name="instance.status"\n                  tabindex="23">\n        </dropdown>\n    </div>\n  </div>\n</form>\n{{/instance}}\n<div class="hideable-holder">\n{{!/div}}\n';
