GGRC.Templates['standards/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            Standard URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="6" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="7" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div>\n    <div class="row-fluid">\n      <div data-id="code_hidden" class="span4 hidable">\n        <label>\n          Code\n          <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n          <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="STANDARD-XXX" type="text" value="{{slug}}">\n      </div>\n      <effective-dates instance="instance"/>\n    </div>\n    <div class="row-fluid">\n      <div data-id="state_hidden" class="span4 hidable">\n        <label>\n          State\n          <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n          <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n          <dropdown options-list="model.statuses"\n                    name="instance.status"\n                    tabindex="23">\n          </dropdown>\n      </div>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['systems/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/notes.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div>\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span3">\n              <h6>Code</h6>\n              {{slug}}\n              </p>\n            </div>\n            <div class="span3">\n              <h6>Network Zone</h6>\n              {{#using network_zone=network_zone}}\n              {{#if network_zone}}\n                <p>\n                  {{network_zone.title}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n              {{/using}}\n            </div>\n            <div class="span3">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span3">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div>\n        </div>\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['systems/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <!-- Title Row -->\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n  <!-- Description & owner/contact block  -->\n  <div class="row-fluid">\n    <div data-id="description_hidden" class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a data-id="hide_description_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 triple wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n  <!-- Notes & System/Reference url block -->\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="5" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            System URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="6" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="7" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <!-- Code & network block -->\n  <div class="row-fluid">\n    <div data-id="code_hidden" class="span6 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n        <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="SYSTEM-XXX" type="text" value="{{slug}}">\n    </div>\n    <div data-id="network_zone_hidden" class="span6 hidable">\n      <label>\n        Network Zone\n        <i class="fa fa-question-circle" rel="tooltip" title="Choose from the options in the drop down list, or leave this set to None."></i>\n        <a data-id="hide_network_zone_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div data-id="network_zone_dd"  tabindex="9">\n        {{{option_select this "network_zone" "network_zone"}}}\n      </div>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <effective-dates instance="instance"/>\n    <div data-id="state_hidden" class="span4 hidable">\n      <label>\n        State\n        <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n        <a data-id="hide_state_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n        <dropdown options-list="model.statuses"\n                  name="instance.status"\n                  tabindex="23">\n        </dropdown>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
