GGRC.Templates['requests/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<li class="tree-item requests" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}" {{addclass "rq-" instance.status}}>\n  <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}} data-model="true">\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n\n            <div class="span{{display_options.title_width}}">\n              <div class="title tree-title-area" title="{{instance.title}}">\n                {{#is_subtree}}\n                  <i class="fa fa-{{instance.request_type}}"></i>\n                {{/is_subtree}}\n                <span>{{instance.title}}</span>\n              </div>\n            </div>\n\n            <div class="span{{display_options.selectable_width}}">\n              <div class="row-fluid">\n                {{#display_attr_list}}\n                  <div class="span{{display_options.selectable_attr_width}}">\n                    {{#if_equals attr_type \'custom\'}}\n                      <div class="custom tree-title-area">\n                        {{#get_custom_attr_value this instance}}\n                          {{! because the object can currently only be a\n                              person there is no need to switch }}\n                          {{#using person=object}}\n                            {{>\'/static/mustache/people/popover.mustache\'}}\n                          {{/using}}\n                        {{/get_custom_attr_value}}\n                      </div>\n                    {{else}}\n                      <div class="owner tree-title-area capitalize">\n                        {{#switch attr_name}}\n                          {{#case \'audit_object\'}}\n                            {{#using audit=instance.audit}}\n                              {{#with_mapping "audit_object_object" instance}}\n                                {{#if audit_object_object.0.instance}}\n                                  {{#is_allowed "view_object_page" audit_object_object.0.instance}}\n                                    <a class="object-title" href="{{audit_object_object.0.instance.viewLink}}" target="_blank">{{audit_object_object.0.instance.title}}</a>\n                                  {{else}}\n                                    <span class="object-title" target="_blank">{{audit_object_object.0.instance.title}}</span>\n                                  {{/is_allowed}}\n                                {{else}}\n                                  {{#with_mapping "authorizations" audit}}\n                                    {{#if_helpers "\\n                                      ^if_equals" instance.status "Accepted" "\\n                                      and ^if_auditor" instance _1_include_admin=false}}\n                                      None\n                                    {{/if_helpers}}\n                                  {{/with_mapping}}\n                                {{/if}}\n                              {{/with_mapping}}\n                            {{/using}}\n                          {{/case}}\n                          {{#case \'assignee\'}}\n                            {{#using assignee=instance.assignee}}\n                              {{{renderLive \'/static/mustache/people/popover.mustache\' person=assignee}}}\n                            {{/using}}\n                          {{/case}}\n                          {{#case \'audit\'}}\n                            {{#using audit=instance.audit}}\n                              {{audit.status}} &mdash; {{audit.title}}\n                            {{/using}}\n                          {{/case}}\n                          {{#case \'mapped_responses\'}}\n                            <i rel="tooltip" data-placement="right" {{#if_equals instance.request_type "documentation"}}data-original-title="# of attached documents" class="fa fa-files-o"{{else}}data-original-title="# of attached meetings" class="fa fa-calendar"{{/if_equals}}></i>\n                            <span class="counter">\n                              {{#child_options}}\n                                {{#with_mapping_count instance mapping}}\n                                  {{count}}\n                                {{/with_mapping_count}}\n                              {{/child_options}}\n                            </span>\n                          {{/case}}\n                          {{#default}}\n                            {{#instance}}\n                              {{get_default_attr_value attr_name instance}}\n                            {{/instance}}\n                          {{/default}}\n                        {{/switch}}\n                      </div>\n                    {{/if_equals attr_type \'custom\'}}\n                  </div>\n                {{/display_attr_list}}\n              </div>\n            </div>\n\n            <div class="span{{display_options.action_width}}">\n              <div class="show-details">\n                <view-object-button instance="instance" />\n              </div>\n            </div>\n\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- tree-item end -->\n\n  {{#if expanded}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      {{#if draw_children}}\n        {{#each child_options}}\n          <div class="inner-tree">\n            <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")).control("tree_view").display() }}>\n            </ul>\n          </div>\n        {{/each}}\n      {{/if}}\n    </div>\n  </div>\n  {{/if}}\n</li>\n';
GGRC.Templates['requests/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel assignable{{/is_info_pin}}">\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    <div class="tier-content">\n      {{> /static/mustache/base_templates/header.mustache}}\n\n      <div class="row-fluid">\n        <div class="span8">\n          {{> /static/mustache/base_objects/description.mustache}}\n\n          <div class="row-fluid wrap-row">\n            <div class="span6">\n              <h6>Evidence</h6>\n              {{{render "/static/mustache/base_templates/attachment_list.mustache" instance=. update_count="false"}}}\n            </div>\n            <div class="span6">\n              <h6>URL</h6>\n              {{> "/static/mustache/base_templates/url_list.mustache"}}\n            </div>\n          </div>\n\n          <div class="row-fluid wrap-row">\n            <div class="span12">\n              {{{render "/static/mustache/base_templates/mapped_objects.mustache" instance=. update_count="false"}}}\n            </div>\n          </div>\n\n          <div class="row-fluid wrap-row">\n            <div class="span6">\n              <h6>Test</h6>\n              <div class="rtf-block">\n                {{{firstnonempty test \'<span class="empty-message">None</span>\'}}}\n              </div>\n            </div>\n            <div class="span6">\n              <h6>Notes</h6>\n              <div class="rtf-block">\n                {{{firstnonempty notes \'<span class="empty-message">None</span>\'}}}\n              </div>\n            </div>\n          </div>\n\n          {{> /static/mustache/custom_attributes/info.mustache }}\n\n        </div>\n        <div class="span4">\n          <div class="row-fluid wrap-row">\n            <div class="span12">\n              <people-list instance="instance" editable="true"></people-list>\n            </div>\n          </div>\n\n          <div class="row-fluid wrap-row">\n            <div class="span12">\n              {{> /static/mustache/requests/dates_list.mustache}}\n            </div>\n          </div>\n\n          <div class="row-fluid wrap-row">\n            <div class="span12">\n              <h6>Code</h6>\n              <div class="side-content">\n                {{slug}}\n              </div>\n            </div>\n          </div>\n\n        </div>\n      </div>\n\n      <div class="tabs-wrap">\n        <tabs instance=".">\n          <tab-panel panels="panels" title-text="Comments and Responses" instance="instance">\n            {{#if_helpers \'\\n              ^if_equals\' status \'Verified\' \'\\n              and ^if_equals\' status \'Final\' }}\n            {{#is_allowed \'update\' instance context=\'for\'}}\n              <div class="tree-item-add">\n                <add-comment data="comments" source_mapping="instance" parent_instance="instance"></add-comment>\n              </div>\n            {{/is_allowed}}\n            {{/if_equals}}\n            {{{render "/static/mustache/base_templates/comment_list.mustache" instance=instance update_count="false"}}}\n          </tab-panel>\n          <tab-panel panels="panels" title-text="Request Log" instance="instance">\n            <revision-log instance="instance"></revision-log>\n          </tab-panel>\n          <tab-panel panels="panels" title-text="Related Requests" instance="instance">\n            {{> \'/static/mustache/requests/related-requests.mustache\' }}\n          </tab-panel>\n        </tabs>\n    </div>\n  </section>\n{{/instance}}\n';
GGRC.Templates['requests/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if allow_mapping_or_creating}}\n{{#if_instance_of parent_instance \'Audit\'}}\n{{#if allow_creating}}\n{{#is_allowed \'create\' model.shortName context=parent_instance.context.id}}\n  <a href="javascript://"\n     class="section-create action-button create-button"\n     rel="tooltip" data-placement="left"\n     data-original-title="Create Request"\n     data-toggle="modal-ajax-form"\n     data-modal-reset="reset"\n     data-modal-class="modal-wide"\n     data-object-singular="Request"\n     data-object-plural="requests"\n     data-object-params=\'{ "{{parent_instance.class.table_singular}}": {"id" : {{parent_instance.id}}, "type" : "{{parent_instance.class.model_singular}}" }, "context": { "id" : {{firstnonempty parent_instance.context.id "null"}}, "href" : "{{parent_instance.context.href}}", "type" : "{{parent_instance.context.type}}" } }\'>\n    Create New\n  </a>\n{{/is_allowed}}\n{{/if}}\n\n{{else}}\n{{#if allow_mapping}}\n{{#is_allowed_to_map parent_instance model.shortName}}\n  <a\n    class="btn btn-mini action-button map-button"\n    href="javascript://"\n    rel="tooltip"\n    data-placement="left"\n    data-toggle="unified-mapper"\n    data-join-mapping="{{mapping}}"\n    data-join-option-type="{{model.shortName}}"\n    data-join-object-id="{{parent_instance.id}}"\n    data-join-object-type="{{parent_instance.class.shortName}}"\n    data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}">\n    Map\n  </a>\n{{/is_allowed_to_map}}\n{{/if}}\n{{/if_instance_of}}\n{{/if}}\n';
GGRC.Templates['requests/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> \'/static/mustache/base_objects/tree_view_filter.mustache\'}}\n\n<header class="header sticky sticky-header tree-header {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n  <div class="row-fluid">\n    <div class="span{{display_options.title_width}}">\n      <div class="title-heading oneline">\n        <span class="widget-col-title" data-field="description">\n          Title\n          <i class="fa fa-caret-up"></i>\n        </span>\n      </div>\n    </div>\n    {{> \'/static/mustache/base_objects/tree_column_names.mustache\'}}\n    <div class="span{{display_options.action_width}}">\n      <ul class="tree-action-list">\n        {{#if add_item_view}}\n          <li>\n            {{{renderLive add_item_view}}}\n          </li>\n        {{/if}}\n        <li>\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu tree-action-list-items" role="menu">\n              {{> \'/static/mustache/import_export/import_export_buttons.mustache\'}}\n              {{> \'/static/mustache/base_objects/filter_trigger.mustache\'}}\n              {{> \'/static/mustache/base_objects/tree_column_selector.mustache\'}}\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n';
GGRC.Templates['requests/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  {{^if new_object_form}}\n    {{#if_in instance.status "Not Started,Completed,Verified"}}\n      <div class="alert warning">\n        <i class="fa fa-exclamation-triangle red"></i>\n        You are about to move request from "{{instance.status}}" to "In Progress".\n      </div>\n    {{/if_in}}\n  {{/if}}\n\n  <!-- Title Row -->\n  <div class="row-fluid">\n    <div class="span6">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input data-id="title_txtbx" tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n    </div>\n    {{#if new_object_form}}\n    <div class="span6">\n      <label>\n        Audit\n        <span class="required">*</span>\n      </label>\n        <div class="objective-selector">\n          {{#using audit=instance.audit }}\n            <input tabindex="11" type="text" name="audit.title" class="span12 search-icon" data-lookup="Audit" data-permission-type="update" placeholder="Enter text to search for Audit" null-if-empty="false" value="{{firstnonempty audit.title \'\'}}">\n          {{/using}}\n        </div>\n    </div>\n    {{/if}}\n  </div>\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Description\n        <a href="javascript://" class="field-hide">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="request_description" class="span12 double wysihtml5" name="description" placeholder="State your request here...">{{{description}}}</textarea>\n      </div>\n    </div>\n    <div class="span6">\n      <div class="row-fluid">\n        <div class="span12">\n          <label>\n            Request Type\n            <span class="required">*</span>\n          </label>\n          <select name="request_type" class="input-block-level" tabindex="3" {{#if responses.length}}disabled="true" readonly="true"{{/if}}>\n            {{#iterate \'Documentation\' \'Interview\'}}\n            <option value="{{iterator.toLowerCase}}" {{#if_equals iterator.toLowerCase instance.request_type}}selected="true"{{/if_equals}}>{{iterator}}</option>\n            {{/iterate}}\n          </select>\n        </div>\n      </div>\n      <div class="row-fluid">\n        <div class="span6">\n          <datepicker\n            set-max-date="end_date"\n            date="start_date"\n            required="{{true}}"\n            label="Starts On"\n            />\n        </div>\n        <div class="span6">\n          <datepicker\n            set-min-date="start_date"\n            date="end_date"\n            required="{{true}}"\n            label="Due On"\n            />\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span6 hidable bottom-spacing">\n      <ggrc-modal-connector\n          parent_instance="instance"\n          instance="instance"\n          source_mapping="info_related_objects"\n          deferred="true"\n          >\n        <label class="border-l">\n          Mapped Objects\n          <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n        </label>\n        {{#prune_context}}\n          <ul class="tree-structure new-tree mapped-objects-tree">\n          {{#each list}}\n            <li class="tree-item task-item" data-object-type="{{class.table_singular}}"\n              {{#instance}}{{ data \'option\' }}{{/instance}} data-id="{{ id }}">\n              <div class="item-main">\n                <div class="item-wrap">\n                  <div class="select">\n                    <div class="item-data">\n                      <div class="row-fluid">\n                        <div class="span10">\n                          <div class="tree-title-area">\n                            {{#update_link .}}\n                              <a class="url" href="{{schemed_url link}}" target="_blank">\n                                <i class="fa fa-{{class.table_singular}} color"></i>\n                                {{firstexist title name email link}}\n                              </a>\n                            {{/update_link}}\n                          </div>\n                        </div> <!-- span10 end -->\n\n                        <div class="span2">\n                          <div class="show-details">\n                            <a href="javascript://" class="unmap" data-toggle="unmap">\n                              <span class="result" {{data \'result\'}}></span>\n                              <i class="fa fa-trash"></i>\n                            </a>\n                          </div>\n                        </div> <!-- span2 end -->\n                      </div> <!-- row-fluid end -->\n                    </div> <!-- item-data end -->\n                  </div> <!-- select end -->\n                </div> <!-- item-wrap end -->\n              </div> <!-- item-main end -->\n            </li>\n          {{/each}}\n        </ul>\n        {{/prune_context}}\n        {{^list.length}}\n          <span class="gray"><em>No mapped objects</em></span>\n        {{/list.length}}\n        <div class="objective-selector">\n          <div style="clear:both">\n            <br>\n            <a class="section-add section-sticky btn btn-small btn-draft"\n              href="javascript://" rel="tooltip"\n              {{data "deferred_to"}}\n              data-placement="left"\n              data-toggle="unified-mapper"\n              data-deferred="true"\n              data-object-source="true"\n              data-join-mapping="business_objects"\n              data-join-object-id="{{instance.id}}"\n              data-join-object-type="{{instance.class.model_singular}}"\n              data-original-title="Map Object to this {{instance.class.title_singular}}" tabindex="6">\n              Map Objects\n            </a>\n          </div>\n        </div>\n      </ggrc-modal-connector>\n    </div>\n    <div class="span6">\n      <people-list instance="instance" editable="true" deferred="true" validate="true"></people-list>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Test\n        <a href="javascript://" class="field-hide">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="8" id="test_description" class="span12 double wysihtml5" name="test" placeholder="Enter Test">{{{test}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hidable">\n      <label>\n        Notes\n        <a href="javascript://" class="field-hide">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="9" id="notes_description" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n        <a href="javascript://" class="field-hide">hide</a>\n      </label>\n      <input tabindex="10" class="input-block-level" name="slug" placeholder="REQUEST-XXX" type="text" value="{{slug}}">\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n';
GGRC.Templates['requests/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> /static/mustache/base_objects/dropdown_menu_tier2.mustache}}\n\n<div class="tier-content">\n  {{{render \'/static/mustache/requests/general_info.mustache\' instance=instance }}}\n  {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n  {{{renderLive \'/static/mustache/base_objects/mappings_detail.mustache\' result=result parent_instance=parent_instance}}}\n  {{{renderLive \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n  {{{renderLive \'/static/mustache/base_objects/notes_and_code.mustache\' instance=instance}}}\n</div>\n';
GGRC.Templates['requests/general_info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{! transcluded via renderLive in, for example, assets/mustache/sections/tier2_content.mustache }}\n{{#instance}}\n  <div class="row-fluid wrap-row">\n    <div class="span6">\n      <h6>Title</h6>\n      <h3>{{instance.description}}</h3>\n    </div>\n    <div class="span6">\n      {{#if status}}\n        <h6>State</h6>\n        <span class="state-value">{{status}}</span>\n      {{/if}}\n    </div>\n  </div>\n  <div class="row-fluid wrap-row">\n    <div class="span12">\n      <h6>Object Review</h6>\n      {{#if class}}\n        {{{render_hooks instance.class.shortName \'info_widget_actions\'}}}\n      {{/if}}\n    </div>\n  </div>\n{{/instance}}\n';
GGRC.Templates['requests/dates_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Dates</h6>\n<div class="side-content">\n  <div {{#instance}}{{data "model"}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({ instance : el.data("model")}); }}>\n    <ul class="label-list dates">\n      {{#if start_date}}\n        <li class="gray">\n          <label>\n            Starts on\n          </label>\n          <span name="start_date">{{localize_date start_date}}</span>\n        </li>\n      {{/if}}\n      {{#if end_date}}\n        <li>\n          {{#toggle change_due_date}}\n            <label>Due on</label>\n            <div class="input-group">\n              <span class="input-group-addon"><i class="fa fa-calendar"></i></span>\n              <input tabindex="5" class="span8 input-block-level date" name="end_date" data-toggle="datepicker" data-after="start_date" placeholder="MM/DD/YYYY" type="text" value="{{localize_date end_date}}">\n            </div>\n          {{else}}\n            {{#is_allowed \'update\' instance context=\'for\'}}\n              {{#if_in instance.status "In Progress"}}\n                <span class="file-controls">\n                <a href="javascript://" {{toggle_button}}><i class="fa fa-pencil-square-o"></i></a>\n              </span>\n              {{/if_in}}\n            {{/is_allowed}}\n            <label>\n              Due on\n            </label>\n            {{localize_date end_date}}\n          {{/toggle}}\n        </li>\n      {{/if}}\n      {{#if_helpers \'#if\' instance.finished_date \'or #if_null\' instance.finished_date}}\n        <li>\n          <label>Finished</label>\n          {{#if instance.finished_date}}\n            <span>{{localize_date instance.finished_date}}</span>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </li>\n      {{/if_helpers}}\n      {{#if_helpers \'#if\' instance.verified_date \'or #if_null\' instance.verified_date}}\n        <li>\n          <label>Verified</label>\n          {{#if instance.verified_date}}\n            <span>{{localize_date instance.verified_date}}</span>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </li>\n      {{/if_helpers}}\n    </ul>\n  </div>\n</div>\n';
GGRC.Templates['requests/related-requests']= '{{!\nCopyright (C) 2016 Google Inc.\nLicensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<related-objects base-instance="instance" related-items-type="Request" order-by="__similarity__">\n    <reusable-objects-list parent-instance="instance" base-instance="instance">\n        <div class="grid-data-toolbar flex-box">\n            <tree-pagination paging="paging" class="flex-size-1"></tree-pagination>\n            {{#is_allowed \'update\' baseInstance context=\'for\'}}\n                <div class="grid-data-toolbar-control">\n                    <button {{^hasSelected}}disabled{{/hasSelected}}\n                            class="btn btn-small btn-full btn-success"\n                            can-click="reuseSelected">\n                      Reuse\n                    </button>\n                </div>\n            {{/is_allowed}}\n        </div>\n        <div class="grid-data-header flex-row flex-box">\n\n            <div class="grid-data-item-index">\n                Date\n            </div>\n            <div class="flex-size-1">\n                Title\n            </div>\n            <div class="flex-size-1">\n                Mapped objects\n            </div>\n            <div class="flex-size-1">\n                Evidence\n            </div>\n        </div>\n        <div class="grid-data-body {{#isLoading}}loading{{/isLoading}}">\n            <object-list items="relatedObjects">\n                <div class="grid-data-row flex-row flex-box">\n                    <div class="grid-data-item-index">\n                      {{localize_date instance.created_at}}\n                    </div>\n                    <div class="flex-size-1">\n                        <a href="{{instance.viewLink}}" target="_blank">\n                          {{instance.title}}\n                        </a>\n                    </div>\n                    <div class="flex-size-1">\n                        <mapped-objects\n                                parent-instance="instance"\n                                is-loading="isLoading"\n                                mapping="{{instance.class.info_pane_options.mapped_objects.mapping}}">\n                          {{> \'/static/mustache/components/assessment/mapped-objects/mapped-related-information-item.mustache\' }}\n                        </mapped-objects>\n                    </div>\n                    <div class="flex-size-1">\n                        <mapped-objects\n                                parent-instance="instance"\n                                is-loading="isLoading"\n                                mapping="{{instance.class.info_pane_options.evidence.mapping}}">\n                            <reusable-objects-item instance="instance"\n                                                   is-saving="isSaving"\n                                                   base-instance="baseInstance"\n                                                   mapping="mapping"\n                                                   check-reused-status="checkReusedStatus"\n                                                   selected-list="evidenceList">\n                                <span class="date">{{date instance.created_at true}}</span>\n                                <a href="{{schemed_url instance.link}}" target="_blank">\n                                    <span>{{firstnonempty instance.title instance.link}}</span>\n                                </a>\n                            </reusable-objects-item>\n                        </mapped-objects>\n                        <mapped-objects\n                                parent-instance="instance"\n                                is-loading="isLoading"\n                                mapping="{{instance.class.info_pane_options.urls.mapping}}">\n                            <reusable-objects-item instance="instance"\n                                                   is-saving="isSaving"\n                                                   base-instance="baseInstance"\n                                                   mapping="mapping"\n                                                   check-reused-status="checkReusedStatus"\n                                                   selected-list="urlList">\n                                <span class="date">{{date instance.created_at true}}</span>\n                                <a href="{{schemed_url instance.link}}" target="_blank">\n                                    <span>{{firstnonempty instance.title instance.link}}</span>\n                                </a>\n                            </reusable-objects-item>\n                        </mapped-objects>\n                    </div>\n                </div>\n            </object-list>\n        </div>\n    </reusable-objects-list>\n</related-objects>\n';
GGRC.Templates['base_templates/subtree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<li class="tree-item task-item"\n    data-object-type="{{instance.class.table_singular}}"\n    {{#instance}}{{ data \'option\' }}{{/instance}}\n    data-id="{{ instance.id }}">\n  <div class="item-main">\n    <div class="item-wrap">\n      <div class="openclose">\n        <span class="status-label status-draft"></span>\n        {{#if isExpandable}}\n        <i class="fa fa-caret-right"></i>\n        {{/if}}\n      </div>\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span8">\n              <div class="tree-title-area">\n                <i class="fa fa-{{instance.class.table_singular}} color"></i>\n\n                {{{#view_object_link instance}}}\n                  {{firstexist instance.name instance.title instance.description}}\n                {{{/view_object_link}}}\n\n                <span class="url-link">\n                  {{firstexist instance.email instance.link}}\n                </span>\n              </div>\n            </div> <!-- span8 end -->\n          </div> <!-- row-fluid end -->\n        </div> <!-- item-data end -->\n      </div> <!-- select end -->\n    </div> <!-- item-wrap end -->\n  </div> <!-- item-main end -->\n\n  {{#if isExpandable}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      <lazy-openclose>\n        {{#show}}\n          <div class="tier-content">\n            {{> /static/mustache/base_objects/title.mustache}}\n            {{> /static/mustache/base_objects/description.mustache}}\n            {{> /static/mustache/base_objects/notes.mustache}}\n          </div>\n        {{/show}}\n      </lazy-openclose>\n    </div>\n  </div>\n  {{/if}}\n\n</li>\n';
GGRC.Templates['objectives/autocomplete_result']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each items}}\n<li class="ui-menu-item" {{data \'ui-autocomplete-item\'}}>\n  <a href="javascript://" class=\'show-extended\' {{data \'model\'}}>\n    {{title}}\n  </a>\n</li>\n{{/each}}\n{{^items}}\n  <li class="ui-menu-item" data-ui-autocomplete-item="">\n    No results\n  </li>\n{{/items}}\n{{#if items_loading}}\n  <li class="spinner ui-menu-item" data-ui-autocomplete-item="">\n    <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n  </li>\n{{/if}}\n{{#is_allowed "create" "Objective" context=null}}\n<li class="ui-menu-item add-new oneline" data-ui-autocomplete-item="">\n  <a data-object-plural="objectives" data-modal-class="modal-wide" href="javascript://" data-object-singular="Objective" data-toggle="modal-ajax-form" data-modal-reset="reset">\n    + Create New\n  </a>\n</li>\n{{/is_allowed}}\n';
GGRC.Templates['meetings/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n  {{#instance}}\n    <div class="row-fluid">\n      <div class="span12">\n        <label>Meeting Title <i class="fa fa-question-circle" rel="tooltip" title="Title"></i></label>\n        <input tabindex="1" type="text" name="title" class="input-block-level" placeholder="Enter Title" value="{{instance.title}}" autofocus>\n      </div>\n    </div>\n    <div class="row-fluid">\n      <div class="span4">\n        <label>Choose Date</label>\n        <div class="input-group">\n          <span class="input-group-addon"><i class="fa fa-calendar"></i></span>\n          <input tabindex="2" class="input-block-level date" name="start_at.date" data-also-set="end_at.date" data-toggle="datepicker" placeholder="MM/DD/YYYY" type="text" value="{{localize_date start_at}}">\n        </div>\n      </div>\n      <div class="span4">\n        <label>Starts at</label>\n        <select class="input-block-level" name="start_at.time" tabindex="3">\n          {{{local_time_range instance.start_at \'09:00\' \'17:01\'}}}\n        </select>\n\n        <label>Ends at</label>\n        <select class="input-block-level" name="end_at.time" tabindex="4">\n          {{{local_time_range instance.end_at \'09:00\' \'17:01\'}}}\n        </select>\n      </div>\n      <div class="span4">\n        Participants\n        <div>\n        {{#using attendees=instance.people response=instance.response}}\n          {{#using reified_people=response.people}}\n            {{#reified_people}}\n            <label>\n            <input tabindex="5"\n                   name="people"\n                   data-binding="people"\n                   model="Person"\n                   type="checkbox"\n                   value="{{id}}"\n                   {{#if new_object_form}}\n                   checked\n                   {{else}}\n                   {{#in_array this attendees}}checked{{/in_array}}\n                   {{/if}} />\n              {{firstnonempty name email \'\'}}</label>\n            {{/reified_people}}\n          {{/using}}\n          </div>\n        {{/using}}\n\n      </div>\n    </div>\n  {{/instance}}\n  {{!/div}}\n\n';
GGRC.Templates['meetings/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#allow_creating}}\n  <a href="javascript://"\n     class="section-create action-button create-button"\n     rel="tooltip"\n     data-placement="left"\n     data-original-title="Create Meeting"\n     data-toggle="modal-ajax-form"\n     data-modal-reset="reset"\n     data-modal-class="modal-wide"\n     data-object-singular="Meeting"\n     data-object-plural="meetings"\n     data-object-params=\'{ "{{parent_instance.class.table_singular}}": {"id" : {{parent_instance.id}}, "type" : "{{parent_instance.class.model_singular}}" }, "context": { "id" : {{firstnonempty parent_instance.context.id "null"}}, "href" : "{{parent_instance.context.href}}", "type" : "{{parent_instance.context.type}}" }, "modal_title" : "Schedule a Meeting" }\'>\n    Create New\n  </a>\n{{/allow_creating}}\n';
GGRC.Templates['meetings/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<li class="tree-item {{#if instance.snapshot}}snapshot{{/if}}" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-wrap">\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span12">\n                <div class="title tree-title-area" title="{{instance.title}}">\n                  <i class="fa fa-calendar"></i>\n                  <a class="url" href="{{schemed_url instance.title}}" target="_blank">\n                    {{instance.title}}\n                  </a>\n                  {{{!render_hooks \'Meeting.tree_view_info\'}}}\n                </div>\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n  </li>\n';
