GGRC.Templates['search/advanced_search_option_items']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each options}}\n<li class="tree-item task-item" data-object-type="{{instance.class.table_singular}}" {{#instance}}{{ data \'option\' }}{{/instance}} data-id="{{ instance.id }}">\n  <div class="item-main">\n    <div class="item-wrap">\n      <div class="openclose" {{data \'expandable\'}}>\n        <span class="status-label status-draft"></span>\n        <i class="fa fa-caret-right"></i>\n      </div>\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span8">\n              <div class="tree-title-area">\n                <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                {{firstexist instance.name instance.email instance.title instance.description}}\n                <span class="url-link">\n                {{firstexist instance.email instance.link}}\n                </span>\n              </div>\n            </div> <!-- span8 end -->\n            <div class="span4">\n              <div class="show-details">\n                <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n              </div>\n            </div>\n          </div> <!-- row-fluid end -->\n        </div> <!-- item-data end -->\n      </div> <!-- select end -->\n    </div> <!-- item-wrap end -->\n  </div> <!-- item-main end -->\n\n\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      <lazy-openclose>\n        {{#show}}\n          {{#find_template \'tier2_content\' instance}}\n            {{{renderLive template}}}\n          {{else}}\n            <div class="show-description">\n              <div class="row-fluid">\n                <div class="span12">\n                  <div class="tree-description short">\n                    {{{instance.description}}}\n                  </div>\n                  {{{show_long}}}\n                </div>\n              </div>\n            </div>\n          {{/find_template}}\n        {{/show}}\n      </lazy-openclose>\n    </div>\n  </div>\n\n</li>\n{{/each}}\n';
GGRC.Templates['search/advanced_search_people_items']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#each options}}\n<li class="tree-item control governance task-item"\n  data-object-type="{{instance.class.table_singular}}"\n  {{#instance}}{{ data \'option\' }}{{/instance}}\n  data-id="{{ instance.id }}">\n    <div class="item-main">\n      <div class="item-wrap">\n        {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span8">\n                <div class="tree-title-area">\n                  <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                  {{#if instance.name}}{{instance.name}}{{/if}}\n                  <span class="url-link">\n                    {{#if instance.email}}{{instance.email}}{{/if}}\n                  </span>\n                  {{#if_equals system_wide_role \'No Access\'}}\n                    <span class="user-disabled">\n                      (Inactive user)\n                    </span>\n                  {{/if_equals}}\n                </div>\n              </div> <!-- span8 end -->\n              <div class="span4">\n                <div class="show-details">\n                  <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                </div>\n              </div>\n            </div> <!-- row-fluid end -->\n          </div> <!-- item-data end-->\n        </div> <!-- select end -->\n      </div> <!-- item-wrap end -->\n    </div> <!-- item-main end -->\n\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#find_template \'tier2_content\' instance}}\n          {{{renderLive template}}}\n        {{else}}\n          <div class="tier-content">\n            <div class="row-fluid wrap-row">\n            {{#instance}}\n              <h6>Email</h6>\n              <span class="person-holder">\n                <a href="/people/{{id}}"><span class="person-tooltip-trigger" data-placement="top">\n                  {{firstnonempty email}}\n                </span></a>\n                {{#if_equals system_wide_role \'No Access\'}}\n                  <span class="user-disabled">\n                    (Inactive user)\n                  </span>\n                {{/if_equals}}\n              </span>\n            {{/instance}}\n            </div>\n          </div>\n        {{/if}}\n      </div>\n    </div>\n</li>\n{{/each}}\n';
GGRC.Templates['import_export/import_export_buttons']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^if_instance_of parent_instance \'Assessment\'}}\n<li>\n  <a href="/import"\n    class="section-import">\n    <i class="fa fa-fw fa-cloud-upload"></i>\n    Import {{model.model_plural}}\n  </a>\n</li>\n{{#if list.length}}\n  <li>\n    <a href="/export?model_type={{model.model_singular}}&relevant_type={{parent_instance.type}}&relevant_id={{parent_instance.id}}"\n      class="section-import">\n      <i class="fa fa-fw fa-download"></i>\n      Export {{model.model_plural}}\n    </a>\n  </li>\n{{/if}}\n{{/if_instance_of}}\n';
GGRC.Templates['import_export/create_template']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="download-template">\n  <h3>Create a CSV template</h3>\n  <p>\n    Don\'t have a CSV template ready? We\'ll <strong>create template</strong> for you right now.\n  </p>\n  <p>\n    Select which <strong>type of objects</strong> you want to import, and we\'ll\n    generate CSV template, with all fields and instructions.\n  </p>\n  <label>I\'m importing/updating...</label>\n  <select id="importSelect" class="input-block-level" multiple>\n    {{#importable}}\n      <option value="{{model_singular}}" label="{{title_plural}}"></option>\n    {{/importable}}\n  </select>\n  <p>A CSV template will be generated for the following object types:</p>\n  <ul class="import-list">\n    {{#each selected}}\n      <li><a data-index="{{@index}}" href="#"><i class="fa fa-trash"></i></a> {{name}}</li>\n    {{/each}}\n  </ul>\n  <div class="center-block">\n    <a href="import_controls_template" class="btn btn-primary btn-large import-button">\n      <i class="fa fa-cloud-upload white"></i>\n      Download template\n    </a>\n  </div>\n</div>\n';
GGRC.Templates['import_export/import_test_result']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="import-main-wrap import-main-wrap-analyze">\n  <div class="row-fluid wrap-row">\n    <div class="span12">\n      <div class="import-wrap import-analyze">\n        <h4>\n          {{filename}}\n          {{#if isLoading}}\n            <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n          {{/if}}\n        </h4>\n        <hr />\n\n        {{#each import}}\n        <ul>\n          <li><strong>Object type</strong>: {{name}}</li>\n          <li><strong>Number of rows</strong>: {{rows}}</li>\n          {{#if created}} <li><strong>To be created</strong>: {{created}}</li> {{/if}}\n          {{#if updated}} <li><strong>To be updated</strong>: {{updated}}</li> {{/if}}\n          {{#if deleted}} <li><strong>To be deleted</strong>: {{deleted}}</li> {{/if}}\n          {{#if ignored}} <li><strong>Ignored</strong>: {{ignored}}</li> {{/if}}\n          {{#data}}\n            <li>\n              <strong>{{status}}</strong>\n              {{#if_equals status "errors"}}\n                <i class="fa fa-exclamation-triangle red"></i>\n              {{/if}}\n              {{#if messages.length}}\n                <ul>\n                  {{#messages}}\n                    <li>{{.}}</li>\n                  {{/messages}}\n                </ul>\n              {{/if}}\n            </li>\n          {{/data}}\n        </ul>\n        <hr />\n        {{/each}}\n      </div>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['import_export/complete']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<div class="import-wrap import-final">\n  <ul>\n    {{#data}}\n      {{#if created}} <li><strong>{{created}}</strong> records created</li>{{/if}}\n      {{#if updated}} <li><strong>{{updated}}</strong> records updated</li>{{/if}}\n      {{#if ignored}} <li><strong>{{ignored}}</strong> records ignored</li>{{/if}}\n      {{#if deleted}} <li><strong>{{deleted}}</strong> records deleted</li>{{/if}}\n    {{/data}}\n  </ul>\n</div>\n';
GGRC.Templates['import_export/faq']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="text-top-space">\n  <h3>Quick import tips</h3>\n  <ul>\n    <li><strong>Not all fields are required!</strong> Templates are flexible. You can remove all non-mandatory columns (refer to row 4 to see which fields are required)</li>\n    <li>You can <strong>map</strong> objects via import. Simply add <strong>map:control</strong> column and use object codes in the column to map objects. You can <strong>unmap</strong> objects in the same way</li>\n    <li>You can <strong>delete</strong> objects in a similar way. Add <strong>delete</strong> column in line 5 and put <em>yes</em> in rows for objects you want to delete</li>\n    <li>If objects already exist, fields <strong>you leave empty</strong> will be ignored. (Object\'s attributes will not be replaced with empty info)</li>\n    <li>For extensive import help, refer to <a href="#">Import Help/FAQ</a> section below</li>\n  </ul>\n</div>\n';
GGRC.Templates['import_export/import']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<csv-import>\n  <div class="info">\n    <div class="row-fluid wrap-row">\n      <div class="span8 import">\n        <p>\n          Here you are able to import all gGRC objects.\n          Please make sure you have your CSV file ready, or\n          download one of the predefined templates.\n        </p>\n        <div class="clearfix">\n          <input type="file" accept=".csv"\n                 data-file-upload class="csv-upload" name="upload"\n                 style="visibility: hidden; width: 1px; height: 1px" />\n          {{#states}}\n            <button id="import_btn" {{#isDisabled}}disabled{{/isDisabled}} class="btn btn-large btn-fixed state-{{state}} {{class}}">{{{text}}}</button>\n            {{^if_equals state "select"}}\n              {{#showSpinner}}\n                <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n              {{/showSpinner}}\n              {{^showSpinner}}\n                <button class="btn btn-small state-reset">Reset</button>\n              {{/showSpinner}}\n            {{/if}}\n          {{/states}}\n        </div>\n        {{#if_equals state "import"}}\n          {{> /static/mustache/import_export/import_test_result.mustache}}\n        {{/if_equals}}\n\n        {{#if_equals state "success"}}\n          {{> /static/mustache/import_export/complete.mustache}}\n        {{/if_equals}}\n\n        {{> /static/mustache/import_export/faq.mustache}}\n      </div>\n      <div class="span4">\n        <csv-template>\n        {{> /static/mustache/import_export/create_template.mustache}}\n        </csv-template>\n      </div>\n    </div>\n    <div id="results-container"></div>\n  </div>\n</csv-import>\n';
GGRC.Templates['import_export/export']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<csv-export filename="Export Objects">\n<div class="info no-space">\n  <div class="choose-object-wrap bottom-space">\n    <div class="row-fluid">\n      <div class="span12">\n        <div class="report-title">\n          {{#if export.edit_filename}}\n            <div class="report-title-change">\n              <input type="text" can-value="export.filename" class="input-medium" placeholder="Enter report name">\n              <a class="btn btn-primary btn-title-change btn-small title-change" href="#">Save</a>\n            </div>\n          {{else}}\n            <h2 class="title">\n              Export Objects\n            </h2>\n          {{/if}}\n        </div>\n      </div>\n    </div>\n\n    <export-group panels="export.panels">\n      <div class="relevant-filter-group">\n        {{#each panels.items}}\n          <div class="new-relevant-block">\n            <export-panel type="{{type}}" item="." panel_number="{{@index}}">\n              <div class="row-fluid">\n                <div class="span4">\n                  <h6>Export object type</h6>\n                  <div class="single-line-filter">\n                    {{#first_panel}}\n                      <button type="submit" class="remove_filter_group">\n                        <i class="fa fa-trash"></i>\n                      </button>\n                    {{/first_panel}}\n                    <select can-value="type" class="input-block-level option-type-selector">\n                      {{#exportable}}\n                        <option value="{{model_singular}}" label="{{title_plural}}"></option>\n                      {{/exportable}}\n                    </select>\n                  </div>\n                </div>\n              </div>\n\n              {{> /static/mustache/import_export/export/attribute_selector.mustache}}\n              {{> /static/mustache/import_export/export/filter_query.mustache}}\n\n              <relevant-filter show_all="true" has_parent="item.has_parent" relevant_menu_item="parent" type="export.type" panel_number="panel_number" relevant="item.relevant" disable-create="true"></relevant-filter>\n            </export-panel>\n          </div>\n        {{/each}}\n      </div>\n      <div class="add-rule add-object">\n        <a href="#" id="addAnotherObjectType" class="addFilterRule btn btn-small btn-info">Add another object type</a>\n      </div>\n    </export-group>\n  </div>\n  <div class="save-template-wrap">\n    <div class="btn-group pull-right">\n      {{^export.loading}}\n        <button type="submit" id="export-csv-button" class="btn btn-success btn-small">\n          <i class="fa fa-download white"></i>\n          Export Objects\n        </button>\n      {{/loading }}\n      {{#export.loading}}\n        <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n      {{/loading }}\n    </div>\n  </div>\n</div>\n</csv-export>\n';
GGRC.Templates['import_export/export/object_selector']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n';
GGRC.Templates['import_export/export/attribute_selector']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid wrap-row">\n  <div class="span12">\n    <h6>\n      Select attributes to show in grid\n    </h6>\n    Attributes:\n    <a data-action="attribute_select_toggle" data-value="true" data-type="attributes" href="javascript://">Select All</a> /\n    <a data-action="attribute_select_toggle" data-value="false" data-type="attributes" href="javascript://">None</a>\n    <br>\n    Mappings:\n    <a data-action="attribute_select_toggle" data-value="true" data-type="mappings" href="javascript://">Select All</a> /\n    <a data-action="attribute_select_toggle" data-value="false" data-type="mappings" href="javascript://">None</a>\n    <div class="attribute-wrap">\n      <ul class="attr-list">\n        {{#each columns}}\n        <li>\n          <label>\n            <input can-value="selected.{{@index}}" type="checkbox" name="{{key}}" checked>\n            {{display_name}}\n          </label>\n        </li>\n        {{/each}}\n      </ul>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['import_export/export/filter_query']= '{{!\n  Copyright (C) 2016 Google Inc.\n  Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid wrap-row">\n  <div class="span10">\n    <h6>Filter Query</h6>\n    <div class="tree-filter__inline-filtering tree-filter__inline-filtering--transparent">\n      <div class="tree-filter__expression-holder{{#isFilterActive}} tree-filter__expression-holder--active{{/isFilterActive}}">\n        <input can-value="item.filter" name="filter_query" type="text" class="tree-filter__input tree-filter__input--wide">\n        <span class="tree-filter__expression-holder__is-expression">\n          <i class="fa {{#if isFilterActive}}fa-check-circle green{{else}}fa-check-circle-o{{/if}}"></i>\n        </span>\n      </div>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['mapper/relevant_filter']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Filter by mapping</h6>\n{{#each relevant}}\n  <div class="single-line-filter">\n    <button type="submit" data-index="{{@index}}" class="remove_filter">\n      <i class="fa fa-trash"></i>\n    </button>\n    <label>\n      {{#if @index}}AND {{/if}}Mapped to:\n    </label>\n    <select class="input-small filter-type-selector select-filter{{@index}}" can-value="model_name">\n      {{#menu}}\n        {{#if model_singular}}<option value="{{model_singular}}" label="{{title_singular}}"></option>{{/if}}\n      {{/menu}}\n    </select>\n    {{#filter}}\n      {{#if_helpers \'\\n        #if\' model_name \'\\n        and ^if_equals\' model_name \'__previous__\'}}\n        {{#model_name}}\n        <div class="modal-search objective-selector">\n          <input\n            class="input-large search-icon search-filter-{{@index}}"\n            placeholder="Enter text to search for {{model_name}}"\n            type="text"\n            data-index="{{@index}}"\n            name="filter_list.{{@index}}.filter"\n            value="{{firstnonempty filter.name filter.email filter.title}}"\n            data-lookup="{{this}}"\n            data-template="/{{#if_equals model_name \'Person\'}}people{{else}}base_objects{{/if_equals}}/autocomplete_result.mustache"\n            {{ autocomplete_select disableCreate }}\n            >\n        </div>\n        {{/model_name}}\n      {{/if_helpers}}\n    {{/filter}}\n  </div>\n{{/each}}\n<div class="add-rule">\n  <a href="javascript://" can-click="addFilter" class="add-filter-rule">+ Add New Rule</a>\n</div>\n';
GGRC.Templates['quick_form/confirm_buttons']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid">\n  <div class="span6">&nbsp;</div>\n  <div class="span6">\n    <div class="confirm-buttons">\n      <a class="btn btn-small btn-info preventdoubleclick" data-toggle="confirm" href="javascript://">\n        {{firstnonempty modal_confirm \'Confirm\'}}\n      </a>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['snapshots/dropdown_menu']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#if_helpers \'\\n#if\' is_info_pin \'\\nand #is_allowed_to_map\' page_instance instance \'\\nor #if\' is_info_pin \'\\nand #if\' instance.viewLink \'\\nor #is_allowed\' \'update\' instance \'\\n\' _4_context=\'for\'}}\n    <div class="details-wrap">\n        <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n        </a>\n        <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n\n<!--\nTODO: Temporary disabled until snapshot view is added.\n            {{#is_info_pin}}\n                {{#if instance.viewLink}}\n                    {{#is_allowed "view_object_page" instance}}\n                        <li>\n                            <a href="{{instance.viewLink}}">\n                            <i class="fa fa-long-arrow-right"></i>\n                            View {{instance.class.title_singular}}\n                            </a>\n                        </li>\n                    {{/is_allowed}}\n                {{/if}}\n            {{/is_info_pin}}\n\n            <li>\n                <clipboard-link\n                    data-test-id="dropdown_settings_get_permalink_75e3bf91"\n                    title="Get permalink"\n                    notify="true"\n                    text="{{get_permalink_for_object instance}}" />\n            </li>\n-->\n            <li>\n                <snapshot-individual-update instance="instance">\n                    <a href="javascript://" can-click="updateIt">\n                        <i class="fa fa-refresh"></i>\n                        Update to latest version\n                    </a>\n                </snapshot-individual-update>\n            </li>\n\n            <li>\n                <a href="{{instance.originalLink}}">\n                    <i class="fa fa-long-arrow-right"></i>\n                    View original {{instance.class.title_singular}}\n                </a>\n            </li>\n\n        </ul>\n    </div>\n{{/if_helpers}}';
GGRC.Templates['ggrc_basic_permissions/people_roles/authorizations_by_person_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="tree-structure new-tree">\n  {{#list}}\n    {{#person}}\n    <li data-model="true" {{data \'person\'}} class="tree-item person">\n      <div class="item-main">\n        <div class="openclose">\n          <div class="row-fluid">\n            <div class="span12">\n\n              <ul class="tree-action-list">\n                {{#if roles.length}}\n                  <li>\n                    <div class="item-data">\n                      <div class="tree-title-area">\n                        <span class="role" title="{{#roles}}{{role.permission_summary}} {{/roles}}">\n                          {{#if_helpers \'\\n                            #if_equals\' roles.0.role.permission_summary \'Mapped\' \'\\n                            and ^if_equals\' roles.length 1}}\n                            {{roles.1.role.permission_summary}}\n                          {{else}}\n                            {{roles.0.role.permission_summary}}\n                          {{/if_helpers}}\n                          {{#roles.1}}\n                            {{#if_in_map roles \'role.permission_summary\' \'Mapped\'}}\n                              {{^if_equals roles.length 2}}\n                                + {{sum roles.length \'-2\'}}\n                              {{/if_equals}}\n                            {{else}}\n                              + {{sum roles.length \'-1\'}}\n                            {{/if_in_map}}\n                          {{/roles.1}}\n                        </span>\n                      </div>\n                    </div>\n                  </li>\n                  {{else}}\n                  <li>\n                    <div class="item-data">\n                      <div class="tree-title-area">\n                        <span class="no-role">\n                          No Role\n                        </span>\n                      </div>\n                    </div>\n                  </li>\n                {{/if}}\n              </ul>\n\n              <div class="item-data">\n                <div class="tree-title-area">\n                  {{{render \'/static/mustache/people/popover.mustache\' person=this}}}\n                    <span class="email">{{email}}</span>\n                </div>\n              </div>\n\n            </div>\n          </div>\n        </div>\n      </div>\n\n      <div class="tier-2-info item-content">\n        <div class="tier-2-info-content">\n          <div class="row-fluid">\n            <div class="span6">\n              <div class="profile-info">\n                <ul>\n                  {{#if_match name \'\\\\S\'}}\n                    {{#name}}\n                      <li>\n                        <span class="name">\n                          <strong>{{name}}</strong>\n                        </span>\n                      </li>\n                    {{/name}}\n                  {{/if_match}}\n                  {{#if_match email \'\\\\S\'}}\n                    {{#email}}\n                      <li>\n                        <span class="email">\n                          <strong>Email:</strong> <a href=\'mailto:{{email}}\' rel=\'tooltip\' title=\'{{email}}\' target="_blank">{{email}}</a>\n                        </span>\n                      </li>\n                    {{/email}}\n                  {{/if}}\n                  {{#if_match company \'\\\\S\'}}\n                    {{#company}}\n                      <li>\n                        <span class="company">\n                          <strong>Company:</strong> {{company}}\n                        </span>\n                      </li>\n                    {{/company}}\n                  {{/if_match}}\n                  <li>\n                    <ul>\n                    {{#roles}}\n                      {{#if_helpers \'\\n                        ^if_equals\' role.permission_summary \'Mapped\' \'\\n                        or #if_equals\' roles.length 1}}\n                        <li class="role">\n                          {{role.permission_summary}}{{#if_equals role.permission_summary \'Auditor\'}}: {{#with_mapping \'audit_via_context\' user_role}}{{audit_via_context.0.instance.title}}{{/with_mapping}}{{/if_equal}}\n                        </li>\n                      {{/if_helpers}}\n                    {{/roles}}\n                    </ul>\n                  </li>\n                </ul>\n              </div>\n            </div>\n            <div class="span6 info person-info">\n              <div>\n                {{#is_allowed \'create\' \'delete\' \'UserRole\' context=parent_instance.context.id}}\n                <div class="pull-right">\n                  <a class="info-edit" data-modal-class="modal-wide" data-placement="left" data-toggle="user-roles-modal-selector" data-modal-selector-options="user_roles" data-person_id="{{ id }}" href="javascript://" rel="tooltip" title="Edit Authorizations">\n                    <i class="fa fa-key"></i> <span>Edit Authorizations</span>\n                  </a>\n                </div>\n                {{/is_allowed}}\n              </div>\n              {{#roles}}\n              {{#if_helpers \'\\n                #if_equals\' role.permission_summary \'Mapped\' \'\\n                and #if\' allow_mapping \'\\n                and #if\' user_role.result.mappings \'\\n                and #is_allowed_all\' \'delete\' user_role.result.mappings}}\n                <a href="javascript://" class="info-action unmap pull-right" data-toggle="unmap">\n                  {{#user_role.result}}<span class="result" {{data \'result\'}}></span>{{/user_role.result}}\n                  <i class="fa fa-ban"></i>\n                  Unmap\n                </a>\n              {{/if_helpers}}\n              {{/roles}}\n            </div>\n          </div>\n          {{#roles}}\n            {{#if_equals role.permission_summary \'Mapped\'}}\n              {{{render \'/static/mustache/base_objects/mappings_detail.mustache\' result=user_role.result parent_instance=parent_instance}}}\n            {{/if_equals}}\n          {{/roles}}\n          <div class="row-fluid">\n            <div class="span6">\n              <div>\n                <a href="/people/{{id}}" class="info-action">\n                  <i class="fa fa-long-arrow-right"></i>\n                  Open Profile Page\n                </a>\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n    </li>\n    {{/person}}\n  {{/list}}\n  {{#if allow_mapping}}\n  {{#is_allowed_to_map parent_instance model.shortName}}\n  <li class="tree-footer tree-item tree-item-add">\n    <div class="row-fluid">\n      <div class="span12">\n\n        <a\n          href="javascript://"\n          rel="tooltip"\n          data-placement="left"\n          data-toggle="unified-mapper"\n          data-join-mapping="{{mapping}}"\n          data-join-option-type="{{model.shortName}}"\n          data-join-object-id="{{parent_instance.id}}"\n          data-join-object-type="{{parent_instance.class.shortName}}"\n          data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}">\n          Map {{model.title_singular}}\n        </a>\n\n      </div>\n    </div>\n  </li>\n  {{/is_allowed_to_map}}\n  {{/if}}\n</ul>\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/authorizations_by_person_tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n    <li class="tree-item" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n      <div class="item-main" {{#instance}}{{data \'person\'}}{{/instance}}>\n        <div class="item-wrap">\n          {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n          <div class="select">\n            <div class="item-data">\n              <div class="row-fluid">\n                <div class="span4">\n                  <div class="title tree-title-area">\n                    {{{render \'/static/mustache/people/popover.mustache\' person=instance}}}\n                  </div>\n                </div>\n                <div class="span2">\n                  <div class="email tree-title-area">\n                    <span class="email">{{instance.email}}</span>\n                  </div>\n                </div>\n                <div class="span2">\n                  <div class="role tree-title-area">\n                    {{#with_program_roles_as "roles" result}}\n                    {{#if roles.length}}\n                      <li>\n                        <div class="item-data">\n                          <div class="tree-title-area">\n                            <span class="role" title="{{#roles}}{{role.permission_summary}} {{/roles}}"\n                              rel="tooltip" data-placement="left" data-original-title="{{.}} ">\n                              {{#if_helpers \'\\n                                #if_equals\' roles.0.role.permission_summary \'Mapped\' \'\\n                                and ^if_equals\' roles.length 1}}\n                                {{pretty_role_name roles.1.role.permission_summary}}\n                              {{else}}\n                                {{pretty_role_name roles.0.role.permission_summary}}\n                              {{/if_helpers}}\n                              {{#roles.1}}\n                                {{#if_in_map roles \'role.permission_summary\' \'Mapped\'}}\n                                  {{^if_equals roles.length 2}}\n                                    + {{sum roles.length \'-2\'}}\n                                  {{/if_equals}}\n                                {{else}}\n                                  + {{sum roles.length \'-1\'}}\n                                {{/if_in_map}}\n                              {{/roles.1}}\n                            </span>\n                          </div>\n                        </div>\n                      </li>\n                      {{else}}\n                      <li>\n                        <div class="item-data">\n                          <div class="tree-title-area">\n                            <span class="no-role">\n                              No Role\n                            </span>\n                          </div>\n                        </div>\n                      </li>\n                    {{/if}}\n                    {{/with_program_roles_as}}\n                  </div>\n                </div>\n\n                <div class="span4">\n                  <div class="show-details">\n                    <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                  </div>\n                </div>\n              </div>\n            </div><!-- item-data end -->\n          </div><!-- select end -->\n        </div><!-- item-wrap end -->\n      </div><!-- item-main end -->\n\n      {{#if expanded}}\n        <div class="tier-2-info item-content">\n          <div class="tier-2-info-content">\n            {{#if draw_children}}\n              {{#child_options}}\n                <div class="inner-tree {{^if list.length}}hide{{/list}}">\n                  <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n                </div>\n              {{/child_options}}\n            {{/if}}\n          </div>\n        </div>\n      {{/if}}\n    </li>\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/authorizations_by_person_tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if allow_mapping}}\n{{#is_allowed_to_map parent_instance model.shortName}}\n<div class="tree-item-add">\n  <a\n    class="btn btn-small btn-draft"\n    href="javascript://"\n    rel="tooltip"\n    data-placement="right"\n    data-toggle="unified-mapper"\n    data-join-mapping="{{mapping}}"\n    data-join-option-type="{{model.shortName}}"\n    data-join-object-id="{{parent_instance.id}}"\n    data-join-object-type="{{parent_instance.class.shortName}}"\n    data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}">\n    <i class="fa fa-plus-circle"></i>\n  </a>\n</div>\n{{/is_allowed_to_map}}\n{{/if}}\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/authorizations_modal_actions']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="widget-action pull-right" rel="tooltip" title="Manage Authorizations">\n  <a href="javascript://" data-toggle="user-roles-modal-selector" data-modal-selector-options="user_roles" data-list-target="refresh" data-modal-class="modal-wide">\n    <i class="fa fa-person"></i>\n  </a>\n</div>\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/base_modal']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!<div id=\'{ id }\' class=\'modal modal-selector fade hide\' tabindex=\'-1\'>}}\n    <div class="modal-header">\n      <a class="modal-dismiss pull-right" href="javascript://" data-dismiss="modal">\n        <i class="fa fa-times black"></i>\n      </a>\n      <h2>\n        {{ modal_title }}\n      </h2>\n    </div>\n    <div class="modal-body">\n      <div class="row-fluid">\n        <div class="span12 object_detail_column">\n          {{#selected_object}}\n            {{{ renderLive object_detail_view }}}\n          {{/selected_object}}\n        </div>\n      </div>\n      <div class="row-fluid">\n        <div class="span12">\n          {{#if selected_object}}\n            <div class="option_column">\n              {{{ renderLive option_column_view }}}\n            </div>\n          {{/if}}\n        </div>\n      </div>\n    </div>\n    <div class="modal-footer">\n      <div class="row-fluid">\n        <div class="span6">\n          &nbsp;\n        </div>\n        <div class="span6">\n          <div class="confirm-buttons">\n            {{#options.0}}\n              <a class="btn btn-small btn-success preventdoubleclick" xdata-toggle="modal-submit" data-dismiss="modal" href="javascript://">\n                Save\n              </a>\n            {{/options}}\n          </div>\n        </div>\n      </div>\n    </div>\n{{!</div>}}\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/audit_modal']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!<div id=\'{ id }\' class=\'modal modal-selector fade hide\' tabindex=\'-1\'>}}\n    <div class="modal-header">\n      <a class="modal-dismiss pull-right" href="javascript://" data-dismiss="modal">\n        <i class="fa fa-times black"></i>\n      </a>\n      <h2>\n        {{ modal_title }}\n      </h2>\n    </div>\n    <div class="modal-body">\n      <form action="javascript://">\n        <div class="row-fluid">\n        <div class="span8">\n          <label>\n            Choose Auditor\n            <span class="required">*</span>\n          </label>\n          <i class="fa fa-person green pull-left icon-field"></i>\n          {{! `with_auditors` requires `authorizations` mapping, so preload it }}\n          {{#with_mapping \'authorizations\' instance}}\n          {{#with_auditors instance}}\n            {{#using auditor=auditors.0.person}}\n            <input tabindex="10" type="text" name="auditor.email" data-lookup="Person" class="span8" value="{{auditor.email}}">\n            {{/using}}\n          {{else}}\n            <input tabindex="10" type="text" name="auditor.email" data-lookup="Person" class="span8" value="">\n          {{/with_auditors instance}}\n          {{/with_mapping \'authorizations\' instance}}\n          <p><small><em><strong>NOTE:</strong> Auditor will be able to: add, edit and update Requests and Responses; to view Evidence and to view Audit Info.</em></small></p>\n        </div>\n        </div>\n      </form>\n    </div>\n    <div class="modal-footer">\n      <div class="row-fluid">\n        <div class="span3">\n          <div class="deny-buttons">\n            <a tabindex="22" class="btn" data-dismiss="modal-reset" href="javascript://">Cancel</a>\n\n          </div>\n        </div>\n        <div class="span3">\n          &nbsp;\n        </div>\n        <div class="span6">\n          <div class="confirm-buttons">\n            <a tabindex="24" class="btn btn-small btn-success preventdoubleclick" data-toggle="modal-submit" href="javascript://">Save</a>\n          </div>\n        </div>\n      </div>\n    </div>\n{{!</div>}}\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/object_column']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="search-title">\n  <div class="row-fluid">\n    <div class="span12">\n      <h4>\n        Users ({{ objects.length }})\n      </h4>\n    </div>\n  </div>\n</div>\n<div class="selector-list">\n  <ul class="new-tree">\n    {{#objects}}\n      <li class="tree-item business" {{ data \'object\' }} data-id="{{ id }}">\n        <div class="item-main">\n          <div class="simple-link">\n            <div class="row-fluid">\n              <div class="span12">\n                <div class="item-data">\n                  <div class="tree-title-area">\n                    {{{render \'/static/mustache/people/popover.mustache\' person=this}}}\n                  </div>\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n      </li>\n    {{/objects}}\n  </ul>\n</div>\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/object_detail']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="selector-info">\n\n  <div class="selector-person-info">\n    <h4>User</h4>\n    <h3>\n      {{{render \'/static/mustache/people/popover.mustache\' person=this}}}\n      <span class="email">\n        <a href="mailto:{{ email }}">\n          {{ email }}\n        </a>\n      </span>\n    </h3>\n  </div>\n\n</div>\n\n\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/option_column']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="search-title">\n  <div class="row-fluid">\n    <div class="span6">\n      <h4>\n        Role\n      </h4>\n    </div>\n    <div class="span6">\n      <div class="guide">\n        &nbsp;\n      </div>\n    </div>\n  </div>\n</div>\n<div class="selector-list people-selector">\n  <ul>\n    {{#options}}\n    <li {{ data \'option\' }} data-id="{{ id }}">\n      <label>\n        {{#if_equals selected_id id}}\n          <input type="radio" checked="checked" name="map" rel="tooltip" title="map">\n        {{else}}\n          <input type="radio" name="map" rel="tooltip" title="map">\n        {{/if_equals}}\n        {{pretty_role_name name}}\n      </label>\n      <div class="role-description">\n        <p>{{{description}}}</p>\n        {{{render_hooks \'Role.option_detail\'}}}\n        {{#scope}}<p><strong>Role Scope:</strong> {{role_scope scope}}</p>{{/scope}}\n      </div>\n    </li>\n    {{/options}}\n  </ul>\n</div>\n';
GGRC.Templates['ggrc_basic_permissions/people_roles/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n  {{#is_info_pin}}\n    <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n  {{/is_info_pin}}\n\n  <div class="info-pane-utility">\n    <div class="details-wrap">\n      <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n        <span class="bubble"></span>\n        <span class="bubble"></span>\n        <span class="bubble"></span>\n      </a>\n      <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n        {{#is_allowed \'create\' \'delete\' \'UserRole\' context=options.parent_instance.context.id}}\n        {{#is_info_pin}}\n        {{#if_equals options.mapping "mapped_and_or_authorized_people"}}\n          <li>\n            <a data-modal-class="modal-wide"\n               data-modal-selector-options="user_roles"\n               data-person_id="{{instance.id}}"\n               data-placement="left"\n               data-toggle="user-roles-modal-selector"\n               href="javascript://">\n              <i class="fa fa-role color"></i>\n              Edit Authorizations\n            </a>\n          </li>\n        {{/options.mapped_and_or_authorized_people}}\n        {{/is_info_pin}}\n        {{/is_allowed}}\n        <li class="border-bottom">\n          <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_for_object instance}}" />\n        </li>\n        {{^if_instance_of options.parent_instance \'Audit\'}}\n          {{#with_program_roles_as "roles" result}}\n            {{#if result.mappings}}\n              {{#is_allowed_all \'delete\' result.mappings}}\n                <li>\n                  <a href="javascript://" class="unmap" data-toggle="unmap">\n                    {{#result}}<span class="result" {{data \'result\'}}></span>{{/result}}\n                    <i class="fa fa-ban"></i>\n                    Unmap\n                  </a>\n                </li>\n              {{/is_allowed_all}}\n            {{/if}}\n          {{/if_instance_of}}\n        {{/with_program_roles_as}}\n        <li>\n          <a href="/people/{{instance.id}}">\n            <i class="fa fa-long-arrow-right"></i>\n            Open Profile Page\n          </a>\n        </li>\n      </ul>\n    </div>\n  </div>\n\n  <div class="tier-content">\n    <div class="pane-header">\n      <div class="row-fluid wrap-row">\n        {{#if_match instance.name \'\\\\S\'}}\n          {{#instance.name}}\n            <div class="span6">\n              <h6>Name</h6>\n              <h3>{{instance.name}}</h3>\n            </div>\n          {{/instance.name}}\n        {{/if_match}}\n\n        {{#if_match instance.email \'\\\\S\'}}\n          {{#instance.email}}\n            <div class="span3">\n              <h6>Email</h6>\n              <p>\n                <a href=\'mailto:{{instance.email}}\' target="_blank">\n                  {{instance.email}}\n                </a>\n              </p>\n            </div>\n          {{/instance.email}}\n        {{/if}}\n      </div>\n    </div>\n\n    <div class="row-fluid wrap-row">\n      <div class="span6">\n        <h6>Authorizations</h6>\n        <p>\n          {{#with_program_roles_as "roles" result}}\n            {{#roles}}\n              {{#if_helpers \'\\n                ^if_equals\' role.permission_summary \'Mapped\' \'\\n                or #if_equals\' roles.length 1}}\n                  {{role.permission_summary}}\n                  {{#if_equals role.permission_summary \'Auditor\'}}:\n                    {{#with_mapping \'audit_via_context\' user_role}}\n                      <span class="role">\n                        {{audit_via_context.0.instance.title}}\n                      </span>\n                    {{/with_mapping}}\n                  {{/if_equals}}\n              {{/if_helpers}}\n            {{/roles}}\n          {{/with_program_roles_as}}\n        </p>\n      </div>\n      {{#if_match instance.company \'\\\\S\'}}\n        {{#instance.company}}\n          <div class="span6">\n            <h6>Company</h6>\n            {{instance.company}}\n          </div>\n        {{/instance.company}}\n      {{/if_match}}\n\n    </div>\n\n    <div class="row-fluid wrap-row">\n      <div class="span12">\n        {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n      </div>\n    </div>\n\n  </div>\n\n</section>\n';
GGRC.Templates['dashboard/lhn_risk_assessments']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed "read" "RiskAssessment" context="any"}}\n<h2 class="risk">Risk Assessment</h2>\n<ul class="top-level">\n  <li class="risk accordion-group" data-tooltip-view="null" data-template="/static/mustache/dashboard/lhn_search_result_risk_assessments.mustache" data-actions="/static/mustache/dashboard/lhn_search_actions_risk_assessments.mustache" data-model-name="RiskAssessment">\n    <a class="risk" href="javascript://" data-object-singular="RiskAssessment" data-parent=".top-level">\n      <i class="fa fa-risk_assessment color"></i>\n      Risk Assessments\n      <small>(<span class="item-count"></span>)</small>\n    </a>\n    <div class="content">\n      <ul class="sub-level"></ul>\n      <ul class="sub-actions"></ul>\n    </div>\n  </li>\n</ul>\n{{/is_allowed}}\n';
GGRC.Templates['dashboard/lhn_search_result_risk_assessments']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#list}}\n<li class="{{class.category}}" data-model="true" {{data \'model\'}}>\n  <a href="{{ggrc_config_value "RISK_ASSESSMENT_URL"}}/riskDetails?risk_assessments_id={{id}}" class="show-extended" target="_blank">\n    <div class="row-fluid">\n      <div class="span8 lhs-main-title">\n        <span class="lhs-item">\n          {{firstnonempty title name email}}\n        </span>\n      </div>\n      <div class="span1 lhs-people">\n      {{^contact}}\n        <i class="fa fa-person red"></i>\n      {{/contact}}\n      </div>\n    </div>\n  </a>\n</li>\n{{/list}}\n{{^list}}\n<li class="spinner">\n</li>\n{{/list}}\n\n{{#list.is_loading}}\n<li class="spinner">\n  <span {{attach_spinner \'{ "radius": 4, "length": 4, "width": 2 }\' \'display:inline-block; top: -3px; left: 30px;\' }}></span>\n</li>\n{{/list.is_loading}}\n';
GGRC.Templates['dashboard/lhn_search_actions_risk_assessments']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'create\' model.shortName context=\'any\'}}\n<li class="add-new" data-ui-autocomplete-item="">\n  <a data-object-plural="{{model.table_plural}}" data-form-target="redirect" data-modal-class="modal-wide" href="javascript://" data-object-singular="{{model.model_singular}}" data-toggle="modal-ajax-form" data-modal-reset="reset" >\n    + Create New\n  </a>\n</li>\n{{/is_allowed}}\n';
GGRC.Templates['risk_assessments/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <li class="tree-item {{#if instance.snapshot}}snapshot{{/if}} "\n    data-object-id="{{instance.id}}"\n    data-object-type="{{instance.class.table_singular}}"\n    {{addclass "t-" instance.workflow_state}}>\n\n    <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-wrap">\n       {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span{{display_options.title_width}}">\n                <div class="title tree-title-area" title="{{instance.title}}">\n                  {{#is_subtree}}\n                    <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                  {{/is_subtree}}\n                  {{{firstnonempty instance.title instance.description_inline instance.name instance.email \'\'}}}\n                </div>\n              </div>\n\n              <div class="span{{display_options.selectable_width}}">\n                <div class="row-fluid">\n                  {{#display_attr_list}}\n                    <div class="span{{display_options.selectable_attr_width}}">\n                      {{#if_equals attr_type \'custom\'}}\n                        <div class="custom tree-title-area">\n                          {{#get_custom_attr_value this instance}}\n                            {{! because the object can currently only be a\n                                person there is no need to switch }}\n                            {{#using person=object}}\n                              {{>\'/static/mustache/people/popover.mustache\'}}\n                            {{/using}}\n                          {{/get_custom_attr_value}}\n                        </div>\n                      {{else}}\n                        <div class="owner tree-title-area">\n                          {{#instance}}\n                            {{#switch attr_name}}\n                              {{#case \'ra_manager\'}}\n                                <span>\n                                  {{#using contact=instance.ra_manager}}\n                                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n                                  {{/using}}\n                                </span>\n                              {{/case}}\n                              {{#case \'ra_counsel\'}}\n                                <span>\n                                  {{#using contact=instance.ra_counsel}}\n                                    {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n                                  {{/using}}\n                                </span>\n                              {{/case}}\n\n                              {{#default}}\n                                {{#if_helpers \'\\n                                  #if_equals\' attr_name \'url\' \'\\n                                  or #if_equals\' attr_name \'reference_url\'}}\n                                    <a class="url" href="{{get_url_value attr_name instance}}" target="_blank">\n                                      {{get_default_attr_value attr_name instance}}\n                                    </a>\n                                {{else}}\n                                  {{get_default_attr_value attr_name instance}}\n                                {{/if_helpers}}\n                              {{/default}}\n                            {{/switch}}\n                          {{/instance}}\n                        </div>\n                      {{/if_equals}}\n                    </div>\n                  {{/display_attr_list}}\n                </div>\n              </div>\n\n              <div class="span{{display_options.action_width}}">\n                <div class="show-details">\n                  {{#child_options}}\n                    {{#if add_item_view}}\n                      {{{renderLive add_item_view}}}\n                    {{/if}}\n                  {{/child_options}}\n                  <view-object-buttons instance="instance" open-is-hidden="is_subtree" />\n                </div>\n                <ul class="tree-action-list">\n                  {{#infer_roles instance}}\n                    <li>\n                      <i class="fa fa-person" rel="tooltip" data-placement="left" data-original-title="{{.}} "></i>\n                    </li>\n                  {{/infer_roles}}\n                  <li>\n                    {{{renderLive \'/static/mustache/base_objects/states.mustache\'}}}\n                  </li>\n                </ul>\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n    {{#if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#if draw_children}}\n          {{#prune_context}} {{! this line is just chopping the context stack down to one element}}\n              {{#child_options}}\n                <div class="inner-tree">\n                  <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n                </div>\n              {{/child_options}}\n          {{/prune_context}}\n        {{/if}}\n      </div>\n    </div>\n    {{/if}}\n  </li>\n';
GGRC.Templates['risk_assessments/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#if allow_mapping_or_creating}}\n{{#if_instance_of parent_instance \'Program\'}}\n    {{#if allow_creating}}\n    {{#is_allowed \'update\' parent_instance}}\n      <a\n        href="javascript://"\n        rel="tooltip"\n        class="section-create action-button create-button"\n        data-placement="left"\n        data-original-title="Create {{model.title_singular}}"\n        data-toggle="modal-ajax-form"\n        data-modal-reset="reset"\n        data-modal-class="modal-wide"\n        data-object-singular="RiskAssessment"\n        data-object-plural="risk_assessments"\n        data-object-params=\'{ "{{parent_instance.class.table_singular}}": { "id" : {{parent_instance.id}}, "title" : "{{parent_instance.title}}" }, "context": { "id" : {{firstnonempty parent_instance.context.id "null"}}, "href" : "{{parent_instance.context.href}}", "type" : "{{parent_instance.context.type}}" } }\'>\n        Create New\n      </a>\n    {{/is_allowed}}\n    {{/if}}\n{{else}}\n    {{#is_allowed_to_map parent_instance model.shortName}}\n      <a\n        href="javascript://"\n        class="action-button create-button"\n        rel="tooltip"\n        data-toggle="modal-ajax-form"\n        data-modal-reset="reset"\n        data-modal-class="modal-wide"\n        data-object-singular="RiskAssessment"\n        data-object-plural="risk_assessments"\n        data-object-params=\'{ "{{parent_instance.class.table_singular}}": { "id" : {{parent_instance.id}}, "title" : "{{parent_instance.title}}" }, "context": { "id" : {{firstnonempty parent_instance.context.id "null"}}, "href" : "{{parent_instance.context.href}}", "type" : "{{parent_instance.context.type}}" } }\'\n        data-placement="right"\n        data-original-title="Create {{model.title_singular}}">\n        Create New\n      </a>\n    {{/is_allowed_to_map}}\n{{/if_instance_of}}\n{{/if}}\n';
GGRC.Templates['risk_assessments/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n  {{#is_info_pin}}\n    <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n  {{/is_info_pin}}\n\n  {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n  <div class="tier-content">\n    {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n    {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n\n    <div class="row-fluid wrap-row">\n      <div class="span12">\n        <h6>Notes</h6>\n        <div class="rtf-block">\n          {{{firstnonempty instance.notes \'<span class="empty-message">None</span>\'}}}\n        </div>\n      </div>\n    </div>\n\n    <div class="row-fluid wrap-row">\n      <div class="span4">\n        <h6>Supporting files</h6>\n        <mapping-tree-view\n          parent-instance="instance"\n          mapping="documents"\n          item-template="/static/mustache/risk_assessments/documents.mustache"\n          >\n        </mapping-tree-view>\n        {{#is_allowed \'update\'}}\n          <ggrc-gdrive-picker-launcher instance="instance" link_text="Attach supporting file" click_event="trigger_upload">\n          </ggrc-gdrive-picker-launcher>\n        {{/is_allowed}}\n      </div>\n      <div class="span4">\n        <h6>Risk Manager</h6>\n        {{#using person=instance.ra_manager}}\n          {{{render \'/static/mustache/people/popover.mustache\'}}}\n        {{/using}}\n      </div>\n      <div class="span4">\n        <h6>Risk Counsel</h6>\n        {{#using person=instance.ra_counsel}}\n          {{{render \'/static/mustache/people/popover.mustache\'}}}\n        {{/using}}\n      </div>\n    </div>\n\n    <div class="custom-attr-wrap">\n      <div class="row-fluid">\n        <div class="span12">\n          <div class="info-expand">\n            <a class="show-hidden-fields info-show-hide" href="javascript://">\n              <span class="out">\n                <i class="fa fa-caret-right"></i>\n                Show\n              </span>\n              <span class="in">\n                <i class="fa fa-caret-down"></i>\n                Hide\n              </span>\n              Advanced\n            </a>\n          </div>\n        </div>\n      </div><!-- row-fluid end -->\n      <div class="hidden-fields-area" style="display:none;">\n        <div class="row-fluid wrap-row">\n          <div class="span4">\n            <h6>Code</h6>\n            <p>\n              {{{instance.slug}}}\n            </p>\n          </div>\n          <div class="span4">\n            <h6>Start Date</h6>\n            {{#if instance.start_date}}\n              <p>\n                {{localize_date instance.start_date}}\n              </p>\n            {{else}}\n              <span class="empty-message">None</span>\n            {{/if}}\n          </div>\n          <div class="span4">\n            <h6>End Date</h6>\n            {{#if instance.end_date}}\n              <p>\n                {{localize_date instance.end_date}}\n              </p>\n            {{else}}\n              <span class="empty-message">None</span>\n            {{/if}}\n          </div>\n        </div><!-- row-fluid end -->\n      </div><!-- hidden-fields-area end -->\n    </div><!-- custom-attr-wrap end -->\n\n  </div>\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n</div>\n';
GGRC.Templates['risk_assessments/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span8">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_singular}} a summary that is easy to search for and indicates the main goals of this {{model.model_singular}}"></i>\n      </label>\n      <input class="input-block-level required" id="risk-assessment-title" placeholder="Enter Title" name="title" type="text" value="{{instance.title}}" tabindex="1" autofocus>\n      <br>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span3">\n      <a data-id="hide_effective_date_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      <datepicker\n        label="Effective Date"\n        date="instance.start_date"\n        set-max-date="instance.end_date"\n        required="{{true}}"\n        />\n    </div>\n    <div class="span3">\n      <a data-id="hide_stop_date_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      <datepicker\n        label="End Date"\n        date="instance.end_date"\n        required="{{true}}"\n        />\n    </div>\n    <div class="span3 hidable">\n      {{#using manager=instance.ra_manager model_singular=model.model_singular}}\n      <label>\n        Risk Manager\n        <i class="fa fa-question-circle" rel="tooltip" title="This is the person in charge of this {{model_singular}}."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input tabindex="4" class="input-block-level" name="ra_manager.email" data-lookup="Person" placeholder="Enter email address" type="text" value="{{firstexist manager.email \'\'}}" />\n      {{/using}}\n      <br>\n    </div>\n    <div class="span3 hidable">\n      {{#using counsel=instance.ra_counsel model_singular=model.model_singular}}\n      <label>\n        Risk Counsel\n        <i class="fa fa-question-circle" rel="tooltip" title="This is the counsel in charge of this {{model_singular}}."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input tabindex="5" class="input-block-level" name="ra_counsel.email" data-lookup="Person" placeholder="Enter email address" type="text" value="{{firstexist counsel.email \'\'}}" />\n      {{/using}}\n      <br>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Describe the Risk Assessment."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea id="risk_assessment_description" class="span12 double wysihtml5" name="description" placeholder="Enter Description" tabindex="6">{{{instance.description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Notes"></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea id="risk_assessment_notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes" tabindex="7">{{{instance.notes}}}</textarea>\n      </div>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div data-id="code_hidden" class="span4 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip" title="The gGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n        <a data-id="hide_code_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input data-id="code_txtbx" tabindex="8" class="input-block-level" name="slug" placeholder="RISKASSESSMENT-XXX" type="text" value="{{instance.slug}}">\n    </div>\n  </div>\n</form>\n';
