GGRC.Templates['risks/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip"\n           title="Give new Risks a name that\'s easy to search for and indicates the main goals of this Risk."></i>\n      </label>\n      <input tabindex="1"\n             class="input-block-level"\n             placeholder="Enter Title"\n             name="title"\n             type="text"\n             value="{{instance.title}}" autofocus>\n      {{#instance.computed_errors.title}}\n        <label class="help-inline warning">{{this}}</label>\n      {{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.description}}field-failure{{/instance.computed_errors.description}}">\n      <label>\n        Description\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip"\n           title="Provide more details on the purpose of this Risk and provide context for how and when this Risk might be used."></i>\n      </label>\n      {{#instance.computed_errors.description}}\n        <label class="help-inline warning">{{this}}</label>\n      {{/instance.computed_errors.description}}\n      <div class="wysiwyg-area">\n        <textarea tabindex="2"\n                  id="program_description"\n                  class="span12 triple wysihtml5"\n                  name="description"\n                  placeholder="Enter Description">{{{instance.description}}}</textarea>\n      </div>\n    </div>\n    <div class="span6 hide-wrap">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n            {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div data-id="note_hidden" class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip"\n           title="Append simple text or html notes here."></i>\n        <a data-id="hide_note_lk" href="javascript://" class="field-hide"\n           tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea data-id="note_txtbx" tabindex="6" id="notes"\n                  class="span12 double wysihtml5"\n                  name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="url_hidden" class="span12 hidable">\n          <label>\n            Risk URL\n            <i class="fa fa-question-circle" rel="tooltip"\n               title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a data-id="hide_url_lk" href="javascript://"\n               class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="url_txtbx" tabindex="7"\n                 class="input-block-level" name="url"\n                 placeholder="http://www.domain.com/"\n                 type="text" value="{{url}}">\n        </div>\n      </div>\n      <div class="row-fluid inner-hide">\n        <div data-id="reference_url_hidden" class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle"\n               rel="tooltip"\n               title="Web links to other references."></i>\n            <a data-id="hide_reference_url_lk"\n               href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input data-id="reference_url_txtbx" tabindex="8"\n                 id="reference_url"\n                 class="input-block-level"\n                 placeholder="https://www.example.com/"\n                 name="reference_url"\n                 type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span4 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip"\n           title="The ZenGRC application will automatically provide codes for new objects. If you would like to override this feature you may manually enter a code of your choosing. You should have a specific reason for doing this."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input tabindex="5" class="input-block-level" name="slug"\n             placeholder="RISK-XXX" type="text" value="{{slug}}">\n    </div>\n    <effective-dates instance="instance"/>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span4 hidable">\n      <label>\n        State\n        <i class="fa fa-question-circle" rel="tooltip"\n           title="Indicates the status of this object."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n        <dropdown options-list="model.statuses"\n                  no-value="true"\n                  no-value-label="---"\n                  name="instance.status"\n                  tabindex="23">\n        </dropdown>\n    </div>\n  </div>\n\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['risks/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    {{> /static/mustache/base_objects/info-pane-utility.mustache}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n      {{{render \'/static/mustache/base_objects/urls.mustache\' instance=instance}}}\n\n      <div class="custom-attr-wrap">\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="info-expand">\n              <a class="show-hidden-fields info-show-hide" href="javascript://">\n                <span class="out">\n                  <i class="fa fa-caret-right"></i>\n                  Show\n                </span>\n                <span class="in">\n                  <i class="fa fa-caret-down"></i>\n                  Hide\n                </span>\n                Advanced\n              </a>\n            </div>\n          </div>\n        </div><!-- row-fluid end -->\n        <div class="hidden-fields-area" style="display:none;">\n          <div class="row-fluid wrap-row">\n            <div class="span4">\n              <h6>Code</h6>\n              <p>\n                {{slug}}\n              </p>\n            </div>\n            <div class="span4">\n              <h6>Effective Date</h6>\n              {{#if start_date}}\n                <p>\n                  {{localize_date start_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n            <div class="span4">\n              <h6>Stop Date</h6>\n              {{#if end_date}}\n                <p>\n                  {{localize_date end_date}}\n                </p>\n              {{else}}\n                <span class="empty-message">None</span>\n              {{/if}}\n            </div>\n          </div><!-- row-fluid end -->\n        </div><!-- hidden-fields-area end -->\n      </div><!-- custom-attr-wrap end -->\n    </div><!-- tier-content end -->\n\n  {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n\n  </section>\n\n  <div class="info-widget-footer">\n    <p>\n      <small>\n        <em>\n          Created at {{date created_at}}\n          &nbsp;&nbsp;&nbsp;&nbsp;\n          Modified by {{#using person=modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}} on {{date updated_at}}\n        </em>\n      </small>\n    </p>\n  </div>\n{{/instance}}\n';
GGRC.Templates['threats/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{!div class="modal-body"}}\n<div class="hideable-holder">\n{{#instance}}\n<form action="javascript://">\n  {{> /static/mustache/base_objects/form_restore.mustache}}\n\n  <div class="row-fluid">\n    <div class="span6 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <label>\n        Title\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Give new {{model.model_plural}} a name that\'s easy to search for and indicates the main goals of this {{model.model_singular}}."></i>\n      </label>\n      <input tabindex="1" class="input-block-level" placeholder="Enter Title" name="title" type="text" value="{{title}}" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Description\n        <i class="fa fa-question-circle" rel="tooltip" title="Provide more details on the purpose of this {{model.model_singular}} and provide context for how and when this {{model.model_singular}} might be used."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="2" id="system_description" class="span12 double wysihtml5" name="description" placeholder="Enter Description">{{{description}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div data-id="owner_hidden" class="span12 hidable bottom-spacing">\n          {{> /static/mustache/people/owners_modal_connector.mustache}}\n        </div>\n      </div>\n      {{> /static/mustache/base_objects/modal_contact_fields.mustache}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span6 hidable">\n      <label>\n        Notes\n        <i class="fa fa-question-circle" rel="tooltip" title="Append simple text or html notes here."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea tabindex="4" id="notes" class="span12 double wysihtml5" name="notes" placeholder="Enter Notes">{{{notes}}}</textarea>\n      </div>\n    </div>\n\n    <div class="span6 hide-wrap hidable">\n      <div class="row-fluid inner-hide">\n        <div class="span12 hidable">\n          <label>\n            Threat URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web link to the Sites page / {{model.model_singular}} documentation."></i>\n            <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input tabindex="5" class="input-block-level" name="url" placeholder="http://www.domain.com/" type="text" value="{{url}}">\n        </div>\n      </div>\n\n      <div class="row-fluid inner-hide">\n        <div class="span12 hidable">\n          <label>\n            Reference URL\n            <i class="fa fa-question-circle" rel="tooltip" title="Web links to other references."></i>\n            <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n          </label>\n          <input tabindex="6" id="reference_url" class="input-block-level" placeholder="https://www.example.com/" name="reference_url" type="text" value="{{reference_url}}">\n        </div>\n      </div>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span4 hidable">\n      <label>\n        Code\n        <i class="fa fa-question-circle" rel="tooltip" title="The ZenGRC application will automatically provide codes for new objects.  If you would like to override this feature you may manually enter a code of your choosing.  You should have a specific reason for doing this."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n      <input tabindex="7" class="input-block-level" name="slug" placeholder="Threat-XXX" type="text" value="{{slug}}">\n    </div>\n    <effective-dates instance="instance"/>\n  </div>\n  <div class="row-fluid">\n    <div class="span4 hidable">\n      <label>\n        State\n        <i class="fa fa-question-circle" rel="tooltip" title="Indicates the status of this object."></i>\n        <a href="javascript://" class="field-hide" tabindex="-1">hide</a>\n      </label>\n        <dropdown options-list="model.statuses"\n                  no-value="true"\n                  no-value-label="---"\n                  name="instance.status"\n                  tabindex="23">\n        </dropdown>\n    </div>\n  </div>\n</form>\n{{/instance}}\n</div>\n{{!/div}}\n';
GGRC.Templates['risk_objects/tree_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed_to_map parent_instance model.shortName}}\n{{#if allow_mapping_or_creating}}\n<li class="tree-footer tree-item tree-item-add">\n  <div class="row-fluid">\n    <div class="span12 section-expandable">\n      <a\n        class="section-add section-sticky"\n        href="javascript://"\n        rel="tooltip"\n        data-placement="left"\n        data-toggle="multitype-object-modal-selector"\n        data-join-mapping="{{mapping}}"\n        data-join-object-id="{{parent_instance.id}}"\n        data-join-object-type="{{parent_instance.class.shortName}}"\n        data-exclude-option-types="{{exclude_option_types}}"\n        data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}"\n        >\n        + Add Objects\n      </a>\n    </div>\n  </div>\n</li>\n{{/if}}\n{{/is_allowed_to_map}}\n';
GGRC.Templates['selectors/object_selector_base_modal']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n    <div class="modal-header">\n      <a class="modal-dismiss pull-right" href="javascript://" data-dismiss="modal">\n        <i class="fa fa-times black"></i>\n      </a>\n      <h2>\n        {{#if_equals option_type_count 1}}\n          {{#option_type_menu}}{{#items}}\n              Map {{model_display}} to {{selected_object.class.title_singular}}\n          {{/#items}}{{/option_type_menu}}\n        {{else}}\n          Map Object {{! modal_title }}\n        {{/if_equals}}\n      </h2>\n    </div>\n\n    <!-- the below block adds a options -->\n    <div class="modal-body choose-object-wrap">\n\n      <div class="row-fluid">\n        <div class="span2">\n          {{> "/static/mustache/base_objects/filters/filter_by_type.mustache"}}\n        </div>\n        <div class="span5">\n          {{> "/static/mustache/base_objects/filters/filter_by_text.mustache"}}\n        </div>\n        <div class="span5">\n          {{> "/static/mustache/base_objects/filters/filter_by_person.mustache"}}\n        </div>\n      </div>\n\n      {{> "/static/mustache/base_objects/filters/filter_by_relevance.mustache"}}\n      {{> "/static/mustache/base_objects/filters/modal_filter_actions.mustache"}}\n\n    </div>\n\n    <div class="results">\n      <!-- Header line of the list -->\n      <ul class="new-tree multitype-tree">\n        <li class="tree-item object-filter">\n          <div class="item-main">\n            <div class="item-wrap">\n              <div class="select">\n                <div class="item-data">\n                  <div class="row-fluid">\n\n                    <div class="span7">\n                      <div class="item-data">\n                        <div class="tree-title-area">\n                          <span>\n                            <strong>\n                              {{ options.length }} Objects found\n                            </strong>\n                          </span>\n                        </div>\n                      </div>\n                    </div>\n\n                    <div class="span2 spinner-place-holder">\n                    </div>\n\n                    <div class="span3">\n                      <div class="object-check all-check">\n                        <label>\n                          Select all\n                          <input type="checkbox" value="1" class="object-check-all" id=\'objectAll\'>\n                        </label>\n                      </div>\n                    </div>\n\n                  </div>\n                </div>\n              </div>\n            </div>\n          </div>\n        </li>\n      </ul>\n\n\n      <div class="results-wrap">\n        <span class="info">Define search criteria above to start</span>\n\n        <div class="option_column" {{data \'options\'}}>\n          {{#if option_column_view}}\n            {{{ renderLive option_column_view }}}\n          {{/if}}\n        </div>\n      </div>\n    </div>\n\n    <!-- footer add/cancel buttons-->\n    <div class="modal-footer">\n      <div class="row-fluid">\n        <div class="span6">\n          &nbsp;\n        </div>\n        <div class="span6">\n          <div class="confirm-buttons">\n            {{#is_page_instance}}\n              <span class="error">Can\'t map an object to itself<span>\n            {{/is_page_instance}}\n            <span>\n              <strong> {{items_selected}} objects selected </strong>\n            </span>\n            <a class="btn btn-small btn-success preventdoubleclick\n              {{#if_helpers \'\\n                ^if\' item_selected \'\\n                or #if\' is_page_instance}} disabled {{/if_helpers}}map-button" href="javascript://">\n                    Add Selected\n            </a>\n          </div>\n        </div>\n      </div>\n    </div>\n{{!</div>}}\n';
GGRC.Templates['selectors/object_selector_option_column']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="tree-structure new-tree multitype-tree">\n</ul>\n';
GGRC.Templates['selectors/object_selector_option_items']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each options}}\n  <li class="tree-item task-item" data-object-type="{{instance.class.table_singular}}"{{#instance}}{{ data \'option\' }}{{/instance}} data-id="{{ instance.id }}">\n    <div class="item-main">\n      <div class="item-wrap">\n        {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span11">\n                <div class="tree-title-area">\n                  {{firstexist instance.name instance.email instance.title instance.link instance.description}}\n                  <span class="url-link">\n                    {{firstexist instance.email instance.link}}\n                  </span>\n                </div>\n              </div>\n\n              <div class="span1">\n                <div class="object-check" align="left">\n\n                  <mapper-checkbox\n                    {{#is_allowed_to_map selected_object instance}}allowed-to-map="true"{{/is_allowed_to_map}}\n                    appended="appended"\n                    instance="instance"\n                    instance_id="{{instance.id}}"\n                    instance_type="{{instance.type}}"\n                    is_loading="is_loading"\n                    options="options"\n                    select_state="select_state"\n                    all_selected="mapper.all_selected"\n                    selected="mapper.selected"\n                    mapper="mapper"\n                    is_saving="mapper.is_saving">\n                    <label\n                      {{#not_allowed_to_map}}\n                      data-original-title="You are not allowed to map this object"\n                      rel="tooltip"\n                      data-placement="left"\n                      {{/not_allowed_to_map}}\n                    >\n                      <input\n                        type="checkbox"\n                        class="object-check-single"\n                        can-value="checkbox"\n                        {{#is_disabled}}disabled="disabled"{{/is_disabled}}\n                      >\n                    </label>\n                  </mapper-checkbox>\n                </div>\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        <lazy-openclose>\n          {{#show}}\n            {{#find_template \'tier2_content\' instance}}\n              {{#instance.class.table_singular}}\n                {{{renderLive template}}}\n              {{/instance}}\n            {{else}}\n              <div class="show-description">\n                <div class="row-fluid">\n                  <div class="span12">\n                    <div class="tree-description short">\n                      {{{instance.description}}}\n                    </div>\n                    {{{show_long}}}\n                  </div>\n                </div>\n              </div>\n            {{/find_template}}\n          {{/show}}\n        </lazy-openclose>\n      </div>\n    </div>\n</li>\n{{/each}}\n';
GGRC.Templates['base_objects/task_group_subtree_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#is_allowed \'create\' \'TaskGroupObject\' context=instance.context}}\n{{#if_task_group_assignee_privileges instance}}\n{{^if_equals parent_instance.status \'Inactive\'}}\n<div class="tree-item-add">\n  <a\n    class="btn btn-small btn-draft"\n    rel="tooltip"\n    data-placement="right"\n    data-original-title="Add Object"\n    href="javascript://"\n    data-toggle="unified-mapper"\n    data-modal-class="modal-wide"\n    data-modal-reset="reset"\n    data-join-mapping="objects"\n    data-join-object-id="{{instance.id}}"\n    data-join-object-type="TaskGroup"\n    data-exclude-option-types="">\n    <i class="fa fa-plus-circle"></i>\n  </a>\n</div>\n{{/if_equals}}\n{{/if}}\n{{/is_allowed}}\n';
GGRC.Templates['base_objects/task_group_subtree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n';
GGRC.Templates['base_objects/task_group_subtree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <li data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <div class="item-main objective-selector field-wrap" data-model="true" {{#instance}}{{data "model"}}{{/instance}}>\n      <div class="row-fluid">\n        <div class="span11">\n          <span class="inline-data-content oneline" rel="tooltip" title="{{instance.title}}">\n            <i class="fa fa-{{instance.class.table_singular}} color"></i>\n            {{instance.title}}\n          </span>\n        </div>\n        <div class="span1">\n          {{^if_equals parent_instance.workflow.reify.status \'Inactive\'}}\n            {{#if mappings}}\n              {{#is_allowed_all \'delete\' mappings}}\n                <a href="javascript://" data-toggle="unmap" data-id="{{instance.id}}" data-type="{{instance.class.shortName}}" class="deleteTrigger">\n                  <i class="fa fa-trash"></i>\n                  <span class="result" {{data \'result\'}}></span>\n                </a>\n              {{/is_allowed_all}}\n            {{/if}}\n          {{/if_equals}}\n        </div>\n      </div>\n    </div>\n  </li>\n';
GGRC.Templates['base_objects/approval_link']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div data-object-id="{{instance.id}}"\n    data-object-type="{{instance.class.table_singular}}" {{#instance}}{{data \'model\'}}{{/instance}}>\n  {{#if_equals instance.status \'Final\'}}\n    <i class="fa fa-check-square-o green"></i>\n    <span class="object-approved">\n      {{#last_approved instance}}\n        Reviewed on {{instance.updated_at}} <br />\n        and approved by\n        {{#using person=instance.modified_by}}\n          {{{> \'/static/mustache/people/popover.mustache\'}}}\n        {{/using}}\n      </span>\n      {{/last_approved}}\n  {{else}}\n    {{#with_mapping \'approval_tasks\' instance}}\n      {{#with_review_task}}\n        {{#with_is_reviewer review_task}}\n          {{#if_helpers \'\\n            #owned_by_current_user\' instance \'\\n            or #current_user_is_admin\'}}\n            {{^if is_reviewer}}\n              {{#if_equals review_task.status \'Declined\'}}\n                {{#with_most_recent_declining_task_entry review_task}}\n                  <div>Review was declined on {{#date instance.created_at}}{{/date}}.</div>\n                {{/with_most_recent_declining_task_entry}}\n              {{/if_equals}}\n            {{/if}}\n          {{/if_helpers}}\n          {{#if is_reviewer}}\n            {{#if_equals review_task.status \'Declined\'}}\n              {{#with_most_recent_declining_task_entry review_task}}\n                <div>Review was declined on {{#date instance.created_at}}{{/date}}.</div>\n              {{/with_most_recent_declining_task_entry}}\n            {{/if_equals}}\n            <div {{#review_task}}{{data \'model\'}}{{/review_task}} {{ (el) -> el.ggrc_controllers_quick_form({instance : el.data("model") }) }}>\n              <button class="btn btn-mini btn-success change-task-status {{instance._disabled}} {{#if instance.snapshot}}disabled{{/if}}" data-name="status" data-value="Verified">Approve</button>\n              <a href="javascript://"\n                class="undoable btn btn-mini btn-danger change-task-status {{instance._disabled}} {{#if instance.snapshot}}disabled{{/if}}"\n                data-toggle="modal-ajax-form"\n                data-object-singular="CycleTaskEntry"\n                data-object-params=\'{\\n                  "cycle" : {{review_task.cycle.id}}, \\n                  "cycle_task_group_object_task" : {{review_task.id}}, \\n                  "is_declining_review" : 1, \\n                  "context": {{firstnonempty review_task.context.id "null"}} }\'\n                data-object-plural="cycle_task_entries"\n                data-object-id=""\n                data-placement="right"\n                data-modal-reset="reset"\n                data-modal-class="modal-wide"\n                data-modal-title-override="Decline Review"\n                data-original-title="Decline Review"\n                data-name="status"\n                data-value="Declined"\n                data-also-undo="description">\n                Decline\n              </a>\n              <small><em>Approval applies only to this object and not to mapped objects.</em></small>\n              {{#current_user_is_admin}}\n                {{#with_mapping \'object_tasks\' instance}}\n                <p>\n                  If you wish to change the current reviewer, please click\n                  <a href="#task_widget/cycle_task_group_object_task/{{object_tasks.0.instance.id}}" class="{{#if instance.snapshot}}disabled{{/if}}">here</a>\n                  and change the assignee.\n                </p>\n                {{/with_mapping}}\n              {{/current_user_is_admin}}\n            </div>\n          {{else}}\n            {{#is_allowed "read" "Workflow" context="any"}}\n              {{#with_mapping_count instance \'current_approval_cycles\'}}\n                {{#if_equals count 0}}\n                  {{#is_allowed "update" instance}}\n                    <a href="javascript://" class="non-transparent {{#if instance.snapshot}}disabled{{/if}}" data-toggle="modal-ajax-approvalform" data-object-singular="{{instance.class.model_singular}}" data-object-id="{{instance.id}}">\n                      <i class="fa fa-check-square-o gray"></i>\n                      Submit For Review \n                    </a>\n                  {{else}}\n                    <i>No review in progress</i>\n                  {{/is_allowed}}\n                {{else}}\n                  <i class="fa fa-check-square-o gray"></i>\n                  <span class="setup-link">\n                    Review in progress\n                    {{#current_cycle_assignee instance}}\n                      by <person-info class="inline-block" person-obj="person"></person-info>\n                    {{/current_cycle_assignee}}\n                  </span>\n                {{/if_equals}}\n              {{/with_mapping_count}}\n            {{/is_allowed}}\n          {{/if}}\n        {{/with_is_reviewer}}\n      {{/with_review_task}}\n    {{/with_mapping}}\n  {{/if_equals}}\n</div>\n';
GGRC.Templates['cycles/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<li class="tree-item" {{addclass "t-" instance.status}} {{addclass "t-" instance.overdue}} data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n  <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span6">\n              <div class="title tree-title-area"  title="{{instance.title}}">\n                {{instance.title}}\n              </div>\n            </div>\n\n            <div class="span2">\n              {{#instance.end_date}}\n                <div class="tree-title-area">\n                  {{localize_date instance.end_date}}\n                </div>\n              {{/instance.end_date}}\n            </div>\n            <div class="span4">\n              <ul class="tree-action-list">\n                <li>\n                  <div class="request-control">\n                    {{#using workflow=instance.workflow}}\n                      {{^if_equals workflow.kind "Backlog"}}\n                        <cycle-end-cycle cycle="instance">\n                          {{#is_allowed \'update\' cycle.workflow.reify}}\n                          {{#if cycle.is_current}}\n                            <button class="btn btn-draft btn-small end-cycle">End Cycle</button>\n                          {{/if}}\n                          {{/is_allowed}}\n                        </cycle-end-cycle>\n                      {{/if_equals}}\n                    {{/using}}\n                  </div>\n                </li>\n              </ul>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  {{#if expanded}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n\n      {{#if draw_children}}\n        {{#child_options}}\n          <div class="inner-tree">\n            <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n          </div>\n        {{/child_options}}\n      {{/if}}\n\n    </div>\n  </div>\n  {{/if}}\n</li>\n';
GGRC.Templates['cycles/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{{> "/static/mustache/base_objects/tree_view_filter.mustache"}}}\n\n<header class="header sticky sticky-header tree-header {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n  <div class="row-fluid">\n    <div class="span6">\n      <div class="title-heading oneline">\n        <span class="widget-col-title" data-field="title">\n          Title\n          <i class="fa fa-caret-down"></i>\n        </span>\n      </div>\n    </div>\n    <div class="span2">\n      <div class="oneline">\n        <span class="widget-col-title" data-field="end_date">\n          End Date\n          <i class="fa fa-caret-down"></i>\n        </span>\n      </div>\n    </div>\n    <div class="span4">\n      <ul class="tree-action-list">\n        {{#if add_item_view}}\n        <li>\n          {{{renderLive add_item_view}}}\n        </li>\n        {{/if}}\n        <li>\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu tree-action-list-items" role="menu">\n              <li class="filter-trigger">\n                <a href="javascript://" {{^filter_is_hidden}}class="active"{{/filter_is_hidden}}>\n                  <i class="fa fa-search"></i>\n                  <span>Hide Filter</span>\n                </a>\n              </li>\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n\n{{^if_equals mapping \'previous_cycles\'}}\n{{#using workflow=parent_instance}}\n{{#workflow.next_cycle_start_date}}\n{{^if_equals workflow.frequency \'one_time\'}}\n{{#if_equals workflow.status \'Active\'}}\n  <div class="filters">\n    <div class="alert" style="border-radius: 0px; margin-bottom: 0px;">\n      <button type="button" class="close" data-dismiss="alert">\n        <span aria-hidden="true">&times;</span>\n      </button>\n      <strong>Note:</strong> Next cycle of this recurring workflow is scheduled to be activated on <strong>{{localize_date workflow.next_cycle_start_date}}</strong>.\n    </div>\n  </div>\n{{/if_equals workflow.status \'Active\'}}\n{{/if_equals}}\n{{/workflow.next_cycle_start_date}}\n{{/using}}\n{{/if_equals}}\n';
GGRC.Templates['cycles/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n  {{#is_info_pin}}\n    <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n  {{/is_info_pin}}\n\n  <div class="info-pane-utility">\n    {{#using workflow=instance.workflow}}\n    <!-- This should have only view link, shouldn\'t edit cycle instance of workflow while active -->\n      <div class="details-wrap">\n        <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n          <span class="bubble"></span>\n          <span class="bubble"></span>\n          <span class="bubble"></span>\n        </a>\n        <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n          {{#if workflow.viewLink}}\n            {{!#is_allowed "view_object_page" instance}}\n              <li>\n                <a href="{{workflow.viewLink}}">\n                  <i class="fa fa-long-arrow-right"></i>\n                  Open {{workflow.class.title_singular}}\n                </a>\n              </li>\n            {{!/is_allowed}}\n          {{/if}}\n          <li>\n            <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n          </li>\n        </ul>\n      </div>\n    {{/using}}\n  </div>\n\n  <div class="tier-content">\n    {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n\n    <div class="row-fluid wrap-row">\n      <div class="span12">\n        <h6>Description</h6>\n        <div class="rtf-block">\n          <p>{{{firstnonempty instance.description \'<span class="empty-message">None</span>\'}}}</p>\n        </div>\n      </div>\n    </div>\n\n    <div class="row-fluid wrap-row">\n      {{#using instance=instance.workflow}}\n        <div class="span6">\n          <div class="rtf-block">\n            {{{render \'/static/mustache/workflows/contacts.mustache\' instance=instance stacked=true}}}\n          </div>\n        </div>\n        <div class="span6">\n          <h6>\n            Frequency\n          </h6>\n          <p>\n            {{#if_equals instance.frequency \'one_time\'}}\n              One time\n            {{else}}\n              {{instance.frequency}}\n            {{/if_equals}}\n          </p>\n        </div>\n      {{/using}}\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span12">\n      {{#using instance=instance.workflow}}\n      <ggrc-gdrive-folder-picker {{^is_allowed \'update\' instance}}readonly="true"{{/is_allowed}} instance="instance" placeholder="If selected, all workflow attachments go here." />\n      {{/using}}\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span12">\n      {{{render \'/static/mustache/custom_attributes/info.mustache\' instance=instance}}}\n    </div>\n  </div>\n\n</section>\n';
GGRC.Templates['cycle_task_entries/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<li\n  data-object-id="{{instance.id}}"\n  data-object-type="{{instance.class.table_singular}}"\n  class="clearfix">\n  <span class="status-label status-"></span>\n  <div class="w-status">\n    {{{instance.description}}}\n\n    <mapping-tree-view\n      parent-instance="instance"\n      mapping="instance.class.info_pane_options.attachments.mapping"\n      item-template="instance.class.info_pane_options.attachments.show_view"\n    />\n    {{^if_equals parent_instance.status \'Verified\'}}\n    {{#with_mapping \'folders\' instance}} {{! dummy to ensure the child content always updates}}\n    {{#defer \'workflowFolder\' instance.workflowFolder allow_fail=true}}\n      {{#if workflowFolder.errors}}\n        <small class="error-inline">\n          <strong>Warning:</strong> You need permission to upload files to workflow folder. <a href="https://drive.google.com/folderview?id={{grdive_msg_to_id workflowFolder.message}}&usp=sharing#">Request access</a>.\n        </small>\n      {{else}}\n        <div class="oneline">\n          {{#if workflowFolder}}\n            <ggrc-gdrive-picker-launcher instance="instance" link_text="Attach supporting file" link_class="entry-attachment tree-item-add" click_event="trigger_upload_parent">\n            </ggrc-gdrive-picker-launcher>\n          {{else}}\n            <small class="error-inline">\n              <strong>Warning:</strong> Workflow folder is not set. Please select a folder before uploading files.\n            </small>\n          {{/if}}\n        </div>\n      {{/need_permission}}\n    {{else}}\n      <div {{attach_spinner \'{ "radius": 3, "length": 3, "width": 2 }\' \'display:inline-block; top: -5px; margin-left: 5px;\' }}></div>\n    {{/defer}}\n    {{/with_mapping}}\n    {{/if_equals}}\n    <span class="entry-author">\n      Modified by\n      {{#using person=instance.modified_by}}{{{render \'/static/mustache/people/popover.mustache\' person=person}}}{{/using}}\n      {{#if instance.created_at}}on {{date instance.created_at}}{{/if}}\n    </span>\n  </div>\n\n  <div class="entry-actions pull-right">\n    <a class="dropdown-toggle" href="#" data-toggle="dropdown"><i class="fa fa-cog"></i></a>\n    <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n      <li>\n        {{^if_equals parent_instance.status \'Verified\'}}\n          <a href="javascript://" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="CycleTaskEntry" data-object-plural="{{instance.class.table_plural}}" data-object-id="{{instance.id}}">\n            <i class="fa fa-pencil-square-o"></i>\n            Edit Comment\n          </a>\n        {{/if_equals}}\n      </li>\n      <li>\n        {{^if_equals parent_instance.status \'Verified\'}}\n            {{#is_allowed \'delete\' instance}}\n            <a data-toggle="modal-ajax-deleteform" data-object-plural="{{instance.class.table_plural}}" data-object-singular="CycleTaskEntry" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n              <i class="fa fa-trash"></i>\n              Delete Comment\n            </a>\n          {{/is_allowed_all}}\n        {{/if_equals}}\n      </li>\n    </ul>\n  </div><!-- entry-actions end -->\n</li>\n';
GGRC.Templates['cycle_task_entries/tree_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^if_equals parent_instance.status \'Verified\'}}\n  {{#using cycle=parent_instance.cycle}}\n    {{#if cycle.is_current}}\n    {{#is_allowed \'update\' instance context=\'for\'}}\n      <li class="tree-footer tree-item-add inner-tree-footer non-stick">\n        <a\n          href="javascript://"\n          class="btn btn-primary btn-mini section-create"\n          data-object-singular-override="Comment"\n          data-toggle="modal-ajax-form"\n          data-modal-reset="reset"\n          data-dirty="#regulations, #combo"\n          data-route="regulations"\n          data-modal-class="modal-wide"\n          data-object-singular="CycleTaskEntry"\n          data-object-plural="cycle_task_entries"\n          data-object-params=\'{\n            "cycle_task_group_object_task": {{parent_instance.id}},\n            "cycle": {{parent_instance.cycle.id}},\n            "context": {{parent_instance.context.id}}\n          }\'>\n          Add Comment\n        </a>\n      </li>\n    {{/is_allowed}}\n    {{/if}}\n  {{/using}}\n{{/if_equals}}\n';
GGRC.Templates['cycle_task_entries/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^if_equals parent_instance.status \'Verified\'}}\n  {{#using cycle=parent_instance.cycle}}\n    {{#if cycle.is_current}}\n    {{#is_allowed \'update\' instance}}\n      <div class="tree-item-add">\n        <a href="javascript://" class="btn btn-primary btn-mini section-create" data-object-singular-override="Comment" data-toggle="modal-ajax-form" data-modal-reset="reset" data-dirty="#regulations, #combo" data-route="regulations" data-modal-class="modal-wide" data-object-singular="CycleTaskEntry" data-object-plural="cycle_task_entries" data-object-params=\'{ "cycle_task_group_object_task": {{parent_instance.id}}, "cycle": {{parent_instance.cycle.id}}, "context": {{parent_instance.context.id}} }\'>\n          Add Comment\n        </a>\n      </div>\n    {{/is_allowed}}\n    {{/if}}\n  {{/using}}\n{{/if_equals}}\n';
GGRC.Templates['cycle_task_entries/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<form action="javascript://">\n  <div class="row-fluid">\n    <div class="span12">\n      <input id="task_id" name="cycle_task_group_object_task" type="hidden" model="CycleTaskGroupObjectTask" value="{{firstnonempty instance.cycle_task_group_object_task.id object_params.cycle_task_group_object_task}}">\n      <input id="cycle_id" name="cycle" type="hidden" model="Cycle" value="{{firstnonempty instance.cycle.id object_params.cycle}}">\n      <input id="context_id" name="context" type="hidden" model="Context" value="{{firstnonempty instance.context.id object_params.context}}">\n      <input id="is_declining_review" name="is_declining_review" type="hidden" value="{{firstnonempty instance.is_declining_review object_params.is_declining_review}}">\n      <label for="program_description">\n        Comment\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Commentary goes here"></i>\n      </label>\n      <div class="wysiwyg-area">\n        <textarea id="program_description" class="span12 double wysihtml5" name="description" placeholder="Enter Comments" tabindex="2" autofocus>{{instance.description}}</textarea>\n      </div>\n    </div>\n  </div>\n</form>\n';
GGRC.Templates['cycle_task_entries/documents']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <li data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <a href="{{schemed_url instance.link}}" href="{{schemed_url instance.link}}" target="_blank">\n      <i class="fa fa-paperclip"></i>\n      <span>{{firstnonempty instance.title instance.link}}</span>\n    </a>\n    {{#using cycle_task_group_object_task=parent_instance.cycle_task_group_object_task}}\n      {{^if_equals cycle_task_group_object_task.status \'Verified\'}}\n        <a href="javascript://" class="info-action unmap" data-toggle="unmap">\n          {{#result}}<span class="result" {{data \'result\'}}></span>{{/result}}\n          <i class="fa fa-trash"></i>\n        </a>\n      {{/if_equals}}\n    {{/using}}\n  </li>\n';
GGRC.Templates['cycle_task_entries/documents_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n';
GGRC.Templates['cycle_task_groups/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<li class="tree-item task-item t-assigned" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}" {{addclass "t-" instance.status}} {{addclass "t-" instance.overdue}}>\n  <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span6">\n              <div class="title tree-title-area" title="{{instance.title}}">\n                <i class="fa fa-task_group color"></i>\n                {{instance.title}}\n              </div>\n            </div>\n            <div class="span6">\n              <ul class="tree-action-list">\n                {{#with_mapping_count instance \'cycle_task_group_tasks\'}}\n                  <li>\n                    <span class="counter" rel="tooltip" data-placement="left" data-original-title="# of mapped tasks">\n                      <i class="fa fa-calendar-check-o"></i>\n                      <strong class="error">{{count}}</strong>\n                    </span>\n                  </li>\n                {{/with_mapping_count}}\n                <li>\n                  {{#with_review_task}}\n                    {{#using cycle=instance.cycle}}\n                      {{#using workflow=cycle.workflow}}\n                      {{^if_equals workflow.kind "Backlog"}}\n                      {{#if_equals cycle.is_current true}}\n                      {{#is_allowed \'update\' instance}}\n                        <div class="request-control"\n                          {{ (el) -> $(el).bind(\'inserted\', function() { el.ggrc_controllers_quick_form({ instance : el.closest(\'.item-main\').data(\'model\')}); });}}\n                          >\n                          {{#if_equals instance.status \'Assigned\'}}\n                            <button class="btn btn-mini btn-info change-task-status {{instance._disabled}}" data-openclose="open" data-name="status" data-value="InProgress">Start</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'InProgress\'}}\n                            <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Declined\'}}\n                            {{#if review_task.object_review}}\n                              <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Verified">Finish</button>\n                            {{else}}\n                              <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                            {{/if}}\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Finished\'}}\n                            <button class="btn btn-mini btn-danger change-task-status {{instance._disabled}}" data-name="status" data-value="Declined">Decline</button>\n                            <button class="btn btn-mini btn-success change-task-status {{instance._disabled}}" data-openclose="close" data-name="status" data-value="Verified">Verify</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Verified\'}}\n                            <span class="task-done">\n                              <em>Verified</em>\n                            </span>\n                          {{/if_equals}}\n                          <!--\n                          {{#instance._undo.0}}\n                            <a href="javascript://" data-name="status" data-value="{{instance._undo.0}}" data-undo="true" class="undo {{instance._disabled}}">Undo</a>\n                          {{/instance._undo.0}}\n                          -->\n                        </div>\n                      {{/is_allowed \'update\' instance}}\n                      {{/if_equals}}\n                      {{/if_equals}}\n                      {{/using}}\n                    {{/using}}\n                  {{/with_review_task}}\n                </li>\n              </ul>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  {{#if expanded}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      {{#if draw_children}}\n        {{#child_options}}\n          <div class="inner-tree {{^if list.length}}hide{{/list}}">\n            <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n          </div>\n        {{/child_options}}\n      {{/if}}\n    </div>\n  </div>\n  {{/if}}\n</li>\n';
GGRC.Templates['cycle_task_groups/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          <h6>{{instance.class.title_singular}} description</h6>\n          <div class="rtf-block">\n            {{{firstnonempty instance.description \'<span class="empty-message">None</span>\'}}}\n          </div>\n        </div>\n      </div>\n    </div>\n\n  </section>\n{{/instance}}\n';
GGRC.Templates['cycle_task_group_objects/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<li class="tree-item t-assigned" {{addclass "t-" instance.status}} {{addclass "t-" instance.overdue}} data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n  <div class="item-main" {{#instance}}{{data \'model\'}}{{/instance}}>\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span4">\n              <div class="title tree-title-area" title="{{instance.title}}">\n                {{#using object=instance.object}}\n                  {{#if object.title}}\n                    <div class="span11">\n                      <i class="fa fa-{{object.class.table_singular}}"></i>\n                      {{object.title}}\n                    </div>\n                  {{else}}\n                    {{instance.title}} <span class="gray">Sorry, you do not have access</span>\n                  {{/if}}\n                {{/using}}\n              </div>\n            </div>\n            <div class="span4">\n              <ul class="tree-action-list">\n                <li>\n                  {{{renderLive \'/static/mustache/tasks/states.mustache\' status=instance.status}}}\n                </li>\n              </ul>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  {{#if expanded}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n\n      {{#if draw_children}}\n        {{#child_options}}\n          <div class="inner-tree">\n            <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n          </div>\n        {{/child_options}}\n      {{/if}}\n\n    </div>\n  </div>\n  {{/if}}\n</li>\n';
GGRC.Templates['cycle_task_group_objects/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n  {{#using object=instance.object}}\n    <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n      {{#is_info_pin}}\n        <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n      {{/is_info_pin}}\n\n      <div class="info-pane-utility">\n        <div class="details-wrap">\n          {{#if object.title}}\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n              {{#is_allowed \'update\' object context=\'for\'}}\n                <li>\n                  <a href="javascript://" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="{{object.class.model_singular}}" data-object-plural="{{object.class.table_plural}}" data-object-id="{{object.id}}">\n                    <i class="fa fa-pencil-square-o"></i>\n                    Edit Object\n                  </a>\n                </li>\n              {{/is_allowed}}\n              <li>\n                <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n              </li>\n              {{#if object.viewLink}}\n                {{#is_allowed "view_object_page" object}}\n                  <li>\n                    <a href="{{object.viewLink}}">\n                      <i class="fa fa-long-arrow-right"></i>\n                      Open {{object.class.title_singular}}\n                    </a>\n                  </li>\n                {{/is_allowed}}\n              {{/if}}\n\n              {{#if_equals instance.cycle.reify.is_current true}}\n              {{#is_allowed \'delete\' instance}}\n                <li>\n                  <a data-toggle="modal-ajax-deleteform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n                    <i class="fa fa-trash"></i>\n                    Delete\n                  </a>\n                </li>\n              {{/is_allowed}}\n              {{/if_equals}}\n            </ul>\n          {{/if}}\n        </div>\n      </div>\n\n      <div class="tier-content">\n        {{#if object.title}}\n          {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=object}}}\n        {{else}}\n          <div class="row-fluid wrap-row">\n            <div class="span6">\n              <h6>Title</h6>\n              <h3>{{title}} <span class="gray">Sorry, you do not have access</span> </h3>\n            </div>\n          </div>\n        {{/if}}\n        {{{render \'/static/mustache/base_objects/description.mustache\' instance=object}}}\n        {{{render \'/static/mustache/base_objects/notes.mustache\' instance=object}}}\n        {{#if_equals object.class.title_singular \'Program\'}}\n          {{{render \'/static/mustache/base_objects/contacts.mustache\' is_program=true instance=object}}}\n        {{else}}\n          {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=object}}}\n        {{/if_equals}}\n      </div>\n    </section>\n  {{/using}}\n{{/instance}}\n';
GGRC.Templates['cycle_task_group_object_tasks/info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#instance}}\n  <section class="info{{#is_info_pin}} sticky-info-panel{{/is_info_pin}}">\n\n    {{#is_info_pin}}\n      <info-pin-buttons class="details-wrap" maximized="maximized" on-change-maximized-state="onChangeMaximizedState" on-close="onClose"></info-pin-buttons>\n    {{/is_info_pin}}\n\n    <div class="info-pane-utility">\n      {{#is_allowed \'update\' instance context=\'for\'}}\n        <div class="details-wrap">\n          <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n          </a>\n          <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n            <li>\n              <a href="javascript://" data-object-singular-override="Task for current Workflow Cycle" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="{{instance.class.model_singular}}" data-object-plural="{{instance.class.table_plural}}" data-object-id="{{instance.id}}">\n                <i class="fa fa-pencil-square-o"></i>\n                Edit Task\n              </a>\n            </li>\n            <li>\n              <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n            </li>\n\n            {{#if_equals instance.cycle.reify.is_current true}}\n            {{#is_allowed \'delete\' instance}}\n              <li>\n                <a data-toggle="modal-ajax-deleteform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n                  <i class="fa fa-trash"></i>\n                  Delete\n                </a>\n              </li>\n            {{/is_allowed}}\n            {{/if_equals}}\n          </ul>\n        </div>\n      {{/is_allowed}}\n    </div>\n\n    <div class="tier-content">\n      {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance}}}\n\n      <div class="row-fluid wrap-row">\n        <div class="span6">\n          <h6>Assignee</h6>\n          {{#if_cycle_assignee_privileges instance}}\n          <div data-force-refresh="true" {{#instance}}{{data \'model\'}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({ instance : el.data(\'model\')}); }}>\n            {{#using contact=instance.contact}}\n              <div class="objective-selector">\n                <input type="text" name="contact.name" data-lookup="Person" class="search-icon input-block-level" {{#if_equals instance.status \'Verified\'}}disabled="disabled"{{/if}} placeholder="Choose Assignee" value="{{firstnonempty contact.name contact.email \'\'}}">\n              </div>\n            {{/using}}\n          </div>\n          {{else}}\n            {{#using contact=instance.contact}}\n            <div>{{firstnonempty contact.name contact.email \'\'}}</div>\n            {{/using}}\n          {{/if_cycle_assignee_privileges}}\n        </div>\n        <div class="span3">\n          <h6>Start date</h6>\n          {{#if instance.start_date}}\n            <p>\n              {{localize_date instance.start_date}}\n            </p>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n\n        <div class="span3">\n          <h6>End date</h6>\n          {{#if instance.end_date}}\n            <p>\n              {{localize_date instance.end_date }}\n            </p>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n      </div>\n      <div class="row-fluid wrap-row">\n        <div class="span6">\n        </div>\n        <div class="span3">\n          <h6>Actual Finish Date</h6>\n          {{#if instance.finished_date}}\n            <p>\n              {{localize_date instance.finished_date}}\n            </p>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n\n        <div class="span3">\n          <h6>Actual Verified Date</h6>\n          {{#if instance.verified_date}}\n            <p>\n              {{localize_date instance.verified_date }}\n            </p>\n          {{else}}\n            <span class="empty-message">None</span>\n          {{/if}}\n        </div>\n      </div>\n\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          {{{render "/static/mustache/base_templates/mapped_objects.mustache" instance=instance }}}\n        </div>\n      </div>\n\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          {{#if_helpers \'\\n            #if_equals\' instance.task_type \'text\' \'\\n            or #if_equals\' instance.task_type "" \'\\n            or #if_null\' instance.task_type}}\n            <h6>Task details</h6>\n            <div class="rtf-block">\n              {{{firstnonempty instance.description \'No additional details\'}}}\n            </div>\n          {{/if_helpers}}\n          {{#if_equals instance.task_type \'menu\'}}\n            <h6>Response Options</h6>\n            {{! here we need to start a quick form or other component}}\n            <ggrc-quick-update instance="instance">\n              <dropdown\n                name="selected_response_options.0"\n                class-name="noop" {{! to not use default from component}}\n                is-disabled="!{{can_edit_response instance instance.status}}"\n                options-list="instance.response_options"></dropdown>\n            </ggrc-quick-update>\n          {{/if_equals}}\n          {{#if_equals instance.task_type \'checkbox\'}}\n            <h6>Response Options</h6>\n            <ggrc-quick-update instance="instance">\n              {{#each instance.response_options}}\n                <div class="row-fluid wrap-row">\n                  <div class="span12">\n                    <label><input\n                      type="checkbox"\n                      {{^if_can_edit_response instance instance.status}}\n                        disabled="disabled"\n                      {{/if}}\n                      multiple="multiple"\n                      name="selected_response_options"\n                      value="{{.}}"\n                      {{#in_array this instance.selected_response_options}}\n                        checked="checked"\n                      {{/in_array}}>\n                    {{.}}\n                    </label>\n                  </div>\n                </div>\n              {{/each}}\n            </ggrc-quick-update>\n          {{/if_equals}}\n          <br>\n        </div>\n      </div>\n\n      {{#using cycle=instance.cycle}}\n      {{#using workflow=cycle.workflow}}\n      {{#if_helpers \'\\n        #is_dashboard\' \'\\n        or ^is_page_instance\' workflow}}\n\n          <div class="row-fluid wrap-row">\n            <div class="span12">\n              <h6>Part of the workflow</h6>\n              <p>\n                <a href="{{workflow.viewLink}}">\n                  {{workflow.title}}\n                  <i class="fa fa-long-arrow-right smallmargin"></i>\n                </a>\n              </p>\n            </div>\n          </div>\n      {{/if_helpers}}\n      {{/using}}\n      {{/using}}\n\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          {{#with_mapping_count instance \'cycle_task_entries\'}}\n            {{#count}}\n              <h6>Comments ({{count}})</h6>\n              <mapping-tree-view\n                parent-instance="instance"\n                mapping="instance.class.info_pane_options.comments.mapping"\n                item-template="instance.class.info_pane_options.comments.show_view"\n                tree-view-class="tree-structure new-tree mapped-objects-tree">\n              </mapping-tree-view>\n            {{/count}}\n            {{^count}}\n              <h6>Comments</h6>\n              No comments.\n            {{/count}}\n          {{/with_mapping_count}}\n\n          <ul class="add-task-comment">\n            {{{render \'/static/mustache/cycle_task_entries/tree_footer.mustache\' parent_instance=instance}}}\n          </ul>\n        </div>\n      </div>\n\n    </div>\n  </section>\n{{/instance}}\n';
GGRC.Templates['cycle_task_group_object_tasks/tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<li class="tree-item item-list" {{addclass "t-" instance.status}} {{addclass "t-" instance.overdue}}\n  data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}"\n  {{#instance}}{{data \'model\'}}{{/instance}}\n  >\n  <div class="item-main" {{data \'model\'}}>\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span{{display_options.title_width}}">\n              <div class="title tree-title-area" title="{{instance.title}}">\n                <i class="fa fa-calendar-check-o"></i>\n                {{instance.title}}\n              </div>\n            </div>\n\n            {{!#is_dashboard}}\n              <div class="span{{display_options.selectable_width}}">\n                <div class="row-fluid">\n                  {{#display_attr_list}}\n                    <div class="span{{display_options.selectable_attr_width}}">\n                      <div class="owner tree-title-area">\n                        {{#instance}}\n                          {{#switch attr_name}}\n                            {{#case \'workflow\'}}\n                              {{#using cycle=instance.cycle}}\n                                {{#using workflow=cycle.workflow}}\n                                    <div class="tree-title-area">\n                                      <span class="tiny-data" rel="tooltip" title="Workflow title">\n                                        {{workflow.title}}\n                                      </span>\n                                    </div>\n                                {{/using}}\n                              {{/using}}\n                            {{/case}}\n                            {{#case \'assignee\'}}\n                              {{#using contact=instance.contact}}\n                                <div>{{firstnonempty contact.name contact.email \'\'}}</div>\n                              {{/using}}\n                            {{/case}}\n                            {{#default}}\n                              {{#if_helpers \'\\n                                #if_equals\' attr_name \'url\' \'\\n                                or #if_equals\' attr_name \'reference_url\'}}\n                                  <a class="url" href="{{get_url_value attr_name instance}}" target="_blank">\n                                    {{get_default_attr_value attr_name instance}}\n                                  </a>\n                              {{else}}\n                                {{get_default_attr_value attr_name instance}}\n                              {{/if_helpers}}\n                            {{/default}}\n                          {{/switch}}\n                        {{/instance}}\n                      </div>\n                    </div>\n                  {{/display_attr_list}}\n                </div>\n              </div>\n            {{!/is_dashboard}}\n\n            <div class="span{{display_options.action_width}}">\n              <ul class="tree-action-list tasks-action">\n                <li>\n                  <div class="date">\n                    <span rel="tooltip" data-placement="top" {{#is_overdue instance.end_date instance.status}}class="error"{{/is_overdue}} data-original-title="Due On">\n                      <i class="fa fa-clock-o {{#is_overdue instance.end_date instance.status}}red{{else}}color{{/is_overdue}}"></i> {{localize_date_today instance.end_date}}\n                    </span>\n                  </div>\n                </li>\n                <li> <!-- number of comment count -->\n                  {{#with_mapping_count instance \'cycle_task_entries\'}}\n                  <span class="counter" rel="tooltip" data-placement="left" data-original-title="# of comments">\n                    <i class="fa fa-comment-o"></i>\n                    <strong class="error">{{count}}</strong>\n                  </span>\n                  {{/with_mapping_count}}\n                </li>\n                <li>\n                  {{#with_mapping_count instance \'related_objects\'}}\n                  <span class="counter" rel="tooltip" data-placement="left" data-original-title="# of objects">\n                    <i class="fa fa-list-alt"></i>\n                    <strong class="error">{{count}}</strong>\n                  </span>\n                  {{/with_mapping_count}}\n                </li>\n                {{#with_review_task}}\n                  {{#using cycle=instance.cycle}}\n                    {{#if_equals cycle.is_current true}}\n                    {{#is_allowed \'update\' instance}}\n                      <li>\n                        <div class="request-control" {{ (el) -> $(el).bind(\'inserted\', function() { el.ggrc_controllers_quick_form({ instance : el.closest(\'.item-list\').data(\'model\')}); }); }}>\n                          {{#if_equals instance.status \'Assigned\'}}\n                            <button class="btn btn-mini btn-info change-task-status {{instance._disabled}}" data-openclose="open" data-name="status" data-value="InProgress">Start</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'InProgress\'}}\n                            <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Declined\'}}\n                            {{#if review_task.object_review}}\n                              <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Verified">Finish</button>\n                            {{else}}\n                              <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                            {{/if}}\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Finished\'}}\n                              <button class="btn btn-mini btn-danger change-task-status {{instance._disabled}}" data-name="status" data-value="Declined">Decline</button>\n                              <button class="btn btn-mini btn-success change-task-status {{instance._disabled}}" data-openclose="close" data-name="status" data-value="Verified">Verify</button>\n                          {{/if_equals}}\n                          {{#if_equals instance.status \'Verified\'}}\n                            <span class="task-done">\n                              <em>Verified</em>\n                            </span>\n                          {{/if_equals}}\n                          {{#instance._undo.0}}\n                            <a href="javascript://" data-name="status" data-value="{{instance._undo.0}}" data-undo="true" class="undo {{instance._disabled}}">Undo</a>\n                          {{/instance._undo.0}}\n                        </div>\n                      </li>\n                    {{/is_allowed}}\n                    {{/if_equals}}\n                  {{/using}}\n                {{/with_review_task}}\n              </ul>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  {{#if expanded}}\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      {{#if draw_children}}\n        {{#child_options.1}}\n          <div class="inner-tree {{^if list.length}}hide{{/if}}">\n            <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")) }}></ul>\n          </div>\n        {{/child_options.1}}\n      {{/if}}\n  {{/if}}\n</li>\n';
GGRC.Templates['cycle_task_group_object_tasks/modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<form action="javascript://">\n  <div class="row-fluid">\n    <div class="span12 {{#instance.computed_errors.title}}field-failure{{/instance.computed_errors.title}}">\n      <h6>Task Summary <span class="required">*</span></h6>\n      <input class="input-block-level required" id="task-title" placeholder="Enter Title" name="title" type="text" value="{{instance.title}}" tabindex="1" autofocus>\n      {{#instance.computed_errors.title}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.title}}\n      <br>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span8">\n      {{#if_helpers \'\\n      #if_equals\' instance.task_type \'text\' \'\\n      or #if_equals\' instance.task_type \'\' \'\\n      or #if_null\' instance.task_type}}\n        <h6>Task Description</h6>\n        <div class="wysiwyg-area bottom-no-margin">\n          <textarea id="tgt_response_options" class="span12 triple wysihtml5" name="description" placeholder="Enter Description" tabindex="2">{{instance.description}}</textarea>\n        </div>\n      {{else}}\n        <label>\n          Selection Response Options (Comma Separated)\n        </label>\n          <textarea id="tgt_response_options" class="span12 double" name="response_options_csv" placeholder="Enter Description" tabindex="2">{{instance.response_options_csv}}</textarea>\n      {{/if_equals}}\n      <br>\n      <select name="task_type">\n        <option value="text" {{#if_equals instance.task_type \'text\'}}selected="selected"{{/if_equals}}>Rich text</option>\n        <option value="menu" {{#if_equals instance.task_type \'menu\'}}selected="selected"{{/if_equals}}>Dropdown</option>\n        <option value="checkbox" {{#if_equals instance.task_type \'checkbox\'}}selected="selected"{{/if_equals}}>Checkbox</option>\n      </select>\n    </div>\n    <div class="span4">\n      <div class="{{#instance.computed_errors.contact}} field-failure {{/instance.computed_errors.contact}}">\n        {{#using contact=instance.contact model_singular=model.model_singular}}\n          <h6>Assignee <span class="required">*</span></h6>\n          <input tabindex="3" class="input-block-level" name="contact.email" data-lookup="Person" placeholder="Enter email address" type="text" value="{{firstexist contact.email \'\'}}" />\n          {{#instance.computed_errors.contact}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.contact}}\n        {{/using}}\n      </div>\n      <br>\n      <div class="{{#instance.computed_errors.workflow}}field-failure{{/instance.computed_errors.workflow}} {{#instance.computed_errors.cycle}}field-failure{{/instance.computed_errors.cycle}}">\n        {{#using workflow=instance.workflow}}\n          <h6 title="An active workflow should be created in advance">\n            Active Workflow <span class="required">*</span>\n          </h6>\n          <input\n            class="input-block-level required"\n            name="workflow."\n            model="Workflow"\n            data-lookup="Workflow"\n            data-params="Workflow:status=Active,frequency=one_time"\n            data-lookup-cb="set_properties_from_workflow"\n            placeholder="Select a Workflow"\n            type="text"\n            value="{{workflow.title}}"\n            tabindex="4" />\n          {{#instance.computed_errors.workflow}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.workflow}}\n          {{^instance.computed_errors.workflow}}{{#instance.computed_errors.cycle}}<label class="help-inline warning">No active cycles in this workflow</label>{{/instance.computed_errors.cycle}}{{/instance.computed_errors.workflow}}\n        {{/using}}\n      </div>\n    </div>\n  </div>\n\n  <div class="row-fluid">\n    <div class="span8">\n      <ggrc-modal-connector\n          parent_instance="instance"\n          instance="instance"\n          mapping="related_objects_as_source"\n          source_mapping="info_related_objects"\n          default_mappings="object_params.pre_mapped_objects"\n          deferred="true"\n          >\n        <h6>Object(s) mapped to task</h6>\n        {{#prune_context}}\n          {{#each list}}\n            <div class="oneline">\n              <a href="javascript://" class="unmap" data-toggle="unmap">\n                <span class="result" {{data \'result\'}}></span>\n                <i class="fa fa-trash"></i>\n              </a>\n              {{#update_link .}}\n                <a class="url" href="{{schemed_url link}}" target="_blank">\n                  {{firstexist title name email link}}\n                </a>\n              {{/update_link}}\n            </div>\n          {{/each}}\n        {{/prune_context}}\n        {{^list.length}}\n          <span class="gray"><em>No mapped objects</em></span>\n        {{/list.length}}\n        <div class="objective-selector">\n          <div style="clear:both">\n            <br>\n            {{^if_in instance.status "Finished,Verified"}}\n            <a class="section-add section-sticky btn btn-small btn-draft"\n              href="javascript://" rel="tooltip"\n              {{data "deferred_to"}}\n              data-placement="left"\n              data-toggle="unified-mapper"\n              data-deferred="true"\n              data-object-source="true"\n              data-join-mapping="info_related_objects"\n              data-join-object-id="{{instance.id}}"\n              data-join-object-type="{{instance.class.model_singular}}"\n              data-original-title="Map Object to this {{instance.class.title_singular}}" tabindex="6">\n              Map Objects\n            </a>\n            {{/if_in}}\n          </div>\n        </div>\n      </ggrc-modal-connector>\n    </div>\n    <div class="span4" id="frequency-div">\n      <div class="row-fluid">\n        <div class="{{#instance.computed_errors.start_date}}field-failure{{/instance.computed_errors.start_date}}">\n          <datepicker\n            set-max-date="instance.end_date"\n            date="instance.start_date"\n            required="{{true}}"\n            label="Starts Date"\n            />\n            {{#instance.computed_errors.start_date}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.start_date}}\n          </div>\n        </div>\n        <div class="{{#instance.computed_errors.end_date}}field-failure{{/instance.computed_errors.end_date}}">\n          <datepicker\n            set-min-date="instance.start_date"\n            date="instance.end_date"\n            required="{{true}}"\n            label="Due Date"\n            />\n            {{#instance.computed_errors.end_date}}<label class="help-inline warning">{{this}}</label>{{/instance.computed_errors.end_date}}\n          </div>\n        </div>\n      </div>\n    </div>\n  </div>\n  <div class="row-fluid hidden">\n    <input type="hidden" name="task_group" model="TaskGroup" value="{{firstnonempty object_params.task_group instance.task_group.id}}" />\n    <input type="hidden" name="cycle_task_group" model="CycleTaskGroup" value="{{firstnonempty object_params.task_group instance.task_group.id}}" data-populated-in-callback />\n    <input type="hidden" name="sort_index" value="{{firstnonempty object_params.sort_index instance.sort_index}}" />\n    <input type="hidden" name="cycle" model="Cycle" value="{{instance.cycle.id}}" data-populated-in-callback />\n    <input type="hidden" name="context" model="Context" value="{{firstnonempty object_params.context instance.context.id}}" data-populated-in-callback />\n    <input type="hidden" name="status" value="{{firstnonempty instance.status \'Assigned\'}}" />\n  </div>\n</form>\n';
GGRC.Templates['cycle_task_group_object_tasks/tree_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#if_equals original_list.length 0}}\n<li>\n  <div class="zero-state">\n    <h2>\n    {{#is_profile}}\n      You have\n    {{else}}\n      {{#with_page_object_as \'page_object\'}}\n        {{page_object.type}} has\n      {{/with_page_object_as}}\n    {{/is_profile}} no assigned tasks</h2>\n  </div>\n</li>\n{{/if_equals}}\n\n{{#if is_subtree}}\n    <!-- {{{renderLive \'/static/mustache/base_objects/tree_footer_zero.mustache\'}}} -->\n{{else}}\n  {{#if original_list.length}}\n    <div class="paging-wrap pull-right">\n        <tree-pagination paging="paging"></tree-pagination>\n    </div>\n    {{/if}}\n{{/if}}\n';
GGRC.Templates['cycle_task_group_object_tasks/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#is_allowed \'create\' \'CycleTaskGroupObjectTask\' context=null}}\n  {{^if_recurring_workflow parent_instance}}\n    <a\n      class="section-add action-button create-button"\n      href="javascript://"\n      rel="tooltip"\n      data-placement="left"\n      data-original-title="Create Cycle Task for object"\n      data-object-plural="cycle_task_group_object_tasks"\n      data-modal-class="modal-wide" href="javascript://"\n      data-object-singular="CycleTaskGroupObjectTask"\n      data-toggle="modal-ajax-form"\n      data-modal-reset="reset"\n      data-object-params=\'{\n        {{^if_instance_of parent_instance \'Person|Workflow\'}}\n          "pre_mapped_objects": [{\n            "type": "{{ parent_instance.type }}",\n            "id": {{ parent_instance.id }}\n          }],\n        {{/if_instance_of}}\n        "modal_title": "Create New Cycle Task"\n        {{#if_equals parent_instance.type \'Workflow\'}}\n          ,"workflow":{"id":{{parent_instance.id}},"type":"Workflow"}\n        {{/if_equals}}\n        }\'>\n\n      Create New\n    </a>\n  {{/if_recurring_workflow}}\n{{/is_allowed}}\n';
GGRC.Templates['cycle_task_group_object_tasks/tree_header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{{> "/static/mustache/base_objects/tree_view_filter.mustache"}}}\n\n<header class="header sticky sticky-header tree-header {{#filter_is_hidden}}no-filter{{/filter_is_hidden}}">\n  <div class="row-fluid">\n    <div class="span{{display_options.title_width}}">\n      <div class="title-heading oneline">\n        <span class="widget-col-title" data-field="title">\n          Title\n          <i class="fa fa-caret-down"></i>\n        </span>\n      </div>\n    </div>\n\n    {{{> "/static/mustache/base_objects/tree_column_names.mustache"}}}\n\n    <div class="span{{display_options.action_width}}">\n      <ul class="tree-action-list">\n        {{#if add_item_view}}\n        <li>\n          {{{renderLive add_item_view}}}\n        </li>\n        {{/if}}\n        <li>\n          <div class="details-wrap">\n            <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n              <span class="bubble"></span>\n            </a>\n            <ul class="dropdown-menu tree-action-list-items" role="menu">\n              <li class="filter-trigger">\n                <a href="javascript://" {{^filter_is_hidden}}class="active"{{/filter_is_hidden}}>\n                  <i class="fa fa-search"></i>\n                  <span>Hide Filter</span>\n                </a>\n              </li>\n              {{{> "/static/mustache/base_objects/tree_column_selector.mustache"}}}\n              {{{> "/static/mustache/base_objects/sub_tree_type_selector.mustache"}}}\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  </div>\n</header>\n<div class="clearfix"></div>\n';
GGRC.Templates['dashboard/widgets']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<!-- Right side workflow and task trees area -->\n<dashboard-widgets class="span{{#system_role \'No Role\'}}12{{/system_role}}{{^system_role \'No Role\'}}9{{/system_role}} content-wrap">\n    <div class="inner-content">\n      <section class="widget dashboard small-margin">\n        <header class="header widget-nav">\n          <div class="row-fluid">\n            <div class="span12">\n              <ul>\n                <li class="active workflow-tab">\n                  <a href="javascript://">\n                    My Workflows ({{workflow_count}})\n                  </a>\n                </li>\n              </ul>\n            </div>\n          </div>\n        </header>\n        <section class="content">\n          {{{ renderLive workflow_view }}}\n          {{#workflow_show_all}}\n            <a href="javascript://" class="workflow-trigger show-all">Show all my workflows</a>\n          {{/workflow_show_all}}\n        </section>\n      </section><!-- Widget end -->\n\n    </div><!-- inner-content end -->\n</dashboard-widgets>\n';
GGRC.Templates['dashboard/object_nav_actions']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#is_allowed \'update\' instance context=\'for\'}}\n  <li class="inner-nav-button">\n    <workflow-activate workflow="instance" deferredData="can_activate">\n      <spinner toggle="waiting"></spinner>\n\n      {{#if_equals instance.status "Draft"}}\n        {{^if waiting}}\n          {{^can_activate}}\n            <p class="tiny-help-text">You need to setup task group(s) before activating workflow</p>\n          {{/can_activate}}\n          <button type="submit" href="#workflowActivate" class="btn btn-info btn-small" {{^can_activate}}disabled="disabled"{{/can_activate}}>\n            Activate Workflow\n          </button>\n        {{/waiting}}\n      {{else}}\n        {{^if_equals instance.status \'Inactive\'}}\n        {{^if waiting}}\n          {{^can_activate}}\n            {{^if_equals instance.kind "Backlog"}}\n            <p class="alert tiny-help-text" style="margin-bottom:0px;"><strong>Note:</strong> At least one of the task groups is missing either a task or an object.</p>\n            {{/if_equals}}\n          {{/can_activate}}\n        {{/if}}\n        {{/if_equals}}\n      {{/if_equals}}\n    </workflow-activate>\n  </li>\n{{/is_allowed}}\n';
GGRC.Templates['dashboard/info/workflow_progress']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="new-tree tree-one dashboard-tree workflow-tree">\n  {{#workflow_data}}\n  {{#list}}\n  {{#using ins=this}}\n    <li class="tree-item">\n      <div class="item-wrap">\n        <div class="row-fluid">\n          <div class="span5">\n            <div class="item-data">\n              <div class="tree-title-area">\n                <div class="oneline">\n                  <a href="{{viewLink}}#current_widget">{{ins.title}}</a>\n                  <ul class="item-util">\n                    <li>\n                      {{{render \'/static/mustache/dashboard/info/wf_owner.mustache\' instance=ins stacked=true}}}\n                    </li>\n                  </ul>\n                </div>\n              </div>\n            </div>\n          </div>\n          <div class="span5">\n            <div class="tree-progress">\n              <div class="progress">\n                {{#if ins.task_data.finished_percentage}}\n                  <div class="bar bar-finish" style="width: {{ins.task_data.finished_percentage}}%;" rel="tooltip" data-original-title="Finished {{ins.task_data.finished_percentage}}%"></div>\n                {{/if}}\n                {{#if ins.task_data.verified_percentage}}\n                  <div class="bar bar-verify" style="width: {{ins.task_data.verified_percentage}}%;" rel="tooltip" data-original-title="Verified {{ins.task_data.verified_percentage}}%"></div>\n                {{/if}}\n                {{#if ins.task_data.in_progress_percentage}}\n                  <div class="bar bar-progress" style="width: {{ins.task_data.in_progress_percentage}}%;" rel="tooltip" data-original-title="In Progress {{ins.task_data.in_progress_percentage}}%"></div>\n                {{/if}}\n                {{#if ins.task_data.assigned_percentage}}\n                  <div class="bar bar-pending" style="width: {{ins.task_data.assigned_percentage}}%;" rel="tooltip" data-original-title="Pending {{ins.task_data.assigned_percentage}}%"></div>\n                {{/if}}\n                {{#if ins.task_data.declined_percentage}}\n                  <div class="bar bar-decline" style="width: {{ins.task_data.declined_percentage}}%;" rel="tooltip" data-original-title="Declined {{ins.task_data.declined_percentage}}%"></div>\n                {{/if}}\n                {{#if ins.task_data.over_due_percentage}}\n                  <div class="bar bar-warning" style="width: {{ins.task_data.over_due_percentage}}%;" rel="tooltip" data-original-title="Overdue {{ins.task_data.over_due_percentage}}%"></div>\n                {{/if}}\n              </div>\n              <ul class="item-util">\n                <li>\n                  {{ins.task_data.task_count}} tasks in total / {{ins.task_data.verified_percentage}}% completed\n                </li>\n              </ul>\n            </div>\n          </div>\n          <div class="span2">\n            <div class="right">\n              {{#if ins.task_data.over_due_flag}}\n                <span class="overdue">Overdue</span>\n              {{else}}\n                Due in {{ins.task_data.days_left_for_first_task}} days\n              {{/if}}\n                <ul class="item-util">\n                  <li>{{{ins.task_data.first_end_date}}}</li>\n                </ul>\n            </div>\n          </div>\n        </div>\n      </div>\n    </li>\n  {{/using}}\n  {{/list}}\n  {{/workflow_data}}\n  {{#if_equals workflow_count 0}}\n    <li>\n      <div class="zero-state">\n        <h2>You don\'t have any workflows in progress</h2>\n      </div>\n    </li>\n  {{/if_equals}}\n</ul><!-- tree-structure end -->\n';
GGRC.Templates['dashboard/info/my_tasks']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n<ul class="tree-structure new-tree dashboard-tree task-tree">\n  {{#task_data}}\n  {{#filtered_list}}\n    <li class="tree-item item-list" {{addclass "t-" instance.status}} {{addclass "t-" instance.overdue}} data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}" {{#instance}}{{data \'model\'}}{{/instance}}>\n      <div class="item-main" {{data \'model\'}}>\n        <div class="openclose">\n          <div class="item-wrap">\n            <div class="row-fluid">\n              <div class="span12">\n                <ul class="tree-action-list tasks-action">\n                  <li> <!-- number of comment count -->\n                    {{#with_mapping_count instance \'cycle_task_entries\'}}\n                    <span class="counter" rel="tooltip" data-placement="left" data-original-title="# of comments">\n                      <i class="fa fa-comment-o"></i>\n                      <strong class="error">{{count}}</strong>\n                    </span>\n                    {{/with_mapping_count}}\n                  </li>\n                  <li> <!-- due date -->\n                    <span rel="tooltip" data-placement="top" {{#is_overdue instance.end_date instance.status}}class="error"{{/is_overdue}} data-original-title="Due On">\n                      <i class="fa fa-clock-o {{#is_overdue instance.end_date instance.status}}red{{else}}color{{/if overdue_warning}}"></i>\n                      {{localize_date instance.end_date}}\n                    </span>\n                  </li>\n                  {{#using cycle=instance.cycle}}\n                    {{#if_equals cycle.is_current true}}\n                    {{#is_allowed \'update\' instance}}\n                    <li> <!-- buttons -->\n                      <div class="request-control" {{ (el) -> $(el).bind(\'inserted\', function() { el.ggrc_controllers_quick_form({ instance : el.closest(\'.item-list\').data(\'model\')}); }); }}>\n                        {{#if_equals instance.status \'Assigned\'}}\n                          <button class="btn btn-mini btn-info change-task-status {{instance._disabled}}" data-openclose="open" data-name="status" data-value="InProgress">Start</button>\n                        {{/status}}\n                        {{#if_equals instance.status \'InProgress\'}}\n                          <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                        {{/if_equals}}\n                        {{#if_equals instance.status \'Declined\'}}\n                          <button class="btn btn-mini btn-draft change-task-status {{instance._disabled}}" data-name="status" data-value="Finished">Finish</button>\n                        {{/if_equals}}\n                        {{#if_equals instance.status \'Finished\'}}\n                          <button class="btn btn-mini btn-danger change-task-status {{instance._disabled}}" data-name="status" data-value="Declined">Decline</button>\n                          <button class="btn btn-mini btn-success change-task-status {{instance._disabled}}" data-openclose="close" data-name="status" data-value="Verified">Verify</button>\n                        {{/status}}\n                        {{#if_equals instance.status \'Verified\'}}\n                          <span class="task-done">\n                            <em>Verified</em>\n                          </span>\n                        {{/if_equals}}\n                        {{#instance._undo.0}}\n                          <a href="javascript://" data-name="status" data-value="{{instance._undo.0}}" data-undo="true" class="undo {{instance._disabled}}">Undo</a>\n                        {{/instance._undo.0}}\n                      </div>\n                    </li>\n                    {{/is_allowed \'update\' instance}}\n                    {{/if_equals}}\n                  {{/using}}\n                </ul>\n                <div class="item-data">\n                  <span class="status-label {{addclass "status-" instance.status}} {{addclass "status-" instance.overdue}}"></span>\n                  <div class="tree-title-area w-status">\n                    <a href="#">{{instance.title}}</a>\n                    <ul class="item-util">\n                      <li>\n                        {{#using cycle=instance.cycle}}\n                          {{#using workflow=cycle.workflow}}\n                            Workflow: <a href="{{workflow.viewLink}}#current_widget">{{workflow.title}}</a>\n                          {{/using}}\n                        {{/using}}\n                      </li>\n                      <li>\n                        {{#using instance=instance.cycle_task_group_object}}\n                          {{#using object=instance.object}}\n                            {{#if object.type}}\n                              {{object.type}} : <a href="{{object.viewLink}}">{{firstnonempty object.title \'\'}}</a>\n                            {{/if}}\n                          {{/using}}\n                        {{/using}}\n                      </li>\n                    </ul>\n                  </div>\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n\n      <!-- 2tier expanded info -->\n      <div class="tier-2-info item-content">\n        <div class="tier-2-info-content">\n          <div class="details-wrap">\n            <a class="btn btn-small btn-draft dropdown-toggle" href="#" data-toggle="dropdown"><i class="fa fa-cog"></i></a>\n            <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n              {{#is_allowed \'update\' instance context=\'for\'}}\n              <li>\n                <a href="javascript://"\n                    data-object-singular-override="Task for current Workflow Cycle"\n                    data-toggle="modal-ajax-form"\n                    data-modal-reset="reset"\n                    data-modal-class="modal-wide"\n                    data-object-singular="{{instance.class.model_singular}}"\n                    data-object-plural="{{instance.class.table_plural}}"\n                    data-object-id="{{instance.id}}">\n                  <i class="fa fa-pencil-square-o"></i>\n                  Edit Task\n                </a>\n              </li>\n              {{/is_allowed}}\n            </ul>\n          </div>\n          <div class="tier-content">\n\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                {{#if_helpers \'\\n                  #if_equals\' instance.task_type \'text\' \'\\n                  or #if_equals\' instance.task_type "" \'\\n                  or #if_null\' instance.task_type}}\n                  <h6>Task details</h6>\n                  <div class="tree-description">\n                    {{{firstnonempty instance.description \'No additional details\'}}}\n                  </div>\n                {{/if_helpers}}\n                <br>\n              </div>\n            </div><!-- row-fluid end -->\n\n            <div class="row-fluid wrap-row">\n              <div class="span6">\n                <h6>Assignee</h6>\n                {{#if_workflow_assignee_privileges instance}}\n                <div {{#instance}}{{data \'model\'}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({ instance : el.data(\'model\')}); }}>\n                  {{#using contact=instance.contact}}\n                    <div class="objective-selector">\n                      <input type="text" name="contact.name" data-lookup="Person" class="search-icon input-block-level" {{#if_equals instance.status \'Verified\'}}disabled="disabled"{{/if}} placeholder="Choose Assignee" value="{{firstnonempty contact.name contact.email \'\'}}">\n                    </div>\n                  {{/using}}\n                </div>\n                {{else}}\n                  {{#using contact=instance.contact}}\n                  <div>{{firstnonempty contact.name contact.email \'\'}}</div>\n                  {{/using}}\n                {{/if_workflow_assignee_privileges}}\n              </div>\n              <div class="span6">\n                <h6>Starts/Ends</h6>\n                {{instance.start_date}} / {{instance.end_date}}\n              </div>\n            </div><!-- row-fluid end -->\n\n            {{#instance.class.tree_view_options.child_options.0}}\n            {{#inject_parent_instance instance}}\n              <div class="row-fluid wrap-row">\n                {{#with_mapping_count instance "cycle_task_entries"}}\n                <h6>Comments ({{count}})</h6>\n                {{/with_mapping_count}}\n                <ul class="entry-list" data-disable-lazy-loading="true" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data(\'options\')).control("tree_view").display() }}>\n                </ul>\n              </div>\n            {{/inject_parent_instance}}\n            {{/instance}}\n          </div><!-- tier-content end -->\n        </div><!-- tier-2-info-content end -->\n      </div><!-- tier-2-info end -->\n    </li>\n  {{/filtered_list}}\n  {{/task_data}}\n  {{#if_equals task_count 0}}\n    <li>\n      <div class="zero-state">\n        <h2>You don\'t have any tasks assigned</h2>\n      </div>\n    </li>\n  {{/if_equals}}\n</ul><!-- tree-structure end -->\n';
GGRC.Templates['dashboard/info/my_audit_requests']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="tree-structure new-tree dashboard-tree audit-tree">\n  {{#audit_requests_data}}\n  {{#list}}\n    <li class="tree-item" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n      <div class="item-main" data-model="true" {{#instance}}{{data \'model\'}}{{/instance}}>\n        <div class="item-wrap">\n          <div class="openclose">\n          <!--/div>\n          <div class="select"--> <!-- Fix in css- this puts the data in 2 lines -->\n            <div class="item-data">\n              <div class="row-fluid">\n                <div class="span6">\n                  <span class="status-label status-{{to_class instance.status \'_\'}}"></span>\n                  <div class="tree-title-area w-status">\n                    {{strip_html_tags instance.description}}\n                    <ul class="item-util">\n                      <li>\n                        {{#using audit=instance.audit}}\n                          Audit: <a href="{{audit.viewLink}}">{{audit.title}}</a>\n                        {{/using}}\n                      </li>\n                     </ul>\n                  </div>\n                </div>\n                <div class="span4">\n                  <span class="state-value">{{instance.status}}</span>\n                </div>\n                <div class="span2">\n                  <div class="show-details">\n                    {{#using audit=instance.audit}}\n                    {{#if audit.viewLink}}\n                      {{#is_allowed "view_object_page" audit}}\n                        <a href="{{audit.viewLink}}#Request_widget/request/{{instance.id}}" target="_blank"><i class="fa fa-long-arrow-right"></i></a>\n                      {{/is_allowed}}\n                    {{/if}}\n                    {{/using}}\n                  </div>\n                  <ul class="tree-action-list">\n\n                          <li>\n                            <span class="counter" rel="tooltip" data-placement="left" data-original-title="# of responses">\n                              <i class="fa fa-files-o"></i>\n                              {{#with_mapping_count instance \'responses\'}}\n                                {{count}}\n                              {{/with_mapping_count}}\n                            </span>\n                          </li>\n\n                  </ul>\n                </div>\n              </div> <!-- row-fluid end -->\n            </div> <!-- item-data end -->\n          </div> <!-- openclose end -->\n        </div> <!-- item-wrap end -->\n      </div> <!-- item-main end -->\n\n      <!-- Tier 2 info -->\n      <div class="tier-2-info item-content">\n        <div class="tier-2-info-content">\n          <div class="details-wrap">\n            <a class="btn btn-small btn-draft dropdown-toggle" href="#" data-toggle="dropdown"><i class="fa fa-cog"></i></a>\n            <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n              {{#using audit=instance.audit}}\n              {{#using program=audit.program}}\n              {{#if_can_edit_request instance}}\n                <li>\n                  <a href="javascript://" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="Request" data-object-plural="requests" data-object-id="{{instance.id}}">\n                    <i class="fa fa-pencil-square-o"></i>\n                    Edit Request\n                  </a>\n                </li>\n              {{/if_can_edit_request}}\n              {{/using}}\n              {{/using}}\n            </ul>\n          </div>\n          <div class="tier-content">\n            <div class="row-fluid wrap-row">\n              <div class="span6">\n                <h6>Starts on</h6>\n                {{#if instance.start_date}}\n                  {{#using start_date=instance.start_date}}\n                    {{localize_date start_date}}\n                  {{/using}}\n                {{/if}}\n              </div>\n              <div class="span6" data-force-refresh="true" {{#instance}}{{data \'model\'}}{{/instance}} {{ (el) -> el.ggrc_controllers_quick_form({ instance : el.data(\'model\')}); }}>\n                <h6>Assignee</h6>\n                {{#using assignee=instance.assignee}}\n                  {{{renderLive \'/static/mustache/people/popover.mustache\' person=assignee}}}\n                {{/using}}\n              </div>\n            </div>\n            {{#using audit=instance.audit}}\n              {{#with_mapping "audit_object_object" instance}}\n                {{#if audit_object_object.0.instance}}\n                  <div class="row-fluid wrap-row">\n                    <div class="span12">\n                      <h6>{{audit.object_model.table_singular}}</h6>\n                      <p class="oneline">\n                        <i class="fa fa-{{audit.object_model.table_singular}} color nomargin"></i>\n                        {{#is_allowed "view_object_page" audit_object_object.0.instance}}\n                          <a class="object-title" href="{{audit_object_object.0.instance.viewLink}}" target="_blank">{{audit_object_object.0.instance.title}}</a>\n                        {{else}}\n                          <span class="object-title" target="_blank">{{audit_object_object.0.instance.title}}</span>\n                        {{/is_allowed}}\n                      </p>\n                    </div>\n                  </div>\n                {{/if}}\n              {{/using}}\n            {{/using}}\n\n            {{{render_hooks \'Request.tree_view_info\'}}}\n\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Notes</h6>\n                {{{instance.notes}}}\n              </div>\n            </div>\n            <div class="row-fluid wrap-row">\n              <div class="span12">\n                <h6>Test</h6>\n                {{{instance.test}}}\n              </div>\n            </div>\n          </div>\n        </div>\n      </div> <!-- tier-2-info -->\n    </li>\n  {{/list}}\n  {{/audit_requests_data}}\n  {{#if_equals audit_requests_count 0}}\n    {{#if_equals show_audit true}}\n      <li>\n        <div class="zero-state">\n          <h2>You don\'t have any requests assigned</h2>\n        </div>\n      </li>\n    {{/if_equals}}\n  {{/if_equals}}\n</ul>\n';
