GGRC.Templates['selectors/multitype_multiselect_option_items']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#each options}}\n<li class="tree-item task-item"\n  data-object-type="{{instance.class.table_singular}}"\n  {{#instance}}{{ data \'option\' }}{{/instance}}\n  data-id="{{ instance.id }}">\n\n  <div class="item-main">\n    <div class="item-wrap">\n      {{> \'/static/mustache/base_objects/open_close.mustache\'}}\n      <div class="select">\n        <div class="item-data">\n          <div class="row-fluid">\n            <div class="span11">\n              <div class="tree-title-area">\n                {{firstexist instance.name instance.title}}\n                <span class="url-link">\n                  {{firstexist instance.email instance.link}}\n                </span>\n              </div>\n            </div>\n\n            <div class="span1">\n              <div class="object-check" align="left">\n                {{#if mappings.length}}\n                  {{#result_direct_mappings bindings_compute selected_object}}\n                    <input type="checkbox" value="{{instance.title}}" checked="checked" disabled="true" class="object-check-single">\n                  {{/result_direct_mappings}}\n                {{else}}\n                  <input type="checkbox" value="{{instance.title}}" class="object-check-single">\n                {{/if}}\n              </div>\n            </div>\n          </div>\n        </div><!-- item-data end -->\n      </div><!-- select end -->\n    </div><!-- item-wrap end -->\n  </div><!-- item-main end -->\n\n  <div class="tier-2-info item-content">\n    <div class="tier-2-info-content">\n      <lazy-openclose>\n        {{#show}}\n          {{#find_template \'tier2_content\' instance}}\n            {{#instance.class.table_singular}}\n              {{{renderLive template}}}\n            {{/instance}}\n          {{else}}\n            <div class="show-description">\n              <div class="row-fluid">\n                <div class="span12">\n                  <div class="tree-description short">\n                    {{{instance.description}}}\n                  </div>\n                  {{{show_long}}}\n                </div>\n              </div>\n            </div>\n          {{/find_template}}\n        {{/show}}\n      </lazy-openclose>\n    </div>\n  </div>\n\n</li>\n{{/each}}\n';
GGRC.Templates['wf_people/tree_add_item']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n{{^if_equals parent_instance.kind "Backlog"}}\n{{#if allow_mapping_or_creating}}\n{{#is_allowed_to_map parent_instance model.shortName}}\n  <a\n    href="javascript://"\n    class="section-add btn btn-mini action-button map-button"\n    rel="tooltip"\n    data-placement="left"\n    data-toggle="unified-mapper"\n    data-join-mapping="{{mapping}}"\n    data-join-option-type="{{model.shortName}}"\n    data-join-object-id="{{parent_instance.id}}"\n    data-join-object-type="{{parent_instance.class.shortName}}"\n    data-original-title="Map {{firstnonempty title_singular model.title_singular \'Object\'}} to this {{firstnonempty parent_instance.class.title_singular \'Object\'}}">\n    Map\n  </a>\n{{/is_allowed_to_map}}\n{{/if}}\n{{/if_equals}}\n';
GGRC.Templates['wf_people/people_items']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n{{#each options}}\n  <li class="tree-item task-item" data-object-type="{{instance.class.table_singular}}"{{#instance}}{{ data \'option\' }}{{/instance}} data-id="{{ instance.id }}">\n    <div class="item-main">\n      <div class="item-wrap">\n        <div class="openclose">\n          <span class="status-label status-{{to_class instance.status \'_\'}}"></span>\n          <i class="fa fa-caret-right"></i>\n        </div>\n        <div class="select">\n          <div class="item-data">\n            <div class="row-fluid">\n              <div class="span10">\n                <div class="tree-title-area">\n                  {{#if instance.name}}{{instance.name}}{{/if}}\n                  <span class="url-link">\n                    {{#if instance.email}}{{instance.email}}{{/if}}\n                  </span>\n                  {{#if_equals system_wide_role \'No Access\'}}\n                    <span class="user-disabled">\n                      (Inactive user)\n                    </span>\n                  {{/if_equals}}\n                </div>\n              </div>\n\n              <div class="span1">&nbsp;</div>\n\n              <div class="span1">\n                <div class="object-check" align="left">\n                  {{#if mappings.length}}\n                    {{#result_direct_mappings bindings_compute selected_object}}\n                      <input type="checkbox" value="{{instance.title}}" checked="checked" disabled="true" class="object-check-single">\n                    {{/result_direct_mappings}}\n                  {{else}}\n                    <input type="checkbox" value="{{instance.title}}" {{#if_equals system_wide_role \'No Role\'}}disabled="true"{{/if_equals}} class="object-check-single">\n                  {{/if}}\n                </div>\n              </div>\n            </div>\n          </div><!-- item-data end -->\n        </div><!-- select end -->\n      </div><!-- item-wrap end -->\n    </div><!-- item-main end -->\n\n    {{! #if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n        {{#find_template \'tier2_content\' instance}}\n          {{#instance.class.table_singular}}\n            {{{renderLive template is_in_selector=true}}}\n          {{/instance}}\n        {{else}}\n          <div class="tier-content">\n            <div class="row-fluid wrap-row">\n            {{#instance}}\n              <h6>Email</h6>\n              <span class="person-holder">\n                <a href="/people/{{id}}"><span class="person-tooltip-trigger" data-placement="top">\n                  {{firstnonempty email}}\n                </span></a>\n                {{#if_equals system_wide_role \'No Access\'}}\n                  <span class="user-disabled">\n                    (Inactive user)\n                  </span>\n                {{/if_equals}}\n              </span>\n            {{/instance}}\n            </div>\n          </div>\n        {{/if}}\n      </div>\n    </div>\n    {{!/if}}\n  </li>\n{{/each}}\n';
GGRC.Templates['task_groups/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#using workflow=instance.workflow}}\n  {{#if_equals workflow.status \'Active\'}}\n    <div class="row-fluid wrap-row">\n      <div class="span12">\n          <div class="alert">\n            <button type="button" class="close" data-dismiss="alert">\n              <span aria-hidden="true">&times;</span>\n            </button>\n            <strong>Note:</strong> This workflow is active. Changes you make here will not affect active cycles. They will only affect future recurrences.\n          </div>\n      </div>\n    </div>\n  {{/if_equals}}\n{{/using}}\n\n{{#instance.description}}\n  <div class="row-fluid">\n    <div class="span12">\n      <h6>Description</h6>\n      <div class="tree-description">\n        {{{instance.description}}}\n      </div>\n    </div>\n  </div>\n{{/instance.description}}\n\n<div class="row-fluid">\n  <div class="span12">\n    <h6>Code</h6>\n    <div class="tree-description">\n      {{{instance.slug}}}\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['tasks/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> /static/mustache/base_objects/dropdown_menu_tier2.mustache}}\n\n        <!-- description -->\n        {{#instance.description}}\n        <div class="show-description">\n          <div class="row-fluid">\n            <div class="span12">\n              <div class="tree-description">\n                {{{instance.description}}}\n              </div>\n            </div>\n          </div>\n        </div>\n        {{/instance.description}}\n\n';
GGRC.Templates['workflows/tier2_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{> /static/mustache/base_objects/dropdown_menu_tier2.mustache}}\n\n<div class="tier-content">\n  {{{render \'/static/mustache/base_objects/general_info.mustache\' instance=instance }}}\n  {{{render \'/static/mustache/base_objects/description.mustache\' instance=instance}}}\n\n  <div class="row-fluid wrap-row">\n    <div class="span6">\n      <h6>Frequency</h6>\n      {{#if_equals instance.frequency \'one_time\'}}\n        One time\n      {{else}}\n        {{instance.frequency}}\n      {{/if_equals}}\n    </div>\n    <div class="span6">\n      <h6>Owner</h6>\n      {{#with_mapping \'authorizations\' instance}}\n        {{#each authorizations}}\n          {{#using role=instance.role}}\n            {{#if_equals role.name \'WorkflowOwner\'}}\n              {{#using contact=instance.person}}\n                {{{renderLive \'/static/mustache/people/popover.mustache\' person=contact}}}\n              {{/using}}\n            {{/if_equals}}\n          {{/using}}\n        {{/each}}\n      {{/with_mapping}}\n    </div>\n  </div>\n  <div class="row-fluid wrap-row">\n    <div class="span12">\n      <ggrc-gdrive-folder-picker {{^is_allowed \'update\' instance}}readonly="true"{{/is_allowed}} instance="instance"/>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['wf_objects/approval_modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<form action="javascript://">\n  <div class="row-fluid">\n    <div class="span12">\n      <label>Object for review <i class="fa fa-question-circle" rel="tooltip" title="Title and description of the object"></i></div>\n      <h3>{{#using object=instance.original_object}}{{object.title}}{{/using}}</h3>\n      {{#using object=instance.original_object}}\n        {{{object.description}}}\n      {{/using}}\n    </div>\n  </div>\n  <br>\n  <div class="row-fluid">\n    <div class="span8">\n      <label for="program_description">\n        Assign reviewer\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Reviewer to assign"></i>\n      </label>\n      <div class="people-autocomplete">\n        <input tabindex="2" type="text" name="contact.email" null-if-empty="true" class="span11 search-icon required" value="{{firstnonempty instance.contact.email}}" placeholder="Find reviewer" data-lookup="Person">\n      </div>\n    </div>\n    <div class="span4">\n      <label for="task_group_assignee">\n        <span>Due date</span>\n        <span class="required">*</span>\n        <i class="fa fa-question-circle" rel="tooltip" title="Set review due date"></i>\n      </label>\n\n      <div class="datepicker__wrapper">\n        <i class="fa fa-calendar"></i>\n        <input\n          tabindex="4"\n          class="date input-small"\n          name="end_date"\n          data-toggle="datepicker_today_or_later"\n          placeholder="MM/DD/YYYY"\n          type="text"\n          autocomplete="off"\n          value="{{localize_date instance.end_date}}">\n      </div>\n    </div>\n  </div>\n</form>\n';
GGRC.Templates['cycle_task_group_object_tasks/current_tg_tree_footer']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{^if_equals parent_instance.status \'Final\'}}\n  {{#with_mapping \'current_task_group_objects\' parent_instance}}\n  {{#using cycle_task_group=current_task_group_objects.0.instance.cycle_task_group}}\n    <li class="tree-footer tree-item tree-item-add">\n      <div class="row-fluid">\n        <div class="span12">\n        {{#with_mapping \'owners\' parent_instance}}\n          <a href="javascript://" class="section-create" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="CycleTask" data-object-plural="cycle_task_group_object_tasks" data-object-params=\'{ \\n            "cycle": {{current_task_group_objects.0.instance.cycle.id}}, \\n            "task_group": {{cycle_task_group.task_group.id}}, \\n            "sort_index": "{{sort_index_at_end original_list}}", \\n            "contact": { "id" : {{firstnonempty owners.0.instance.id "null"}} , "type": "Person" }, \\n            "owners": [{ "id" : {{firstnonempty owners.0.instance.id "null"}} , "type": "Person" }], \\n            "context": { "id" : {{firstnonempty cycle_task_group.context.id "null"}} }, \\n            "modal_title": "Add Task" }\'>\n            <strong>Add Task</strong>\n          </a>\n        {{/with_mapping}}\n        </div>\n      </div>\n    </li>\n  {{/with_mapping}}\n  {{/with_mapping}}\n{{/if_equals}}\n';
GGRC.Templates['workflows/clone_modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n<form action="javascript://">\n  <h3>\n    By clicking Proceed below, you will make a new Workflow, owned by you, with the same metadata and Task Groups as "{{source_workflow.title}}"\n  </h3>\n\n  <div class="row-fluid">&nbsp;</div>\n  <div class="row-fluid">\n    <div class="span1">\n      <input type="checkbox" name="clone_tasks" {{#clone_tasks}}checked="checked"{{/clone_tasks}}>\n    </div>\n    <div class="span11">\n      <label for="clone_tasks">Also clone the Tasks in this Workflow\'s Task Groups<label>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span1">\n      <input type="checkbox" name="clone_people" {{#clone_people}}checked="checked"{{/clone_people}}>\n    </div>\n    <div class="span11">\n      <label for="clone_people">Also clone the People in this Workflow (Workflow Owners and Members will also be Members of the new Workflow)<label>\n    </div>\n  </div>\n</form>\n{{/instance}}\n';
GGRC.Templates['task_groups/clone_modal_content']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#instance}}\n<form action="javascript://">\n  <h3>\n    By clicking Proceed below, you will make a new Task Group, owned by you, with the same metadata and Tasks as "{{source_task_group.title}}"\n  </h3>\n\n  <div class="row-fluid">&nbsp;</div>\n  <div class="row-fluid">\n    <div class="span1">\n      <input type="checkbox" name="clone_tasks" {{#clone_tasks}}checked="checked"{{/clone_tasks}}>\n    </div>\n    <div class="span11">\n      <label for="clone_tasks">Also clone the Tasks in this Task Group<label>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span1">\n      <input type="checkbox" name="clone_objects" {{#clone_objects}}checked="checked"{{/clone_objects}}>\n    </div>\n    <div class="span11">\n      <label for="clone_objects">Also clone the Objects in this Task Group<label>\n    </div>\n  </div>\n  <div class="row-fluid">\n    <div class="span1">\n      <input type="checkbox" name="clone_people" {{#clone_people}}checked="checked"{{/clone_people}}>\n    </div>\n    <div class="span11">\n      <label for="clone_people">Also clone the People in this Task Group<label>\n    </div>\n  </div>\n</form>\n{{/instance}}\n';
GGRC.Templates['responses/gdrive_upload_evidence']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{! upload evidence to GDrive by clicking this button}}\n<div style="clear:both">\n\n<div>\n  <br>\n  {{#is_allowed "create" \'ObjectDocument\' context=instance.context.id}}\n    <ggrc-gdrive-folder-picker deferred="true" instance="instance" no_detach="true"/>\n  {{/is_allowed}}\n</div>\n{{#if_helpers \'\\n  #is_allowed\' "create" \'ObjectDocument\' _0_context=instance.context.id \'\\n    and ^if_equals\' instance.status \'Accepted\' \'\\n  or #is_allowed\' \'__GGRC_ADMIN__\' \'Audit\' _2_context=null}}\n<div>\n  <br>\n  <ggrc-gdrive-picker-launcher instance="instance" deferred="true" click_event="trigger_upload_parent">\n  </ggrc-gdrive-picker-launcher>\n</div>\n{{/if_helpers}}\n</div>\n';
GGRC.Templates['responses/gdrive_evidence_tree']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n  <li class="tree-item" data-object-id="{{instance.id}}" data-object-type="{{instance.class.table_singular}}">\n    <div class=\'item-main\' {{data \'model\'}}>\n      <div class="openclose">\n        <a class="openclose-placeholder" href="javascript://"> </a>\n        <div class=\'row-fluid\'>\n          <div class=\'span12\'>\n            <div class=\'item-data\'>\n              <div class=\'tree-title-area\'>\n                <i class="fa fa-{{instance.class.table_singular}} color"></i>\n                {{firstnonempty instance.title instance.link}}\n                <span class="option">\n                </span>\n              </div>\n            </div>\n          </div>\n        </div>\n      </div>\n    </div>\n\n    {{#if expanded}}\n    <div class="tier-2-info item-content">\n      <div class="tier-2-info-content">\n\n        <div class="row-fluid">\n\n          <div class="span6">&nbsp;</div>\n\n          <div class="span6">\n          {{#if mappings}}\n          {{#is_allowed_all \'delete\' mappings}}\n            {{#using request=parent_instance.request}}{{#with_mapping "folders" request}}\n            <a href="javascript://" class="info-action unmap pull-right" data-toggle="modal-ajax-unmapform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-title="Warning" data-modal-class="modal" data-object-id="{{instance.id}}" data-object-params=\'{ "folder": { "title": "{{folders.0.instance.title}}", "link": "{{folders.0.instance.alternateLink}}" } }\' href="javascript://">\n              {{#result}}<span class="result" {{data \'result\'}}></span>{{/result}}\n              <i class="fa fa-ban"></i>\n              Unmap\n            </a>\n            {{else}}{{/with_mapping}}{{/using}}\n          {{/is_allowed_all}}\n          {{/if}}\n          </div>\n\n        </div>\n\n        <div class="row-fluid">\n          <div class="span12">\n            {{#if instance.description}}\n            <div class="tree-description">\n              {{{instance.description}}}\n            </div>\n\n            {{else}}\n              <span class="empty-message">None</span>\n            {{/if}}\n          </div>\n        </div>\n\n\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="tree-info">\n              <strong>GDrive Path: </strong>\n            {{#with_mapping \'files\' instance}}\n              {{#if files.0.instance.selfLink}}\n              <a class="reference" href="{{schemed_url instance.link}}" target="_blank">\n              /{{#using request=parent_instance.request}}{{#with_mapping \'folders\' request}}{{folders.0.instance.title}}{{/with_mapping}}{{/using}}/{{files.0.instance.title}}\n            </a>\n            {{else}}\n              No GDrive file found for this evidence.\n            {{/if}}\n          {{else}}\n            {{! This is a failure state for with_mapping, if something in the mapping doesn\'t refresh properly }}\n            {{#if error.errors}}\n              <small>\n                You need permission to access this file. <a href="https://drive.google.com/folderview?id={{grdive_msg_to_id error.message}}&usp=sharing#">Request access.</a>\n              </small>\n            {{else}}\n              The GDrive file for this evidence could not be accessed.\n              {{#using request=parent_instance.request}}\n                {{{render \'/static/mustache/gdrive/gapi_errors.mustache\' type="file" instance=request error=error}}}\n            {{/if}}\n\n            {{/using}}\n          {{/with_mapping}}\n\n\n              </a>\n            </div>\n          </div>\n        </div>\n\n        <div class="row-fluid">\n          <div class="span12">\n            <div class="tree-info">\n              {{#with_mapping \'files\' instance}}\n                {{#defer "revisions" files.0.instance.findRevisions}}\n                  {{#with revisions.0}}\n                  Added by:\n                  {{lastModifyingUserName}}\n                  on\n                  {{localize_date modifiedDate}}\n                  {{/with}}\n                {{/defer}}\n              {{else}}\n              {{/with_mapping}}\n            </div>\n          </div>\n        </div>\n\n        {{{renderLive \'/static/mustache/base_objects/mappings_detail.mustache\' result=result parent_instance=parent_instance}}}\n        {{#is_allowed \'update\' instance}}\n        <div class="row-fluid">\n          <div class="span12">\n            <a class="utility-link" href="javascript://" data-toggle="modal-ajax-form" data-modal-reset="reset" data-modal-class="modal-wide" data-object-singular="Document" data-object-plural="documents" data-object-id="{{instance.id}}" data-object-params=\'{ "{{parent_instance.class.table_singular}}": { "id" : {{parent_instance.id}}, "title" : "{{parent_instance.title}}" }, "modal_title" : "Edit Evidence" }\'>\n            <i class="fa fa-pencil-square-o"></i>\n            Edit Evidence\n          </a>\n\n          </div>\n        </div>\n        {{/is_allowed}}\n      </div>\n\n      {{#if draw_children}}\n        {{#if child_options.length}}\n          {{#prune_context}} {{! this line is just chopping the context stack down to one element}}\n            <div class="inner-tree">\n              <h6>Mapped Objects ({{child_count}})</h6>\n              {{#child_options}}\n                <ul class="tree-structure new-tree" {{data \'options\'}} {{ (el) -> el.cms_controllers_tree_view(el.data("options")).control("tree_view").display() }}></ul>\n              {{/child_options}}\n            </div>\n          {{/prune_context}}\n        {{/if}}\n      {{/if}}\n\n    </div>\n    {{/if}}\n  </li>\n';
GGRC.Templates['meetings/gcal_info']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  <div class="row-fluid">\n    <div class="span8">\n      <div class="tree-info">\n        <strong>gCal Event Link: </strong>\n        {{#with_mapping \'events\' instance}}\n          {{#if events.length}}\n          <a href="javascript://" class="show-meeting" {{#instance}}{{data \'model\'}}{{/instance}} {{#events.0.instance}}{{data \'cev\'}}{{/events.0.instance}}>\n            {{events.0.instance.summary}}\n          </a>\n          {{else}}\n            <a href="javascript://" class="create-meeting" {{#instance}}{{data \'model\'}}{{/instance}}>\n              Create Google Calendar meeting\n            </a>\n          {{/if}}\n        {{/with_mapping}}\n      </div>\n    </div>\n  </div>\n';
GGRC.Templates['people/gplus_actions']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n  {{#google_plus person}}\n    <div class="profile-contact">\n      <ul>\n        <li>\n          <a rel="tooltip" data-original-title="Start a video call" href="javascript://"><i class="fa fa-video-camera"></i></a>\n        </li>\n        <li>\n          <a rel="tooltip" data-original-title="Chat with this contact" href="javascript://"><i class="fa fa-comment-o"></i></a>\n        </li>\n        <li>\n          <a rel="tooltip" data-original-title="Email this contact" href="javascript://"><i class="fa fa-envelope"></i></a>\n        </li>\n        <li>\n          <a rel="tooltip" data-original-title="Phone call" href="javascript://"><i class="fa fa-phone"></i></a>\n        </li>\n        <li class="circle-holder">\n          <a class="btn btn-mini square-trigger" href="javascript://">Add to Circles <i class="fa fa-caret-down"></i></a>\n          <div class="square-popover">\n            <ul>\n              {{#circles}}\n              <li>\n                <label>\n                  <input type="checkbox">\n                  {{displayName}} <span>{{people.totalItems}}</span>\n                </label>\n              </li>\n              {{/circles}}\n              <li class="create">\n                <a href="javascript://">Create new circle</a>\n              </li>\n            </ul>\n          </div>\n        </li>\n      </ul>\n    </div>\n  {{/google_plus}}\n';
GGRC.Templates['people/gplus_photo']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="span4">\n  {{#google_plus person}}\n    <div class="profile-photo">\n      <img {{#image.url}}src="{{.}}"{{/image.url}} alt="{{displayName}}">\n    </div>\n  {{/google_plus}}\n</div>\n';
GGRC.Templates['gdrive/auth_button']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h2>Please press the button below to launch the Google authorization dialog for these services:</h2>\n<ul>\n{{#scopes}}\n  <li>{{.}}</li>\n{{/scopes}}\n</ul>\n\n<div class="row-fluid">\n  <div class="span4">&nbsp;</div>\n  <div class="span4">\n    <a class="btn" href="javascript://" data-toggle="gapi">Authorize Google API</a>\n  </div>\n  <div class="span4">&nbsp;</div>\n</div>\n';
GGRC.Templates['gdrive/gapi_errors']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n\n  {{#switch error.code}}\n    {{#case 404}}The {{type}} was not found in Drive, or you do not have access to read it.{{/case}}\n    {{#case 403}}You were not permitted to access Drive.{{/case}}\n    {{#case 401}}You are not logged in to Drive.{{/case}}\n    {{#case 500}}Google Drive encountered an internal error.{{/case}}\n    {{#case 400}}Google API didn\'t understand the request from GGRC{{/case}}\n    {{#default}}No error code was returned or the error was not recognized{{/default}}\n  {{/switch}}\n\n  <div class="info info-expand">\n    <a href="javascript://">\n      <span class="out">\n        More\n      </span>\n      <span class="in">\n        Hide\n      </span>\n       Error Info\n    </a>\n    <div class="error-container hidden-fields-area in">\n      {{error.message}} <strong>(Code {{error.code}})</strong>\n    </div>\n  </div>\n\n  {{#if_auditor instance}}\n    <div>\n      {{#switch error.code}}\n        {{#case 404}}\n          {{#if_instance_of instance \'Request\'}}\n            Contact your Audit Lead ({{#using audit=instance.audit}}{{#using assignee=audit.contact}}<a href="mailto:{{assignee.email}}">{{assignee.email}}</a>{{/using}}{{/using}}) to request access.\n          {{else}}\n            Contact your Audit Lead ({{#using assignee=instance.contact}}<a href="mailto:{{assignee.email}}">{{assignee.email}}</a>{{/using}}) to request access.\n          {{/if_instance_of}}\n        {{/case}}\n        {{#case 403}}\n          {{#if_instance_of instance \'Request\'}}\n            Contact your Audit Lead ({{#using audit=instance.audit}}{{#using assignee=audit.contact}}<a href="mailto:{{assignee.email}}">{{assignee.email}}</a>{{/using}}{{/using}}) to request access.\n          {{else}}\n            Contact your Audit Lead ({{#using assignee=instance.contact}}<a href="mailto:{{assignee.email}}">{{assignee.email}}</a>{{/using}}) to request access.\n          {{/if_instance_of}}      \n        {{/case}}\n        {{#case 401}}\n          Try reloading this page. If you see a popup for Google API, make sure to click the Authorize buttons\n        {{/case}}\n        {{#case 500}}\n          Try again later to see if the problem resolves.\n        {{/case}}\n        {{#case 400}}\n          Please contact your Administrators (<a href="mailto:{{ggrc_config_value \'GAPI_ADMIN_GROUP\'}}">{{ggrc_config_value \'GAPI_ADMIN_GROUP\'}}</a>) and report this problem.\n        {{/case}}\n        {{#default}}\n          Try again later to see if the problem resolves.\n        {{/default}}\n      {{/switch}}  \n    </div>\n  {{/if}}\n';
GGRC.Templates['gdrive/gdrive_folder']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid wrap-row">\n  <div class="span12 small-info">\n    <label class="multi-type-label">\n      {{instance.class.title_singular}} Folder\n      <label class="inline">\n        <i class="fa fa-question-circle" rel="tooltip" data-original-title="If selected, all {{instance.class.title_singular}} attachments go here."></i>\n      </label>\n      <a data-id="hide_folder_lk" href="javascript://" class="field-hide" tabindex="-1">hide</a>\n    </label>\n\n    <spinner toggle="_folder_change_pending"></spinner>\n\n    {{#if current_folder}}\n      <div class="oneline">\n        <a href="{{current_folder.alternateLink}}" target="_blank">\n          <i class="fa fa-paperclip attach-blue"></i> {{current_folder.title}}\n        </a>\n        {{^if readonly}}\n        <a class="icon-link entry-attachment" href="javascript://" rel="tooltip" data-original-title="{{firstnonempty placeholder \'Choose a new folder\'}}" data-toggle="gdrive-picker" data-model="{{instance.class.model_singular}}" data-id="{{instance.id}}" data-type="folders" data-replace="true">\n          <i class="fa fa-pencil-square-o"></i>\n        </a>\n        {{^if no_detach}}\n        <a class="icon-link" href="javascript://" rel="tooltip" data-original-title="Detach folder from {{instance.class.title_singular}}" data-toggle="gdrive-remover"\n          data-model="{{instance.class.model_singular}}" data-id="{{instance.id}}">\n          <i class="fa fa-trash"></i>\n        </a>\n        {{/if}}\n        {{/if}}\n      </div>\n      {{else}}\n        {{#if folder_error}}\n          <small>\n            <strong>Warning:</strong> You need permission to access {{instance.class.title_singular}} folder. <a href="https://drive.google.com/folderview?id={{grdive_msg_to_id folder_error.message}}&usp=sharing#">Request access.</a>\n          </small>\n          {{^if readonly}}\n          <a class="icon-link entry-attachment" href="javascript://" rel="tooltip" data-original-title="{{firstnonempty placeholder \'Choose a new folder\'}}" data-toggle="gdrive-picker" data-model="{{instance.class.model_singular}}" data-id="{{instance.id}}" data-type="folders" data-replace="true">\n            <i class="fa fa-pencil-square-o"></i>\n          </a>\n          {{^if no_detach}}\n          <a class="icon-link" href="javascript://" rel="tooltip" data-original-title="Detach folder from {{instance.class.title_singular}}" data-toggle="gdrive-remover"\n            data-model="{{instance.class.model_singular}}" data-id="{{instance.id}}">\n            <i class="fa fa-trash"></i>\n          </a>\n          {{/if}}\n          {{/if}}\n        {{else}}\n          <span class="error">\n            No folder for this {{instance.class.title_singular}}.\n          </span>\n          {{^if readonly}}\n          <a href="javascript://"\n            tabindex="{{firstnonempty tabindex \'0\'}}"\n            class="entry-attachment tree-item-add"\n            data-toggle="gdrive-picker"\n            data-model="{{instance.class.model_singular}}"\n            data-type="folders"\n            data-id="{{instance.id}}">\n              <i class="fa fa-paperclip attach-blue"></i> Assign folder\n          </a>\n          {{/if}}\n        {{/if}}\n    {{/if}}\n  </div>\n</div>\n';
GGRC.Templates['gdrive/gdrive_file']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<a href="javascript://"\n    class="{{link_class}} btn-small btn-draft\n           {{#if pickerActive}}disabled{{/if}}"\n    data-object-source="true"\n    data-toggle="evidence-gdrive-picker"\n    can-click="{{click_event}}">\n    <i class="fa fa-{{firstexist icon \'paperclip\'}}"></i>\n    {{firstexist link_text "Upload Files to GDrive"}}\n\n    <spinner toggle="pending" />\n</a>\n';
