GGRC.Templates['roles/gdrive_option_detail']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#if_equals name "Administrator"}}\n  <p>\n  <strong>Note</strong>: This role does not automatically confer Admin access to gDrive resources.\n    Please ensure that all Administrator users are also members of <span style="white-space: nowrap">{{ggrc_config_value "GAPI_ADMIN_GROUP"}}</span>\n  </p>\n{{/if_equals}}\n';
GGRC.Templates['audits/gdrive_folder_picker']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid">\n  <div data-id="folder_hidden" class="span6 hidable">\n    <ggrc-gdrive-folder-picker instance="instance" deferred="true" tabindex="13"></ggrc-gdrive-folder-picker>\n  </div>\n</div>\n';
GGRC.Templates['workflows/gdrive_folder_picker']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid">\n  <div data-id="workflow_folder_hidden" class="span8 hidable">\n    <ggrc-gdrive-folder-picker instance="instance" deferred="true" tabindex="6" placeholder="If selected, all workflow attachments go here."/>\n  </div>\n</div>\n';
GGRC.Templates['requests/gdrive_evidence_storage']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#with_mapping \'extended_folders\' instance}}\n  <div class="oneline">\n    {{#if_in instance.status "Completed,Verified"}}\n      {{#if extended_folders.length}}\n        <ggrc-gdrive-picker-launcher\n          instance="instance"\n          link_text="Attach evidence"\n          click_event="trigger_upload_parent"\n          verify_event="true"\n          modal_description=\'You are about to move request from "{{instance.status}}" to "In Progress" - are you sure about that?\'\n          modal_title=\'Confirm moving Request to "In Progress"\'\n          modal_button=\'Confirm\'\n          >\n        </ggrc-gdrive-picker-launcher>\n      {{else}}\n        <ggrc-gdrive-picker-launcher\n          instance="instance"\n          link_text="Attach evidence"\n          click_event="trigger_upload"\n          verify_event="true"\n          modal_description=\'You are about to move request from "{{instance.status}}" to "In Progress" - are you sure about that?\'\n          modal_title=\'Confirm moving Request to "In Progress"\'\n          modal_button=\'Confirm\'\n          >\n        </ggrc-gdrive-picker-launcher>\n        <i class="fa fa-exclamation-triangle red" style="margin-top:4px" rel="tooltip" data-placement="bottom" data-original-title="Audit folder not set. Files will be uploaded to your GDrive root folder."></i>\n      {{/if}}\n    {{else}}\n      {{#if extended_folders.length}}\n        <ggrc-gdrive-picker-launcher\n          icon="paperclip"\n          instance="instance"\n          link_text="Attach evidence"\n          click_event="trigger_upload_parent">\n        </ggrc-gdrive-picker-launcher>\n      {{else}}\n        <ggrc-gdrive-picker-launcher\n          icon="paperclip"\n          instance="instance"\n          link_text="Attach evidence"\n          click_event="trigger_upload">\n        </ggrc-gdrive-picker-launcher>\n        <i class="fa fa-exclamation-triangle red" style="margin-top:4px" rel="tooltip" data-placement="bottom" data-original-title="Audit folder not set. Files will be uploaded to your GDrive root folder."></i>\n      {{/if}}\n    {{/if_in}}\n  </div>\n{{else}}\n  {{! This is a failure state for with_mapping, if something in the mapping doesn\'t refresh properly }}\n  {{#if error.errors}}\n    <small>\n      You need permission to upload files to the audit folder. <a href="https://drive.google.com/folderview?id={{grdive_msg_to_id error.message}}&usp=sharing#">Request access.</a>\n    </small>\n  {{else}}\n    The GDrive folder for this evidence could not be accessed.\n    {{#using request=parent_instance.request}}\n      {{{render \'/static/mustache/gdrive/gapi_errors.mustache\' type="file" instance=request error=error}}}\n    {{/using}}\n  {{/if}}\n{{/with_mapping}}\n';
GGRC.Templates['requests/gdrive_comment_attachment']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n{{#with_mapping \'extended_folders\' parent_instance}}\n  {{#if extended_folders.length}}\n    <ggrc-gdrive-picker-launcher\n      icon="paperclip"\n      instance="instance"\n      folder_instance="parent_instance"\n      link_text="Attach evidence"\n      deferred="true"\n      click_event="trigger_upload_parent">\n    </ggrc-gdrive-picker-launcher>\n  {{else}}\n    <ggrc-gdrive-picker-launcher\n      icon="paperclip"\n      instance="instance"\n      link_text="Attach evidence"\n      deferred="true"\n      click_event="trigger_upload">\n    </ggrc-gdrive-picker-launcher><i class="fa fa-exclamation-triangle red" style="margin-top:4px" rel="tooltip" data-placement="bottom" data-original-title="Audit folder not set. Files will be uploaded to your GDrive root folder."></i>\n  {{/if}}\n{{else}}\n  {{! This is a failure state for with_mapping, if something in the mapping doesn\'t refresh properly }}\n  {{#if error.errors}}\n    <small>\n      You need permission to upload files to the audit folder.\n      <a href="https://drive.google.com/folderview?id={{grdive_msg_to_id error.message}}&amp;usp=sharing#">Request access.</a>\n    </small>\n  {{else}}\n    The GDrive folder for this evidence could not be accessed.\n    {{#using request=parent_instance.request}}\n      {{{render \'/static/mustache/gdrive/gapi_errors.mustache\' type="file" instance=request error=error}}}\n    {{/using}}\n  {{/if}}\n{{/with_mapping}}\n';
