GGRC.Templates['mockup_base_templates/info_panel/header']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="pane-header">\n  <div class="row-fluid wrap-row">\n    <div class="span9">\n      <h6>Title</h6>\n      <h3>{{info_title}}</h3>\n      {{#state}}\n      <span class="state-value state-{{class_name}}">{{title}}</span>\n      {{/state}}\n      <span class="gray">\n        (Documentation)\n      </span>\n    </div>\n    <div class="span3">\n      <div class="info-pane-utility">\n        <div class="details-wrap">\n          <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n            <span class="bubble"></span>\n          </a>\n          <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n            {{#is_allowed \'update\' instance context=\'for\'}}\n              {{> /static/mustache/base_objects/edit_object_link.mustache}}\n            {{/is_allowed}}\n            <li>\n              <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n            </li>\n\n            {{#is_info_pin}}\n              {{#is_allowed_to_map page_instance instance}}\n                {{^options.is_in_selector}}\n                  {{> /static/mustache/base_objects/unmap.mustache}}\n\n                {{/options}}\n              {{/is_allowed_to_map}}\n              {{#if instance.viewLink}}\n                {{#is_allowed "view_object_page" instance}}\n                  <li>\n                    <a href="{{instance.viewLink}}">\n                      <i class="fa fa-long-arrow-right"></i>\n                      View {{instance.class.title_singular}}\n                    </a>\n                  </li>\n                {{/is_allowed}}\n              {{/if}}\n            {{/is_info_pin}}\n\n            {{#is_allowed \'delete\' instance}}\n              <li>\n                <a data-toggle="modal-ajax-deleteform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n                  <i class="fa fa-trash"></i>\n                  Delete\n                </a>\n              </li>\n            {{/is_allowed}}\n\n          </ul>\n        </div>\n\n        <a href="javascript://" class="btn-info-pin-header btn btn-small btn-primary pull-right">Complete</a>\n      </div>\n    </div>\n  </div>\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/info_panel_utility']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="info-pane-utility">\n  <div class="details-wrap">\n    <a class="btn btn-draft dropdown-toggle" href="#" data-toggle="dropdown">\n      <span class="bubble"></span>\n      <span class="bubble"></span>\n      <span class="bubble"></span>\n    </a>\n    <ul class="dropdown-menu" aria-labelledby="drop1" role="menu">\n      {{#is_allowed \'update\' instance context=\'for\'}}\n        <li>\n          <a href="#WorkflowSetup" data-toggle="modal">\n            <i class="fa fa-pencil-square-o"></i>\n            Edit Workflow\n          </a>\n        </li>\n      {{/is_allowed}}\n      <li>\n        <clipboard-link title="Get permalink" notify="true" text="{{get_permalink_url}}" />\n      </li>\n\n      {{#is_info_pin}}\n        {{#is_allowed_to_map page_instance instance}}\n          {{^options.is_in_selector}}\n            {{> /static/mustache/base_objects/unmap.mustache}}\n\n          {{/options}}\n        {{/is_allowed_to_map}}\n        {{#if instance.viewLink}}\n          {{#is_allowed "view_object_page" instance}}\n            <li>\n              <a href="{{instance.viewLink}}">\n                <i class="fa fa-long-arrow-right"></i>\n                View {{instance.class.title_singular}}\n              </a>\n            </li>\n          {{/is_allowed}}\n        {{/if}}\n      {{/is_info_pin}}\n\n      {{#is_allowed \'delete\' instance}}\n        <li>\n          <a data-toggle="modal-ajax-deleteform" data-object-plural="{{model.table_plural}}" data-object-singular="{{model.model_singular}}" data-modal-reset="reset" data-modal-class="modal" data-object-id="{{instance.id}}" href="javascript://">\n            <i class="fa fa-trash"></i>\n            Delete\n          </a>\n        </li>\n      {{/is_allowed}}\n\n    </ul>\n  </div>\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/description']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Description</h6>\n<div class="rtf-block">\n  {{description}}\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/mapped_objects']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Mapped objects</h6>\n<ul class="attachment-list">\n  {{#each mapped.objects}}\n    <li>\n      <span class="file-controls">\n        <a href="javascript://" class="info-action unmap" data-toggle="unmap">\n          <i class="fa fa-trash"></i>\n        </a>\n      </span>\n      <i class="fa fa-{{icon}} color"></i>\n      {{title}}\n      <a class="more" href="javascript://" target="_blank"><i class="fa fa-long-arrow-right"></i></a>\n    </li>\n  {{/each}}\n</ul>\n<a class="btn btn-draft btn-small" href="javascript://">\n  Map objects\n</a>\n';
GGRC.Templates['mockup_base_templates/info_panel/people_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="side-content">\n  <ul class="label-list">\n    {{#each people}}\n    <li>\n      <label>\n        <span class="file-controls">\n          <a href="javascript://">\n            <i class="fa fa-plus-circle"></i>\n          </a>\n        </span>\n        <span class="person-label {{@key}}"></span>\n        {{@key}}\n      </label>\n      <ul>\n        {{#each .}}\n          <li>\n            <span class="file-controls">\n              <a href="javascript://"><i class="fa fa-trash"></i></a>\n            </span>\n            <a href="javascript://">{{name}}</a>\n          </li>\n        {{/each}}\n      </ul>\n    </li>\n    {{/each}}\n  </ul>\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/task_people_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="side-content">\n  <ul class="label-list">\n    {{#each task_people}}\n    <li>\n      <label>\n        <span class="file-controls">\n          <a href="javascript://">\n            <i class="fa fa-plus-circle"></i>\n          </a>\n        </span>\n        <span class="person-label {{@key}}"></span>\n        {{@key}}\n      </label>\n      <ul>\n        {{#each .}}\n          <li>\n            <span class="file-controls">\n              <a href="javascript://"><i class="fa fa-trash"></i></a>\n            </span>\n            <a href="javascript://">{{name}}</a>\n          </li>\n        {{/each}}\n      </ul>\n    </li>\n    {{/each}}\n  </ul>\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/dates_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<h6>Dates</h6>\n<div class="side-content">\n  <ul class="label-list dates">\n    <li class="gray">\n      <label>\n        {{#if_equals type \'task\'}}\n          Start date\n        {{else}}\n          Created on\n        {{/if_equals}}\n      </label>\n      {{created_on}}\n    </li>\n    <li>\n      <span class="file-controls">\n        <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n      </span>\n      <label>\n        {{#if_equals type \'task\'}}\n          End date\n        {{else}}\n          Due on\n        {{/if_equals}}\n      </label>\n      {{due_on}}\n    </li>\n  </ul>\n</div>\n';
GGRC.Templates['mockup_base_templates/info_panel/comment_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="entry-list">\n  {{#each comments}}\n    <li>\n      <span class="person-label {{author.type}}"></span>\n      <div class="w-status">\n        <span class="entry-author">\n          {{#author}}\n            <a href="javascript://">{{name}}</a>\n          {{/author}}\n          added a response - {{timestamp}}\n        </span>\n        {{comment}}\n        <ul class="attachment-list">\n          {{#attachments}}\n            <li>\n              <i class="fa fa-file{{#icon}}-{{icon}}{{/icon}}"></i>\n              <a href="{{url}}" target="_blank">\n                {{name}}\n              </a>\n            </li>\n          {{/attachments}}\n        </ul>\n      </div>\n    </li>\n  {{/each}}\n</ul>\n';
GGRC.Templates['mockup_base_templates/info_panel/log_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="entry-list">\n  {{#logs}}\n    <li>\n      <span class="person-label {{author.type}}"></span>\n      <div class="w-status">\n        {{#data}}\n        <span class="entry-author">\n          {{#author}}\n            <a href="javascript://">{{name}}</a>\n          {{/author}}\n          {{status}} - {{timestamp}}\n        </span>\n        <div class="clearfix">\n          <div class="third-col">\n            <p class="instruction">Field</p>\n            {{field}}\n          </div>\n          <div class="third-col">\n            <p class="instruction">Original value</p>\n            {{#original}}\n              {{text}}\n              <ul class="attachment-list">\n                {{#files}}\n                <li class="oneline">\n                  <i class="fa fa-file{{#icon}}-{{icon}}{{/icon}}"></i>\n                  {{name}}\n                </li>\n                {{/files}}\n              </ul>\n            {{/original}}\n          </div>\n          <div class="third-col">\n            <p class="instruction">New value</p>\n            {{#changed}}\n              {{#author}}\n                {{name}}\n              {{/author}}\n              {{text}}\n              <ul class="attachment-list">\n                {{#files}}\n                <li class="oneline">\n                  <i class="fa fa-file{{#icon}}-{{icon}}{{/icon}}"></i>\n                  {{name}}\n                </li>\n                {{/files}}\n              </ul>\n            {{/changed}}\n          </div>\n        </div>\n      </div>\n      {{/data}}\n    </li>\n  {{/logs}}\n</ul>\n';
GGRC.Templates['mockup_base_templates/info_panel/past_items_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="past-items-list">\n  <li class="clearfix list-title">\n    <div class="mini-col">\n      <h6>Date</h6>\n    </div>\n    <div class="xmedium-col">\n      <h6>Title</h6>\n    </div>\n    <div class="small-col">\n      <h6>Mapped objects</h6>\n    </div>\n    <div class="medium-col">\n      <h6>Evidence</h6>\n    </div>\n    <div class="mini-col">\n      <a href="javascript://" can-click="reuseIt" class="btn {{#disableReuse}}disabled{{/disableReuse}} btn-small btn-full btn-draft">Reuse</a>\n    </div>\n  </li>\n  {{#past_requests}}\n    <li class="clearfix">\n      <div class="mini-col">\n        {{timestamp}}\n      </div>\n      <div class="xmedium-col">\n        <a target="_blank" href="javascript://">\n          {{title}}\n        </a>\n      </div>\n      <div class="small-col">\n        <ul class="mapped-list">\n          {{#each mapped.objects}}\n            <li>\n              <i class="fa fa-{{icon}} color"></i>\n              <span class="person-holder">\n                <a href=\'mailto:{{email}}\' target="_blank">\n                  <span class="person-tooltip-trigger" data-placement="top">\n                    {{title}}\n                  </span>\n                </a>\n\n                <div class="custom-popover-content">\n                  <div class="preview-container person-preview">\n                    <div class="row-fluid">\n                      <div class="span12">\n                        <div class="profile-info">\n                          <div class="row-fluid wrap-row">\n                            <div class="span8">\n                              <h6>Title</h6>\n                              <h5 class="oneline"><i class="fa fa-{{icon}} color"></i> {{title}}</h5>\n                            </div>\n                            <div class="span4">\n                              <h6>Status</h6>\n                              {{state}}\n                            </div>\n                          </div>\n                          <div class="row-fluid wrap-row">\n                            <div class="span12">\n                              <h6>Description</h6>\n                              {{description}}\n                            </div>\n                          </div>\n\n                          {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n\n                        </div>\n                      </div>\n                    </div>\n                  </div>\n                </div>\n              </span>\n            </li>\n          {{/each}}\n        </ul>\n      </div>\n      <div class="large-col">\n        <ul class="attachment-list">\n          {{#each files}}\n            <li>\n              <label>\n                <i class="fa fa-file{{#icon}}-{{icon}}{{/icon}}"></i>\n                {{name}}\n              </label>\n              <input type="checkbox" class="js-trigger-pastfile" {{{data "item"}}}>\n            </li>\n          {{/files}}\n        </ul>\n      </div>\n    </li>\n  {{/past_requests}}\n</ul>\n';
