GGRC.Templates['mockup_base_templates/info_panel/requests_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="mapped-list left-spacing">\n  {{#each mapped.requests}}\n    <li>\n      <span class="file-controls">\n        <a href="javascript://"><i class="fa fa-ban"></i></a>\n        <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n      </span>\n      <i class="fa fa-{{icon}} color"></i>\n      <span class="person-holder">\n        <a href=\'{{url}}\' target="_blank">\n          <span class="person-tooltip-trigger" data-placement="top">\n            {{title}}\n          </span>\n        </a>\n\n        <div class="custom-popover-content">\n          <div class="preview-container person-preview">\n            <div class="row-fluid">\n              <div class="span12">\n                <div class="profile-info">\n                  <div class="row-fluid wrap-row">\n                    <div class="span8">\n                      <h6>Title</h6>\n                      <h5 class="oneline"><i class="fa fa-{{icon}} color"></i> {{title}}</h5>\n                    </div>\n                    <div class="span4">\n                      <h6>Status</h6>\n                      {{state}}\n                    </div>\n                  </div>\n                  <div class="row-fluid wrap-row">\n                    <div class="span12">\n                      <h6>Description</h6>\n                      {{description}}\n                    </div>\n                  </div>\n\n                  {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n      </span>\n    </li>\n  {{/each}}\n</ul>\n<a href="#" class="btn btn-small btn-primary">Request information</a>\n';
GGRC.Templates['mockup_base_templates/info_panel/issues_list']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<ul class="mapped-list left-spacing">\n  {{#each mapped.issues}}\n    <li>\n      <span class="file-controls">\n        <a href="javascript://"><i class="fa fa-ban"></i></a>\n        <a href="javascript://"><i class="fa fa-pencil-square-o"></i></a>\n      </span>\n      <i class="fa fa-{{icon}}-color"></i>\n      <span class="person-holder">\n        <a href=\'{{url}}\' target="_blank">\n          <span class="person-tooltip-trigger" data-placement="top">\n            {{title}}\n          </span>\n        </a>\n\n        <div class="custom-popover-content">\n          <div class="preview-container person-preview">\n            <div class="row-fluid">\n              <div class="span12">\n                <div class="profile-info">\n                  <div class="row-fluid wrap-row">\n                    <div class="span8">\n                      <h6>Title</h6>\n                      <h5 class="oneline"><i class="fa fa-{{icon}}-color"></i> {{title}}</h5>\n                    </div>\n                    <div class="span4">\n                      <h6>Status</h6>\n                      {{state}}\n                    </div>\n                  </div>\n                  <div class="row-fluid wrap-row">\n                    <div class="span12">\n                      <h6>Description</h6>\n                      {{description}}\n                    </div>\n                  </div>\n\n                  {{{render \'/static/mustache/base_objects/contacts.mustache\' instance=instance}}}\n\n                </div>\n              </div>\n            </div>\n          </div>\n        </div>\n      </span>\n    </li>\n  {{/each}}\n</ul>\n<a href="#" class="btn btn-small btn-danger">Raise an issue</a>\n';
GGRC.Templates['mockup_base_templates/info_panel/assessment_custom']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="row-fluid wrap-row">\n  <div class="span3">\n    <h6>Contract Length</h6>\n    <select class="input-block-level">\n      <option value="">-- Choose --</option>\n      <option value="">1 year</option>\n      <option value="">2 years</option>\n      <option value="">3 or more years</option>\n    </select>\n  </div>\n  <div class="span3">\n    <h6># of employees</h6>\n    <select class="input-block-level">\n      <option value="">-- Choose --</option>\n      <option value="">1-2</option>\n      <option value="">2-5</option>\n      <option value="">5-10</option>\n      <option value="">10 or more</option>\n    </select>\n  </div>\n  <div class="span3">\n    <h6>Insured business?</h6>\n    <select class="input-block-level">\n      <option value="">Yes</option>\n      <option value="">No</option>\n    </select>\n  </div>\n  <div class="span3">\n    <h6>Provisioned employees</h6>\n    <select class="input-block-level">\n      <option value="">1</option>\n      <option value="">2</option>\n    </select>\n  </div>\n</div>\n';
