GGRC.Templates['mockup_base_templates/object_setup/fields_visibility_control']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<span class="state-value state-draft">Draft</span>\n<div class="dropdown-wrap">\n  <a href="#" class="btn btn-small btn-draft dropdown-toggle" tabindex="36" data-toggle="dropdown">\n    Show more\n    <i class="fa fa-caret-down"></i>\n  </a>\n  <div class="dropdown-menu dropdown-menu-form">\n    <a class="pull-right close-dropdown"><i class="fa fa-times black"></i></a>\n    <h5>Selectively show or hide all optional fields</h5>\n    <ul class="attr-list">\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="all" class="notify-wrap attr-checkbox all">\n          All\n        </label>\n      </li>\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="workflow_description" class="notify-wrap attr-checkbox" checked>\n          Workflow Description\n        </label>\n      </li>\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="workflow_custom" class="notify-wrap attr-checkbox">\n          Custom "Start Workflow" email message\n        </label>\n      </li>\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="task_description" class="notify-wrap attr-checkbox">\n          Task Description\n        </label>\n      </li>\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="task_type" class="notify-wrap attr-checkbox">\n          Task Type\n        </label>\n      </li>\n      <li>\n        <label class="checkbox-inline">\n          <input type="checkbox" value="task_group" class="notify-wrap attr-checkbox">\n          Task group\n        </label>\n      </li>\n    </ul>\n  </div>\n</div>\n';
