GGRC.Templates['mockup_base_templates/object_setup/repeating_block']= '{{!\n    Copyright (C) 2016 Google Inc.\n    Licensed under http://www.apache.org/licenses/LICENSE-2.0 <see LICENSE file>\n}}\n\n<div class="repeated-block info">\n  <div class="close-block hidden">\n    <a class="close-trigger" href="javascript://">\n      <i class="fa fa-trash"></i>\n    </a>\n  </div>\n  <div class="row-fluid">\n    <div class="span6">\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          <h6>{{type}} title</h6>\n          {{#title}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/object_input.mustache}}\n            {{else}}\n              <p>{{value}}</p>\n            {{/if}}\n          {{/title}}\n        </div>\n      </div>\n      <div class="row-fluid wrap-row">\n        <div class="span12 hidable hidden" data-value="task_description">\n          <h6>Description</h6>\n          {{#description}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/object_textarea.mustache}}\n            {{else}}\n              <p>{{value}}</p>\n            {{/if}}\n          {{/description}}\n        </div>\n      </div>\n      <div class="row-fluid wrap-row">\n        <div class="span12 hidable hidden" data-value="task_type">\n          <div class="wrap-row">\n            <h6>{{type}} type</h6>\n            {{#task_type}}\n              {{#if editable}}\n                {{> /static/mustache/mockup_base_templates/object_setup/object_select_type.mustache}}\n              {{else}}\n                <p>{{option}}</p>\n              {{/if}}\n            {{/task_type}}\n          </div>\n\n          {{#task_type}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/type_fields.mustache}}\n            {{else}}\n              <div class="wrap-row text-wrap">\n                <h6>{{type}} Notes</h6>\n                <p>{{value}}</p>\n              </div>\n            {{/if}}\n          {{/task_type}}\n        </div>\n      </div>\n      <div class="row-fluid">\n        <div class="span12">\n          {{> /static/mustache/mockup_base_templates/info_panel/mapped_objects.mustache}}\n        </div>\n      </div>\n    </div>\n    <div class="span2">\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          <h6>Start Date</h6>\n          {{#start_date}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/object_input.mustache}}\n            {{else}}\n              <p>{{value}}</p>\n            {{/if}}\n          {{/start_date}}\n        </div>\n      </div>\n      <div class="row-fluid wrap-row">\n        <div class="span12">\n          <h6>End Date</h6>\n          {{#end_date}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/object_input.mustache}}\n            {{else}}\n              <p>{{value}}</p>\n            {{/if}}\n          {{/end_date}}\n        </div>\n      </div>\n      <div class="row-fluid">\n        <div class="span12 hidable hidden" data-value="task_group">\n          <h6>{{type}} Group</h6>\n          {{#group}}\n            {{#if editable}}\n              {{> /static/mustache/mockup_base_templates/object_setup/object_input.mustache}}\n            {{else}}\n              <p>{{value}}</p>\n            {{/if}}\n          {{/group}}\n        </div>\n      </div>\n    </div>\n    <div class="span4">\n      <div class="row-fluid">\n        <div class="span12">\n          <h6>People</h6>\n          {{> /static/mustache/mockup_base_templates/info_panel/task_people_list.mustache}}\n        </div>\n      </div>\n    </div>\n  </div>\n</div>\n';
